            { PBEAbility.Drizzle, new PBELocalizedString("The Pokémon makes it rain\nif it appears in battle.", "Le Pokémon invoque la pluie\nquand il entre au combat.", "Ruft im Kampf\nRegen herbei.", "Quando scende in campo,\nil Pokémon attira pioggia.", "せんとうに　でると\nあめを　ふらす。", "戦闘に　でると\n雨を　降らす。", "배틀에 나가면\n비를 내린다.", "Hace que llueva cuando\nentra en combate.") },
            { PBEAbility.Drought, new PBELocalizedString("Turns the sunlight harsh\nif it is in battle.", "Intensifie les rayons du\nsoleil pendant le combat.", "Erzeugt im Kampf\ngleißendes Sonnenlicht.", "Aumenta l'intensità dei\nraggi solari nella lotta.", "せんとうに　でると\nひざしが　つよくなる。", "戦闘に　でると\n日差しが　強くなる。", "배틀에 나가면\n햇살이 강해진다.", "El sol sale si el Pokémon\nentra en combate.") },
            { PBEAbility.DrySkin, new PBELocalizedString("Reduces HP if it is hot.\nWater restores HP.", "Perd des PV à la chaleur.\nL'eau les restaure.", "Heißes Wetter reduziert\nKP. Wasser steigert KP.", "Riduce PS se fa caldo.\nL'acqua ridà PS.", "あついと　ＨＰが　へる。\nみずで　ＨＰを　かいふく。", "あついと　ＨＰが　減る。\nみずで　ＨＰを　回復。", "더우면 HP가 줄어든다.\n물로 HP를 회복한다.", "Pierde PS si hace calor.\nLos recupera con agua.") },
            { PBEAbility.EarlyBird, new PBELocalizedString("The Pokémon awakens\nquickly from sleep.", "Le Pokémon se réveille\nplus rapidement.", "Schnelles Aufwachen.", "Il Pokémon si risveglia\nrapidamente dal sonno.", "おきるのが　はやくなる。\n", "起きるのが　早くなる。\n", "잠듦 상태에서\n빨리 깨어난다.", "El Pokémon se despierta\nrápidamente.") },
            { PBEAbility.EffectSpore, new PBELocalizedString("Contact may poison or\ncause paralysis or sleep.", "Peut paralyser, empoison-\nner, endormir au contact.", "Bei Kontakt evtl. Paraly.,\nVergiftg. oder Schlaf.", "Al contatto può causare\nveleno, paralisi o sonno.", "ふれると　どく　まひ\nねむりに　することがある。", "触れると　どく　まひ\nねむりに　することがある。", "스치면 독, 마비, 잠듦\n상태가 될 때가 있다.", "Puede dormir, envenenar\no paralizar al contacto.") },
            { PBEAbility.Filter, new PBELocalizedString("Reduces damage from\nsupereffective attacks.", "Affaiblit les capacités\n“super efficaces\".", "Senkt die Stärke sehr\neffektiver Attacken.", "Riduce la potenza delle\nmosse superefficaci.", "こうかばつぐん　のとき\nいりょくを　よわめる。", "効果バツグン　のとき\n威力を　弱める。", "효과가 굉장한 기술의\n위력을 약하게 한다.", "Mitiga los movimientos\nsupereficaces.") },
            { PBEAbility.FlameBody, new PBELocalizedString("Contact with the Pokémon\nmay burn the attacker.", "Peut brûler l'ennemi s'il y\na contact.", "Verbrennt den Angreifer\neventuell bei Berührung.", "Il contatto subito può\nscottare il nemico.", "ふれた　あいてを　やけど\nさせることがある。", "触れた　相手を　やけど\nさせることがある。", "접촉한 상대에게\n화상을 입힐 때가 있다.", "Puede quemar al mínimo\ncontacto.") },
            { PBEAbility.FlareBoost, new PBELocalizedString("Powers up special attacks\nwhen burned.", "Booste les att. spéciales\nsi le statut est brûlure.", "Verstärkt Spezial-Att. im\nFall von Verbrennungen.", "Aumenta l'Att. Sp. se il\nPokémon è scottato.", "やけどのとき　とくしゅの\nいりょくが　あがる。", "やけどのとき　特殊の\n威力が　あがる。", "화상 상태일 때 특수\n공격의 위력이 올라간다.", "Potencia el Ataque\nEspecial al ser quemado.") },
            { PBEAbility.FlashFire, new PBELocalizedString("It powers up Fire-type\nmoves if it's hit by one.", "Booste capacités Feu si\ntouché par attaque Feu.", "Verstärkt Feuer-A., wenn\nv. Feuer-A. getroffen.", "Potenzia le proprie mosse\nfuoco se ne subisce una.", "ほのおを　うけると\nほのおわざが　つよくなる。", "ほのおを　受けると\nほのお技が　強くなる。", "불꽃을 받으면\n불꽃 기술이 강해진다.", "Potencia movs. de tipo\nFuego si sufre uno antes.") },
            { PBEAbility.FlowerGift, new PBELocalizedString("Powers up party Pokémon\nwhen it is sunny.", "Pokémon de l'équipe plus\npuissants au soleil.", "Steigert Effektivität von\nTeam-Pokémon bei Sonne.", "Potenzia i Pokémon della\nsquadra se c'è il sole.", "はれのとき　じぶんと\nみかたが　つよくなる。", "晴れのとき　自分と\n味方が　強くなる。", "맑을 때 자신과\n같은 편이 강해진다.", "Potencia los Pokémon del\nequipo si está soleado.") },
            { PBEAbility.Forecast, new PBELocalizedString("Castform transforms with\nthe weather.", "Morphéo change de forme\nselon le climat.", "Formeo verändert seine\nForm abhängig vom Wetter.", "La forma di Castform\ncambia in base al tempo.", "てんきで　ポワルンが\nへんかする。", "天気で　ポワルンが\n変化する。", "날씨에 따라\n캐스퐁이 변화한다.", "Se transforma con el\ntiempo atmosférico.") },
            { PBEAbility.Forewarn, new PBELocalizedString("Determines what moves\na foe has.", "Découvre la capacité\nennemie la plus puissante.", "Gibt Auskunft über die\nstärkste gegn. Attacke.", "Scopre la mossa più\npotente del nemico.", "あいての　もつ　わざを\nよみとることが　できる。", "相手の　持つ　技を\n読み取ることが　できる。", "상대가 지닌 기술을\n꿰뚫어볼 수 있다.", "Determina el movimiento\nmás potente del rival.") },
            { PBEAbility.FriendGuard, new PBELocalizedString("Reduces damage done\nto allies.", "Diminue les dégâts subis\npar les alliés.", "Verringert den Schaden\nan Mitstreitern.", "Riduce il danno subito dai\ncompagni.", "みかたの　ダメージを\nへらすことができる。", "味方の　ダメージを\n減らすことができる。", "같은 편의 데미지를\n줄일 수 있다.", "Reduce el daño que sufren\nlos aliados.") },
            { PBEAbility.Frisk, new PBELocalizedString("The Pokémon can check\na foe's held item.", "Permet de connaître\nl'objet tenu par l'ennemi.", "Gibt Auskunft über\ngegnerische Items.", "Il Pokémon scopre che\nstrumento ha il nemico.", "あいての　もちものを\nしることが　できる。", "相手の　持ち物を\n知ることが　できる。", "상대가 지닌 물건을\n알 수 있다.", "El Pokémon puede ver el\nobjeto que lleva el rival.") },
            { PBEAbility.Gluttony, new PBELocalizedString("Encourages the early use\nof a held Berry.", "Encourage l'utilisation\nd'une Baie tenue.", "Ermutigt zum frühzeitigen\nVerwenden einer Beere.", "Favorisce l'uso anticipato\ndi una Bacca tenuta.", "きのみを　いつもより\nはやく　つかう。", "きのみを　いつもより\n早く　使う。", "나무열매를 여느 때보다\n빨리 사용한다.", "Fomenta el uso rápido de\nla Baya que porte.") },
            { PBEAbility.Guts, new PBELocalizedString("Boosts Attack if there is\na status problem.", "Augmente l'Attaque s'il y\na un problème de statut.", "Steigert Angriff bei\nStatusproblemen.", "Aumenta l'Attacco in\ncaso di problemi di stato.", "じょうたい　いじょうで\nこうげきが　あがる。", "状態異常で\n攻撃が　あがる。", "상태 이상이 되면\n공격이 올라간다.", "Sube el Ataque si sufre\nun problema de estado.") },
            { PBEAbility.Harvest, new PBELocalizedString("May create another\nBerry after one is used.", "Les Baies utilisées\npeuvent repousser.", "Aufgebrauchte Beeren\nkönnen nachwachsen.", "Può ricreare una Bacca\nutilizzata.", "つかった　きのみを\nなんかいも　つくりだす。", "使った　きのみを\n何回も　つくりだす。", "사용한 나무열매를\n몇 번이고 만들어 낸다.", "Permite reutilizar varias\nveces una misma Baya.") },
            { PBEAbility.Healer, new PBELocalizedString("May heal an ally's status\nconditions.", "Guérit parfois le statut\ndes alliés alentour.", "Heilt gel. Statusprobleme\nv. Partnern in der Nähe.", "A volte cura i problemi di\nstato dei compagni vicini.", "みかたの　じょうたい\nいじょうを　たまに　なおす。", "味方の　状態異常を\nたまに　治す。", "같은 편의 상태 이상을\n가끔 회복시킨다.", "Puede curar los cambios de\nestado de aliados vecinos.") },
            { PBEAbility.Heatproof, new PBELocalizedString("Weakens the power of\nFire-type moves.", "Réduit la puissance des\ncapacités de type Feu.", "Senkt die Kraft von\nFeuer-Attacken.", "Indebolisce l'effetto di\nmosse di tipo Fuoco.", "ほのお　わざの\nいりょくを　よわめる。", "ほのお　技の\n威力を　弱める。", "불꽃 기술의\n위력을 약하게 한다.", "Atenúa los movimientos\nde tipo Fuego.") },
            { PBEAbility.HeavyMetal, new PBELocalizedString("Doubles the Pokémon's\nweight.", "Double le poids\ndu Pokémon.", "Verdoppelt das eigene\nGewicht.", "Raddoppia il peso\ndel Pokémon.", "じぶんの　おもさが\n２ばいに　なる。", "自分の　重さが\n２倍に　なる。", "자신의 무게가\n2배가 된다.", "Duplica el peso\ndel Pokémon.") },
            { PBEAbility.HoneyGather, new PBELocalizedString("The Pokémon may gather\nHoney from somewhere.", "Le Pokémon peut parfois\ntrouver du Miel.", "Das Pokémon sammelt\ngelegentlich Honig auf.", "Il Pokémon può\nraccogliere Miele.", "あまいミツを　あつめて\nくることが　ある。", "あまいミツを　集めて\nくることが　ある。", "달콤한꿀을 모아서\n올 때가 있다.", "El Pokémon recoge Miel\nde donde puede.") },
            { PBEAbility.HugePower, new PBELocalizedString("Raises the Pokémon's\nAttack stat.", "Augmente l'Attaque du\nPokémon.", "Stärkt physische\nAttacken.", "Migliora la statistica\nAttacco del Pokémon.", "ぶつり　こうげきの\nいりょくが　あがる。", "物理攻撃の\n威力が　あがる。", "물리 공격의\n위력이 올라간다.", "Aumenta el Ataque.") },
