            { PBEAbility.HyperCutter, new PBELocalizedString("Prevents other Pokémon\nfrom lowering Attack stat.", "Empêche la réduction de\nl'Attaque.", "Hindert Angreifer, den\nAngriffs-Wert zu senken.", "Evita che la statistica\nAttacco venga ridotta.", "あいてに　こうげきを\nさげられない。", "相手に　攻撃を\nさげられない。", "상대가 공격을\n떨어뜨리지 못한다.", "Evita que disminuya\nel Ataque.") },
            { PBEAbility.IceBody, new PBELocalizedString("The Pokémon gradually\nregains HP in a hailstorm.", "Régénère ses PV lors des\ntempêtes de grêle.", "Regeneriert bei Hagel\nnach und nach KP.", "Il Pokémon recupera PS\nquando grandina.", "あられのとき　HPを\nすこしずつ　かいふく。", "あられのとき　ＨＰを\n少しずつ　回復。", "싸라기눈일 때 HP를\n조금씩 회복한다.", "Recupera algunos PS en\ntormentas de granizo.") },
            { PBEAbility.Illuminate, new PBELocalizedString("Raises the likelihood of\nmeeting wild Pokémon.", "Augmente les rencontres\navec Pokémon sauvages.", "Mehr Pokémon-\nBegegnungen.", "È più probabile incontrare\nPokémon selvatici.", "やせいの　ポケモンに\nそうぐう　しやすくなる。", "野生の　ポケモンに\n遭遇しやすくなる。", "야생 포켓몬과\n만나기 쉬워진다.", "Aumenta la probabilidad\nde encontrar Pokémon.") },
