            { PBEAbility.Immunity, new PBELocalizedString("Prevents the Pokémon\nfrom getting poisoned.", "Empêche le Pokémon d'être\nempoisonné.", "Verhindert Vergiftungen.", "Impedisce al Pokémon di\nvenire avvelenato.", "どく　じょうたいに\nならない。", "どく状態に\nならない。", "독 상태가\n되지 않는다.", "Evita el envenenamiento.") },
            { PBEAbility.Imposter, new PBELocalizedString("It transforms itself into\nthe Pokémon it is facing.", "Adopte l'apparence du\nPokémon adverse.", "Kämpft als Kopie\nseines Gegenübers.", "Si trasforma nel Pokémon\nche ha davanti.", "めのまえの　ポケモンに\nへんしん　してしまう。", "目の前の　ポケモンに\n変身してしまう。", "눈앞의 포켓몬으로\n변신해버린다.", "Se transforma en el que\ntiene enfrente.") },
            { PBEAbility.Infiltrator, new PBELocalizedString("Passes through the foe's\nbarrier and strikes.", "Traverse les barrières de\nl'ennemi pour attaquer.", "Überwindet gegnerische\nSchilde und greift an.", "Attacca evitando\nle barriere del nemico.", "あいての　かべを\nすりぬけて　こうげき。", "相手の　かべを\nすりぬけて　攻撃。", "상대의 벽을\n뚫고 공격한다.", "Ataca rodeando la\nbarrera del rival.") },
            { PBEAbility.InnerFocus, new PBELocalizedString("The Pokémon is protected\nfrom flinching.", "Empêche le Pokémon\nd'avoir peur.", "Verhindert\nZurückschrecken.", "Evita che il Pokémon\ntentenni.", "ひるまない。\n", "ひるまない。\n", "풀죽지 않는다.\n", "Evita el retroceso.") },
