            { PBEAbility.Limber, new PBELocalizedString("The Pokémon is protected\nfrom paralysis.", "Protège le Pokémon de\nla paralysie.", "Verhindert Paralyse.", "Il Pokémon è protetto\ncontro la paralisi.", "まひ　じょうたいに\nならない。", "まひ状態に\nならない。", "마비 상태가\n되지 않는다.", "Evita ser paralizado.") },
            { PBEAbility.LiquidOoze, new PBELocalizedString("Damages attackers\nusing any draining move.", "Blesse l'ennemi qui draine\nl'énergie du Pokémon.", "Schadet Angreifern, die\nSaug-Attacken einsetzen.", "Danneggia i nemici che\nusano mosse ruba-PS.", "すいとった　あいての\nＨＰを　へらす。", "吸いとった　相手の\nＨＰを　減らす。", "흡수한 상대의\nHP를 줄인다.", "Hiere a los enemigos\nque drenan PS.") },
            { PBEAbility.MagicBounce, new PBELocalizedString("Reflects status-\nchanging moves.", "Renvoie les attaques\nde statut.", "Lenkt Status-Attacken\nauf den Angreifer um.", "Riflette al mittente\nle mosse di stato.", "へんかわざを　かえす\nことが　できる。", "変化技を　返す\nことが　できる。", "변화 기술을\n되받아칠 수 있다.", "Permite devolver\nlos ataques de estado.") },
            { PBEAbility.MagicGuard, new PBELocalizedString("The Pokémon only takes\ndamage from attacks.", "Seule une attaque directe\npeut blesser le Pokémon.", "Pokémon nimmt nur durch\ndirekte Attacken Schaden.", "Il Pokémon subisce danni\nsolo da attacchi diretti.", "こうげき　いがいでは\nダメージを　うけない。", "攻撃　以外では\nダメージを　受けない。", "공격 이외에는\n데미지를 입지 않는다.", "Solo dañan al Pokémon\nlos ataques directos.") },
            { PBEAbility.MagmaArmor, new PBELocalizedString("Prevents the Pokémon\nfrom becoming frozen.", "Protège le Pokémon\ncontre le gel.", "Verhindert Einfrieren.", "Evita che il Pokémon\nvenga congelato.", "こおり　じょうたいに\nならない。", "こおり状態に\nならない。", "얼음 상태가\n되지 않는다.", "Evita la congelación.") },
            { PBEAbility.MagnetPull, new PBELocalizedString("Prevents Steel-type\nPokémon from escaping.", "Empêche les Pokémon Acier\nde fuir.", "Hindert Stahl-Pokémon an\nder Flucht.", "Impedisce la fuga ai\nPokémon di tipo Acciaio.", "はがねの　ポケモンを\nにげられなくする。", "はがねの　ポケモンを\n逃げられなくする。", "강철의 포켓몬을\n도망칠 수 없게 한다.", "Impide huir a los Pokémon\nde tipo Acero.") },
            { PBEAbility.MarvelScale, new PBELocalizedString("Ups Defense if there is\na status problem.", "Augmente la Défense s'il y\na un problème de statut.", "Steigert Verteidigung bei\nStatusproblemen.", "Aumenta la Difesa in caso\ndi problemi di stato.", "じょうたい　いじょうで\nぼうぎょが　あがる。", "状態異常で\n防御が　あがる。", "상태 이상이 되면\n방어가 올라간다.", "Sube la Defensa si sufre\nun problema de estado.") },
            { PBEAbility.Minus, new PBELocalizedString("Ups Sp. Atk if another\nPokémon has Plus or Minus.", "Augmente l'Atq. Spé. si un\nPokémon a Minus ou Plus.", "Erhöht Sp.-Ang., wenn Plus\noder Minus vertreten sind.", "Aumenta l'Attacco Speciale\nse ci sono Meno o Più.", "プラスかマイナスがいると\nとくこうが　あがる。", "プラスかマイナスがいると\n特攻が　あがる。", "플러스나 마이너스가 있으면\n특수공격이 올라간다.", "Potencia el At. Esp. si\notro tiene Más o Menos.") },
            { PBEAbility.MoldBreaker, new PBELocalizedString("Moves can be used\nregardless of Abilities.", "Les cap. spé. adverses ne\nbloquent pas ses cap.", "Att. können ungeachtet d.\nFähigkeit verw. werden.", "Neutralizza le abilità\nche bloccano le mosse.", "とくせいに　かんけいなく\nあいてに　わざを　だせる。", "特性に　関係なく\n相手に　技を　だせる。", "특성에 관계없이 상대에게\n기술을 쓸 수 있다.", "Usa movimientos sin que\nimporten las habilidades.") },
            { PBEAbility.Moody, new PBELocalizedString("Raises one stat and\nlowers another.", "Monte une stat tout en\nen baissant une autre.", "Erhöht einen Statuswert\nund senkt einen anderen.", "Aumenta una statistica\ne ne riduce un'altra.", "のうりょくが　あがったり\nさがったりする。", "能力が　あがったり\nさがったりする。", "능력 변화가\n오르락내리락한다.", "Sube una característica\nmucho, pero baja otra.") },
            { PBEAbility.MotorDrive, new PBELocalizedString("Raises Speed if hit by an\nElectric-type move.", "Augmente la Vit. si touché\npar une cap. Électrik.", "Erhöht Initiative nach\neiner Elektro-Attacke.", "Le mosse elettro subite\naumentano la Velocità.", "でんきを　うけると\nすばやさが　あがる。", "でんきを　受けると\n素早さが　あがる。", "전기를 받으면\n스피드가 올라간다.", "Sube la Velocidad si le\nda un ataque eléctrico.") },
            { PBEAbility.Moxie, new PBELocalizedString("Boosts Attack after\nknocking out any Pokémon.", "Monte l'Attaque quand\nil met un ennemi K.O.", "Besiegt es ein Pokémon,\nsteigt sein Angriffs-Wert.", "Aumenta l'Attacco quando\nmanda un nemico KO.", "あいてを　たおすと\nこうげきが　あがる。", "相手を　倒すと\n攻撃が　あがる。", "상대를 쓰러뜨리면\n공격이 올라간다.", "Potencia el Ataque al\ndebilitar a un objetivo.") },
            { PBEAbility.Multiscale, new PBELocalizedString("Reduces damage when HP\nis full.", "Reçoit moins de dégâts si\nles PV sont au maximum.", "Verringert erlittenen\nSchaden bei vollen KP.", "Riduce il danno subito se\ni PS sono al massimo.", "ＨＰが　まんたんのときに\nダメージが　すくなくなる。", "ＨＰが　満タンのときに\nダメージが　少なくなる。", "HP가 꽉 찼을 때\n데미지가 줄어든다.", "Reduce el daño sufrido\nsi los PS están al máximo.") },
            { PBEAbility.Multitype, new PBELocalizedString("Changes type to match\nthe held Plate.", "Modifie le type en fonction\nde la Plaque tenue.", "Passt seinen Typ der\ngetragenen Tafel an.", "Cambia tipo a seconda\ndella lastra che ha.", "もっている　プレートで\nタイプが　かわる。", "持っている　プレートで\nタイプが　変わる。", "지니고 있는 플레이트에\n따라 타입이 바뀐다.", "Cambia el tipo al de la\nTabla que lleve.") },
            { PBEAbility.Mummy, new PBELocalizedString("Contact with this Pokémon\nspreads this Ability.", "Momifie la cap. spé. de\nl'ennemi qui le touche.", "Berührt man es, übernimmt\nman seine Fähigkeit.", "L'abilità del nemico cambia\nin Mummia dopo il contatto.", "あいてに　さわられると\nあいてを　ミイラにする。", "相手に　触られると\n相手を　ミイラにする。", "상대의 기술을 받으면\n상대를 미라로 만든다.", "Contagia Momia al\nrival que lo toque.") },
            { PBEAbility.NaturalCure, new PBELocalizedString("All status problems heal\nwhen it switches out.", "Quitter le combat soigne\nles problèmes de statut.", "Heilt bei Austausch\nStatusprobleme.", "Cura i problemi di stato\ndel Pokémon sostituito.", "じょうたい　いじょうが\nひっこむと　なおる。", "状態異常が\nひっこむと　治る。", "배틀에서 일단 물러나면\n상태 이상이 회복된다.", "Cura problemas de estado\nal cambiar de Pokémon.") },
            { PBEAbility.NoGuard, new PBELocalizedString("Ensures attacks by or\nagainst the Pokémon land.", "Capacités du Pokémon et\nde l'ennemi réussissent.", "Attacken d. Pokémon u. v.\nAngreifern treffen immer.", "Le mosse di entrambi i\nPokémon vanno a segno.", "おたがいの　わざが\nかならず　あたる。", "お互いの　技が\n必ず　当たる。", "서로의 기술이\n반드시 맞는다.", "El movimiento del Pokémon\ny el del rival acertarán.") },
            { PBEAbility.Normalize, new PBELocalizedString("All the Pokémon's moves\nbecome the Normal type.", "Toutes les capacités sont\nde type Normal.", "Alle Attacken des Pokémon\nwerden zu Normal-Att.", "Le mosse del Pokémon\ndiventano di tipo Normale.", "だしたわざが　すべて\nノーマルタイプに　なる。", "だした技が　すべて\nノーマルタイプに　なる。", "쓴 기술이 모두\n노말타입이 된다.", "Todos los movimientos se\nvuelven de tipo Normal.") },
            { PBEAbility.Oblivious, new PBELocalizedString("Prevents it from\nbecoming infatuated.", "Empêche le Pokémon de\ntomber amoureux.", "Verhindert Anziehung.", "Impedisce al Pokémon di\ninfatuarsi.", "メロメロ　じょうたいに\nならない。", "メロメロ　状態に\nならない。", "헤롱헤롱 상태가\n되지 않는다.", "Evita que el Pokémon\nse enamore.") },
            { PBEAbility.Overcoat, new PBELocalizedString("Protects the Pokémon from\ndamage from weather.", "Neutralise les dégâts dus\nà la météo.", "Nimmt keinen Schaden\ndurch Wetterbedingungen.", "Non subisce danni causati\ndalle condizioni meteo.", "てんきの　えいきょうで\nダメージを　うけない。", "天気の　影響で\nダメージを　受けない。", "날씨의 영향으로\n데미지를 입지 않는다.", "Anula los daños causados\npor el tiempo atmosférico.") },
            { PBEAbility.Overgrow, new PBELocalizedString("Powers up Grass-type\nmoves in a pinch.", "Booste les capacités\nPlante en cas de besoin.", "Stärkt Pflanzen-Attacken,\nwenn Not am Mann ist.", "In difficoltà, potenzia\nle mosse di tipo Erba.", "ピンチのとき　くさの\nいりょくが　あがる。", "ピンチのとき　くさの\n威力が　あがる。", "위급할 때 풀타입의\n위력이 올라간다.", "Potencia los ataques de\ntipo Planta en un apuro.") },
            { PBEAbility.OwnTempo, new PBELocalizedString("Prevents the Pokémon\nfrom becoming confused.", "Empêche la confusion.", "Verhindert Verwirrung.", "Impedisce al Pokémon\ndi venire confuso.", "こんらん　じょうたいに\nならない。", "混乱状態に\nならない。", "혼란 상태가\n되지 않는다.", "Evita ser confundido.") },
            { PBEAbility.Pickpocket, new PBELocalizedString("Steals an item when hit\nby another Pokémon.", "Vole l'objet de l'ennemi\nsi son attaque touche.", "Stiehlt Item, wenn es\nvon Träger berührt wird.", "Al contatto subito, ruba\nlo strumento nemico.", "さわられた　あいてから\nどうぐを　ぬすむ。", "触られた　相手から\n道具を　盗む。", "닿은 상대로부터\n도구를 훔친다.", "Al ser golpeado, roba\nel objeto del objetivo.") },
            { PBEAbility.Pickup, new PBELocalizedString("The Pokémon may\npick up items.", "Permet parfois au Pokémon\nde ramasser des objets.", "Hebt gelegentlich Items\nauf.", "Aumenta la probabilità\ndi trovare strumenti.", "どうぐを　ひろってくる\nことが　ある。", "道具を　拾ってくる\nことが　ある。", "도구를 주워올\n때가 있다.", "El Pokémon puede\nencontrar objetos.") },
            { PBEAbility.Plus, new PBELocalizedString("Ups Sp. Atk if another\nPokémon has Plus or Minus.", "Augmente l'Atq. Spé. si un\nPokémon a Minus ou Plus.", "Erhöht Sp.-Ang., wenn Plus\noder Minus vertreten sind.", "Aumenta l'Attacco Speciale\nse ci sono Meno o Più.", "プラスかマイナスがいると\nとくこうが　あがる。", "プラスかマイナスがいると\n特攻が　あがる。", "플러스나 마이너스가 있으면\n특수공격이 올라간다.", "Potencia el At. Esp. si\notro tiene Más o Menos.") },
            { PBEAbility.PoisonHeal, new PBELocalizedString("Restores HP if the\nPokémon is poisoned.", "Restaure des PV si le\nPokémon est empoisonné.", "Steigert KP, wenn das\nPokémon vergiftet ist.", "Ridà PS se il Pokémon\nè avvelenato.", "どくじょうたいに　なると\nＨＰを　かいふくする。", "どく状態に　なると\nＨＰを　回復する。", "독 상태가 되면\nHP를 회복한다.", "Recupera PS si el Pokémon\nha sido envenenado.") },
            { PBEAbility.PoisonPoint, new PBELocalizedString("Contact with the Pokémon\nmay poison the attacker.", "Peut empoisonner l'ennemi\ns'il y a contact.", "Vergiftet den Angreifer\neventuell bei Berührung.", "Al contatto subito, può\navvelenare il nemico.", "ふれた　あいてに　どくを\nおわせる　ことがある。", "触れた　相手に　毒を\nおわせる　ことがある。", "접촉한 상대를\n중독 시킬 때가 있다.", "Puede envenenar al\nmínimo contacto.") },
            { PBEAbility.PoisonTouch, new PBELocalizedString("May poison targets when a\nPokémon makes contact.", "Peut empoisonner l'ennemi\npar simple contact.", "Kann Ziel durch bloßes\nBerühren vergiften.", "Può avvelenare il nemico\nal solo contatto fisico.", "さわるだけで　あいてを　\nどくにすることがある。", "触るだけで　相手を　\n毒にすることがある。", "접촉하기만 해도 상대를\n독 상태로 만들 때가 있다.", "Puede envenenar al\nobjetivo con solo tocarlo.") },
            { PBEAbility.Prankster, new PBELocalizedString("Gives priority to a\nstatus move.", "Utilise les attaques de\nstatut en premier.", "Ermöglicht Erstschlag\nmit Status-Attacken.", "Dà priorità a mosse che\nalterano lo stato.", "へんかわざを　せんせいで\nだすことが　できる。", "変化技を　先制で\nだすことが　できる。", "변화 기술을 먼저\n쓸 수 있다.", "Permite lanzar primero\nun ataque de estado.") },
            { PBEAbility.Pressure, new PBELocalizedString("The Pokémon raises the\nfoe's PP usage.", "Force l'ennemi à\ndépenser plus de PP.", "Steigert den Verbrauch\nder AP des Gegners.", "Il nemico usa più PP.", "あいての　つかう　わざの\nＰＰを　おおく　へらす。", "相手の　使う　技の\nＰＰを　多く　減らす。", "상대가 사용하는 기술의\nPP를 많이 줄인다.", "Hace que los PP del rival\nse acaben antes.") },
            { PBEAbility.PurePower, new PBELocalizedString("Raises the Pokémon's\nAttack stat.", "Augmente l'Attaque du\nPokémon.", "Stärkt physische\nAttacken.", "Migliora la statistica\nAttacco del Pokémon.", "ぶつり　こうげきの\nいりょくが　あがる。", "物理攻撃の\n威力が　あがる。", "물리 공격의\n위력이 올라간다.", "Aumenta el Ataque.") },
            { PBEAbility.QuickFeet, new PBELocalizedString("Boosts Speed if there is\na status problem.", "Augmente la Vitesse en cas\nde problème de statut.", "Erhöht Initiative bei\nStatusproblemen.", "Aumenta la Velocità se\nc'è un problema di stato.", "じょうたい　いじょうで\nすばやさが　あがる。", "状態異常で\n素早さが　あがる。", "상태 이상이 되면\n스피드가 올라간다.", "Potencia la Velocidad si\nhay problemas de estado.") },
            { PBEAbility.RainDish, new PBELocalizedString("The Pokémon gradually\nregains HP in rain.", "Récupère progressivement\ndes PV par temps de pluie.", "Langsame Wiederherstellg.\nvon KP bei Regen.", "Il Pokémon recupera PS\nquando piove.", "あめのとき　すこしずつ\nＨＰを　かいふくする。", "雨のとき　少しずつ\nＨＰを　回復する。", "비가 올 때 조금씩\nHP를 회복한다.", "El Pokémon gana PS\ngradualmente si llueve.") },
            { PBEAbility.Rattled, new PBELocalizedString("Some move types scare\nit and boost its Speed.", "Sa peur de certains types\naugmente sa Vitesse.", "Aus Angst vor manchen\nAttacken steigt die Init.", "Per la paura di certi tipi\ndi mosse la Velocità sale.", "びびって　すばやさが\nあがる　タイプがある。", "びびって　素早さが\nあがる　タイプがある。", "주눅이 들어 스피드가\n올라가는 타입이 있다.", "El miedo a algunos ataques\nsube su Velocidad.") },
            { PBEAbility.Reckless, new PBELocalizedString("Powers up moves that\nhave recoil damage.", "Booste les cap. ayant des\ndégâts de contrecoups.", "Steigert Attacken mit\nRückstoß-Schaden.", "Potenzia le mosse che\nprovocano contraccolpi.", "はんどうで　ダメージを\nうけるわざが　つよくなる。", "反動で　ダメージを\n受ける技が　強くなる。", "반동 데미지를\n받는 기술이 강해진다.", "Potencia los movimientos\nque dañan al usuario.") },
            { PBEAbility.Regenerator, new PBELocalizedString("Restores a little HP when\nwithdrawn from battle.", "Restaure un peu de PV\nsi le Pokémon est retiré.", "Heilt beim Austauschen\neine geringe Menge an KP.", "Recupera un po' di PS\nquando viene sostituito.", "ひっこめると ＨＰが\nすこし　かいふくする。", "ひっこめると ＨＰが\n少し　回復する。", "볼에 넣으면 HP가\n조금 회복된다.", "Recupera unos pocos PS\nal cambiar de Pokémon.") },
            { PBEAbility.Rivalry, new PBELocalizedString("Deals more damage to a\nPokémon of same gender.", "Devient plus fort si\nl'ennemi est du même sexe.", "Erhöht Schaden, wenn Ziel\ndasselbe Geschlecht hat.", "Rende più forti contro\nnemici dello stesso sesso.", "あいてと　せいべつが\nおなじだと　つよくなる。", "相手と　性別が\n同じだと　強くなる。", "상대와 성별이\n같으면 강해진다.", "Si el objetivo es del mismo\nsexo, inflige más daño.") },
            { PBEAbility.RockHead, new PBELocalizedString("Protects the Pokémon\nfrom recoil damage.", "Protège le Pokémon des\ndégâts de contrecoups.", "Verhindert Schaden, der\ndurch Rückstoß entsteht.", "Protegge il Pokémon dai\ncontraccolpi.", "ぶつかっても\nはんどうを　うけない。", "ぶつかっても\n反動を　受けない。", "부딪쳐도\n반동을 받지 않는다.", "Impide que el Pokémon\nse dañe con sus movs.") },
            { PBEAbility.RoughSkin, new PBELocalizedString("Inflicts damage to the\nattacker on contact.", "Le Pokémon blesse\nl'ennemi qui le touche.", "Verletzt Angreifer\nbei Berührung.", "Danneggia il nemico\nal minimo contatto fisico.", "ふれた　あいてを\nキズつける。", "触れた　相手を\nキズつける。", "접촉한 상대에게\n상처를 입힌다.", "Hiere al tacto.") },
            { PBEAbility.RunAway, new PBELocalizedString("Enables a sure getaway\nfrom wild Pokémon.", "Permet de fuir n'importe\nquel Pokémon sauvage.", "Die Flucht vor wilden\nPokémon gelingt immer.", "Garantisce la fuga dai\nPokémon selvatici.", "やせいの　ポケモンから\nかならず　にげられる。", "野生の　ポケモンから\n必ず　逃げられる。", "야생 포켓몬으로부터\n반드시 도망칠 수 있다.", "Permite escapar de todos\nlos Pokémon salvajes.") },
            { PBEAbility.SandForce, new PBELocalizedString("Boosts certain moves'\npower in a sandstorm.", "Renforce des capacités en\ncas de tempête de sable.", "Manche Attacken wirken\nin Sandstürmen besser.", "Potenzia alcune mosse\nnelle tempeste di sabbia.", "すなあらしで　いりょくが\nあがる　わざがある。", "砂あらしで　威力が\nあがる　技がある。", "모래바람으로 위력이\n올라가는 기술이 있다.", "Potencia algunos movs.\nen tormentas de arena.") },
            { PBEAbility.SandRush, new PBELocalizedString("Boosts the Pokémon's\nSpeed in a sandstorm.", "Augmente la Vitesse lors\ndes tempêtes de sable.", "Erhöht in Sandstürmen\ndie Initiative.", "La Velocità aumenta\nnelle tempeste di sabbia.", "すなあらしで\nすばやさが　あがる。", "砂あらしで\n素早さが　あがる。", "모래바람으로\n스피드가 올라간다.", "Aumenta la Velocidad en\ntormentas de arena.") },
            { PBEAbility.SandStream, new PBELocalizedString("The Pokémon summons a\nsandstorm in battle.", "Le Pokémon invoque une\ntempête de sable.", "Erzeugt einen Sandsturm.", "Causa una tempesta di\nsabbia durante la lotta.", "せんとうで　すなあらしを\nおこす。", "戦闘で　砂あらしを\nおこす。", "배틀에서 모래바람을\n일으킨다.", "Crea una tormenta de\narena en el combate.") },
            { PBEAbility.SandVeil, new PBELocalizedString("Boosts the Pokémon's\nevasion in a sandstorm.", "Augmente l'Esquive lors\ndes tempêtes de sable.", "Erhöht in Sandstürmen den\nFluchtwert.", "L'elusione aumenta\nnelle tempeste di sabbia.", "すなあらしで\nかいひりつが　あがる。", "砂あらしで\n回避率が　あがる。", "모래바람으로\n회피율이 올라간다.", "Aumenta la Evasión en\nlas tormentas de arena.") },
            { PBEAbility.SapSipper, new PBELocalizedString("Boosts Attack when hit by\na Grass-type move.", "Augmente l'Attaque après\nune attaque Plante.", "Treffer durch Pflanzen-\nAttacken stärken Angriff.", "L'Attacco aumenta se si è\ncolpiti da una mossa erba.", "くさの　わざを　うけると\nこうげきが　あがる。", "くさの　技を　受けると\n攻撃が　あがる。", "풀 기술을 받으면\n공격이 올라간다.", "Sube el Ataque al recibir\nuno de tipo Planta.") },
            { PBEAbility.Scrappy, new PBELocalizedString("Enables moves to hit\nGhost-type Pokémon.", "Les cap. Normal touchent\nles Pokémon Spectre.", "Normal- u. Kampf-Attacken\ntreffen Geist-Pokémon.", "I Pokémon spettro sono\ncolpiti dalle mosse Normali.", "ゴーストタイプに\nノーマルわざが　あたる。", "ゴーストタイプに\nノーマル技が　当たる。", "고스트타입에\n노말 기술이 맞는다.", "Movs. tipo Lucha y Normal\ngolpean Pokémon Fantasma.") },
            { PBEAbility.SereneGrace, new PBELocalizedString("Boosts the likelihood of\nadded effects appearing.", "Augmente la fréquence\ndu cumul d'effets.", "Erhöht Chance für\nZusatz-Effekte.", "Gli effetti aggiuntivi sono\npiù probabili.", "わざの　ついかこうかが\nでやすい。", "技の　追加効果が\nでやすい。", "기술의 추가 효과가\n나오기 쉽다.", "Aumenta la probabilidad\nde efectos secundarios.") },
            { PBEAbility.ShadowTag, new PBELocalizedString("Prevents the foe from\nescaping.", "Empêche le Pokémon ennemi\nde quitter le combat.", "Hindert Gegner an Flucht.", "Impedisce la fuga al\nnemico.", "あいての　かげをふみ\nにげられなくする。", "相手の　影をふみ\n逃げられなくする。", "상대의 그림자를 밟아\n도망칠 수 없게 한다.", "Evita que el enemigo\nhuya.") },
            { PBEAbility.ShedSkin, new PBELocalizedString("The Pokémon may heal its\nown status problems.", "Le Pokémon soigne parfois\nses problèmes de statut.", "Pokémon heilt eventuell\nseine Statusprobleme.", "Può guarire i problemi di\nstato del Pokémon.", "じょうたい　いじょうを\nなおす　ことがある。", "状態異常を\n治すことが　ある。", "상태 이상을\n회복할 때가 있다.", "Cura los problemas de\nestado mudando la piel.") },
            { PBEAbility.SheerForce, new PBELocalizedString("Removes added effects to\nincrease move damage.", "Frappe plus fort mais\nannule les effets cumulés.", "Erhöht die Kraft, aber\nhebt Zusatz-Effekte auf.", "Aumenta i danni annullando\naltri effetti delle mosse.", "ちからが　つよくなるが\nついかこうかが　なくなる。", "力が　強くなるが\n追加効果が　なくなる。", "힘이 강해지지만\n추가 효과가 없어진다.", "Sube la potencia y anula\nlos efectos secundarios.") },
            { PBEAbility.ShellArmor, new PBELocalizedString("The Pokémon is protected\nagainst critical hits.", "Le Pokémon est protégé\ndes coups critiques.", "Wehrt Volltreffer ab.", "Evita che il Pokémon\nsubisca brutti colpi.", "あいての　こうげきが\nきゅうしょに　あたらない。", "相手の　攻撃が\n急所に　当たらない。", "상대의 공격이\n급소에 맞지 않는다.", "Impide recibir daño\ncrítico.") },
            { PBEAbility.ShieldDust, new PBELocalizedString("Blocks the added effects\nof attacks taken.", "Annule les effets cumulés\nd'une attaque ennemie.", "Blockiert Zusatz-Effekte\ngegnerischer Angriffe.", "Gli effetti secondari delle\nmosse subite svaniscono.", "わざの　ついかこうかを\nうけない。", "技の　追加効果を\n受けない。", "기술의 추가 효과를\n받지 않는다.", "Anula el efecto secundario\ndel ataque sufrido.") },
            { PBEAbility.Simple, new PBELocalizedString("The Pokémon is prone to\nwild stat changes.", "Le Pokémon est sujet à\ndes variations de stats.", "Das Pokémon ist anfällig\nfür Statusveränderungen.", "Le statistiche possono\ncambiare senza preavviso.", "のうりょく　へんかが\nいつもより　はげしい。", "能力　変化が\nいつもより　はげしい。", "능력 변화가\n여느 때보다 심하다.", "Aumenta los cambios\nen las características.") },
            { PBEAbility.SkillLink, new PBELocalizedString("Increases the frequency\nof multi-strike moves.", "Augmente la fréquence des\nattaques multiples.", "Längere Treffer-Serien mit\nMehrfach-Attacken.", "Aumenta la frequenza di\nmosse multicolpo.", "れんぞく　わざを\nたくさん　だせる。", "連続技を\nたくさん　だせる。", "연속 기술을\n많이 쓸 수 있다.", "Aumenta la frecuencia de\nlos movimientos múltiples.") },
            { PBEAbility.SlowStart, new PBELocalizedString("Temporarily halves\nAttack and Speed.", "Divise temporairement\nVitesse et Attaque par 2.", "Halbiert zeitweilig Angriff\nund Initiative.", "Dimezza per un po'\nl'Attacco e la Velocità.", "こうげきと　すばやさが\nしばらく　はんぶんになる。", "攻撃と　素早さが\nしばらく　半分になる。", "공격과 스피드가\n잠시 동안 절반이 된다.", "Baja a la mitad el Ataque y\nla Velocidad por un tiempo.") },
            { PBEAbility.Sniper, new PBELocalizedString("Powers up moves if they\nbecome critical hits.", "Augmente la puissance\ndes coups critiques.", "Steigert den Schaden\nvon Volltreffern.", "Potenzia le mosse se\ndiventano brutti colpi.", "きゅうしょに　あてたとき\nいりょくが　あがる。", "急所に　当てたとき\n威力が　あがる。", "급소에 맞혔을 때\n위력이 올라간다.", "Potencia los movimientos\nsi se vuelven críticos.") },
            { PBEAbility.SnowCloak, new PBELocalizedString("Raises evasion in a\nhailstorm.", "Augmente l'Esquive durant\nles tempêtes de grêle.", "Erhöht bei Hagel\nden Fluchtwert.", "Aumenta l'elusione\nquando grandina.", "てんきが　あられのとき　\nかいひりつが　あがる。", "天気が　あられのとき　\n回避率が　あがる。", "날씨가 싸라기눈일 때\n회피율이 올라간다.", "Sube la Evasión durante\nuna tormenta de granizo.") },
            { PBEAbility.SnowWarning, new PBELocalizedString("The Pokémon summons a\nhailstorm in battle.", "Déclenche une tempête de\ngrêle pendant le combat.", "Pokémon beschwört einen\nHagelsturm herauf.", "Il Pokémon provoca una\ngrandinata in lotta.", "せんとうに　でると\nあられを　ふらす。", "戦闘に　でると\nあられを　降らす。", "배틀에 나가면\n싸라기눈을 내리게 한다.", "El Pokémon invoca una\ntormenta de granizo.") },
            { PBEAbility.SolarPower, new PBELocalizedString("In sunshine, Sp. Atk is\nboosted but HP decreases.", "Augmente l'Atq. Spé. mais\nbaisse les PV au soleil.", "Steigert Spezial-Angriff\nbei Sonne, senkt aber KP.", "Se c'è il sole aumenta\nl'Att. Sp. ma riduce i PS.", "はれると　ＨＰが　へるが\nとくこうが　あがる。", "晴れると　ＨＰが　減るが\n特攻が　あがる。", "맑으면 HP가 줄지만\n특수공격이 올라간다.", "Si hace sol, baja los PS,\npero potencia el At. Esp.") },
            { PBEAbility.SolidRock, new PBELocalizedString("Reduces damage from\nsupereffective attacks.", "Affaiblit les capacités\n“super efficaces\".", "Senkt die Stärke sehr\neffektiver Attacken.", "Riduce i danni delle\nmosse superefficaci.", "こうかばつぐん　のとき\nいりょくを　よわめる。", "効果バツグン　のとき\n威力を　弱める。", "효과가 굉장한 기술의\n위력을 약하게 한다.", "Mitiga los movimientos\nsupereficaces.") },
            { PBEAbility.Soundproof, new PBELocalizedString("Gives full immunity to all\nsound-based moves.", "Protège de toutes les\ncapacités sonores.", "Immun gegen Lärm-\nAttacken.", "Fornisce immunità dalle\nmosse basate sul suono.", "おとの　わざを\nうけない。", "音の　技を\n受けない。", "소리 기술을\n받지 않는다.", "Evita ataques de sonido.") },
            { PBEAbility.SpeedBoost, new PBELocalizedString("Its Speed stat is\ngradually boosted.", "La Vitesse du Pokémon\naugmente progressivement.", "Erhöht Initiative nach\nund nach.", "La statistica Velocità\naumenta gradualmente.", "ちょっとずつ\nすばやく　なっていく。", "ちょっとずつ\n素早く　なっていく。", "조금씩\n스피드가 높아진다.", "Aumenta la Velocidad\ngradualmente.") },
            { PBEAbility.Stall, new PBELocalizedString("The Pokémon moves after\nall other Pokémon do.", "Attaque toujours après\nl'ennemi, même plus lent.", "Handelt erst nach Pokémon\nmit niedrigerer Initiative.", "Anche i nemici più lenti\nattaccano per primi.", "あいてより　すばやくても\nこうどうが　おそくなる。", "相手より　素早くても\n行動が　遅くなる。", "상대보다 재빨라도\n행동이 느려진다.", "El Pokémon se mueve\ntras todos los demás.") },
            { PBEAbility.Static, new PBELocalizedString("Contact with the Pokémon\nmay cause paralysis.", "Un contact avec le\nPokémon peut paralyser.", "Kann bei Berührung\nparalysieren.", "Il contatto fisico con il\nPokémon può dare paralisi.", "さわった　あいてを\nまひさせる　ことがある。", "触った　相手を\nまひさせる　ことがある。", "접촉한 상대를\n마비시킬 때가 있다.", "Puede paralizar al\nmínimo contacto.") },
            { PBEAbility.Steadfast, new PBELocalizedString("Raises Speed each time\nthe Pokémon flinches.", "Augmente la Vitesse quand\nle Pokémon a peur.", "Erhöht Initiative, sobald\nPokémon zurückschreckt.", "Aumenta la Velocità se\nil Pokémon tentenna.", "ひるむ　たびに\nすばやさが　あがる。", "ひるむ　たびに\n素早さが　あがる。", "풀죽을 때마다\n스피드가 올라간다.", "Cada vez que retrocede\nsube su Velocidad.") },
            { PBEAbility.Stench, new PBELocalizedString("The stench may cause\nthe target to flinch.", "La puanteur peut\neffrayer l'adversaire.", "Lässt den Gegner durch\nGestank zurückschrecken.", "A volte fa tentennare\ni nemici.", "くさくて　あいてが\nひるむ　ことがある。", "臭くて　相手が\nひるむ　ことがある。", "악취 때문에 상대가\n풀죽을 때가 있다.", "Es posible que el rival\nretroceda por el mal olor.") },
            { PBEAbility.StickyHold, new PBELocalizedString("Protects the Pokémon\nfrom item theft.", "Fait s'agripper à l'objet\npour en empêcher le vol.", "Verhindert durch Klebe-\neffekt Item-Diebstähle.", "Protegge il Pokémon\ndal furto di strumenti.", "ねんちゃくして\nどうぐを　まもる。", "ねんちゃくして\n道具を　守る。", "달라붙어서\n도구를 지킨다.", "Impide el robo de objetos.") },
            { PBEAbility.StormDrain, new PBELocalizedString("Draws in all Water-type\nmoves to up Sp. Attack.", "Attire l'eau et augmente\nl'Atq. Spé.", "Ruft Wasser herbei und\nerhöht Sp.-Ang.", "Le mosse acqua subite\naumentano l'Att. Sp.", "みずを　よびこんで\nとくこうを　あげる。", "みずを　呼び込んで\n特攻を　あげる。", "물을 끌어모아\n특수공격을 올린다.", "Atrae agua para aumentar\nel At. Esp.") },
            { PBEAbility.Sturdy, new PBELocalizedString("It cannot be knocked\nout with one hit.", "Protège des capacités\nmettant K.O. en un coup.", "Schutz gegen\nK.O.-Treffer-Angriffe.", "Evita che il Pokémon vada\nKO in un sol colpo.", "いちげきで\nたおされない。", "一撃で　倒されない。\n", "일격으로\n쓰러지지 않는다.", "Evita que el rival pueda\ndebilitar de un solo golpe.") },
            { PBEAbility.SuctionCups, new PBELocalizedString("Negates all moves that\nforce switching out.", "Annule les cap. et obj. qui\nfont changer de Pokémon.", "Negiert Att. und Items,\ndie PKMN austauschen.", "Resiste a strumenti e\nmosse di sostituzione.", "いれかえさせる　わざや\nどうぐが　きかない。", "入れ替えさせる　技や\n道具が　効かない。", "교체시키는 기술이나 도구의\n효과를 받지 않는다.", "Anula movs. y objetos que\nfuerzan el relevo.") },
            { PBEAbility.SuperLuck, new PBELocalizedString("Heightens the critical-\nhit ratios of moves.", "Augmente la fréquence\ndes coups critiques.", "Erhöht die Chance auf\neinen Volltreffer.", "Aumenta la probabilità di\ninfliggere brutti colpi.", "あいての　きゅうしょに\nこうげきが　あたりやすい。", "相手の　急所に\n攻撃が　当たりやすい。", "상대의 급소에\n공격이 맞기 쉽다.", "Aumenta la probabilidad\nde dar golpes críticos.") },
            { PBEAbility.Swarm, new PBELocalizedString("Powers up Bug-type\nmoves in a pinch.", "Booste les capacités\nInsecte en cas de besoin.", "Stärkt Käfer-Attacken,\nwenn Not am Mann ist.", "In difficoltà, potenzia le\nmosse di tipo Coleottero.", "ピンチのとき　むしの\nいりょくが　あがる。", "ピンチのとき　むしの\n威力が　あがる。", "위급할 때 벌레타입의\n위력이 올라간다.", "Potencia los ataques de\ntipo Bicho en un apuro.") },
            { PBEAbility.SwiftSwim, new PBELocalizedString("Boosts the Pokémon's\nSpeed in rain.", "Augmente la Vitesse du\nPokémon s'il pleut.", "Steigert Initiative bei\nRegen.", "Se piove, la statistica\nVelocità aumenta.", "あめのとき　すばやさが\nあがる。", "雨のとき　素早さが\nあがる。", "비가 올 때 스피드가\n올라간다.", "Sube la Velocidad cuando\nhay lluvia.") },
            { PBEAbility.Synchronize, new PBELocalizedString("Passes a burn, poison,\nor paralysis to the foe.", "Transmet brûlure, poison\nou paralysie à l'ennemi.", "Gibt Verbr., Vergift. u.\nParaly. an Angreifer.", "Passa veleno, paralisi o\nscottatura al nemico.", "どく　まひ　やけどを\nあいてに　うつす。", "どく　まひ　やけどを\n相手に　うつす。", "독, 마비, 화상을\n상대에게 옮긴다.", "Contagia envenenamiento,\nparálisis o quemaduras.") },
            { PBEAbility.TangledFeet, new PBELocalizedString("Raises evasion if the\nPokémon is confused.", "Augmente l'Esquive si le\nPokémon est confus.", "Erhöht Fluchtwert, wenn\ndas Pokémon verwirrt ist.", "Aumenta l'elusione se\nil Pokémon è confuso.", "こんらん　していると\nかいひ　しやすくなる。", "混乱していると\n回避しやすくなる。", "혼란에 빠져있으면\n회피하기 쉬워진다.", "Sube la Evasión si el\nPokémon está confuso.") },
            { PBEAbility.Technician, new PBELocalizedString("Powers up the Pokémon's\nweaker moves.", "Augmente la puissance des\ncapacités les plus faibles.", "Steigert Effektivität von\nschwächeren Attacken.", "Potenzia le mosse più\ndeboli del Pokémon.", "よわい　わざの\nいりょくが　あがる。", "弱い　技の\n威力が　あがる。", "약한 기술의\n위력이 올라간다.", "Potencia los movimientos\nmás débiles del Pokémon.") },
            { PBEAbility.Telepathy, new PBELocalizedString("Anticipates an ally's\nattack and dodges it.", "Anticipe et évite les\nattaques de ses alliés.", "Erkennt und pariert\nAngriffe von Mitstreitern.", "Prevede ed evita gli\nattacchi dei compagni.", "みかたの　こうげきを\nよみとって　うけない。", "味方の　攻撃を\n読み取って　受けない。", "같은 편의 공격의 낌새를\n읽고 기술을 받지 않는다.", "Elude los ataques de\naliados en batalla.") },
            { PBEAbility.Teravolt, new PBELocalizedString("Moves can be used\nregardless of Abilities.", "Les cap. spé. adverses ne\nbloquent pas ses cap.", "Att. können ungeachtet d.\nFähigkeit verw. werden.", "Neutralizza le abilità\nche bloccano le mosse.", "とくせいに　かんけいなく\nあいてに　わざを　だせる。", "特性に　関係なく\n相手に　技を　だせる。", "특성에 관계없이 상대에게\n기술을 쓸 수 있다.", "Usa movimientos sin que\nimporten las habilidades.") },
            { PBEAbility.ThickFat, new PBELocalizedString("Ups resistance to Fire-\nand Ice-type moves.", "Augmente la résistance\naux cap. Feu et Glace.", "Stark gegen Attacken der\nTypen Feuer und Eis.", "Aumenta la resistenza alle\nmosse fuoco e ghiaccio.", "ほのおと　こおりタイプの\nわざに　つよい。", "ほのおと　こおりタイプの\n技に　強い。", "불꽃과 얼음타입의\n기술에 강하다.", "Atenúa los ataques de\ntipo Fuego y Hielo.") },
            { PBEAbility.TintedLens, new PBELocalizedString("Powers up “not very\neffective\" moves.", "Améliore les capacités\n“pas très efficaces\".", "Verstärkt nicht sehr\neffektive Attacken.", "Potenzia le mosse\nnon molto efficaci.", "こうかいまひとつ　のとき\nわざを　つよめる。", "効果いまひとつ　のとき\n技を　強める。", "효과가 별로인 기술이\n강해진다.", "Potencia los movimientos\npoco eficaces.") },
            { PBEAbility.Torrent, new PBELocalizedString("Powers up Water-type\nmoves in a pinch.", "Booste les capacités\nEau en cas de besoin.", "Stärkt Wasser-Attacken,\nwenn Not am Mann ist.", "In difficoltà, potenzia\nle mosse di tipo Acqua.", "ピンチのとき　みずの\nいりょくが　あがる。", "ピンチのとき　みずの\n威力が　あがる。", "위급할 때 물타입의\n위력이 올라간다.", "Potencia los ataques de\ntipo Agua en un apuro.") },
            { PBEAbility.ToxicBoost, new PBELocalizedString("Powers up physical\nattacks when poisoned.", "Booste les att. physiques\nsi le statut est poison.", "Verstärkt bei Vergiftung\nphysische Angriffe.", "Aumenta l'Attacco se il\nPokémon è avvelenato.", "どくのとき　ぶつりの\nいりょくが　あがる。", "毒のとき　物理の\n威力が　あがる。", "독 상태일 때 물리 공격의\n위력이 올라간다.", "Aumenta la potencia física\nsi está envenenado.") },
            { PBEAbility.Trace, new PBELocalizedString("The Pokémon copies a\nfoe's Ability.", "Imite la capacité spéciale\nde l'ennemi.", "Das Pokémon kopiert\nFähigkeit des Gegners.", "Copia l'abilità del nemico.", "あいてと　おなじ\nとくせいに　なる。", "相手と　同じ\n特性に　なる。", "상대와 같은\n특성이 된다.", "El Pokémon copia la\nhabilidad del rival.") },
            { PBEAbility.Truant, new PBELocalizedString("Pokémon can't attack\non consecutive turns.", "Le Pokémon ne frappe\nqu'un tour sur deux.", "Greift nur in jeder\nzweiten Runde an.", "Il Pokémon attacca\nun turno sì e uno no.", "こうげきが　れんぞくで\nだせない。", "攻撃が　連続で\nだせない。", "연속으로\n공격할 수 없다.", "El Pokémon no atacará en\nturnos consecutivos.") },
            { PBEAbility.Turboblaze, new PBELocalizedString("Moves can be used\nregardless of Abilities.", "Les cap. spé. adverses ne\nbloquent pas ses cap.", "Att. können ungeachtet d.\nFähigkeit verw. werden.", "Neutralizza le abilità\nche bloccano le mosse.", "とくせいに　かんけいなく\nあいてに　わざを　だせる。", "特性に　関係なく\n相手に　技を　だせる。", "특성에 관계없이 상대에게\n기술을 쓸 수 있다.", "Usa movimientos sin que\nimporten las habilidades.") },
            { PBEAbility.Unaware, new PBELocalizedString("Ignores any stat changes\nin the Pokémon.", "Ignore les changements de\nstats de l'ennemi.", "Ignoriert Wechsel der\nStatuswerte des Zieles.", "Ignora i cambi delle\nstatistiche del nemico.", "あいての　のうりょくの\nへんかを　むしする。", "相手の　能力の\n変化を　無視する。", "상대의 능력\n변화를 무시한다.", "Ignora mejoras en las\ncaracterísticas del rival.") },
