        public static ReadOnlyDictionary<PBEItem, PBELocalizedString> Descriptions { get; } = new ReadOnlyDictionary<PBEItem, PBELocalizedString>(new Dictionary<PBEItem, PBELocalizedString>()
        {
            { PBEItem.None, new PBELocalizedString("???", "???", "???", "???", "？？？", "？？？", "???", "???") },
            { PBEItem.AbsorbBulb, new PBELocalizedString("A consumable bulb. If the holder is hit\nby a Water-type move, its Sp. Atk\nwill rise.", "Bulbe jetable. Tenu, il augmente\nl'Attaque Spéciale lorsque le Pokémon\nsubit une attaque de type Eau.", "Eine einmalig einsetzbare Knolle.\nErhöht den Spez.-Angr. des Trägers,\nwenn ihn eine Wasser-Attacke trifft.", "Bulbo monouso. Se il Pokémon che\nlo tiene subisce una mossa di tipo\nAcqua, il suo Attacco Speciale aumenta.", "つかいすての　きゅうこん。\nもたせて　みずの　わざを　うけると\nとくこうが　あがる。", "使い捨ての　きゅうこん。\n持たせて　みずの　技を　受けると\n特攻が　あがる。", "단 한 번 쓰면 없어지는 구근.\n지니게 하고 물 기술을 받으면\n특수공격이 올라간다.", "Raíz de usar y tirar que sube el Ataque\nEspecial cuando se recibe un ataque\nde tipo Agua.") },
            { PBEItem.AdamantOrb, new PBELocalizedString("A brightly gleaming orb to be held by\nDialga. It boosts the power of Dragon-\nand Steel-type moves.", "Orbe très brillant destiné à Dialga.\nAugmente la puissance des capacités\nde type Dragon et Acier.", "Ein hell leuchtender Orb.\nVerstärkt Attacken vom Typ Drachen\nund Stahl, wenn Dialga ihn trägt.", "Sfera molto luminosa da far tenere\na Dialga. Potenzia le mosse di tipo\nDrago e Acciaio.", "ディアルガに　もたせると　ドラゴンと\nはがねタイプの　わざの　いりょくが\nあがる　ひかり　かがやく　たま。", "ディアルガに　持たせると　ドラゴンと\nはがねタイプの　技の　威力が\nあがる　光り　輝く　珠。", "디아루가에게 지니게 하면 드래곤과\n강철타입 기술의 위력이 올라가는\n반짝반짝 빛나는 구슬.", "Una esfera brillante que potencia los\nmovimientos de tipo Acero y Dragón.\nDebe llevarla Dialga.") },
            { PBEItem.AguavBerry, new PBELocalizedString("If held by a Pokémon, it restores the\nuser's HP in a pinch, but will cause\nconfusion if it hates the taste.", "Tenue, cette Baie restaure des PV en\ncas de besoin, mais rend confus si\nle Pokémon n'aime pas son goût.", "Als getragenes Item füllt die Beere die\nKP eines Pokémon auf, doch ihr Aroma\nverwirrt jene, die es nicht mögen.", "Se tenuta da un Pokémon in difficoltà,\nrestituisce PS. Se al Pokémon non piace\nil suo sapore, può causare confusione.", "ポケモンに　もたせると\nピンチのとき　ＨＰを　かいふくする。\nきらいな　あじだと　こんらん　する。", "ポケモンに　持たせると\nピンチのとき　ＨＰを　回復する。\nきらいな味だと　混乱する。", "포켓몬에게 지니게 하면\n위급할 때 HP를 회복한다.\n싫어하는 맛이면 혼란에 빠진다.", "Los Pokémon pueden llevarla o usarla\npara restaurar algunos PS. Su sabor\npuede causar confusión.") },
            { PBEItem.AirBalloon, new PBELocalizedString("When held by a Pokémon, the Pokémon\nwill float into the air. When the holder\nis attacked, this item will burst.", "Tenu par un Pokémon, cet objet lui\npermet de flotter dans les airs.\nIl éclate en cas d'attaque.", "Gibt man es einem Pokémon zum Tragen,\nfängt es an zu schweben. Wird es\ngetroffen, platzt das Item.", "Permette al Pokémon che lo tiene di\nfluttuare in aria. Scoppia se è colpito\nda un attacco.", "ポケモンに　もたせると\nポケモンが　ちゅうに　うく。\nこうげきを　うけると　われてしまう。", "ポケモンに　持たせると\nポケモンが　宙に　浮く。\n攻撃を　受けると　割れてしまう。", "포켓몬에게 지니게 하면\n포켓몬이 허공에 뜬다.\n공격을 받으면 터져버린다.", "El Pokémon que lo lleve flotará en el\naire. Si recibe un golpe, estallará.") },
            { PBEItem.AmuletCoin, new PBELocalizedString("An item to be held by a Pokémon.\nIt doubles a battle's prize money if\nthe holding Pokémon joins in.", "Objet à tenir. Double l'argent gagné\nsi le porteur se joint au combat.", "Ein Item, das ein Pokémon trägt und\ndas doppeltes Preisgeld bringt, wenn\nder Träger am Kampf teilnimmt.", "Strumento da far tenere a un Pokémon.\nRaddoppia i guadagni nella lotta se\nchi lo tiene vi partecipa.", "もたせた　ポケモンが　いちどでも\nせんとうに　かおを　だすと\nおかねが　２ばい　もらえる。", "持たせた　ポケモンが　１度でも\n戦闘に　顔を　だすと\nお金が　２倍　もらえる。", "지니게 한 포켓몬이\n한 번이라도 배틀에 나가면\n돈을 2배로 받을 수 있다.", "Si el Pokémon que la lleva lucha en\nun combate, duplica las ganancias.") },
            { PBEItem.Antidote, new PBELocalizedString("A spray-type medicine.\nIt lifts the effect of poison from one\nPokémon.", "Un médicament sous forme de spray.\nGuérit un Pokémon empoisonné.", "Ein Spray, das Vergiftungen\neines Pokémon heilt.", "Rimedio spray.\nCura un Pokémon dall'avvelenamento.", "スプレーしきの　くすり。\nポケモン　１ひきの　どくの\nじょうたいを　かいふくする。", "スプレー式の　薬。\nポケモン　１匹の　どくの\n状態を　回復する。", "스프레이식의 약.\n포켓몬 1마리의\n독 상태를 회복한다.", "Medicina en espray que contrarresta\nlos efectos del veneno en un Pokémon.") },
            { PBEItem.ApicotBerry, new PBELocalizedString("If held by a Pokémon, it raises its\nSp. Def stat in a pinch.", "Tenue, cette Baie monte la Déf. Spé.\nen cas de besoin.", "Als getragenes Item erhöht diese Beere\ndie Spezial-Verteidigung eines Pokémon,\nsobald es in einer Notlage steckt.", "Aumenta la Difesa Speciale, se\nil Pokémon che la tiene si trova\nin difficoltà.", "ポケモンに　もたせると\nピンチのとき　じぶんの\nとくぼうが　あがる。", "ポケモンに　持たせると\nピンチのとき　自分の\n特防が　あがる。", "포켓몬에게 지니게 하면\n위급할 때 자신의\n특수방어가 올라간다.", "Si la lleva un Pokémon, le sube\nla Defensa Especial en un momento\nde apuro.") },
            { PBEItem.ArmorFossil, new PBELocalizedString("A fossil from a prehistoric Pokémon\nthat lived on the land. It appears to\nbe part of a collar.", "Fossile d'un Pokémon préhistorique\nterrestre. On dirait une partie d'un\ncollier.", "Das Fossil eines uralten Pokémon,\ndas an Land lebte. Es sieht wie\nein Schlüsselbein aus.", "Fossile di un Pokémon preistorico che\nviveva sulla terraferma. Sembra far\nparte di un collare.", "おおむかし　ちじょうで　くらした\nこだい　ポケモンの　カセキ。\nえりかざりの　いちぶ　らしい。", "大昔　地上で　暮らした\n古代ポケモンの　カセキ。\n襟飾りの　一部らしい。", "오랜 옛날 지상에서 살았던\n고대 포켓몬의 화석.\n깃 장식의 일부인 듯하다.", "Fósil de un Pokémon prehistórico\nterrestre. Parece ser parte\nde un cuello.") },
            { PBEItem.AspearBerry, new PBELocalizedString("If held by a Pokémon,\nit defrosts it.", "Baie qui soigne le gel lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon zum\nTragen, heilt diese es von Frost.", "Se tenuta da un Pokémon, lo cura\ndal congelamento.", "ポケモンに　もたせると\nこおりを　かいふくする。\n", "ポケモンに　持たせると\nこおりを　回復する。\n", "포켓몬에게 지니게 하면\n얼음을 회복한다.\n", "Los Pokémon pueden llevarla o usarla\npara descongelarse.") },
            { PBEItem.Awakening, new PBELocalizedString("A spray-type medicine.\nIt awakens a Pokémon from the\nclutches of sleep.", "Un médicament sous forme de spray.\nRéveille un Pokémon endormi.", "Ein Spray, das ein schlafendes\nPokémon aufweckt.", "Rimedio spray.\nSveglia un Pokémon addormentato.", "スプレーしきの　くすり。\nポケモン　１ひきの　ねむりの\nじょうたいを　かいふくする。", "スプレー式の　薬。\nポケモン　１匹の　ねむりの\n状態を　回復する。", "스프레이식의 약.\n포켓몬 1마리의\n잠듦 상태를 회복한다.", "Medicina en espray que libera un\nPokémon de las garras del sueño.") },
            { PBEItem.BabiriBerry, new PBELocalizedString("Weakens a supereffective Steel-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Acier.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Stahl.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Acciaio\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　はがね　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　はがね技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 강철 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Acero\nde un enemigo.") },
            { PBEItem.BalmMushroom, new PBELocalizedString("A rare mushroom which gives off a nice\nfragrance.\nA maniac will buy it for a high price.", "Champignon rare qui répand un\nagréable parfum autour de lui.\nLes amateurs en donnent un bon prix.", "Ein seltener Pilz, der ein angenehmes\nAroma verbreitet. Kenner geben ihr\nletztes Hemd dafür.", "Fungo raro che emana un profumo\ngradevolissimo. Si può vendere a un\nprezzo piuttosto alto agli intenditori.", "あたりいったいに　いい　かおりが\nひろがる　めずらしい　キノコ。\nマニアが　たかく　かってくれる。", "あたりいったいに　いい　香りが\nひろがる　珍しい　キノコ。\nマニアが　高く　買ってくれる。", "주변 일대에 좋은 향기가\n퍼지는 희귀한 버섯.\n마니아가 비싼 값에 산다.", "Extraña seta que desprende un\nagradable aroma. Muy apreciada\npor los gourmets.") },
            { PBEItem.BelueBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.BerryJuice, new PBELocalizedString("A 100% pure juice made of Berries.\nIt restores the HP of one Pokémon by\njust 20 points.", "Une boisson 100% pur jus de Baies.\nRestaure 20 PV à un Pokémon.", "100 %iger Fruchtsaft, aus Beeren\ngekeltert. Er stellt 20 KP eines\nPokémon wieder her.", "Bevanda a base di puro succo di Bacche.\nRestituisce 20 PS ad un Pokémon.", "きのみ　１００％の　ジュース。\nポケモン　１ひきの　ＨＰを\n２０だけ　かいふくする。", "きのみ　１００％の　ジュース。\nポケモン　１匹の　ＨＰを\n２０だけ　回復する。", "나무열매 100% 쥬스.\n포켓몬 1마리의 HP를\n20만큼 회복한다.", "Zumo de Bayas 100% natural que\nrestaura 20 PS de un Pokémon.") },
            { PBEItem.BigMushroom, new PBELocalizedString("A large and rare mushroom.\nIt is sought after by collectors.", "Un grand champignon plutôt rare.\nIl est très populaire parmi\ncertains fans dévoués.", "Ein großer und seltener Pilz. Liebhaber\ndieses Pilzes freuen sich ein Loch in\nden Bauch, wenn sie einen finden.", "Fungo grande e raro. È molto\napprezzato da alcuni intenditori.", "おおきな　めずらしい　キノコ。\nいちぶの　マニアの　あいだでは\nとても　にんきが　たかい。", "おおきな　珍しい　キノコ。\n一部の　マニアの　あいだでは\nとても　人気が　高い。", "크고 진귀한 버섯.\n일부 마니아 사이에서는\n매우 인기가 높다.", "Una gran seta que es poco común\ny muy popular entre determinados\ngrupos de gourmets.") },
            { PBEItem.BigNugget, new PBELocalizedString("A big nugget of pure gold that gives off\na lustrous gleam.\nA maniac will buy it for a high price.", "Grosse pépite d'or pur qui brille\nmagnifiquement.\nLes amateurs en donnent un bon prix.", "Ein großes Nugget aus purem Gold, das\neinen schimmernden Glanz besitzt.\nSammler zahlen einen hohen Preis dafür.", "Grossa pepita d'oro puro dal luccichio\nsfavillante. Si può vendere a un prezzo\npiuttosto alto agli intenditori.", "キラキラと　きんいろに　ひかる\nじゅんきんせいの　おおきな　たま。\nマニアが　たかく　かってくれる。", "キラキラと　金色に　光る\n純金製の　おおきな　玉。\nマニアが　高く　買ってくれる。", "금색으로 반짝반짝 빛나는\n순금으로 만들어진 큰 구슬.\n마니아가 비싼 값에 산다.", "Pepita grande de oro puro que\ndesprende un brillo espectacular.\nMuy valorada por los coleccionistas.") },
            { PBEItem.BigPearl, new PBELocalizedString("A quite-large pearl that sparkles in a\npretty silver color. It can be sold at\na high price to shops.", "Une grande perle qui brille d'une jolie\ncouleur argent. Peut être vendue à\nbon prix aux magasins.", "Eine relativ große Perle, die in\nschönem Silber funkelt. Sie ist\nvon hohem Wert.", "Perla piuttosto grande dai graziosi\nriflessi argentei. Si può vendere\na un prezzo piuttosto alto nei negozi.", "きれいな　ぎんいろに　ひかる\nかなり　おおつぶの　しんじゅ。\nショップで　たかく　うれる。", "きれいな　銀色に　光る\nかなり　大粒の　真珠。\nショップで　高く　売れる。", "예쁜 은색으로 빛나는\n상당히 큰 낱알의 진주.\n상점에서 비싸게 팔린다.", "Brillante perla plateada de gran\ntamaño que puede venderse\na buen precio en las tiendas.") },
            { PBEItem.BigRoot, new PBELocalizedString("A Pokémon held item that boosts the\npower of HP-stealing moves to let the\nholder recover more HP.", "Objet à tenir augmentant la puissance\ndes attaques volant des PV.", "Ein Item zum Tragen. Verstärkt\nKP-stehlende Attacken.", "Strumento da far tenere a un Pokémon.\nPotenzia le mosse ruba-PS facendo\nrecuperare più PS a chi lo usa.", "もたせると　ＨＰを　すいとる　わざで\nいつもより　おおく　じぶんの　ＨＰを\nかいふくできる。", "持たせると　ＨＰを　吸いとる　技で\nいつもより　おおく　自分の　ＨＰを\n回復できる。", "지니게 하면 HP 흡수 기술을 썼을 때\n여느 때보다 많은 HP를\n자신이 회복할 수 있다.", "El Pokémon que la lleva recupera\nmás PS al usar movimientos que\ndrenan al objetivo.") },
            { PBEItem.BindingBand, new PBELocalizedString("A band that increases the power of\nbinding moves when held.", "Objet à tenir augmentant la puissance\ndes attaques immobilisantes telles\nque Ligotage ou Étreinte.", "Ein Band, das die Stärke aller\nKlammerattacken des Trägers\nerhöht.", "Strumento da far tenere a un Pokémon\nche aumenta la potenza di mosse come\nAvvolgibotta e Legatutto.", "しめつけるわざを　ほじょする　バンド。\nもたせると　しめつけるわざの\nいりょくが　つよく　なる。", "締めつける技を　補助する　バンド。\n持たせると　締めつける技の\n威力が　強くなる。", "조이기 기술을 보조하는 밴드.\n지니게 하면 조이기 기술의\n위력이 강해진다.", "Banda que potencia los movimientos\nque estrujan al rival.") },
            { PBEItem.BlackBelt, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a belt that boosts determination\nand Fighting-type moves.", "Objet à tenir. Ceinture qui augmente\nla détermination et la puissance des\ncapacités de type Combat.", "Ein Item zum Tragen. Der Gürtel\nverstärkt die Entschlossenheit\nund Attacken vom Typ Kampf.", "Strumento da far tenere a un Pokémon.\nCintura che potenzia la determinazione\ne le mosse di tipo Lotta.", "きが　ひきしまる　おび。\nもたせると　かくとうタイプの\nわざの　いりょくが　あがる。", "気が　ひきしまる　帯。\n持たせると　かくとうタイプの\n技の　威力が　あがる。", "마음을 긴장시키는 띠.\n지니게 하면 격투타입\n기술의 위력이 올라간다.", "Debe llevarlo un Pokémon.\nAumenta la decisión y fortalece\nlos movimientos de tipo Lucha.") },
            { PBEItem.BlackFlute, new PBELocalizedString("A toy flute made from black glass.\nA maniac will buy it for a high price.", "Jouet en forme de flûte en verre\nnoir. Les amateurs en donnent\nun bon prix.", "Eine Spielzeugflöte aus schwarzem Glas.\nSammler geben ihr letztes Hemd dafür\nher.", "Flauto giocattolo fatto di vetro nero.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "くろい　ガラスで　できている\nおもちゃの　ふえ。\nマニアが　たかく　かってくれる。", "黒い　ガラスで　できている\nおもちゃの　笛。\nマニアが　高く　買ってくれる。", "검은 유리로 만들어진\n장난감 피리.\n마니아가 비싼 값에 산다.", "Una flauta de juguete hecha de\ncristal negro.\nMuy valorada por los coleccionistas.") },
            { PBEItem.BlackGlasses, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a shady-looking pair of glasses\nthat boosts Dark-type moves.", "Objet à tenir. Paire de lunettes à\nl'aspect louche qui monte la puissance\ndes capacités de type Ténèbres.", "Ein Item zum Tragen. Eine\ndunkle Brille, die Attacken\nvom Typ Unlicht verstärkt.", "Strumento da far tenere a un Pokémon.\nInquietante paio di occhiali che\npotenzia le mosse di tipo Buio.", "あやしく　みえる　メガネ。\nもたせると　あくタイプの\nわざの　いりょくが　あがる。", "怪しく　みえる　メガネ。\n持たせると　あくタイプの\n技の　威力が　あがる。", "괴상하게 보이는 안경.\n지니게 하면 악타입\n기술의 위력이 올라간다.", "Debe llevarlas un Pokémon.\nFortalecen los movimientos de\ntipo Siniestro.") },
            { PBEItem.BlackSludge, new PBELocalizedString("A held item that gradually restores\nthe HP of Poison-type Pokémon.\nIt inflicts damage on all other types.", "Objet à tenir restaurant peu à peu les\nPV des Pokémon de type Poison. Inflige\ndes dégâts à tous les autres types.", "Ein Item zum Tragen, das einem\nGift-Pokémon KP zurückgibt, anderen\nPokémon aber Schaden zufügt.", "Strumento da far tenere a un Pokémon:\nridà gradualmente PS ai Pokémon di\ntipo Veleno. Danneggia tutti gli altri.", "もたせると　どくタイプの　ポケモンは\nすこしずつ　ＨＰを　かいふくする。\nそれいがいは　ＨＰが　へってしまう。", "持たせると　どくタイプの　ポケモンは\n少しずつ　ＨＰを　回復する。\nそれ以外は　ＨＰが　減ってしまう。", "지니게 하면 독타입의 포켓몬은\n조금씩 HP를 회복한다.\n그 이외의 타입은 HP가 줄어 버린다.", "Restaura gradualmente los PS\nde los Pokémon de tipo Veneno\ny daña a todos los demás.") },
            { PBEItem.BlkApricorn, new PBELocalizedString("A black Apricorn\nIt has an indescribable scent.", "Un Noigrume de couleur noire.\nIl dégage un arôme indéfinissable.", "Eine schwarze Aprikoko. Sie riecht\nirgendwie unbeschreiblich.", "Una Ghicocca di colore nero.\nEmana un odore indefinibile.", "くろい　ぼんぐり。\nなんともいえない　においがする。\n", "くろい　ぼんぐり。\nなんともいえない　においがする。\n", "검은색의 규토리.\n말로는 표현할 수 없는 향기가 난다.\n", "Bonguri de color negro y aroma\nindescriptible.") },
            { PBEItem.BluApricorn, new PBELocalizedString("A blue Apricorn.\nIt smells a bit like grass.", "Un Noigrume de couleur bleue.\nIl dégage un arôme un peu aigre.", "Eine blaue Aprikoko. Ein frischer\nGeruch geht von ihr aus.", "Una Ghicocca di colore blu.\nEmana un odore un po' aspro.", "あおい　ぼんぐり。\nすこし　あおくさい　かおりがする。\n", "あおい　ぼんぐり。\n少し　あおくさい　かおりがする。\n", "파란색의 규토리.\n약간 풋내가 난다.\n", "Bonguri de color azul y aroma tierno.") },
            { PBEItem.BlueFlute, new PBELocalizedString("A toy flute made from blue glass.\nA maniac will buy it for a high price.", "Jouet en forme de flûte en verre\nbleu. Les amateurs en donnent\nun bon prix.", "Eine Spielzeugflöte aus blauem Glas.\nSammler geben ihr letztes Hemd dafür\nher.", "Flauto giocattolo fatto di vetro blu.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "あおい　ガラスで　できている\nおもちゃの　ふえ。\nマニアが　たかく　かってくれる。", "青い　ガラスで　できている\nおもちゃの　笛。\nマニアが　高く　買ってくれる。", "파란 유리로 만들어진\n장난감 피리.\n마니아가 비싼 값에 산다.", "Una flauta de juguete hecha\nde cristal azul.\nMuy valorada por los coleccionistas.") },
            { PBEItem.BlueScarf, new PBELocalizedString("An item to be held by a Pokémon.\nIt boosts the Beauty aspect of the\nholder in a Contest.", "Objet à tenir. Augmente la Beauté\nd'un Pokémon dans les Concours.", "Ein Item zum Tragen. Trainer erhöhen\ndamit in Wettbewerben die „Schönheit“\nihres Pokémon.", "Strumento da far tenere a un Pokémon.\nAumenta la “Bellezza\" di chi lo tiene\nin una Gara.", "もたせて　コンテストに　さんかすると\nその　ポケモンは　いつもより\nうつくしく　みられる。", "持たせて　コンテストに　参加すると\nその　ポケモンは　いつもより\nうつくしく　みられる。", "지니게 하여 콘테스트에 참가하면\n그 포켓몬은 여느 때보다\n아름답게 보인다.", "Debe llevarlo un Pokémon.\nAumenta la belleza en los concursos.") },
            { PBEItem.BlueShard, new PBELocalizedString("A small blue shard.\nIt appears to be from some sort of\nimplement made long ago.", "Petit tesson bleu.\nIl semble provenir d'un outil très\nancien.", "Eine blaue Scherbe eines antiken\nWerkzeugs, das vor langer Zeit\nangefertigt wurde.", "Un piccolo coccio blu che con ogni\nprobabilità è un frammento di un antico\nstrumento.", "あおい　ちいさな　かけら。\nむかしに　つくられた　なにかの\nどうぐの　いちぶ　らしい。", "青い　小さな　かけら。\n昔に　つくられた　なにかの\n道具の　一部らしい。", "파란 작은 조각.\n옛날에 만들어진 어떤\n도구의 일부인 듯하다.", "Un pequeño fragmento azul.\nParece formar parte de algún tipo\nde herramienta antigua.") },
            { PBEItem.BlukBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.BridgeMailD, new PBELocalizedString("Stationery featuring a print of\na red drawbridge.\nLet a Pokémon hold it for use.", "Un papier à lettres avec un motif de\npont mobile rouge. Peut être tenu\net livré par un Pokémon.", "Das Briefpapier hat eine knallrote\nZugbrücke als Hintergrund.\nGib es einem Pokémon zum Tragen.", "Carta da lettera che riporta l'immagine\ndi un ponte mobile di color rosso fuoco.\nUn Pokémon può tenerla e consegnarla.", "まっかな　はねばしの　えがらが\nプリントされた　びんせん。\nポケモンに　もたせて　つかう。", "真っ赤な　跳ね橋の　絵柄が\nプリントされた　レターペーパー。\nポケモンに　持たせて　使う。", "새빨간 도개교의 무늬가\n프린트된 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Carta con la imagen de un puente\nlevadizo de color rojo. Haz que\nla entregue un Pokémon.") },
            { PBEItem.BridgeMailM, new PBELocalizedString("Stationery featuring a print of\nan arched bridge.\nLet a Pokémon hold it for use.", "Un papier à lettres avec un motif de\npont bâti sur des arches. Peut être\ntenu et livré par un Pokémon.", "Das Briefpapier hat eine geschwungene\nBrücke als Hintergrundmotiv.\nGib es einem Pokémon zum Tragen.", "Carta da lettera che riporta l'immagine\ndi un ponte ad archi.\nUn Pokémon può tenerla e consegnarla.", "アーチを　えがいた　はしの　えがらが\nプリントされた　びんせん。\nポケモンに　もたせて　つかう。", "アーチを　描いた　橋の　絵柄が\nプリントされた　レターペーパー。\nポケモンに　持たせて　使う。", "아치가 그려진 다리 무늬가\n프린트된 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Carta con la imagen de un puente\ncon forma de varios arcos. Haz\nque la entregue un Pokémon.") },
            { PBEItem.BridgeMailS, new PBELocalizedString("Stationery featuring a print of\na sky-piercing bridge.\nLet a Pokémon hold it for use.", "Un papier à lettres avec un motif de\npont traversant le ciel. Peut être\ntenu et livré par un Pokémon.", "Das Briefpapier hat eine weit in den\nHimmel ragende Brücke als Hintergrund.\nGib es einem Pokémon zum Tragen.", "Carta da lettera che riporta l'immagine\ndi un ponte che attraversa il cielo.\nUn Pokémon può tenerla e consegnarla.", "そらを　つらぬく　はしの　えがらが\nプリントされた　びんせん。\nポケモンに　もたせて　つかう。", "空を　つらぬく　橋の　絵柄が\nプリントされた　レターペーパー。\nポケモンに　持たせて　使う。", "하늘을 관통하는 다리의 무늬가\n프린트된 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Carta con la imagen de un puente\nque se alza hacia el cielo. Haz que\nla entregue un Pokémon.") },
            { PBEItem.BridgeMailT, new PBELocalizedString("Stationery featuring a print of\na steel suspension bridge.\nLet a Pokémon hold it for use.", "Un papier à lettres avec un motif de\npont suspendu en acier. Peut être\ntenu et livré par un Pokémon.", "Das Briefpapier hat eine stählerne\nHängebrücke als Hintergrund.\nGib es einem Pokémon zum Tragen.", "Carta da lettera che riporta l'immagine\ndi un ponte sospeso in acciaio.\nUn Pokémon può tenerla e consegnarla.", "こうてつの　つりばしの　えがらが\nプリントされた　びんせん。\nポケモンに　もたせて　つかう。", "鋼鉄の　つり橋の　絵柄が\nプリントされた　レターペーパー。\nポケモンに　持たせて　使う。", "강철의 현수교 무늬가\n프린트된 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Carta con la imagen de un puente\ncolgante de acero. Haz que la\nentregue un Pokémon.") },
            { PBEItem.BridgeMailV, new PBELocalizedString("Stationery featuring a print of\na brick bridge.\nLet a Pokémon hold it for use.", "Un papier à lettres avec un motif\nde pont en briques. Peut être tenu\net livré par un Pokémon.", "Das Briefpapier hat eine Brücke\naus Ziegelsteinen als Hintergrund.\nGib es einem Pokémon zum Tragen.", "Carta da lettera che riporta l'immagine\ndi un ponte fatto di mattoni.\nUn Pokémon può tenerla e consegnarla.", "レンガづくりの　はしの　えがらが\nプリントされた　びんせん。\nポケモンに　もたせて　つかう。", "レンガ造りの　橋の　絵柄が\nプリントされた　レターペーパー。\nポケモンに　持たせて　使う。", "벽돌로 만든 다리 무늬가\n프린트된 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Carta con la imagen de un puente\nde ladrillos. Haz que la entregue\nun Pokémon.") },
            { PBEItem.BrightPowder, new PBELocalizedString("An item to be held by a Pokémon.\nIt casts a tricky glare that lowers\nthe opponent's accuracy.", "Objet à tenir qui intimide et baisse\nla Précision de l'ennemi.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nsenkt die Genauigkeit des Gegners.", "Strumento da far tenere a un Pokémon.\nEmette un bagliore insidioso che riduce\nla precisione dell'avversario.", "キラキラ　ひかるこな。　もたせると\nひかりが　あいてを　まどわして\nわざが　めいちゅう　しにくくなる。", "キラキラ　光る粉。　持たせると\n光が　相手を　惑わして\n技が　命中しにくくなる。", "반짝반짝 빛나는 가루. 지니게 하면\n빛이 상대를 혼란시켜\n기술이 명중되기 어려워진다.", "Lanza un destello que baja la Precisión\ndel enemigo. Debe llevarlo un Pokémon.") },
            { PBEItem.BugGem, new PBELocalizedString("A gem with an insect-like essence.\nWhen held, it strengthens the power of\na Bug-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Insecte.", "Ein Juwel vom Typ Käfer. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Käfer.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Coleottero.", "むしの　タイプの　ジュエル。\nもたせると　いちどだけ　むしの\nわざの　いりょくが　つよまる。", "むしの　タイプの　ジュエル。\n持たせると　１度だけ　むしの\n技の　威力が　強まる。", "벌레타입의 주얼.\n지니게 하면 한 번만 벌레\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Bicho.\nDebe llevarla un Pokémon.") },
            { PBEItem.BurnDrive, new PBELocalizedString("A cassette to be held by Genesect.\nIt changes Techno Blast to a\nFire-type move.", "Tenu par Genesect, ce module\nchange la capacité TechnoBuster\nen une capacité de type Feu.", "Gibt man es Genesect zum Tragen,\nwandelt es den Typ der Attacke\nTechblaster in Feuer um.", "Modulo da far tenere a Genesect.\nTrasforma Tecnobotto in una mossa\ndi tipo Fuoco.", "ゲノセクトに　もたせると\nテクノバスターという　わざが\nほのおタイプになる　カセット。", "ゲノセクトに　持たせると\nテクノバスターという　技が\nほのおタイプになる　カセット。", "게노세크트에게 지니게 하면\n테크노버스터라는 기술이\n불꽃타입으로 바뀌는 카세트.", "Cartucho que convierte\nTecno Shock de Genesect\nen un movimiento de tipo Fuego.") },
            { PBEItem.BurnHeal, new PBELocalizedString("A spray-type medicine.\nIt heals a single Pokémon that is\nsuffering from a burn.", "Un médicament sous forme de spray.\nSoigne les brûlures d'un Pokémon.", "Ein Spray, das Verbrennungen\neines Pokémon heilt.", "Rimedio spray.\nCura un Pokémon dalla scottatura.", "スプレーしきの　くすり。\nポケモン　１ひきの　やけどの\nじょうたいを　かいふくする。", "スプレー式の　薬。\nポケモン　１匹の　やけどの\n状態を　回復する。", "스프레이식의 약.\n포켓몬 1마리의\n화상 상태를 회복한다.", "Medicina en espray que cura\nlas quemaduras a un Pokémon.") },
            { PBEItem.Calcium, new PBELocalizedString("A nutritious drink for Pokémon.\nIt raises the base Sp. Atk (Special\nAttack) stat of a single Pokémon.", "Une boisson très nutritive qui\nmonte l'Attaque Spéciale de\nbase d'un Pokémon.", "Ein gehaltvolles Getränk, das den\nSpezial-Angriff eines Pokémon\ndauerhaft erhöht.", "Bevanda nutriente per Pokémon.\nAumenta l'Attacco Speciale di base.", "ポケモンの　えいようドリンク。\nポケモン　１ひきの　とくこうの\nきそポイントを　あげる。", "ポケモンの　栄養ドリンク。\nポケモン　１匹の　特攻の\n基礎ポイントを　あげる。", "포켓몬의 영양 드링크.\n포켓몬 1마리의 특수공격\n기초 포인트를 올린다.", "Nutritiva bebida que aumenta el Ataque\nEspecial de base de un Pokémon.") },
            { PBEItem.Carbos, new PBELocalizedString("A nutritious drink for Pokémon.\nIt raises the base Speed stat of a\nsingle Pokémon.", "Une boisson très nutritive qui monte\nla Vitesse de base d'un Pokémon.", "Ein gehaltvolles Getränk, das den\nInitiative-Wert eines Pokémon\ndauerhaft erhöht.", "Bevanda nutriente per Pokémon.\nAumenta la Velocità di base.", "ポケモンの　えいようドリンク。\nポケモン　１ひきの　すばやさの\nきそポイントを　あげる。", "ポケモンの　栄養ドリンク。\nポケモン　１匹の　素早さの\n基礎ポイントを　あげる。", "포켓몬의 영양 드링크.\n포켓몬 1마리의 스피드\n기초 포인트를 올린다.", "Nutritiva bebida que aumenta la\nVelocidad de base de un Pokémon.") },
            { PBEItem.Casteliacone, new PBELocalizedString("Castelia City's specialty, soft-serve\nice cream. It heals all the status\nproblems of a single Pokémon.", "Spécialité de Volucité. Elle\nsoigne tous les problèmes\nde statut d'un Pokémon.", "Dieses Eis ist eine Spezialität\naus Stratos City. Es behebt alle\nStatusprobleme eines Pokémon.", "Il famoso gelato di Austropoli. Cura\ntutti i problemi di stato di un Pokémon.", "ヒウンシティの　めいぶつアイス。\nポケモン　１ひきの　じょうたいの\nいじょうを　すべて　かいふくする。", "ヒウンシティの　名物アイス。\nポケモン　１匹の　状態の\n異常を　すべて　回復する。", "구름시티의 명물 아이스.\n포켓몬 1마리의 상태 이상을\n모두 회복한다.", "Especialidad de Ciudad Porcelana.\nCura los problemas de estado de\nun Pokémon.") },
            { PBEItem.CellBattery, new PBELocalizedString("A consumable battery. If the\nholder is hit by an Electric-type\nmove, its Attack will rise.", "Pile jetable. Tenue, elle augmente\nl'Attaque lorsque le Pokémon subit\nune attaque de type Électrik.", "Ein einmalig aufladbarer Akku.\nErhöht den Angriff des Trägers, wenn\nihn eine Elektro-Attacke trifft.", "Pila monouso. Se il Pokémon che la\ntiene subisce un attacco di tipo\nElettro, il suo Attacco aumenta.", "つかいすての　じゅうでんち。\nもたせて　でんきの　わざを　うけると\nこうげきが　あがる。", "使い捨ての　じゅうでんち。\n持たせて　でんきの　技を　受けると\n攻撃が　あがる。", "단 한 번 쓰면 없어지는 충전지.\n지니게 하고 전기 기술을 받으면\n공격이 올라간다.", "Batería de usar y tirar que sube\nel Ataque de quien la lleva cuando\nrecibe un golpe de tipo Eléctrico.") },
            { PBEItem.Charcoal, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a combustible fuel that boosts\nthe power of Fire-type moves.", "Objet à tenir. Combustible montant la\npuissance des capacités de type Feu.", "Ein Item zum Tragen. Ein Brennstoff,\nder Attacken vom Typ Feuer verstärkt.", "Strumento da far tenere a un Pokémon.\nSostanza combustibile che potenzia\nle mosse di tipo Fuoco.", "ものを　もやす　ねんりょう。\nもたせると　ほのおタイプの\nわざの　いりょくが　あがる。", "ものを　燃やす　燃料。\n持たせると　ほのおタイプの\n技の　威力が　あがる。", "물건을 태우는 연료.\n지니게 하면 불꽃타입\n기술의 위력이 올라간다.", "Actúa como combustible y potencia\nlos movimientos de tipo Fuego.\nDebe llevarlo un Pokémon.") },
            { PBEItem.ChartiBerry, new PBELocalizedString("Weakens a supereffective Rock-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Roche.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Gestein.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Roccia\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　いわ　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　いわ技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 바위 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Roca\nde un enemigo.") },
            { PBEItem.CheriBerry, new PBELocalizedString("If held by a Pokémon,\nit recovers from paralysis.", "Baie qui soigne la paralysie lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon zum\nTragen, heilt diese es von Paralyse.", "Se tenuta da un Pokémon, lo cura\ndalla paralisi.", "ポケモンに　もたせると\nまひを　かいふくする。\n", "ポケモンに　持たせると\nまひを　回復する。\n", "포켓몬에게 지니게 하면\n마비를 회복한다.\n", "Los Pokémon pueden llevarla o usarla\npara recuperarse de la parálisis.") },
            { PBEItem.CherishBall, new PBELocalizedString("A quite rare Poké Ball that has been\nspecially crafted to commemorate\nan occasion of some sort.", "Une Poké Ball plutôt rare qui fut créée\npour une occasion spéciale.", "Ein seltener Ball, der als\nReminiszenz an irgendein\nEreignis hergestellt wurde.", "Poké Ball molto esclusiva creata per\ncommemorare un evento speciale.", "なにかの　きねんの　しなとして\nとくべつに　つくられた\nかなり　めずらしい　ボール。", "なにかの　記念の　品として\n特別に　つくられた\nかなり　珍しい　ボール。", "무언가의 기념품으로\n특별히 만들어진\n상당히 진귀한 볼.", "Es una Poké Ball bastante singular\nque se creó para conmemorar\nalguna ocasión especial.") },
            { PBEItem.ChestoBerry, new PBELocalizedString("If held by a Pokémon,\nit recovers from sleep.", "Baie qui soigne le sommeil lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon\nzum Tragen, erwacht es dadurch\naus dem Schlaf.", "Se tenuta da un Pokémon, lo risveglia\ndal sonno.", "ポケモンに　もたせると\nねむりを　かいふくする。\n", "ポケモンに　持たせると\n眠りを　回復する。\n", "포켓몬에게 지니게 하면\n잠듦을 회복한다.\n", "Los Pokémon pueden llevarla o usarla\npara despertar del sueño.") },
            { PBEItem.ChilanBerry, new PBELocalizedString("Weakens a Normal-type attack against\nthe Pokémon holding this berry.", "Tenue, cette Baie affaiblit une attaque\nennemie de type Normal.", "Als getragenes Item schwächt diese\nBeere Attacken des Angreifers vom\nTyp Normal.", "Se tenuta da un Pokémon, indebolisce\nl'attacco di tipo Normale del nemico.", "ポケモンに　もたせると\nノーマル　わざを　うけたとき\nいりょくが　よわまる。", "ポケモンに　持たせると\nノーマル技を　受けたとき\n威力が　弱まる。", "포켓몬에게 지니게 하면\n노말 기술을 당했을 때\n위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque de tipo Normal de un enemigo.") },
            { PBEItem.ChillDrive, new PBELocalizedString("A cassette to be held by Genesect.\nIt changes Techno Blast to an\nIce-type move.", "Tenu par Genesect, ce module\nchange la capacité TechnoBuster\nen une capacité de type Glace.", "Gibt man es Genesect zum Tragen,\nwandelt es den Typ der Attacke\nTechblaster in Eis um.", "Modulo da far tenere a Genesect.\nTrasforma Tecnobotto in una mossa\ndi tipo Ghiaccio.", "ゲノセクトに　もたせると\nテクノバスターという　わざが\nこおりタイプになる　カセット。", "ゲノセクトに　持たせると\nテクノバスターという　技が\nこおりタイプになる　カセット。", "게노세크트에게 지니게 하면\n테크노버스터라는 기술이\n얼음타입으로 바뀌는 카세트.", "Cartucho que convierte\nTecno Shock de Genesect\nen un movimiento de tipo Hielo.") },
            { PBEItem.ChoiceBand, new PBELocalizedString("An item to be held by a Pokémon.\nThis headband ups Attack, but allows\nthe use of only one of its moves.", "Objet à tenir. Ce bandeau monte\nl'Attaque, mais ne permet d'utiliser\nqu'une seule capacité par combat.", "Ein Item, das ein Pokémon tragen\nkann. Es stärkt den Angriffs-Wert,\naber nur eine Attacke ist einsetzbar.", "Strumento da far tenere a un Pokémon.\nQuesta benda aumenta l'Attacco, ma\npermette l'uso di una sola mossa.", "ちょっと　こだわった　ハチマキ。\nもたせると　こうげきは　あがるが\nおなじ　わざしか　だせなくなる。", "ちょっと　こだわった　ハチマキ。\n持たせると　攻撃は　あがるが\n同じ　技しか　だせなくなる。", "기술을 구애받는 머리띠.\n지니게 하면 공격은 올라가지만\n똑같은 기술밖에 쓰지 못한다.", "Aumenta el Ataque, pero solo permite\nusar un movimiento.\nDebe llevarla un Pokémon.") },
            { PBEItem.ChoiceScarf, new PBELocalizedString("An item to be held by a Pokémon.\nThis scarf boosts Speed, but allows\nthe use of only one of its moves.", "Objet à tenir augmentant la Vitesse,\nmais ne permettant d'utiliser qu'une\nseule capacité par combat.", "Ein Item zum Tragen. Der Schal\nerhöht den Initiative-Wert, aber nur\neine Attacke ist einsetzbar.", "Strumento da far tenere a un Pokémon.\nStola che aumenta la Velocità, ma\nconsente l'uso di una sola mossa.", "ちょっと　こだわった　スカーフ。\nもたせると　すばやさは　あがるが\nおなじ　わざしか　だせなくなる。", "ちょっと　こだわった　スカーフ。\n持たせると　素早さは　あがるが\n同じ　技しか　だせなくなる。", "기술을 구애받는 스카프.\n지니게 하면 스피드는 올라가지만\n같은 기술밖에 쓸 수 없다.", "Debe llevarlo un Pokémon. Potencia\nla Velocidad, pero solo permite usar\nun movimiento.") },
            { PBEItem.ChoiceSpecs, new PBELocalizedString("An item to be held by a Pokémon. These\ndistinctive glasses boost Sp. Atk but\nallow the use of only one of its moves.", "Objet à tenir augmentant l'Attaque\nSpéciale, mais ne permettant d'utiliser\nqu'une seule capacité par combat.", "Ein Item zum Tragen. Die Brille\nerhöht den Spezial-Angriff, aber\nnur eine Attacke ist einsetzbar.", "Strumento da far tenere a un Pokémon.\nQuesti occhiali aumentano l'Att. Sp., ma\npermettono l'uso di una sola mossa.", "ちょっと　こだわった　メガネ。\nもたせると　とくこうは　あがるが\nおなじ　わざしか　だせなくなる。", "ちょっと　こだわった　メガネ。\n持たせると　特攻は　あがるが\n同じ　技しか　だせなくなる。", "기술을 구애받는 안경.\n지니게 하면 특수공격이 올라가지만\n같은 기술밖에 쓸 수 없다.", "Potencian el Ataque Especial, pero solo\npermiten usar un movimiento.\nDebe llevarlas un Pokémon.") },
            { PBEItem.ChopleBerry, new PBELocalizedString("Weakens a supereffective Fighting-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Combat.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Kampf.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Lotta\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　かくとう　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　かくとう技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 격투 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Lucha\nde un enemigo.") },
            { PBEItem.ClawFossil, new PBELocalizedString("A fossil of an ancient Pokémon that\nlived in the sea. It appears to be\npart of a claw.", "Fossile d'un ancien Pokémon aquatique.\nOn dirait un morceau de griffe.", "Das Fossil eines uralten Pokémon,\ndas in der Tiefsee lebte. Es sieht\nwie eine Klaue aus.", "Fossile di un antico Pokémon che\nviveva in mare. Sembra essere\nparte di un artiglio.", "おおむかし　うみに　すんでいた\nこだい　ポケモンの　カセキ。\nツメの　いちぶ　らしい。", "大昔　海に　すんでいた\n古代ポケモンの　カセキ。\nツメの　一部らしい。", "오랜 옛날 바다에 살았던\n고대 포켓몬의 화석.\n발톱의 일부인 듯하다.", "Fósil de un Pokémon ancestral que\nvivió en el fondo del mar. Parece\nser parte de una garra.") },
            { PBEItem.CleanseTag, new PBELocalizedString("An item to be held by a Pokémon.\nIt helps keep wild Pokémon away if the\nholder is the first one in the party.", "Objet à tenir. Aide à repousser les\nPokémon sauvages si le porteur est\nle premier Pokémon de l'équipe.", "Ein Item zum Tragen. Reduziert\nBegegnungen mit wilden Pokémon, wenn\nder Träger an erster Stelle steht.", "Strumento da far tenere a un Pokémon.\nAiuta ad allontanare i Pokémon selvatici\nse chi lo tiene è il primo della squadra.", "いちばん　まえの　ポケモンに\nもたせると　やせいポケモンが\nでて　きにくくなる。", "１番　前の　ポケモンに\n持たせると　野生ポケモンが\nでて　きにくくなる。", "맨 앞의 포켓몬에게\n지니게 하면 야생 포켓몬이\n나타날 확률이 낮아진다.", "Si lo lleva el primer Pokémon del\nequipo, se reduce la probabilidad de\nque se acerquen Pokémon salvajes.") },
            { PBEItem.CleverWing, new PBELocalizedString("An item for use on a Pokémon. It\nslightly increases the base Sp. Def\nstat of a single Pokémon.", "Objet à utiliser sur un Pokémon.\nIl augmente un peu sa\nDéfense Spéciale de base.", "Schenkt man einem Pokémon dieses\nItem, wird dessen Spezial-Verteidigung\ndauerhaft erhöht.", "Strumento da usare su un Pokémon.\nNe aumenta di poco la Difesa Speciale\ndi base.", "ポケモンに　つかう　どうぐ。\nポケモン　１ひきの　とくぼうの\nきそポイントを　ちょっと　あげる。", "ポケモンに　使う　道具。\nポケモン　１匹の　特防の\n基礎ポイントを　ちょっと　あげる。", "포켓몬에게 사용하는 도구.\n포켓몬 1마리의 특수방어\n기초 포인트를 조금 올린다.", "Pluma que aumenta un poco\nla Defensa Especial de un Pokémon\nal subir de nivel.") },
            { PBEItem.CobaBerry, new PBELocalizedString("Weakens a supereffective Flying-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Vol.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Flug.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Volante\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　ひこう　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　ひこう技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 비행 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Volador\nde un enemigo.") },
            { PBEItem.ColburBerry, new PBELocalizedString("Weakens a supereffective Dark-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une\nattaque ennemie super efficace\nde type Ténèbres.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Unlicht.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Buio\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　あく　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　あく技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 악 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Siniestro\nde un enemigo.") },
            { PBEItem.CometShard, new PBELocalizedString("A shard which fell to the ground when\na comet approached.\nA maniac will buy it for a high price.", "Un fragment de comète, tombé au sol\nquand elle traversait l'atmosphère.\nLes amateurs en donnent un bon prix.", "Ein Kometensplitter, der aus dem All\nherabgefallen ist. Sammler geben ihr\nletztes Hemd dafür.", "Scheggia di cometa caduta al suolo.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "すいせいが　ちかづいたとき\nちひょうに　おちた　かけら。\nマニアが　たかく　かってくれる。", "すい星が　近づいたとき\n地表に　落ちた　かけら。\nマニアが　高く　買ってくれる。", "혜성이 가까워졌을 때\n지표면에 떨어진 조각.\n마니아가 비싼 값에 산다.", "Fragmento de un cometa caído al\nsuelo al atravesar la atmósfera.\nMuy valorado por los coleccionistas.") },
            { PBEItem.CornnBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.CoverFossil, new PBELocalizedString("A fossil of an ancient Pokémon that\nlived in the sea in ancient times.\nIt appears to be part of its back.", "Fossile d'un ancien Pokémon\naquatique. Il semble que ce\nsoit une partie de son dos.", "Das Fossil eines uralten Pokémon,\ndas in der Tiefsee lebte. Offenbar\nhandelt sich um einen Teil des Rückens.", "Fossile di un Pokémon preistorico che\nviveva nel mare. Sembra essere parte\ndel suo dorso.", "おおむかし　うみに　すんでいた\nこだい　ポケモンの　カセキ。\nせなかの　いちぶ　らしい。", "大昔　海に　すんでいた\n古代ポケモンの　カセキ。\nせなかの　一部らしい。", "오랜 옛날 바다에 살았던\n고대 포켓몬의 화석.\n등의 일부인 듯하다.", "Fósil de un Pokémon ancestral que\nvivió en el fondo del mar. Parece ser\nun fragmento de concha marina.") },
            { PBEItem.CustapBerry, new PBELocalizedString("If held by a Pokémon, it gets to move\nfirst just once in a pinch.", "Tenue, cette Baie permet d'attaquer\nen premier une fois en cas de besoin.", "Als getragenes Item ermöglicht\ndiese Beere dem Träger in einer\nNotlage den Erstschlag.", "Se il Pokémon che ce l'ha è in difficoltà,\ngli consente di fare per primo la sua\nmossa. Funziona una sola volta.", "ポケモンに　もたせると\nピンチのとき　いちどだけ\nこうどうが　はやくなる。", "ポケモンに　持たせると\nピンチのとき　１度だけ\n行動が　早くなる。", "포켓몬에게 지니게 하면\n위급할 때 한 번만\n행동이 빨라진다.", "El Pokémon que la lleva puede actuar\nen primer lugar una vez cuando se\nencuentra en un momento de apuro.") },
            { PBEItem.DampMulch, new PBELocalizedString("A fertilizer to be spread on soft soil\nin regions where Berries are grown.\nA maniac will buy it for a high price.", "Un engrais utilisé sur sol meuble.\nLes amateurs en donnent un bon prix.", "In Regionen, wo man Beeren anbaut,\nmengt man ihn unter weiche Erde.\nSammler geben ihr letztes Hemd dafür.", "Usato per favorire la crescita delle\nBacche nelle regioni dove si coltivano.\nGli intenditori pagano caro per averlo.", "きのみを　そだてる　ちほうでは\nふかふかのつちに　まいて　つかう。\nマニアが　たかく　かってくれる。", "きのみを　育てる　地方では\nふかふかのつちに　まいて　使う。\nマニアが　高く　買ってくれる。", "나무열매를 키우는 지방에서는\n푹신푹신한 땅에 뿌려 쓴다.\n마니아가 비싼 값에 산다.", "Un fertilizante usado en suelo fértil\ndonde crecen las Bayas.\nMuy apreciado por los coleccionistas.") },
            { PBEItem.DampRock, new PBELocalizedString("A Pokémon held item that extends the\nduration of the move Rain Dance used\nby the holder.", "Objet à tenir augmentant la durée\nde la capacité Danse Pluie utilisée\npar le porteur.", "Ein Item zum Tragen. Verlängert\ndie Dauer von Regentanz, wenn\nvon Träger eingesetzt.", "Strumento da far tenere a un Pokémon\nche allunga la durata della mossa\nPioggiadanza se usata da chi lo tiene.", "もたせた　ポケモンが　あまごいを\nつかうと　いつもより　あめの\nじかんが　ながくなる。", "持たせた　ポケモンが　あまごいを\n使うと　いつもより　あめの\n時間が　長くなる。", "지니게 한 포켓몬이 비바라기를\n쓰면 여느 때보다 비내리는\n시간이 길어진다.", "Prolonga la duración del movimiento\nDanza Lluvia que use el Pokémon\nque la lleva.") },
            { PBEItem.DarkGem, new PBELocalizedString("A gem with an essence of darkness.\nWhen held, it strengthens the power of\na Dark-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Ténèbres.", "Ein Juwel vom Typ Unlicht. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Unlicht.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Buio.", "あくの　タイプの　ジュエル。\nもたせると　いちどだけ　あくの\nわざの　いりょくが　つよまる。", "あくの　タイプの　ジュエル。\n持たせると　１度だけ　あくの\n技の　威力が　強まる。", "악타입의 주얼.\n지니게 하면 한 번만 악\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Siniestro.\nDebe llevarla un Pokémon.") },
            { PBEItem.DawnStone, new PBELocalizedString("A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt sparkles like eyes.", "Pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle brille comme un œil.", "Dieser besondere, glitzernde Stein\nhilft manchen Pokémon bei\nihrer Evolution.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nÈ luccicante come gli occhi.", "ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nひとみのように　まばゆい。", "ある　特定の　ポケモンを\n進化させる　不思議な　石。\nひとみのように　まばゆい。", "어느 특정 포켓몬을\n진화시키는 이상한 돌.\n눈동자처럼 아름답다.", "Una piedra peculiar que hace\nevolucionar a algunos Pokémon.\nBrilla como un lucero.") },
            { PBEItem.DeepSeaScale, new PBELocalizedString("An item to be held by Clamperl.\nA scale that shines a faint pink, it\nraises the Sp. Def stat.", "Objet à faire tenir à Coquiperl.\nÉcaille de couleur rose montant\nsa Défense Spéciale.", "Ein Item, das von Perlu getragen\nwerden kann. Es schimmert pink\nund hebt die Spezial-Verteidigung.", "Strumento da far tenere a Clamperl.\nSquama dai bagliori rosei che aumenta\nla Difesa Speciale.", "パールルに　もたせると\nとくぼうが　あがる　ウロコ。\nうすい　ピンクいろに　ひかる。", "パールルに　持たせると\n特防が　あがる　ウロコ。\nうすい　ピンク色に　光る。", "진주몽에게 지니게 하면\n특수방어가 올라가는 비늘.\n연한 분홍색으로 빛난다.", "Tiene un débil brillo rosado\ny debe llevarla Clamperl.\nSube la Defensa Especial.") },
            { PBEItem.DeepSeaTooth, new PBELocalizedString("An item to be held by Clamperl.\nA fang that gleams a sharp silver, it\nraises the Sp. Atk stat.", "Objet à faire tenir à Coquiperl.\nDent de couleur argent montant\nson Attaque Spéciale.", "Ein Item, das von Perlu getragen\nwerden kann. Es schimmert silbern\nund hebt den Spezial-Angriff.", "Strumento da far tenere a Clamperl.\nDente dai bagliori argentei che aumenta\nl'Attacco Speciale.", "パールルに　もたせると\nとくこうが　あがる　キバ。\nするどく　ぎんいろに　ひかる。", "パールルに　持たせると\n特攻が　あがる　キバ。\n鋭く　銀色に　光る。", "진주몽에게 지니게 하면\n특수공격이 올라가는 이빨.\n날카로운 은색으로 빛난다.", "Tiene el brillo afilado de la plata\ny debe llevarlo Clamperl.\nSube el Ataque Especial.") },
            { PBEItem.DestinyKnot, new PBELocalizedString("A long, thin, bright-red string to\nbe held by a Pokémon. If the holder\nbecomes infatuated, the foe does too.", "Un long fil rouge vif à faire tenir à un\nPokémon. Si le porteur succombe à\nl'attirance, l'ennemi aussi.", "Ein Item zum Tragen. Wird\nder Träger betört, gilt\ndies auch für den Angreifer.", "Gomitolo di filo rosso sottile da dare\na un Pokémon. Se il Pokémon rimane\ninfatuato, contagia anche il nemico.", "ほそくて　ながい　まっかな　いと。\nもたせると　メロメロに　なったとき\nあいても　メロメロに　できる。", "細くて　長い　真っ赤な　糸。\n持たせると　メロメロに　なったとき\n相手も　メロメロに　できる。", "가늘고 긴 새빨간 실.\n지니게 하면 자신이 헤롱헤롱해졌을 때\n상대도 헤롱헤롱해지게 된다.", "Un hilo largo y delgado de color rojo\nque transmite el enamoramiento del\nPokémon que lo lleva a su objetivo.") },
            { PBEItem.DireHit, new PBELocalizedString("It raises the critical-hit ratio greatly.\nIt can be used only once and wears off\nif the Pokémon is withdrawn.", "Monte fortement le taux de critiques.\nUtilisable une seule fois. L'effet\ndisparaît si le Pokémon se retire.", "Hebt einmalig die Volltrefferquote des\nPokémon im Kampf stark an. Die Wirkung\nendet mit dem Austausch des Pokémon.", "Brutti colpi molto più probabili.\nSi può usare una sola volta e l'effetto\nsvanisce se il Pokémon è sostituito.", "きゅうしょりつが　おおきく　あがる。\nいちどしか　つかうことが　できない。\nひっこめると　もとに　もどる。", "急所率が　大きく　あがる。\n１度しか　使うことが　できない。\nひっこめると　元に　戻る。", "급소 명중률이 크게 올라간다.\n한 번밖에 쓸 수 없다. 포켓몬을 볼에\n넣어버리면 원래대로 되돌아간다.", "Aumenta el índice de golpe crítico\nen combate. Al cambiar de\nPokémon, el efecto desaparece.") },
            { PBEItem.DiveBall, new PBELocalizedString("A somewhat different Poké Ball that\nworks especially well on Pokémon that\nlive underwater.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien\nsur les Pokémon sous-marins.", "Ein Ball, mit dem sich Pokémon,\ndie im Wasser leben, besser\nfangen lassen.", "Poké Ball un po' diversa. Risulta\nparticolarmente efficace sui Pokémon\nche vivono in acqua.", "みずの　せかいで　くらしている\nポケモンが　つかまえやすくなる\nちょっと　かわった　ボール。", "水の　世界で　暮らしている\nポケモンが　捕まえやすくなる\nちょっと　変わった　ボール。", "물의 세계에서 사는\n포켓몬을 잡기 쉬운\n조금 특이한 볼.", "Es algo distinta a las demás. Funciona\nespecialmente bien con los Pokémon\nque viven bajo el agua.") },
            { PBEItem.DomeFossil, new PBELocalizedString("A fossil of an ancient Pokémon that\nlived in the sea. It appears to be\npart of a shell.", "Fossile d'un ancien Pokémon aquatique.\nOn dirait un morceau de carapace.", "Das Fossil eines uralten Pokémon,\ndas in der Tiefsee lebte. Es sieht\nwie das Stück einer Muschel aus.", "Fossile di un Pokémon antico che\nviveva in mare. Sembra essere\nparte di una corazza.", "おおむかし　うみに　すんでいた\nこだい　ポケモンの　カセキ。\nこうらの　いちぶ　らしい。", "大昔　海に　すんでいた\n古代ポケモンの　カセキ。\nこうらの　一部らしい。", "오랜 옛날 바다에 살았던\n고대 포켓몬의 화석.\n등 껍질의 일부인 듯하다.", "Fósil de un Pokémon ancestral\nque vivió en el fondo del mar.\nParece ser un fragmento de concha.") },
            { PBEItem.DouseDrive, new PBELocalizedString("A cassette to be held by Genesect.\nIt changes Techno Blast to a\nWater-type move.", "Tenu par Genesect, ce module\nchange la capacité TechnoBuster\nen une capacité de type Eau.", "Gibt man es Genesect zum Tragen,\nwandelt es den Typ der Attacke\nTechblaster in Wasser um.", "Modulo da far tenere a Genesect.\nTrasforma Tecnobotto in una mossa\ndi tipo Acqua.", "ゲノセクトに　もたせると\nテクノバスターという　わざが\nみずタイプになる　カセット。", "ゲノセクトに　持たせると\nテクノバスターという　技が\nみずタイプになる　カセット。", "게노세크트에게 지니게 하면\n테크노버스터라는 기술이\n물타입으로 바뀌는 카세트.", "Cartucho que convierte\nTecno Shock de Genesect\nen un movimiento de tipo Agua.") },
            { PBEItem.DracoPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Dragon-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Dragon.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Drachen.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Drago.", "ドラゴンの　タイプの　せきばん。\nもたせると　ドラゴンタイプの\nわざの　いりょくが　つよまる。", "ドラゴンの　タイプの　石盤。\n持たせると　ドラゴンタイプの\n技の　威力が　強まる。", "드래곤타입의 석판.\n지니게 하면 드래곤타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Dragón.\nDebe llevarla un Pokémon.") },
            { PBEItem.DragonFang, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a hard and sharp fang that ups\nthe power of Dragon-type moves.", "Objet à tenir. Croc dur et pointu\nmontant la puissance des capacités\nde type Dragon.", "Ein Item zum Tragen. Ein harter und\nscharfer Reißzahn, der Attacken vom\nTyp Drachen verstärkt.", "Strumento da far tenere a un Pokémon.\nDente duro e affilato che potenzia\nle mosse di tipo Drago.", "かたくて　するどい　キバ。\nもたせると　ドラゴンタイプの\nわざの　いりょくが　あがる。", "硬くて　鋭い　キバ。\n持たせると　ドラゴンタイプの\n技の　威力が　あがる。", "단단하고 날카로운 이빨.\n지니게 하면 드래곤타입\n기술의 위력이 올라간다.", "Colmillo de gran dureza que refuerza\nlos movimientos de tipo Dragón.\nDebe llevarlo un Pokémon.") },
            { PBEItem.DragonGem, new PBELocalizedString("A gem with a draconic essence.\nWhen held, it strengthens the power\nof a Dragon-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Dragon.", "Ein Juwel vom Typ Drachen. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Drachen.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Drago.", "ドラゴンの　タイプの　ジュエル。\nもたせると　いちどだけ　ドラゴンの\nわざの　いりょくが　つよまる。", "ドラゴンの　タイプの　ジュエル。\n持たせると　１度だけ　ドラゴンの\n技の　威力が　強まる。", "드래곤타입의 주얼.\n지니게 하면 한 번만 드래곤\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Dragón.\nDebe llevarla un Pokémon.") },
            { PBEItem.DragonScale, new PBELocalizedString("A thick and tough scale.\nDragon-type Pokémon may be holding\nthis item when caught.", "Une écaille épaisse et dure tenue\nparfois par les Pokémon de type Dragon\nquand ils sont attrapés.", "Eine dicke und starke Haut.\nDrachen-Pokémon tragen\ndieses Item gern bei sich.", "Squama spessa e dura.\nPuò essere tenuta da un Pokémon\ndi tipo Drago quando viene catturato.", "かたくて　じょうぶな　ウロコ。\nドラゴンタイプの　ポケモンが\nもっていることが　ある。", "硬くて　丈夫な　ウロコ。\nドラゴンタイプの　ポケモンが\n持っていることが　ある。", "단단하고 튼튼한 비늘.\n드래곤타입의 포켓몬이\n지니고 있는 경우가 있다.", "Una gruesa y resistente escama que\npueden llevar los Pokémon de tipo\nDragón cuando son capturados.") },
            { PBEItem.DreadPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Dark-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Ténèbres.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Unlicht.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Buio.", "あくの　タイプの　せきばん。\nもたせると　あくタイプの\nわざの　いりょくが　つよまる。", "あくの　タイプの　石盤。\n持たせると　あくタイプの\n技の　威力が　強まる。", "악타입의 석판.\n지니게 하면 악타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Siniestro.\nDebe llevarla un Pokémon.") },
            { PBEItem.DreamBall, new PBELocalizedString("A special Poké Ball that appears out of\nnowhere in a bag at the Entree Forest.\nIt can catch any Pokémon.", "Poké Ball du rêve. Apparue dans le\nSac à la Forêt du Heylink. Permet\nd'attraper n'importe quel Pokémon.", "Ein Ball wie aus einem Traum, der im\nKontaktebenenhain plötzlich im Beutel\nauftaucht. Fängt jedes Pokémon.", "Ball onirica. Appare all'improvviso nella\nBorsa quando si è nel Bosco Intramondo.\nPuò acchiappare qualsiasi Pokémon.", "ハイリンクのもりで　いつのまにか\nバッグに　あらわれる　ゆめのボール。\nどんな　ポケモンも　つかまえられる。", "ハイリンクの森で　いつのまにか\nバッグに　現れる　夢のボール。\nどんな　ポケモンも　捕まえられる。", "하일링크의 숲에서 어느 사이엔지\n가방 속에 나타나는 꿈의 볼.\n어떤 포켓몬이라도 잡을 수 있다.", "Bola especial que aparece de pronto\nen tu Bolsa en el Bosque Nexo y\npuede atrapar a cualquier Pokémon.") },
            { PBEItem.DubiousDisc, new PBELocalizedString("A transparent device overflowing with\ndubious data.\nIts producer is unknown.", "Appareil transparent rempli de données\ndouteuses. Son fabricant n'est pas\nconnu.", "Eine Scheibe mit seltsamen Daten.\nIhr Ursprung ist unbekannt.", "Dispositivo trasparente strapieno\ndi dati di dubbia entità. Non se ne\nconosce il produttore.", "なかに　あやしい　じょうほうが\nつまった　とうめいな　きかい。\nせいさくしゃは　ふめい。", "なかに　怪しい　情報が\nつまった　透明な　機械。\n製作者は　不明。", "안에 괴상한 정보가\n가득한 투명한 기계다.\n제작자 불명.", "Dispositivo transparente que contiene\ndatos misteriosos. Es de fabricante\ndesconocido.") },
            { PBEItem.DurinBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.DuskBall, new PBELocalizedString("A somewhat different Poké Ball that\nmakes it easier to catch wild Pokémon\nat night or in dark places like caves.", "Une Poké Ball efficace pour attraper\nles Pokémon de nuit ou dans des\nendroits sombres tels que les grottes.", "Ein mysteriöser Ball,\ngeeignet für Einsätze in der\nNacht oder an dunklen Orten.", "Poké Ball un po' diversa che rende più\nfacile catturare Pokémon selvatici di\nnotte o in luoghi bui come le grotte.", "よるや　どうくつなど　くらいところで\nポケモンが　つかまえやすくなる\nちょっと　かわった　ボール。", "夜や　どうくつなど　暗いところで\nポケモンが　捕まえやすくなる\nちょっと　変わった　ボール。", "밤이나 동굴 등 어두운 곳에서\n포켓몬을 잡기 쉬운\n조금 특이한 볼.", "Es algo distinta. Hace que sea más fácil\ncapturar a los Pokémon salvajes por la\nnoche o en lugares oscuros.") },
            { PBEItem.DuskStone, new PBELocalizedString("A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt is as dark as dark can be.", "Pierre étrange qui fait évoluer\ncertaines espèces de Pokémon. Elle\nest sombre comme une nuit sans lune.", "Dieser besondere, düstere Stein\nhilft manchen Pokémon bei\nihrer Evolution.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nÈ scura come le tenebre.", "ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nやみのように　くらい。", "ある　特定の　ポケモンを\n進化させる　不思議な　石。\nやみのように　暗い。", "어느 특정 포켓몬을\n진화시키는 이상한 돌.\n칠흑처럼 어둡다.", "Una piedra peculiar que hace\nevolucionar a algunos Pokémon.\nEs oscura como la noche.") },
            { PBEItem.EarthPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Ground-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Sol.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Boden.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Terra.", "じめんの　タイプの　せきばん。\nもたせると　じめんタイプの\nわざの　いりょくが　つよまる。", "じめんの　タイプの　石盤。\n持たせると　じめんタイプの\n技の　威力が　強まる。", "땅타입의 석판.\n지니게 하면 땅타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Tierra.\nDebe llevarla un Pokémon.") },
            { PBEItem.EjectButton, new PBELocalizedString("If the holder is hit by an attack, it will\nswitch with another Pokémon in\nyour party.", "Si le Pokémon qui le tient subit une\nattaque, il s'enfuit pour être remplacé\npar un autre membre de l'équipe.", "Wird der Träger getroffen, flieht er\naus dem Kampf und wird mit einem\nTeam-Pokémon ausgetauscht.", "Se il Pokémon che lo tiene è colpito da\nun attacco, viene immediatamente\nsostituito da un compagno.", "もたせて　わざを　うけると\nせんとうから　だっしゅつして\nてもちと　いれかわる　ことができる。", "持たせて　技を　受けると\n戦闘から　脱出して\n手持ちと　入れ替わる　ことができる。", "지니게 하고 기술을 받으면\n배틀에서 탈출하여\n지닌 포켓몬과 교체할 수 있다.", "Si el portador es alcanzado por un\nataque, saldrá del combate y será\nsustituido por otro Pokémon del equipo.") },
            { PBEItem.Electirizer, new PBELocalizedString("A box packed with a tremendous\namount of electric energy.\nIt is loved by a certain Pokémon.", "Une boîte remplie d'une énorme quantité\nd'énergie électrique. Appréciée d'un\ncertain Pokémon.", "Eine Schachtel mit elektrischer\nEnergie. Das Lieblingsstück\neines bestimmten Pokémon.", "Scatola strapiena di energia elettrica.\nUn Pokémon in particolare adora questo\nstrumento.", "とてつもない　でんきの　エネルギーが\nたくわえられている　はこ。\nある　ポケモンが　このんでいる。", "とてつもない　電気の　エネルギーが\n蓄えられている　箱。\nある　ポケモンが　好んでいる。", "엄청난 전기의 에너지가\n비축되어 있는 상자.\n어떤 포켓몬이 좋아하는 물건이다.", "Este dispositivo contiene una gran\ncantidad de energía eléctrica.\nCierto Pokémon lo adora.") },
            { PBEItem.ElectricGem, new PBELocalizedString("A gem with an essence of electricity.\nWhen held, it strengthens the power of\nan Electric-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Électrik.", "Ein Juwel vom Typ Elektro. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Elektro.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Elettro.", "でんきの　タイプの　ジュエル。\nもたせると　いちどだけ　でんきの\nわざの　いりょくが　つよまる。", "でんきの　タイプの　ジュエル。\n持たせると　１度だけ　でんきの\n技の　威力が　強まる。", "전기타입의 주얼.\n지니게 하면 한 번만 전기\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Eléctrico.\nDebe llevarla un Pokémon.") },
            { PBEItem.Elixir, new PBELocalizedString("It restores the PP of all the\nmoves learned by the targeted Pokémon\nby 10 points each.", "Restaure 10 PP de toutes les\ncapacités d'un Pokémon.", "Füllt die AP aller Attacken eines\nPokémon um 10 Punkte auf.", "Restituisce 10 PP a ciascuna delle\nmosse imparate dal Pokémon.", "ポケモンが　おぼえている\n４つの　わざの　ＰＰを\n１０ずつ　かいふくする。", "ポケモンが　覚えている\n４つの　技の　ＰＰを\n１０ずつ　回復する。", "포켓몬이 기억하고 있는\n4개 기술의 PP를\n10씩 회복한다.", "Restaura 10 PP de todos los movimientos\naprendidos por el Pokémon elegido.") },
            { PBEItem.EnergyPowder, new PBELocalizedString("A very bitter medicine powder.\nIt restores the HP of one Pokémon by\n50 points.", "Une poudre médicinale très amère.\nRestaure 50 PV à un Pokémon.", "Ein bitteres medizinisches Pulver,\ndas die KP eines Pokémon um\n50 Punkte auffüllt.", "Rimedio amarissimo in polvere.\nRestituisce 50 PS ad un Pokémon.", "とても　にがい　こなぐすり。\nポケモン　１ひきの　ＨＰを\n５０だけ　かいふくする。", "とても　にがい　粉薬。\nポケモン　１匹の　ＨＰを\n５０だけ　回復する。", "매우 쓴 가루약.\n포켓몬 1마리의 HP를\n50만큼 회복한다.", "Polvo medicinal muy amargo que\nrestaura 50 PS de un Pokémon.") },
            { PBEItem.EnergyRoot, new PBELocalizedString("A very bitter root.\nIt restores the HP of one Pokémon by\n200 points.", "Une racine très amère.\nRestaure 200 PV à un Pokémon.", "Eine bittere Wurzel. Sie füllt die\nKP eines Pokémon um 200 Punkte auf.", "Radice amarissima.\nRestituisce 200 PS ad un Pokémon.", "とても　にがい　ねっこ。\nポケモン　１ひきの　ＨＰを\n２００だけ　かいふくする。", "とても　にがい　ねっこ。\nポケモン　１匹の　ＨＰを\n２００だけ　回復する。", "매우 쓴 뿌리.\n포켓몬 1마리의 HP를\n200만큼 회복한다.", "Raíz muy amarga que restaura\n200 PS de un Pokémon.") },
            { PBEItem.EnigmaBerry, new PBELocalizedString("If held by a Pokémon, it restores\nits HP if it is hit by any\nsupereffective attack.", "Tenue, cette Baie restaure les PV\ndu Pokémon s'il subit une attaque\nsuper efficace.", "Als getragenes Item füllt die Beere die\nKP eines Pokémon auf, wenn es von sehr\neffektiven Attacken getroffen wird.", "Ripristina i PS, se il Pokémon che\nla tiene è colpito da un attacco\nsuperefficace.", "ポケモンに　もたせると\nこうかばつぐんの　わざを\nうけたとき　ＨＰを　かいふくする。", "ポケモンに　持たせると\n効果バツグンの　技を\n受けたとき　ＨＰを　回復する。", "포켓몬에게 지니게 하면\n효과가 뛰어난 공격을\n받았을 때 HP를 회복한다.", "Restaura los PS de un Pokémon\nsi la lleva cuando le alcanza\nun ataque supereficaz.") },
            { PBEItem.EscapeRope, new PBELocalizedString("A long, durable rope.\nUse it to escape instantly from a cave\nor a dungeon.", "Une corde longue et solide permettant\nde sortir rapidement d'une grotte ou\nd'un donjon.", "Ein langes, festes Seil, das die\nsofortige Flucht aus Höhlen oder\nÄhnlichem ermöglicht.", "Corda lunga e resistente che si usa\nper fuggire in un batter d'occhio\ndalle grotte o da certi luoghi chiusi.", "ながくて　じょうぶな　ヒモ。\nどうくつや　ダンジョンから\nぬけだすことが　できる。", "長くて　丈夫な　ヒモ。\nどうくつや　ダンジョンから\n抜け出すことが　できる。", "길고 튼튼한 끈.\n동굴이나 던전에서\n빠져나올 수 있다.", "Cuerda larga y resistente que sirve\npara huir de cuevas y sitios cerrados\nen general.") },
            { PBEItem.Ether, new PBELocalizedString("It restores the PP of a Pokémon's\nselected move by a maximum of\n10 points.", "Restaure jusqu'à 10 PP d'une\ncapacité sélectionnée.", "Füllt die AP einer ausgewählten Attacke\neines Pokémon um 10 Punkte auf.", "Restituisce un massimo di 10 PP\na una mossa a scelta che è stata\nimparata dal Pokémon.", "ポケモンが　おぼえている\nわざのうち　１つの　ＰＰを\n１０だけ　かいふくする。", "ポケモンが　覚えている\n技のうち　１つの　ＰＰを\n１０だけ　回復する。", "포켓몬이 기억하고 있는\n기술 중 1개의 PP를\n10만큼 회복한다.", "Restaura un máximo de 10 PP del\nmovimiento elegido de un Pokémon.") },
            { PBEItem.Everstone, new PBELocalizedString("An item to be held by a Pokémon.\nThe Pokémon holding this peculiar stone\nis prevented from evolving.", "Objet à tenir. Pierre merveilleuse\nempêchant l'évolution.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nbewirkt einen Entwicklungsstopp.", "Strumento da far tenere a un Pokémon.\nChi tiene questa pietra particolare\nevita l'evoluzione.", "ポケモンに　もたせると\nそのあいだ　しんか　しなくなる\nふしぎな　いし。", "ポケモンに　持たせると\nそのあいだ　進化しなくなる\n不思議な　石。", "포켓몬에게 지니게 하면\n지닌 동안에는 진화하지 않게 되는\n이상한 돌.", "El Pokémon que lleva esta piedra\nmágica no evoluciona.") },
            { PBEItem.Eviolite, new PBELocalizedString("A mysterious evolutionary lump. When\nheld, it raises the Defense and Sp. Def\nof a Pokémon that can still evolve.", "Un étrange concentré d'évolution qui\naugmente la Défense et la Déf. Spé.\nd'un Pokémon pouvant évoluer.", "Ein mysteriöser Klumpen, der die Vert.\nu. Spez.-Vert. von Pokémon erhöht,\ndie sich noch entwickeln können.", "Misteriosa sfera evolutiva. Aumenta la\nDifesa e la Difesa Speciale di un Pokémon\nche può ancora evolversi.", "しんかの　ふしぎな　かたまり。\nもたせると　しんかまえ　ポケモンの\nぼうぎょと　とくぼうが　あがる。", "進化の　不思議な　かたまり。\n持たせると　進化前　ポケモンの\n防御と　特防が　あがる。", "진화의 이상한 덩어리.\n지니게 하면 진화 전 포켓몬의\n방어와 특수방어가 올라간다.", "Piedra evolutiva. El Pokémon portador\naumentará su Defensa y su Defensa\nEspecial si aún puede evolucionar.") },
            { PBEItem.ExpertBelt, new PBELocalizedString("An item to be held by a Pokémon. It is\na well-worn belt that slightly boosts\nthe power of supereffective moves.", "Objet à tenir. Ceinture usée\naugmentant légèrement la puissance\ndes capacités super efficaces.", "Ein Item zum Tragen.\nDieser alte Gürtel verstärkt\nsehr effektive Attacken.", "Strumento da far tenere a un Pokémon.\nCintura consumata che potenzia di poco\nle mosse superefficaci.", "つかいこまれた　くろい　おび。\nもたせると　こうかばつぐんの　とき\nわざの　いりょくが　すこし　あがる。", "使いこまれた　黒い　帯。\n持たせると　効果バツグンの　とき\n技の　威力が　少し　あがる。", "오래 사용한 까만 띠.\n지니게 하면 효과가 뛰어날 때\n기술의 위력이 조금 올라간다.", "Potencia ligeramente los movimientos\nsupereficaces.\nDebe llevarla un Pokémon.") },
            { PBEItem.ExpShare, new PBELocalizedString("An item to be held by a Pokémon.\nThe holder gets a share of a battle's\nExp. Points without battling.", "Objet à tenir. Le porteur reçoit une\npartie des points Exp. du combat sans\navoir à combattre.", "Ein Item, das einem Pokémon zum Tragen\ngegeben werden kann. Es teilt die\nErfahrungs-Punkte aus einem Kampf auf.", "Strumento da far tenere a un Pokémon.\nChi lo tiene ottiene Punti Esperienza\ndalla lotta anche se non vi partecipa.", "もたせた　ポケモンは　せんとうに\nでなくても　けいけんちを\nわけてもらえる　べんりな　そうち。", "持たせた　ポケモンは　戦闘に\nでなくても　経験値を\nわけてもらえる　便利な　装置。", "지니게 한 포켓몬은 배틀에\n나가지 않아도 경험치를\n나누어 받을 수 있는 편리한 장치.", "El Pokémon que lo lleva obtiene parte\nde los Puntos de Exp. del combate\nsin participar en él.") },
            { PBEItem.FastBall, new PBELocalizedString("A Poké Ball that makes it easier to\ncatch Pokémon which are quick to\nrun away.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien sur\nles Pokémon qui ont tendance à fuir.", "Ein Ball, der sich besonders gut eignet,\num flinke Pokémon zu fangen.", "Poké Ball un po' originale che funziona\nbenissimo sui Pokémon bravi a scappare.", "にげあしの　はやい　ポケモンが\nつかまえやすくなる\nちょっと　かわった　ボール。　", "にげあしの　はやい　ポケモンが\n捕まえやすくなる\nちょっと　変わった　ボール。　", "도주가 빠른 포켓몬이\n잡기 쉬워지는\n조금 특이한 볼.", "Poké Ball algo distinta, especialmente\nindicada para capturar Pokémon\nevasivos.") },
            { PBEItem.FavoredMail, new PBELocalizedString("Stationery designed for\nwriting about your favorite things.\nLet a Pokémon hold it for delivery.", "Un papier à lettres idéal pour parler\nde ce que l'on aime. Peut être tenu\net livré par un Pokémon.", "Briefpapier, mit dem man jemandem\nverraten kann, was man gern mag.\nGib es einem Pokémon zum Tragen.", "Carta da lettera ideale per comunicare\nciò che si preferisce. Un Pokémon può\ntenerla e consegnarla.", "すきなものを　つたえる　メールが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "好きなものを　伝える　メールが\n書きやすい　レターペーパー。\nポケモンに　持たせて　使う。", "좋아하는 것을 전하는 메일을\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Carta para escribir sobre tus cosas\nfavoritas. Haz que la entregue\nun Pokémon.") },
            { PBEItem.FightingGem, new PBELocalizedString("A gem with an essence of combat. When\nheld, it strengthens the power of a\nFighting-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Combat.", "Ein Juwel vom Typ Kampf. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Kampf.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Lotta.", "かくとうの　タイプの　ジュエル。\nもたせると　いちどだけ　かくとうの\nわざの　いりょくが　つよまる。", "かくとうの　タイプの　ジュエル。\n持たせると　１度だけ　かくとうの\n技の　威力が　強まる。", "격투타입의 주얼.\n지니게 하면 한 번만 격투\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Lucha.\nDebe llevarla un Pokémon.") },
            { PBEItem.FigyBerry, new PBELocalizedString("If held by a Pokémon, it restores the\nuser's HP in a pinch, but will cause\nconfusion if it hates the taste.", "Tenue, cette Baie restaure des PV en\ncas de besoin, mais rend confus si\nle Pokémon n'aime pas son goût.", "Als getragenes Item füllt die Beere die\nKP eines Pokémon auf, doch ihr Aroma\nverwirrt jene, die es nicht mögen.", "Se tenuta da un Pokémon in difficoltà,\nrestituisce PS. Se al Pokémon non piace\nil suo sapore, può causare confusione.", "ポケモンに　もたせると\nピンチのとき　ＨＰを　かいふくする。\nきらいな　あじだと　こんらん　する。", "ポケモンに　持たせると\nピンチのとき　ＨＰを　回復する。\nきらいな味だと　混乱する。", "포켓몬에게 지니게 하면\n위급할 때 HP를 회복한다.\n싫어하는 맛이면 혼란에 빠진다.", "Los Pokémon pueden llevarla o usarla\npara restaurar algunos PS. Su sabor\npuede causar confusión.") },
            { PBEItem.FireGem, new PBELocalizedString("A gem with an essence of fire. When\nheld, it strengthens the power of a\nFire-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Feu.", "Ein Juwel vom Typ Feuer. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Feuer.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Fuoco.", "ほのおの　タイプの　ジュエル。\nもたせると　いちどだけ　ほのおの\nわざの　いりょくが　つよまる。", "ほのおの　タイプの　ジュエル。\n持たせると　１度だけ　ほのおの\n技の　威力が　強まる。", "불꽃타입의 주얼.\n지니게 하면 한 번만 불꽃\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Fuego.\nDebe llevarla un Pokémon.") },
            { PBEItem.FireStone, new PBELocalizedString("A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt is colored orange.", "Une pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle est jaune et orange.", "Dieser spezielle Stein löst bei\nbestimmten Pokémon die Entwicklung\naus. Er ist orange.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nÈ gialla ed arancione.", "ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nだいだいいろを　している。", "ある　特定の　ポケモンを\n進化させる　不思議な　石。\nだいだい色を　している。", "어느 특정한 포켓몬을\n진화시키는 이상한 돌.\n주황색을 띠고 있다.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nEs amarilla con una marca naranja.") },
            { PBEItem.FistPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Fighting-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Combat.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Kampf.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Lotta.", "かくとうの　タイプの　せきばん。\nもたせると　かくとうタイプの\nわざの　いりょくが　つよまる。", "かくとうの　タイプの　石盤。\n持たせると　かくとうタイプの\n技の　威力が　強まる。", "격투타입의 석판.\n지니게 하면 격투타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Lucha.\nDebe llevarla un Pokémon.") },
            { PBEItem.FlameOrb, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a bizarre orb that inflicts a\nburn on the holder in battle.", "Objet à tenir. Orbe bizarre brûlant\nson porteur durant le combat.", "Ein Item zum Tragen.\nDieser bizarre Orb fügt seinem\nTräger Verbrennungen zu.", "Strumento da far tenere a un Pokémon.\nSfera bizzarra che procura una\nscottatura a chi la tiene in lotta.", "さわると　ねつをだす　ふしぎなたま。\nもたせると　せんとうちゅうに\nやけどの　じょうたいに　なる。", "触ると　熱をだす　不思議な玉。\n持たせると　戦闘中に\nやけどの　状態に　なる。", "만지면 열을 내는 이상한 구슬.\n지니게 하면 배틀 중에\n화상 상태가 된다.", "Extraña esfera que causa quemaduras\nal Pokémon que la lleva en combate.") },
            { PBEItem.FlamePlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Fire-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Feu.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Feuer.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Fuoco.", "ほのおの　タイプの　せきばん。\nもたせると　ほのおタイプの\nわざの　いりょくが　つよまる。", "ほのおの　タイプの　石盤。\n持たせると　ほのおタイプの\n技の　威力が　強まる。", "불꽃타입의 석판.\n지니게 하면 불꽃타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Fuego.\nDebe llevarla un Pokémon.") },
            { PBEItem.FloatStone, new PBELocalizedString("A very light stone. It reduces the\nweight of a Pokémon when held.", "Une pierre très légère.\nElle réduit le poids du\nPokémon qui la tient.", "Ein federleichter Stein, der das\nGewicht des Trägers verringert.", "Pietra leggerissima che riduce il peso\ndel Pokémon che la tiene.", "とても　かるい　いし。\nもたせると　ポケモンの\nたいじゅうが　かるくなる。", "とても　軽い　石。\n持たせると　ポケモンの\n体重が　軽くなる。", "매우 가벼운 돌.\n지니게 하면 포켓몬의\n체중이 가벼워진다.", "Piedra muy ligera que reduce el peso\ndel Pokémon que la lleve.") },
            { PBEItem.FluffyTail, new PBELocalizedString("An item that attracts Pokémon.\nUse it to flee from any battle with a\nwild Pokémon.", "Objet qui attire les Pokémon. Permet\nde s'enfuir d'un combat contre un\nPokémon sauvage.", "Ein hübsches Item. Es ermöglicht\ndie Flucht im Kampf gegen wilde\nPokémon.", "Strumento che attrae i Pokémon. È utile\nper fuggire dalla lotta con Pokémon\nselvatici.", "ポケモンの　きを　ひかせる　どうぐ。\nやせいポケモンとの　せんとうから\nぜったいに　にげられる。", "ポケモンの　気をひかせる　道具。\n野生ポケモンとの　戦闘から\n絶対に　逃げられる。", "포켓몬의 관심을 끄는 도구.\n야생 포켓몬과의 배틀에서\n반드시 도망칠 수 있다.", "Un objeto que atrae a los Pokémon.\nEs útil para huir de un combate\ncontra un Pokémon salvaje.") },
            { PBEItem.FlyingGem, new PBELocalizedString("A gem with an essence of air. When held,\nit strengthens the power of a\nFlying-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Vol.", "Ein Juwel vom Typ Flug. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Flug.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Volante.", "ひこうの　タイプの　ジュエル。\nもたせると　いちどだけ　ひこうの\nわざの　いりょくが　つよまる。", "ひこうの　タイプの　ジュエル。\n持たせると　１度だけ　ひこうの\n技の　威力が　強まる。", "비행타입의 주얼.\n지니게 하면 한 번만 비행\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Volador.\nDebe llevarla un Pokémon.") },
            { PBEItem.FocusBand, new PBELocalizedString("An item to be held by a Pokémon.\nThe holder may endure a potential KO\nattack, leaving it with just 1 HP.", "Objet à tenir pouvant parfois empêcher\nd'être mis K.O., ne laissant qu'un PV.", "Ein Item, das die Kampfunfähigkeit\nverhindern kann, da dem Träger\n1 KP bleibt.", "Strumento da far tenere a un Pokémon.\nChi lo tiene può evitare un potenziale\nKO e rimanere con un solo PS.", "もたせると　ひんしに　なりそうな\nわざを　うけても　ＨＰを　１だけ\nのこして　たえることが　ある。", "持たせると　ひんしに　なりそうな\n技を　受けても　ＨＰを　１だけ\n残して　耐えることが　ある。", "지니게 하면\n기절할 듯한 기술을 받아도\nHP를 1 남겨서 견디기도 한다.", "El Pokémon que la lleva puede resistir\nun golpe que potencialmente lo deja\nK.O. con un PS.") },
            { PBEItem.FocusSash, new PBELocalizedString("An item to be held by a Pokémon. If it\nhas full HP, the holder will endure one\npotential KO attack, leaving 1 HP.", "Objet à tenir permettant au porteur,\ns'il a ses PV pleins, d'éviter un\npotentiel K.O. en conservant un PV.", "Ein Item zum Tragen. Bei vollen KP\nreduziert eine K.O.-Attacke die\nKP nur auf 1.", "Strumento da far tenere a un Pokémon.\nSe chi lo tiene ha tutti i PS, eviterà\nun potenziale KO, rimanendo con un PS.", "もたせると　ＨＰが　まんたんのとき\nひんしになりそうな　わざを　うけても　\nＨＰ１で　いちどだけ　たえる。", "持たせると　ＨＰが　満タンのとき\nひんしになりそうな　技を　受けても\nＨＰ１で　１度だけ　耐える。", "지니게 하면 HP가 꽉 찼을 때\n기절할 듯한 기술을 당해도\nHP1로 한 번은 버틴다.", "Si el Pokémon que la lleva tiene los PS\nal máximo, puede resistir un golpe que\npotencialmente lo deja K.O. con un PS.") },
            { PBEItem.FreshWater, new PBELocalizedString("Water with a high mineral content.\nIt restores the HP of one Pokémon by\n50 points.", "Une eau riche en minéraux.\nRestaure 50 PV à un Pokémon.", "Ein stilles Wasser mit hohem\nMineralienanteil, das die KP eines\nPokémon um 50 Punkte auffüllt.", "Acqua ricca di sostanze minerali.\nRestituisce 50 PS ad un Pokémon.", "ミネラル　たっぷりの　みず。\nポケモン　１ひきの　ＨＰを\n５０だけ　かいふくする。", "ミネラル　たっぷりの　水。\nポケモン　１匹の　ＨＰを\n５０だけ　回復する。", "미네랄이 가득한 물.\n포켓몬 1마리의 HP를\n50만큼 회복한다.", "Agua de alto contenido mineral que\nrestaura 50 PS de un Pokémon.") },
            { PBEItem.FriendBall, new PBELocalizedString("A Poké Ball that makes caught Pokémon\nmore friendly.", "Une Poké Ball un peu spéciale qui\npermet de rendre rapidement amicaux\nles Pokémon sauvages attrapés avec.", "Ein besonderer Ball, der die\nSympathie von gefangenen Pokémon\nschneller ansteigen lässt.", "Poké Ball un po' originale che fa sì che\ni Pokémon selvatici con essa catturati\nsi affezionino più rapidamente.", "つかまえた　やせいポケモンが\nすぐに　なついて　くれる\nちょっと　かわった　ボール。　", "捕まえた　野生ポケモンが\nすぐに　なついて　くれる\nちょっと　変わった　ボール。　", "잡은 야생 포켓몬이\n바로 친밀해지는\n조금 특이한 볼.", "Poké Ball algo distinta que hace más\namistosos a los Pokémon salvajes que\nse capturan con ella.") },
            { PBEItem.FullHeal, new PBELocalizedString("A spray-type medicine.\nIt heals all the status problems of a\nsingle Pokémon.", "Un médicament sous forme de spray.\nSoigne tous les problèmes de statut\nd'un Pokémon.", "Ein Spray, das alle Statusprobleme\neines Pokémon behebt.", "Rimedio spray.\nRisolve tutti i problemi di stato\ndi un Pokémon.", "スプレーしきの　くすり。\nポケモン　１ひきの　じょうたいの\nいじょうを　すべて　かいふくする。", "スプレー式の　薬。\nポケモン　１匹の　状態の\n異常を　すべて　回復する。", "스프레이식의 약.\n포켓몬 1마리의\n상태 이상을 모두 회복한다.", "Medicina en espray que cura todos los\nproblemas de estado de un Pokémon.") },
            { PBEItem.FullIncense, new PBELocalizedString("An item to be held by a Pokémon. It is\nan exotic-smelling incense that makes\nthe holder bloated and slow moving.", "Objet à tenir. Encens au parfum\nexotique gonflant son porteur et\nle faisant se déplacer lentement.", "Ein Item zum Tragen. Dieses exotische\nDuftkraut macht den Träger benommen\nund langsam.", "Strumento da far tenere a un Pokémon.\nIncenso dal profumo esotico che\nrende gonfio e lento chi lo tiene.", "ふしぎな　かおりの　おこう。\nもたせた　ポケモンは　いつもより\nこうどうが　おそくなる。", "不思議な　香りの　御香。\n持たせた　ポケモンは　いつもより\n行動が　遅くなる。", "이상한 향기의 향로.\n지니게 한 포켓몬은 여느 때보다\n행동이 느려진다.", "Incienso de exótico aroma que hace\nque el Pokémon que lo lleve esté\naturdido y se mueva más despacio.") },
            { PBEItem.FullRestore, new PBELocalizedString("A medicine that fully restores the HP\nand heals any status problems of a\nsingle Pokémon.", "Un médicament qui restaure tous les\nPV d'un Pokémon et soigne tous ses\nproblèmes de statut.", "Dieses Item stellt alle KP eines\nPokémon wieder her und behebt\nalle Statusprobleme.", "Rimedio che restituisce tutti i PS ad\nun Pokémon e risolve i suoi problemi\ndi stato.", "ポケモン　１ひきの　ＨＰと\nじょうたい　いじょうを　すべて\nかいふくする。", "ポケモン　１匹の　ＨＰと\n状態異常を　すべて\n回復する。", "포켓몬 1마리의 HP와\n상태 이상을 모두\n회복한다.", "Medicina que restaura todos los PS\ny cura todos los problemas de\nestado de un Pokémon.") },
            { PBEItem.GanlonBerry, new PBELocalizedString("If held by a Pokémon, it raises its\nDefense stat in a pinch.", "Tenue, cette Baie monte la Défense\nen cas de besoin.", "Als getragenes Item erhöht diese Beere\nden Verteidigungs-Wert eines Pokémon,\nsobald es in einer Notlage steckt.", "Aumenta la Difesa, se il Pokémon che\nla tiene si trova in difficoltà.", "ポケモンに　もたせると\nピンチのとき　じぶんの\nぼうぎょが　あがる。", "ポケモンに　持たせると\nピンチのとき　自分の\n防御が　あがる。", "포켓몬에게 지니게 하면\n위급할 때 자신의\n방어가 올라간다.", "Si la lleva un Pokémon, le sube\nla Defensa en un momento de apuro.") },
            { PBEItem.GeniusWing, new PBELocalizedString("An item for use on a Pokémon. It\nslightly increases the base Sp. Atk\nstat of a single Pokémon.", "Objet à utiliser sur un Pokémon.\nIl augmente un peu son\nAttaque Spéciale de base.", "Schenkt man einem Pokémon dieses\nItem, wird dessen Spezial-Angriff\ndauerhaft erhöht.", "Strumento da usare su un Pokémon.\nNe aumenta di poco l'Attacco Speciale\ndi base.", "ポケモンに　つかう　どうぐ。\nポケモン　１ひきの　とくこうの\nきそポイントを　ちょっと　あげる。", "ポケモンに　使う　道具。\nポケモン　１匹の　特攻の\n基礎ポイントを　ちょっと　あげる。", "포켓몬에게 사용하는 도구.\n포켓몬 1마리의 특수공격\n기초 포인트를 조금 올린다.", "Pluma que aumenta un poco\nel Ataque Especial de un Pokémon\nal subir de nivel.") },
            { PBEItem.GhostGem, new PBELocalizedString("A gem with a spectral essence. When\nheld, it strengthens the power of a\nGhost-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Spectre.", "Ein Juwel vom Typ Geist. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Geist.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Spettro.", "ゴーストの　タイプの　ジュエル。\nもたせると　いちどだけ　ゴーストの\nわざの　いりょくが　つよまる。", "ゴーストの　タイプの　ジュエル。\n持たせると　１度だけ　ゴーストの\n技の　威力が　強まる。", "고스트타입의 주얼.\n지니게 하면 한 번만 고스트\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Fantasma.\nDebe llevarla un Pokémon.") },
            { PBEItem.GooeyMulch, new PBELocalizedString("A fertilizer to be spread on soft soil\nin regions where Berries are grown.\nA maniac will buy it for a high price.", "Un engrais utilisé sur sol meuble.\nLes amateurs en donnent un bon prix.", "In Regionen, wo man Beeren anbaut,\nmengt man ihn unter weiche Erde.\nSammler geben ihr letztes Hemd dafür.", "Usata per favorire la crescita delle\nBacche nelle regioni dove si coltivano.\nGli intenditori pagano caro per averla.", "きのみを　そだてる　ちほうでは\nふかふかのつちに　まいて　つかう。\nマニアが　たかく　かってくれる。", "きのみを　育てる　地方では\nふかふかのつちに　まいて　使う。\nマニアが　高く　買ってくれる。", "나무열매를 키우는 지방에서는\n푹신푹신한 땅에 뿌려 쓴다.\n마니아가 비싼 값에 산다.", "Un fertilizante usado en suelo fértil\ndonde crecen las Bayas.\nMuy apreciado por los coleccionistas.") },
            { PBEItem.GrassGem, new PBELocalizedString("A gem with an essence of nature. When\nheld, it strengthens the power of a\nGrass-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Plante.", "Ein Juwel vom Typ Pflanze. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Pflanze.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Erba.", "くさの　タイプの　ジュエル。\nもたせると　いちどだけ　くさの\nわざの　いりょくが　つよまる。", "くさの　タイプの　ジュエル。\n持たせると　１度だけ　くさの\n技の　威力が　強まる。", "풀타입의 주얼.\n지니게 하면 한 번만 풀\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Planta.\nDebe llevarla un Pokémon.") },
            { PBEItem.GreatBall, new PBELocalizedString("A good, high-performance Ball that\nprovides a higher Pokémon catch rate\nthan a standard Poké Ball.", "Une Ball très performante dont le taux\nde réussite est supérieur à celui de la\nPoké Ball.", "Ein Ball mit guter Erfolgsquote.\nDem Pokéball in allen\nPunkten überlegen.", "Ball dalle ottime prestazioni: fornisce\nmaggiori probabilità di cattura rispetto\nalla Poké Ball.", "モンスターボールよりも　さらに\nポケモンを　つかまえやすくなった\nすこし　せいのうのいい　ボール。", "モンスターボールよりも　さらに\nポケモンを　捕まえやすくなった\n少し　性能のいい　ボール。", "몬스터볼보다도 더욱\n포켓몬을 잡기 쉬워진\n약간 성능이 좋은 볼.", "Poké Ball de alto rendimiento.\nTiene un índice de éxito superior\nal de la Poké Ball.") },
            { PBEItem.GreenScarf, new PBELocalizedString("An item to be held by a Pokémon.\nIt boosts the Smart aspect of the\nholder in a Contest.", "Objet à tenir. Augmente l'Intelligence\nd'un Pokémon dans les Concours.", "Ein Item zum Tragen. Trainer erhöhen\ndamit in Wettbewerben die „Klugheit“\nihres Pokémon.", "Strumento da far tenere a un Pokémon.\nAumenta l'“Acume\" di chi lo tiene\nin una Gara.", "もたせて　コンテストに　さんかすると\nその　ポケモンは　いつもより\nかしこく　みられる。", "持たせて　コンテストに　参加すると\nその　ポケモンは　いつもより\nかしこく　みられる。", "지니게 하여 콘테스트에 참가하면\n그 포켓몬은 여느 때보다\n슬기롭게 보인다.", "Debe llevarlo un Pokémon.\nAumenta el ingenio en los concursos.") },
            { PBEItem.GreenShard, new PBELocalizedString("A small green shard.\nIt appears to be from some sort of\nimplement made long ago.", "Petit tesson vert.\nIl semble provenir d'un outil très\nancien.", "Eine grüne Scherbe eines antiken\nWerkzeugs, das vor langer Zeit\nangefertigt wurde.", "Un piccolo coccio verde che con ogni\nprobabilità è un frammento di un antico\nstrumento.", "みどりの　ちいさな　かけら。\nむかしに　つくられた　なにかの\nどうぐの　いちぶ　らしい。", "緑の　小さな　かけら。\n昔に　つくられた　なにかの\n道具の　一部らしい。", "초록색 작은 조각.\n옛날에 만들어진 어떤\n도구의 일부인 듯하다.", "Un pequeño fragmento verde.\nParece formar parte de algún tipo\nde herramienta antigua.") },
            { PBEItem.GreetMail, new PBELocalizedString("Stationery designed for\nintroductory greetings.\nLet a Pokémon hold it for delivery.", "Un papier à lettres idéal pour\nse présenter. Peut être tenu\net livré par un Pokémon.", "Briefpapier, mit dem man sich\nbei jemandem vorstellen kann.\nGib es einem Pokémon zum Tragen.", "Carta da lettera ideale per presentarsi\na qualcuno. Un Pokémon può tenerla e\nconsegnarla.", "はじめましての　あいさつが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "はじめましての　あいさつが\n書きやすい　レターペーパー。\nポケモンに　持たせて　使う。", "첫인사를\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Carta para presentarse a alguien.\nHaz que la entregue un Pokémon.") },
            { PBEItem.GrepaBerry, new PBELocalizedString("Using it on a Pokémon makes it more\nfriendly, but it also lowers its base\nSp. Def stat.", "Rend les Pokémon plus amicaux, mais\nbaisse leur stat Défense Spéciale de\nbase.", "Isst ein Pokémon diese Beere,\nstärkt dies eure Freundschaft, ver-\nringert aber seinen Spez.-Vert.-Wert.", "Utilizzata su un Pokémon, lo rende più\namichevole, ma diminuisce la Difesa\nSpeciale di base.", "ポケモンに　あげると\nとても　なつき　やすくなるが\nとくぼうの　きそポイントは　さがる。", "ポケモンに　あげると\nとても　なつき　やすくなるが\n特防の　基礎ポイントは　さがる。", "포켓몬에게 주면\n매우 친밀해지기 쉬워지지만\n특수방어의 기초 포인트가 떨어진다.", "Al usarla con un Pokémon, se gana\nsu amistad, pero también reduce\nsu Defensa Especial de base.") },
            { PBEItem.GripClaw, new PBELocalizedString("A Pokémon held item that extends the\nduration of multiturn attacks like\nBind and Wrap.", "Objet à tenir augmentant la durée des\nattaques à tours multiples telles que\nLigotage et Étreinte.", "Ein Item zum Tragen.\nVerlängert die Dauer von\nKlammergriff, Wickel etc.", "Strumento da far tenere a un Pokémon\nche prolunga gli attacchi multiturno\ncome Avvolgibotta e Legatutto.", "もたせると　しめつける　まきつくなど\nダメージを　あたえつづける　わざの\nターンすうが　ふえる。", "持たせると　しめつける　まきつくなど\nダメージを　あたえつづける　技の\nターン数が　増える。", "지니게 하면 조이기나 김밥말이 등\n데미지를 계속 주는 기술의\n턴 수가 늘어난다.", "Prolonga la duración de ataques\nmultiturno como Atadura y Repetición.\nDebe llevarla un Pokémon.") },
            { PBEItem.GriseousOrb, new PBELocalizedString("A glowing orb to be held by Giratina.\nIt boosts the power of Dragon- and\nGhost-type moves.", "Mystérieux objet destiné à Giratina.\nAméliore les capacités de type Dragon\net Spectre.", "Ein glühender Orb. Verstärkt\nAttacken vom Typ Drachen und\nGeist, wenn Giratina ihn trägt.", "Poliedro splendente da far tenere a\nGiratina. Aumenta la potenza delle\nmosse di tipo Drago e Spettro.", "ギラティナに　もたせると　ドラゴンと\nゴーストタイプの　わざの　いりょくが\nあがる　ひかり　かがやく　たま。", "ギラティナに　持たせると　ドラゴンと\nゴーストタイプの　技の　威力が\nあがる　光り　輝く　珠。", "기라티나에게 지니게 하면 드래곤과\n고스트타입 기술의 위력이 올라가는\n반짝반짝 빛나는 구슬.", "Una esfera brillante que potencia los\nmovimientos de tipo Dragón y Fantasma.\nDebe llevarla Giratina.") },
            { PBEItem.GrnApricorn, new PBELocalizedString("A green Apricorn.\nIt has a mysterious, aromatic scent.", "Un Noigrume de couleur verte.\nIl dégage un arôme étonnamment riche.", "Eine grüne Aprikoko. Sie verströmt\neinen seltsam aromatischen Duft.", "Una Ghicocca di colore verde.\nEmana un profumo intenso.", "みどりの　ぼんぐり。\nふしぎと　こうばしい　かおりがする。\n", "みどりの　ぼんぐり。\n不思議と　こうばしい　かおりがする。\n", "초록색의 규토리.\n이상하게도 고소한 향기가 난다.\n", "Bonguri de color verde y aroma peculiar\nque recuerda al café tostado.") },
            { PBEItem.GroundGem, new PBELocalizedString("A gem with an essence of land. When\nheld, it strengthens the power of a\nGround-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Sol.", "Ein Juwel vom Typ Boden. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Boden.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Terra.", "じめんの　タイプの　ジュエル。\nもたせると　いちどだけ　じめんの\nわざの　いりょくが　つよまる。", "じめんの　タイプの　ジュエル。\n持たせると　１度だけ　じめんの\n技の　威力が　強まる。", "땅타입의 주얼.\n지니게 하면 한 번만 땅\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Tierra.\nDebe llevarla un Pokémon.") },
            { PBEItem.GrowthMulch, new PBELocalizedString("A fertilizer to be spread on soft soil\nin regions where Berries are grown.\nA maniac will buy it for a high price.", "Un engrais utilisé sur sol meuble.\nLes amateurs en donnent un bon prix.", "In Regionen, wo man Beeren anbaut,\nmengt man ihn unter weiche Erde.\nSammler geben ihr letztes Hemd dafür.", "Usato per favorire la crescita delle\nBacche nelle regioni dove si coltivano.\nGli intenditori pagano caro per averlo.", "きのみを　そだてる　ちほうでは\nふかふかのつちに　まいて　つかう。\nマニアが　たかく　かってくれる。", "きのみを　育てる　地方では\nふかふかのつちに　まいて　使う。\nマニアが　高く　買ってくれる。", "나무열매를 키우는 지방에서는\n푹신푹신한 땅에 뿌려 쓴다.\n마니아가 비싼 값에 산다.", "Un fertilizante usado en suelo fértil\ndonde crecen las Bayas.\nMuy apreciado por los coleccionistas.") },
            { PBEItem.GuardSpec, new PBELocalizedString("An item that prevents stat reduction\namong the Trainer's party Pokémon\nfor five turns after use.", "Empêche la réduction des stats\nde tous les Pokémon de l'équipe\npendant 5 tours.", "Eine mögliche Statusveränderung\nder Pokémon im Team wird für\n5 Runden verhindert.", "Per cinque turni dopo l'uso, evita\nla riduzione delle statistiche dei\nPokémon della squadra dell'Allenatore.", "せんとうちゅう　５ターンの　あいだ\nみかたの　のうりょくを\nさげられなくする　どうぐ。", "戦闘中　５ターンの　あいだ\n味方の　能力を\nさげられなくする　道具。", "배틀 중 5턴 동안\n같은 편의 능력을\n떨어뜨리지 않게 하는 도구.", "Evita que bajen las características\ndel equipo Pokémon durante\ncinco turnos.") },
            { PBEItem.HabanBerry, new PBELocalizedString("Weakens a supereffective Dragon-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Dragon.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Drachen.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Drago\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　ドラゴン　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　ドラゴン技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 드래곤 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Dragón\nde un enemigo.") },
            { PBEItem.HardStone, new PBELocalizedString("An item to be held by a Pokémon.\nIt is an unbreakable stone that ups\nthe power of Rock-type moves.", "Objet à tenir. Pierre incassable qui\nmonte la puissance des capacités de\ntype Roche.", "Ein Item zum Tragen. Ein unzer-\nbrechlicher Stein, der Attacken\nvom Typ Gestein verstärkt.", "Strumento da far tenere a un Pokémon.\nPietra indistruttibile che potenzia\nle mosse di tipo Roccia.", "ぜったいに　われない　いし。\nもたせると　いわタイプの\nわざの　いりょくが　あがる。", "絶対に　割れない　石。\n持たせると　いわタイプの\n技の　威力が　あがる。", "절대 깨지지 않는 돌.\n지니게 하면 바위타입\n기술의 위력이 올라간다.", "Debe llevarla un Pokémon.\nLa robustez de la piedra fortalece\nlos movimientos de tipo Roca.") },
            { PBEItem.HealBall, new PBELocalizedString("A remedial Poké Ball that restores the\ncaught Pokémon's HP and eliminates\nany status problem.", "Une Poké Ball de soin qui rend tous les\nPV du Pokémon attrapé et qui soigne\ntous les changements de statut.", "Ein wohltuender Ball. Er füllt alle KP\ndes gefangenen Pokémon auf und\nbehebt alle Statusprobleme.", "Poké Ball curativa che ridà PS\nal Pokémon catturato e risolve ogni\nproblema di stato.", "つかまえた　ポケモンの　ＨＰと\nじょうたい　いじょうを　かいふくする\nちょっと　やさしい　ボール。", "捕まえた　ポケモンの　ＨＰと\n状態異常を　回復する\nちょっと　やさしい　ボール。", "잡은 포켓몬의 HP와\n상태 이상을 회복시키는\n조금 다정한 볼.", "Poké Ball curativa que restaura los PS\ny cura todos los problemas de estado\nde un Pokémon después de capturarlo.") },
            { PBEItem.HealPowder, new PBELocalizedString("A very bitter medicine powder.\nIt heals all the status problems of a\nsingle Pokémon.", "Une poudre médicinale très amère.\nSoigne tous les problèmes de statut\nd'un Pokémon.", "Ein bitteres medizinisches Pulver,\ndas alle Statusprobleme eines\nPokémon aufhebt.", "Rimedio amarissimo in polvere.\nRisolve tutti i problemi di stato\ndi un Pokémon.", "とても　にがい　こなぐすり。\nポケモン　１ひきの　じょうたいの\nいじょうを　すべて　かいふくする。", "とても　にがい　粉薬。\nポケモン　１匹の　状態の\n異常を　すべて　回復する。", "매우 쓴 가루약.\n포켓몬 1마리의\n상태 이상을 모두 회복한다.", "Polvos medicinales muy amargos que\ncuran todos los problemas de estado\nde un Pokémon.") },
            { PBEItem.HealthWing, new PBELocalizedString("An item for use on a Pokémon. It\nslightly increases the base HP of a\nsingle Pokémon.", "Objet à utiliser sur un Pokémon.\nIl augmente un peu ses PV de base.", "Schenkt man einem Pokémon dieses\nItem, wird dessen Basis-KP-Anzahl\ndauerhaft erhöht.", "Strumento da usare su un Pokémon.\nNe aumenta di poco i PS di base.", "ポケモンに　つかう　どうぐ。\nポケモン　１ひきの　ＨＰの\nきそポイントを　ちょっと　あげる。", "ポケモンに　使う　道具。\nポケモン　１匹の　ＨＰの\n基礎ポイントを　ちょっと　あげる。", "포켓몬에게 사용하는 도구.\n포켓몬 1마리의 HP\n기초 포인트를 조금 올린다.", "Pluma que aumenta un poco los PS\nde un Pokémon al subir de nivel.") },
            { PBEItem.HeartScale, new PBELocalizedString("A pretty, heart-shaped scale that is\nextremely rare. It glows faintly in the\ncolors of the rainbow.", "Une jolie écaille en forme de cœur qui\nest très rare. Elle brille légèrement\nd'un éclat arc-en-ciel.", "Eine hübsche, herzförmige, sehr\nseltene Schuppe. Sie erstrahlt\nin den Farben des Regenbogens.", "Squama graziosa, a forma di cuore e\nmolto rara. Riflette tutti i colori\ndell'iride.", "きれいな　ハートの　かたちの\nとても　めずらしい　ウロコ。\nにじいろに　うすく　かがやく。", "きれいな　ハートの　形の\nとても　珍しい　ウロコ。\nにじ色に　うすく　輝く。", "예쁜 하트 모양의\n무척 진귀한 비늘.\n무지개색으로 연하게 반짝인다.", "Bella escama con forma de corazón\nque brilla ligeramente como los colores\ndel arcoíris. Es muy poco común.") },
            { PBEItem.HeatRock, new PBELocalizedString("A Pokémon held item that extends the\nduration of the move Sunny Day used\nby the holder.", "Objet à tenir augmentant la durée\nde la capacité Zénith utilisée par le\nporteur.", "Ein Item zum Tragen. Verlängert\ndie Dauer von Sonnentag, wenn\nvon Träger eingesetzt.", "Strumento da far tenere a un Pokémon\nche allunga la durata della mossa\nGiornodisole se usata da chi lo tiene.", "もたせた　ポケモンが　にほんばれを\nつかうと　いつもより　はれの\nじかんが　ながくなる。", "持たせた　ポケモンが　にほんばれを\n使うと　いつもより　はれの\n時間が　長くなる。", "지니게 한 포켓몬이 쾌청을\n쓰면 여느 때보다 쾌청의\n시간이 길어진다.", "Prolonga la duración del movimiento\nDía Soleado que use el Pokémon\nque la lleva.") },
            { PBEItem.HeavyBall, new PBELocalizedString("A Poké Ball for catching very heavy\nPokémon.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien sur\nles Pokémon lourds.", "Ein Ball, der sich besonders gut eignet,\num Pokémon mit hohem Körpergewicht\nzu fangen.", "Poké Ball un po' originale che funziona\nparticolarmente bene sui Pokémon\npesanti.", "たいじゅうの　おもい　ポケモンが\nつかまえやすくなる\nちょっと　かわった　ボール。　", "体重の　重い　ポケモンが\n捕まえやすくなる\nちょっと　変わった　ボール。　", "체중이 무거운 포켓몬이\n잡기 쉬워지는\n조금 특이한 볼.", "Poké Ball algo distinta que funciona\nespecialmente bien a la hora de atrapar\nPokémon de mucho peso.") },
            { PBEItem.HelixFossil, new PBELocalizedString("A fossil of an ancient Pokémon that\nlived in the sea. It appears to be\npart of a seashell.", "Fossile d'un ancien Pokémon aquatique.\nOn dirait un morceau de coquillage.", "Das Fossil eines uralten Pokémon,\ndas in der Tiefsee lebte. Es sieht\nwie ein Muschelstück aus.", "Fossile di un Pokémon antico che\nviveva in mare. Sembra essere\nparte di una conchiglia.", "おおむかし　うみに　すんでいた\nこだい　ポケモンの　カセキ。\nかいがらの　いちぶ　らしい。", "大昔　海に　すんでいた\n古代ポケモンの　カセキ。\n貝殻の　一部らしい。", "오랜 옛날 바다에 살았던\n고대 포켓몬의 화석.\n조개껍질의 일부인 듯하다.", "Fósil de un Pokémon ancestral que\nvivió en el fondo del mar. Parece ser\nun fragmento de concha marina.") },
            { PBEItem.HondewBerry, new PBELocalizedString("Using it on a Pokémon makes it more\nfriendly, but it also lowers its base\nSp. Atk stat.", "Rend les Pokémon plus amicaux, mais\nbaisse leur stat Attaque Spéciale de\nbase.", "Isst ein Pokémon diese Beere,\nstärkt dies eure Freundschaft, ver-\nringert aber seinen Spez.-Angr.-Wert.", "Utilizzata su un Pokémon, lo rende più\namichevole, ma diminuisce l'Attacco\nSpeciale di base.", "ポケモンに　あげると\nとても　なつき　やすくなるが\nとくこうの　きそポイントは　さがる。", "ポケモンに　あげると\nとても　なつき　やすくなるが\n特攻の　基礎ポイントは　さがる。", "포켓몬에게 주면\n매우 친밀해지기 쉬워지지만\n특수공격의 기초 포인트가 떨어진다.", "Al usarla con un Pokémon, se gana\nsu amistad, pero también reduce\nsu Ataque Especial de base.") },
            { PBEItem.Honey, new PBELocalizedString("A sweet honey with a lush aroma that\nattracts wild Pokémon when it is used\nin grass, caves, or on special trees.", "Un miel au parfum riche qui, utilisé dans\nl'herbe, une grotte ou sur des arbres\nspéciaux, attire les Pokémon sauvages.", "Der Duft dieses Honigs lockt wilde\nPokémon an. Verwende ihn in hohem\nGras oder an besonderen Bäumen.", "Miele dolce dall'aroma intenso che\nattrae i Pokémon selvatici. Usalo nelle\ngrotte, nell'erba o su alberi speciali.", "くさむらや　どうくつ　などで\nつかうと　あまいにおいに　さそわれて\nやせいポケモンが　あらわれる。", "草むらや　どうくつ　などで\n使うと　あまいにおいに　誘われて\n野生ポケモンが　あらわれる。", "풀밭이나 동굴 등에서 사용하면\n달콤한 냄새에 이끌려서\n야생 포켓몬이 나타난다.", "Su delicioso aroma atrae a Pokémon\nsalvajes si se usa en zonas de hierba\nalta, cuevas o árboles especiales.") },
            { PBEItem.HPUp, new PBELocalizedString("A nutritious drink for Pokémon.\nIt raises the base HP of a single\nPokémon.", "Une boisson très nutritive qui monte\nles PV de base d'un Pokémon.", "Ein gehaltvolles Getränk, das die\nBasis-KP-Anzahl eines Pokémon\ndauerhaft erhöht.", "Bevanda nutriente per Pokémon.\nAumenta i PS di base.", "ポケモンの　えいようドリンク。\nポケモン　１ひきの　ＨＰの\nきそポイントを　あげる。", "ポケモンの　栄養ドリンク。\nポケモン　１匹の　ＨＰの\n基礎ポイントを　あげる。", "포켓몬의 영양 드링크.\n포켓몬 1마리의 HP\n기초 포인트를 올린다.", "Nutritiva bebida que aumenta los PS\nde base de un Pokémon.") },
            { PBEItem.HyperPotion, new PBELocalizedString("A spray-type medicine for wounds.\nIt restores the HP of one Pokémon by\n200 points.", "Un spray qui soigne les blessures.\nRestaure 200 PV à un Pokémon.", "Hilft bei Wunden. Füllt\ndie KP eines Pokémon\num 200 Punkte auf.", "Rimedio spray per le ferite.\nRestituisce 200 PS ad un Pokémon.", "スプレーしきの　キズぐすり。\nポケモン　１ひきの　ＨＰを\n２００だけ　かいふくする。", "スプレー式の　キズぐすり。\nポケモン　１匹の　ＨＰを\n２００だけ　回復する。", "스프레이식의 상처약.\n포켓몬 1마리의 HP를\n200만큼 회복한다.", "Medicina en espray que cura heridas\ny restaura 200 PS de un Pokémon.") },
            { PBEItem.IapapaBerry, new PBELocalizedString("If held by a Pokémon, it restores the\nuser's HP in a pinch, but will cause\nconfusion if it hates the taste.", "Tenue, cette Baie restaure des PV en\ncas de besoin, mais rend confus si\nle Pokémon n'aime pas son goût.", "Als getragenes Item füllt die Beere die\nKP eines Pokémon auf, doch ihr Aroma\nverwirrt jene, die es nicht mögen.", "Se tenuta da un Pokémon in difficoltà,\nrestituisce PS. Se al Pokémon non piace\nil suo sapore, può causare confusione.", "ポケモンに　もたせると\nピンチのとき　ＨＰを　かいふくする。\nきらいな　あじだと　こんらん　する。", "ポケモンに　持たせると\nピンチのとき　ＨＰを　回復する。\nきらいな味だと　混乱する。", "포켓몬에게 지니게 하면\n위급할 때 HP를 회복한다.\n싫어하는 맛이면 혼란에 빠진다.", "Los Pokémon pueden llevarla o usarla\npara restaurar algunos PS. Su sabor\npuede causar confusión.") },
            { PBEItem.IceGem, new PBELocalizedString("A gem with an essence of ice. When held,\nit strengthens the power of an\nIce-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Glace.", "Ein Juwel vom Typ Eis. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Eis.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Ghiaccio.", "こおりの　タイプの　ジュエル。\nもたせると　いちどだけ　こおりの\nわざの　いりょくが　つよまる。", "こおりの　タイプの　ジュエル。\n持たせると　１度だけ　こおりの\n技の　威力が　強まる。", "얼음타입의 주얼.\n지니게 하면 한 번만 얼음\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Hielo.\nDebe llevarla un Pokémon.") },
            { PBEItem.IceHeal, new PBELocalizedString("A spray-type medicine.\nIt defrosts a Pokémon that has been\nfrozen solid.", "Un médicament sous forme de spray.\nRéchauffe un Pokémon gelé.", "Ein Spray, das ein eingefrorenes\nPokémon auftaut.", "Rimedio spray.\nCura un Pokémon dal congelamento.", "スプレーしきの　くすり。\nポケモン　１ひきの　こおりの\nじょうたいを　かいふくする。", "スプレー式の　薬。\nポケモン　１匹の　こおりの\n状態を　回復する。", "스프레이식의 약.\n포켓몬 1마리의\n얼음 상태를 회복한다.", "Medicina en espray que descongela\nun Pokémon.") },
            { PBEItem.IciclePlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Ice-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Glace.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Eis.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Ghiaccio.", "こおりの　タイプの　せきばん。\nもたせると　こおりタイプの\nわざの　いりょくが　つよまる。", "こおりの　タイプの　石盤。\n持たせると　こおりタイプの\n技の　威力が　強まる。", "얼음타입의 석판.\n지니게 하면 얼음타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Hielo.\nDebe llevarla un Pokémon.") },
            { PBEItem.IcyRock, new PBELocalizedString("A Pokémon held item that extends the\nduration of the move Hail used by the\nholder.", "Objet à tenir augmentant la durée\nde la capacité Grêle utilisée par le\nporteur.", "Ein Item zum Tragen. Verlängert\ndie Dauer von Hagelsturm, wenn\nvon Träger eingesetzt.", "Strumento da far tenere a un Pokémon\nche allunga la durata della mossa\nGrandine se usata da chi lo tiene.", "もたせた　ポケモンが　あられを\nつかうと　いつもより　あられの\nじかんが　ながくなる。", "持たせた　ポケモンが　あられを\n使うと　いつもより　あられの\n時間が　長くなる。", "지니게 한 포켓몬이 싸라기눈을\n쓰면 여느 때보다 싸라기눈의\n시간이 길어진다.", "Prolonga la duración del movimiento\nGranizo que use el Pokémon que\nla lleva.") },
            { PBEItem.InquiryMail, new PBELocalizedString("Stationery designed for\nwriting questions.\nLet a Pokémon hold it for delivery.", "Un papier à lettres idéal pour\nposer des questions. Peut être\ntenu et livré par un Pokémon.", "Briefpapier, mit dem man jemandem\neine Frage stellen kann.\nGib es einem Pokémon zum Tragen.", "Carta da lettera ideale per scrivere\nuna domanda a qualcuno. Un Pokémon\npuò tenerla e consegnarla.", "あいてへの　しつもんの　メールが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "相手への　質問の　メールが\n書きやすい　レターペーパー。\nポケモンに　持たせて　使う。", "상대에게 질문하는 메일을\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Carta para preguntar a un compañero.\nHaz que la entregue un Pokémon.") },
            { PBEItem.InsectPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Bug-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Insecte.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Käfer.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Coleottero.", "むしの　タイプの　せきばん。\nもたせると　むしタイプの\nわざの　いりょくが　つよまる。", "むしの　タイプの　石盤。\n持たせると　むしタイプの\n技の　威力が　強まる。", "벌레타입의 석판.\n지니게 하면 벌레타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Bicho.\nDebe llevarla un Pokémon.") },
            { PBEItem.Iron, new PBELocalizedString("A nutritious drink for Pokémon.\nIt raises the base Defense stat of a\nsingle Pokémon.", "Une boisson très nutritive qui monte\nla Défense de base d'un Pokémon.", "Ein gehaltvolles Getränk, das den\nVerteidigungs-Wert eines Pokémon\ndauerhaft erhöht.", "Bevanda nutriente per Pokémon.\nAumenta la Difesa di base.", "ポケモンの　えいようドリンク。\nポケモン　１ひきの　ぼうぎょの\nきそポイントを　あげる。", "ポケモンの　栄養ドリンク。\nポケモン　１匹の　防御の\n基礎ポイントを　あげる。", "포켓몬의 영양 드링크.\n포켓몬 1마리의 방어\n기초 포인트를 올린다.", "Nutritiva bebida que aumenta la Defensa\nde base de un Pokémon.") },
            { PBEItem.IronBall, new PBELocalizedString("A Pokémon held item that cuts Speed.\nIt makes Flying-type and levitating\nholders susceptible to Ground moves.", "Objet à tenir réduisant la Vitesse. Rend\nles porteurs de type Vol et lévitant\nsensibles aux capacités de type Sol.", "Ein Item zum Tragen, das Flug-Pokémon\nanfällig für Attacken vom Typ Boden\nmacht. Senkt den Initiative-Wert.", "Riduce la Velocità e rende vulnerabili\nalle mosse terra se chi la tiene è di tipo\nVolante o ha l'abilità Levitazione.", "もたせると　すばやさが　さがる。\nひこうタイプや　ふゆう　ポケモンは\nじめんわざが　あたって　しまう。", "持たせると　素早さが　さがる。\nひこうタイプや　ふゆう　ポケモンは\nじめん技が　当たってしまう。", "지니게 하면 스피드가 떨어진다.\n비행타입이나 부유포켓몬은\n땅 기술에 맞아 버린다.", "Reduce la Velocidad. Los Pokémon que\nlevitan y los de tipo Volador se tornan\nsensibles a movimientos de tipo Tierra.") },
            { PBEItem.IronPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Steel-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Acier.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Stahl.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Acciaio.", "はがねの　タイプの　せきばん。\nもたせると　はがねタイプの\nわざの　いりょくが　つよまる。", "はがねの　タイプの　石盤。\n持たせると　はがねタイプの\n技の　威力が　強まる。", "강철타입의 석판.\n지니게 하면 강철타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Acero.\nDebe llevarla un Pokémon.") },
            { PBEItem.JabocaBerry, new PBELocalizedString("If held by a Pokémon and a physical\nattack lands, the attacker also\ntakes damage.", "Tenue, cette Baie inflige des dégâts\nà l'ennemi s'il utilise une attaque\nphysique qui atteint son but.", "Als getragenes Item bewirkt die Beere\nSchaden bei denen, die den Träger\nmit einer physischen Attacke treffen.", "Se tenuta da un Pokémon, il nemico\nsubirà danni se il suo attacco fisico\nva a segno.", "ポケモンに　もたせると\nぶつりわざを　うけたとき\nあいてにも　ダメージを　あたえる。", "ポケモンに　持たせると\n物理技を　受けたとき\n相手にも　ダメージを　あたえる。", "포켓몬에게 지니게 하면\n물리 기술을 당했을 때\n상대에게도 데미지를 준다.", "Si la lleva un Pokémon cuando otro\nlanza un ataque físico, este último\ntambién recibe daño.") },
            { PBEItem.KasibBerry, new PBELocalizedString("Weakens a supereffective Ghost-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Spectre.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Geist.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Spettro\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　ゴースト　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　ゴースト技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 고스트 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Fantasma\nde un enemigo.") },
            { PBEItem.KebiaBerry, new PBELocalizedString("Weakens a supereffective Poison-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Poison.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Gift.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Veleno\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　どく　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　どく技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 독 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Veneno\nde un enemigo.") },
            { PBEItem.KelpsyBerry, new PBELocalizedString("Using it on a Pokémon makes it more\nfriendly, but it also lowers its base\nAttack stat.", "Rend les Pokémon plus amicaux, mais\nbaisse leur stat Attaque de base.", "Isst ein Pokémon diese Beere,\nstärkt dies eure Freundschaft,\nverringert aber seinen Angriffs-Wert.", "Utilizzata su un Pokémon, lo rende più\namichevole, ma diminuisce l'Attacco\ndi base.", "ポケモンに　あげると\nとても　なつき　やすくなるが\nこうげきの　きそポイントは　さがる。", "ポケモンに　あげると\nとても　なつき　やすくなるが\n攻撃の　基礎ポイントは　さがる。", "포켓몬에게 주면\n매우 친밀해지기 쉬워지지만\n공격의 기초 포인트가 떨어진다.", "Al usarla con un Pokémon, se gana\nsu amistad, pero también reduce\nsu Ataque de base.") },
            { PBEItem.KingsRock, new PBELocalizedString("An item to be held by a Pokémon.\nWhen the holder inflicts damage,\nthe target may flinch.", "Objet à tenir. Peut apeurer l'ennemi\nlorsque le porteur lui inflige des\ndégâts.", "Gibst du es einem Pokémon zum Tragen,\nlässt es mit seinem Angriff das Ziel\ngelegentlich zurückschrecken.", "Strumento da far tenere a un Pokémon.\nSe chi lo usa arreca danni, può far\ntentennare il nemico.", "もたせると　こうげきして\nダメージを　あたえたときに\nあいてを　ひるませることが　ある。", "持たせると　攻撃して\nダメージを　あたえたときに\n相手を　ひるませることが　ある。", "지니게 하면\n공격해서 데미지를 줄 때\n상대를 풀죽이기도 한다.", "El Pokémon que la lleva puede\nhacer retroceder al Pokémon\nal que le inflige daño.") },
            { PBEItem.LaggingTail, new PBELocalizedString("An item to be held by a Pokémon.\nIt is tremendously heavy and makes\nthe holder move slower than usual.", "Objet à tenir extrêmement lourd qui\nralentit le porteur.", "Ein Item zum Tragen. Es ist immens\nschwer und senkt die Geschwindigkeit\ndes Trägers.", "Strumento da far tenere a un Pokémon.\nÈ tremendamente pesante e rende chi\nlo tiene più lento del normale.", "とても　おもたい　なにかの　しっぽ。\nもたせると　いつもより　こうどうが\nおそくなる。", "とても　重たい　なにかの　しっぽ。\n持たせると　いつもより　行動が\n遅くなる。", "매우 무거운 무언가의 꼬리.\n지니게 하면 여느 때보다\n행동이 느려진다.", "Es muy pesada y hace que el Pokémon\nque la lleva se mueva más lento de\nlo normal.") },
            { PBEItem.LansatBerry, new PBELocalizedString("If held by a Pokémon, it raises its\ncritical-hit ratio in a pinch.", "Tenue, cette Baie monte le taux de\ncritiques en cas de besoin.", "Als getragenes Item erhöht diese Beere\ndie Volltrefferquote eines Pokémon,\nsobald es in einer Notlage steckt.", "Brutti colpi più probabili se il Pokémon\nche la tiene si trova in difficoltà.", "ポケモンに　もたせると\nピンチのとき　こうげきが\nきゅうしょに　あたりやすくなる。", "ポケモンに　持たせると\nピンチのとき　攻撃が\n急所に　当たりやすくなる。", "포켓몬에게 지니게 하면\n위급할 때 공격이\n급소를 맞히기 쉬워진다.", "Si la lleva un Pokémon, aumenta\nla probabilidad de dar un golpe\ncrítico en un momento de apuro.") },
            { PBEItem.LavaCookie, new PBELocalizedString("Lavaridge Town's local specialty.\nIt heals all the status problems of\none Pokémon.", "La spécialité de la ville de Vermilava.\nSoigne tous les problèmes de statut\nd'un Pokémon.", "Eine Spezialität aus Bad Lavastadt,\ndie alle Statusprobleme eines\nPokémon heilt.", "Specialità tipica di Cuordilava.\nRisolve tutti i problemi di stato\ndi un Pokémon.", "フエンめいぶつの　せんべい。\nポケモン　１ひきの　じょうたいの\nいじょうを　すべて　かいふくする。", "フエン名物の　せんべい。\nポケモン　１匹の　状態の\n異常を　すべて　回復する。", "용암마을의 명물인 전병.\n포켓몬 1마리의\n상태 이상을 회복한다.", "Dulce típico de Pueblo Lavacalda\nque cura todos los problemas de\nestado de un Pokémon.") },
            { PBEItem.LaxIncense, new PBELocalizedString("An item to be held by a Pokémon. The\ntricky aroma of this incense may make\nattacks miss the holder.", "Objet à tenir. Le parfum trompeur\nde cet encens baisse la Précision de\nl'ennemi.", "Ein Item zum Tragen. Das\nverwirrende Aroma senkt die\nGenauigkeit des Gegners.", "Strumento da far tenere a un Pokémon.\nIl suo aroma intenso riduce\nla precisione del nemico.", "もたせると　おこうの　ふしぎな\nかおりが　あいてを　まどわせて\nわざが　めいちゅう　しにくくなる。", "持たせると　御香の　不思議な\n香りが　相手を　まどわせて\n技が　命中　しにくくなる。", "지니게 하면 향로의 이상한\n향기가 상대를 혼란시켜\n기술이 명중하기 어려워진다.", "Debe llevarlo un Pokémon.\nSu peculiar aroma reduce la Precisión\ndel objetivo.") },
            { PBEItem.LeafStone, new PBELocalizedString("A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt has a leaf pattern.", "Une pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nUne feuille est dessinée dessus.", "Dieser spezielle Stein löst bei\nbestimmten Pokémon die Entwicklung\naus. Er hat ein Blattmuster.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nRiporta l'immagine di una foglia.", "ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nはっぱの　もようが　ある。", "ある　特定の　ポケモンを\n進化させる　不思議な　石。\nはっぱの　模様が　ある。", "어느 특정한 포켓몬을\n진화시키는 이상한 돌.\n잎사귀 무늬가 있다.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nTiene grabada una hoja.") },
            { PBEItem.Leftovers, new PBELocalizedString("An item to be held by a Pokémon.\nThe holder's HP is gradually restored\nduring battle.", "Objet à tenir permettant de restaurer\nles PV du porteur graduellement durant\nle combat.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nfüllt im Kampf stetig KP auf.", "Strumento da far tenere a un Pokémon.\nFa recuperare gradualmente i PS\ndurante la lotta.", "もたせると　ポケモンの　ＨＰが\nせんとうの　あいだ　すこしずつ\nかいふくする。", "持たせると　ポケモンの　ＨＰが\n戦闘の　あいだ　少しずつ\n回復する。", "지니게 하면 포켓몬의 HP가\n배틀을 하는 동안 조금씩\n회복된다.", "Restaura gradualmente los PS\ndel Pokémon que lo lleva\ndurante el combate.") },
            { PBEItem.Lemonade, new PBELocalizedString("A very sweet drink.\nIt restores the HP of one Pokémon by\n80 points.", "Une boisson très sucrée.\nRestaure 80 PV à un Pokémon.", "Ein sehr süßes Getränk, das die KP\neines Pokémon um 80 Punkte auffüllt.", "Bevanda molto dolce.\nRestituisce 80 PS ad un Pokémon.", "とても　あまい　ジュース。\nポケモン　１ひきの　ＨＰを\n８０だけ　かいふくする。", "とても　あまい　ジュース。\nポケモン　１匹の　ＨＰを\n８０だけ　回復する。", "매우 달콤한 쥬스.\n포켓몬 1마리의 HP를\n80만큼 회복한다.", "Bebida muy dulce que restaura 80 PS\nde un Pokémon.") },
            { PBEItem.LeppaBerry, new PBELocalizedString("If held by a Pokémon,\nit restores a move's PP by 10.", "Baie qui restaure 10 PP lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon\nzum Tragen, füllt sie die AP einer\nAttacke um 10 Punkte auf.", "Se tenuta da un Pokémon restituisce\n10 PP a una mossa.", "ポケモンに　もたせると\nＰＰを　１０だけ　かいふくする。\n", "ポケモンに　持たせると\nＰＰを　１０だけ　回復する。\n", "포켓몬에게 지니게 하면\nPP를 10만큼 회복한다.\n", "Los Pokémon pueden llevarla o usarla\npara restaurar 10 PP de un movimiento.") },
            { PBEItem.LevelBall, new PBELocalizedString("A Poké Ball for catching Pokémon that\nare a lower level than your own.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien sur\nles Pokémon de niveau inférieur au sien.", "Ein Ball, mit dem sich Pokémon umso\nleichter fangen lassen, je niedriger ihr\nLevel im Vergleich zu deinem Team ist.", "Poké Ball un po' originale che funziona\nparticolarmente bene sui Pokémon\ndi livello inferiore al proprio.", "じぶんの　ポケモンより　レベルが\nひくいほど　つかまえやすくなる\nちょっと　かわった　ボール。　", "自分の　ポケモンより　レベルが\n低いほど　捕まえやすくなる\nちょっと　変わった　ボール。　", "자신의 포켓몬보다 레벨이\n낮을수록 잡기 쉬워지는\n조금 특이한 볼.", "Poké Ball algo distinta que funciona\nespecialmente bien con Pokémon\nde nivel inferior a los que se posee.") },
            { PBEItem.LiechiBerry, new PBELocalizedString("If held by a Pokémon, it raises its\nAttack stat in a pinch.", "Tenue, cette Baie monte l'Attaque\nen cas de besoin.", "Als getragenes Item erhöht diese Beere\nden Angriffs-Wert eines Pokémon, sobald\nes in einer Notlage steckt.", "Aumenta l'Attacco, se il Pokémon che\nla tiene si trova in difficoltà.", "ポケモンに　もたせると\nピンチのとき　じぶんの\nこうげきが　あがる。", "ポケモンに　持たせると\nピンチのとき　自分の\n攻撃が　あがる。", "포켓몬에게 지니게 하면\n위급할 때 자신의\n공격이 올라간다.", "Si la lleva un Pokémon, le sube\nel Ataque en un momento de apuro.") },
            { PBEItem.LifeOrb, new PBELocalizedString("An item to be held by a Pokémon.\nIt boosts the power of moves, but at\nthe cost of some HP on each hit.", "Objet à tenir augmentant la puissance\ndes capacités, mais qui dépense des\nPV à chaque coup.", "Ein Item zum Tragen.\nVerstärkt Attacken, aber jede\nAttacke kostet den Träger KP.", "Strumento da far tenere a un Pokémon.\nPotenzia le mosse, a scapito di alcuni\nPS per ogni colpo.", "もたせると　こうげきする　たびに\nＨＰが　すこし　へってしまうが\nわざの　いりょくが　あがる。", "持たせると　攻撃する　たびに\nＨＰが　少し　減ってしまうが\n技の　威力が　あがる。", "지니게 하면 공격할 때마다\nHP가 조금씩 줄지만\n기술의 위력이 올라간다.", "Potencia los movimientos, pero\nconsume PS con cada ataque.\nDebe llevarla un Pokémon.") },
            { PBEItem.LightBall, new PBELocalizedString("An item to be held by Pikachu.\nIt is a puzzling orb that raises\nthe Attack and Sp. Atk stat.", "Objet à faire tenir par Pikachu. Orbe\nénigmatique qui monte son Attaque et\nson Attaque Spéciale.", "Ein Item, das von Pikachu getragen\nwerden kann. Es erhöht den Angriffs-\nWert und Spezial-Angriff.", "Strumento da far tenere a Pikachu.\nSfera insolita che aumenta l'Attacco e\nl'Attacco Speciale.", "ピカチュウに　もたせると\nこうげきと　とくこうの　いりょくが\nあがる　ふしぎな　たま。", "ピカチュウに　持たせると\n攻撃と　特攻の　威力が\nあがる　不思議な　玉。", "피카츄에게 지니게 하면\n공격과 특수공격의 위력이\n올라가는 이상한 구슬.", "Asombrosa esfera que debe llevar\nPikachu y que aumenta su Ataque\ny su Ataque Especial.") },
            { PBEItem.LightClay, new PBELocalizedString("A Pokémon held item that extends the\nduration of barrier moves like Light\nScreen and Reflect used by the holder.", "Objet à tenir augmentant la durée\nde capacités barrières telles que\nMur Lumière et Protection.", "Ein Item zum Tragen.\nVerlängert die Dauer von\nLichtschild, Reflektor etc.", "Strumento da far tenere a un Pokémon.\nAllunga la durata delle mosse barriera\ncome Schermoluce e Riflesso.", "もたせた　ポケモンが　ひかりのかべや\nリフレクターを　つかったとき\nいつもよりも　ながく　のこる。", "持たせた　ポケモンが　ひかりのかべや\nリフレクターを　使ったとき\nいつもよりも　長く　残る。", "지니게 한 포켓몬이 빛의장막이나\n리플렉터를 사용했을 때\n여느 때보다 긴 시간동안 지속된다.", "Prolonga la duración de barreras\ncomo Reflejo y Pantalla de Luz.\nDebe llevarla un Pokémon.") },
            { PBEItem.LikeMail, new PBELocalizedString("Stationery designed for\nwriting recommendations.\nLet a Pokémon hold it for delivery.", "Un papier à lettres idéal pour\ndonner des conseils. Peut être\ntenu et livré par un Pokémon.", "Briefpapier, mit dem man jemandem\neinen Tipp geben kann.\nGib es einem Pokémon zum Tragen.", "Carta da lettera ideale per consigliare\nqualcosa a qualcuno. Un Pokémon può\ntenerla e consegnarla.", "なにかを　おすすめする　メールが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "なにかを　おすすめする　メールが\n書きやすい　レターペーパー。\nポケモンに　持たせて　使う。", "뭔가 추천하는 메일을\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Carta para recomendar algo.\nHaz que la entregue un Pokémon.") },
            { PBEItem.LoveBall, new PBELocalizedString("Poké Ball for catching Pokémon that are\nthe opposite gender of your Pokémon.", "Une Poké Ball un peu spéciale qui\nfonctionne mieux sur un Pokémon de\nla même espèce mais de sexe opposé.", "Ein Ball, der sich besonders gut eignet,\num Pokémon zu fangen, die ein anderes\nGeschlecht haben als dein Pokémon.", "Poké Ball speciale che funziona meglio\nsui Pokémon di sesso opposto a quello\ndel proprio e della stessa specie.", "じぶんの　ポケモンと　せいべつが\nちがうと　つかまえやすくなる\nちょっと　かわった　ボール。　", "自分の　ポケモンと　性別が\n違うと　捕まえやすくなる\nちょっと　変わった　ボール。　", "자신의 포켓몬과 성별이\n다르면 잡기 쉬워지는\n조금 특이한 볼.", "Poké Ball algo distinta que atrapa mejor\nPokémon de la misma especie que los\nque se posee, pero de sexo diferente.") },
            { PBEItem.LuckIncense, new PBELocalizedString("An item to be held by a Pokémon.\nIt doubles a battle's prize money if\nthe holding Pokémon joins in.", "Objet à tenir. Double l'argent gagné\nsi le porteur se joint au combat.", "Ein Item, das ein Pokémon trägt und\ndas doppelte Preisgeld bringt, wenn\nder Träger am Kampf teilnimmt.", "Strumento da far tenere a un Pokémon.\nRaddoppia i guadagni nella lotta se\nchi lo tiene vi partecipa.", "もたせた　ポケモンが　いちどでも\nせんとうに　かおを　だすと\nおかねが　２ばい　もらえる。", "持たせた　ポケモンが　１度でも\n戦闘に　顔を　だすと\nお金が　２倍　もらえる。", "지니게 한 포켓몬이\n한 번이라도 배틀에 나가면\n돈을 2배로 받을 수 있다.", "Si el Pokémon que lo lleva lucha en\nun combate, duplica las ganancias.") },
            { PBEItem.LuckyEgg, new PBELocalizedString("An item to be held by a Pokémon.\nIt is an egg filled with happiness that\nearns extra Exp. Points in battle.", "Objet à tenir. Œuf rempli de joie qui\naugmente le nombre de points Exp.\ngagnés au combat.", "Ein getragenes Item, das mit Glück\ngefüllt ist und für zusätzliche\nE.-Punkte im Kampf sorgt.", "Strumento da far tenere a un Pokémon.\nUovo pieno di felicità che aumenta\ni Punti Esperienza in lotta.", "しあわせが　つまった　タマゴ。\nもたせた　ポケモンは　もらえる\nけいけんちが　すこし　ふえる。", "しあわせが　つまった　タマゴ。\n持たせた　ポケモンは　もらえる\n経験値が　少し　ふえる。", "행복이 가득한 알.\n지니게 한 포켓몬은 받을 수 있는\n경험치가 조금 늘어난다.", "Es un huevo lleno de felicidad que te\nhace ganar Puntos de Experiencia extra\nen combate. Debe llevarlo un Pokémon.") },
            { PBEItem.LuckyPunch, new PBELocalizedString("An item to be held by Chansey. It is a\npair of gloves that boosts Chansey's\ncritical-hit ratio.", "Objet à faire tenir à Leveinard. Paire\nde gants montant son taux de critiques.", "Ein Item, das von Chaneira getragen\nwerden kann. Diese Handschuhe erhöhen\ndie Volltrefferquote von Chaneira.", "Strumento da far tenere a Chansey.\nGuanti che aumentano di molto la\nprobabilità di sferrare brutti colpi.", "こううんを　よぶ　グローブ。\nラッキーに　もたせると　わざが\nきゅうしょに　あたりやすくなる。", "幸運を　よぶ　グローブ。\nラッキーに　持たせると　技が\n急所に　当たりやすくなる。", "행운을 부르는 글러브다.\n럭키에게 지니게 하면 기술이\n급소에 맞기 쉬워진다.", "Un par de guantes que debe llevar\nChansey. Potencian su índice\nde golpe crítico mucho.") },
            { PBEItem.LumBerry, new PBELocalizedString("If held by a Pokémon,\nit recovers from any status problem.", "Baie qui soigne tous les problèmes\nde statut lorsqu'elle est tenue\npar un Pokémon.", "Gibst du die Beere einem Pokémon\nzum Tragen, hebt sie alle seine\nStatusprobleme auf.", "Se tenuta da un Pokémon risolve\nqualsiasi problema di stato.", "ポケモンに　もたせると\nすべての　じょうたい　いじょうを\nかいふくする。", "ポケモンに　持たせると\nすべての　状態異常を\n回復する。", "포켓몬에게 지니게 하면\n모든 상태 이상을\n회복한다.", "Los Pokémon pueden llevarla o usarla\npara recuperarse de problemas\nde estado.") },
            { PBEItem.LureBall, new PBELocalizedString("A Poké Ball for catching Pokémon hooked\nby a Rod when fishing.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien sur les\nPokémon ferrés avec une canne à pêche.", "Ein Ball, der sich besonders gut\neignet, um Pokémon zu fangen,\ndie an deiner Angel hängen.", "Poké Ball un po' originale che funziona\nparticolarmente bene sui Pokémon\npescati con un Amo.", "つりざおで　つりあげた　ポケモンだと\nつかまえやすくなる\nちょっと　かわった　ボール。　", "つりざおで　つりあげた　ポケモンだと\n捕まえやすくなる\nちょっと　変わった　ボール。　", "낚싯대로 낚아올린 포켓몬이라면\n잡기 쉬워지는\n조금 특이한 볼.", "Poké Ball algo distinta, especialmente\nindicada para capturar los Pokémon\nque se han pescado con una Caña.") },
            { PBEItem.LustrousOrb, new PBELocalizedString("A beautifully glowing orb to be held by\nPalkia. It boosts the power of Dragon-\nand Water-type moves.", "Bel orbe luisant destiné à Palkia.\nAugmente la puissance des capacités\nde type Dragon et Eau.", "Ein hell leuchtender Orb.\nVerstärkt Attacken vom Typ Drachen\nund Wasser, wenn Palkia ihn trägt.", "Sfera splendente da far tenere\na Palkia. Potenzia le mosse di tipo\nDrago e Acqua.", "パルキアに　もたせると　ドラゴンと\nみずタイプの　わざの　いりょくが\nあがる　うつくしく　かがやく　たま。", "パルキアに　持たせると　ドラゴンと\nみずタイプの　技の　威力が\nあがる　美しく　輝く　珠。", "펄기아에게 지니게 하면 드래곤과\n물타입 기술의 위력이 올라가는\n아름답게 반짝이는 구슬.", "Una bonita esfera que potencia\nlos movimientos de tipo Dragón y Agua.\nDebe llevarla Palkia.") },
            { PBEItem.LuxuryBall, new PBELocalizedString("A comfortable Poké Ball that makes a\ncaught wild Pokémon quickly grow\nfriendly.", "Une Poké Ball pratique qui permet\nde gagner rapidement l'amitié d'un\nPokémon sauvage attrapé.", "Ein behaglicher Ball, der Pokémon\nveranlasst, nach dem Fang schneller\nfreundlicher zu werden.", "Poké Ball utile che rende più amichevoli\ni Pokémon selvatici catturati.", "つかまえた　やせいポケモンが\nとても　なつきやすくなる\nいごこちの　いい　ボール。", "捕まえた　野生ポケモンが\nとても　なつきやすくなる\nいごこちの　いい　ボール。", "잡은 야생 포켓몬이\n매우 친밀해지기 쉬운\n편안한 볼.", "Acogedora Poké Ball que hace más\namistosos a los Pokémon salvajes\ncapturados.") },
            { PBEItem.MachoBrace, new PBELocalizedString("An item to be held by a Pokémon. It is\na stiff and heavy brace that promotes\nstrong growth but lowers Speed.", "Objet à tenir. C'est un bracelet dur\net lourd qui rend le porteur plus fort,\nmais baisse sa Vitesse.", "Ein Item, das ein Pokémon tragen\nkann. Der Initiative-Wert sinkt zwar,\naber es wird dafür umso stärker.", "Strumento da far tenere a un Pokémon.\nAttrezzo pesante che riduce la Velocità\nma aiuta la crescita delle statistiche.", "かたくて　おもい　ギプス。\nもたせると　すばやさは　さがるが\nふつうより　つよく　そだちやすい。", "硬くて　重い　ギプス。\n持たせると　素早さは　さがるが\nふつうより　強く　育ちやすい。", "단단하고 무거운 깁스.\n지니게 하면 스피드는 떨어지지만\n여느 때보다 강하게 키우기 쉽다.", "Sólido y pesado brazal. Sube más las\ncaracterísticas al subir nivel, pero\nbaja la Velocidad de quien lo lleva.") },
            { PBEItem.Magmarizer, new PBELocalizedString("A box packed with a tremendous\namount of magma energy.\nIt is loved by a certain Pokémon.", "Une boîte remplie d'une énorme quantité\nd'énergie magmatique. Appréciée d'un\ncertain Pokémon.", "Eine Schachtel mit Magmaenergie.\nDas Lieblingsstück eines bestimmten\nPokémon.", "Scatola strapiena di energia lavica.\nUn Pokémon in particolare adora\nquesto strumento.", "とてつもない　マグマの　エネルギーが\nたくわえられている　はこ。\nある　ポケモンが　このんでいる。", "とてつもない　マグマの　エネルギーが\n蓄えられている　箱。\nある　ポケモンが　好んでいる。", "엄청난 마그마의 에너지가\n비축되어 있는 상자.\n어떤 포켓몬이 좋아하는 물건이다.", "Esta caja contiene una enorme\ncantidad de energía magmática.\nCierto Pokémon lo adora.") },
            { PBEItem.Magnet, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a powerful magnet that boosts\nthe power of Electric-type moves.", "Objet à tenir. Aimant puissant montant\nla puissance des capacités de type\nÉlectrik.", "Ein Item zum Tragen, dessen\nmagnetische Kraft Attacken\nvom Typ Elektro verstärkt.", "Strumento da far tenere a un Pokémon.\nPotente magnete che potenzia le mosse\ndi tipo Elettro.", "きょうりょくな　じしゃく。\nもたせると　でんきタイプの\nわざの　いりょくが　あがる。", "強力な　磁石。\n持たせると　でんきタイプの\n技の　威力が　あがる。", "강력한 자석.\n지니게 하면 전기타입\n기술의 위력이 올라간다.", "Debe llevarlo un Pokémon.\nSu potente magnetismo fortalece\nlos movimientos de tipo Eléctrico.") },
            { PBEItem.MagoBerry, new PBELocalizedString("If held by a Pokémon, it restores the\nuser's HP in a pinch, but will cause\nconfusion if it hates the taste.", "Tenue, cette Baie restaure des PV en\ncas de besoin, mais rend confus si\nle Pokémon n'aime pas son goût.", "Als getragenes Item füllt die Beere die\nKP eines Pokémon auf, doch ihr Aroma\nverwirrt jene, die es nicht mögen.", "Se tenuta da un Pokémon in difficoltà,\nrestituisce PS. Se al Pokémon non piace\nil suo sapore, può causare confusione.", "ポケモンに　もたせると\nピンチのとき　ＨＰを　かいふくする。\nきらいな　あじだと　こんらん　する。", "ポケモンに　持たせると\nピンチのとき　ＨＰを　回復する。\nきらいな味だと　混乱する。", "포켓몬에게 지니게 하면\n위급할 때 HP를 회복한다.\n싫어하는 맛이면 혼란에 빠진다.", "Los Pokémon pueden llevarla o usarla\npara restaurar algunos PS. Su sabor\npuede causar confusión.") },
            { PBEItem.MagostBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.MasterBall, new PBELocalizedString("The best Ball with the ultimate level of\nperformance. It will catch any wild\nPokémon without fail.", "Assurément la Ball la plus performante.\nElle permet de capturer à coup sûr un\nPokémon sauvage.", "Der beste Ball!\nDamit fängst du garantiert\njedes wilde Pokémon.", "La Ball dalle prestazioni migliori:\ncattura qualsiasi Pokémon selvatico\nsenza mai fallire.", "やせいの　ポケモンを　かならず\nつかまえることが　できる\nさいこう　せいのうの　ボール。", "野生の　ポケモンを　必ず\n捕まえることが　できる\n最高　性能の　ボール。", "야생 포켓몬을 반드시\n잡을 수 있는\n최고 성능의 볼.", "La Poké Ball definitiva. Atrapa\ncualquier Pokémon salvaje y no\nfalla nunca.") },
            { PBEItem.MaxElixir, new PBELocalizedString("It fully restores the PP of all the\nmoves learned by the targeted\nPokémon.", "Restaure tous les PP de toutes\nles capacités d'un Pokémon.", "Füllt alle AP aller Attacken eines\nPokémon vollständig auf.", "Restituisce tutti i PP a ogni mossa\nimparata dal Pokémon.", "ポケモンが　おぼえている\n４つの　わざの　ＰＰを\nすべて　かいふくする。", "ポケモンが　覚えている\n４つの　技の　ＰＰを\nすべて　回復する。", "포켓몬이 기억하고 있는\n4개 기술의 PP를\n모두 회복한다.", "Restaura completamente los PP de\ntodos los movimientos aprendidos\npor el Pokémon elegido.") },
            { PBEItem.MaxEther, new PBELocalizedString("It fully restores the PP of a single\nselected move that has been learned\nby the target Pokémon.", "Restaure tous les PP d'une capacité\nsélectionnée.", "Füllt die AP einer ausgewählten Attacke\neines Pokémon vollständig auf.", "Restituisce tutti i PP a una mossa\na scelta che è stata imparata\ndal Pokémon.", "ポケモンが　おぼえている\nわざのうち　１つの　ＰＰを\nすべて　かいふくする。", "ポケモンが　覚えている\n技のうち　１つの　ＰＰを\nすべて　回復する。", "포켓몬이 기억하고 있는\n기술 중 1개의 PP를\n모두 회복한다.", "Restaura completamente los PP del\nmovimiento elegido de un Pokémon.") },
            { PBEItem.MaxPotion, new PBELocalizedString("A spray-type medicine for wounds.\nIt completely restores the HP of a\nsingle Pokémon.", "Un spray qui soigne les blessures.\nRestaure tous les PV d'un Pokémon.", "Hilft bei Wunden. Füllt\ndie KP eines Pokémon\nvollständig wieder auf.", "Rimedio spray per le ferite.\nRestituisce tutti i PS ad un Pokémon.", "スプレーしきの　キズぐすり。\nポケモン　１ひきの　ＨＰを\nすべて　かいふくする。", "スプレー式の　キズぐすり。\nポケモン　１匹の　ＨＰを\nすべて　回復する。", "스프레이식의 상처약.\n포켓몬 1마리의 HP를\n모두 회복한다.", "Medicina en espray que cura heridas\ny restaura todos los PS de un Pokémon.") },
            { PBEItem.MaxRepel, new PBELocalizedString("An item that prevents weak wild\nPokémon from appearing for 250 steps\nafter its use.", "Repousse les Pokémon sauvages\nfaibles pendant 250 pas.", "Hält 250 Schritte lang schwache,\nwilde Pokémon ab.", "Repellente che evita l'incontro con i\nPokémon selvatici deboli per 250 passi\ndopo l'utilizzo.", "２５０ぽ　すすむ　あいだ\nよわい　やせいの　ポケモンが\nまったく　でて　こなくなる。", "２５０歩　進む　あいだ\n弱い　野生の　ポケモンが\nまったく　でて　こなくなる。", "250보 걸어가는 동안\n약한 야생 포켓몬이\n전혀 나오지 않게 된다.", "Repele Pokémon salvajes débiles\nen un recorrido de 250 pasos.") },
            { PBEItem.MaxRevive, new PBELocalizedString("A medicine that revives a fainted\nPokémon. It fully restores the\nPokémon's HP.", "Un médicament qui ranime un Pokémon\nK.O. et restaure tous ses PV.", "Eine Medizin, die ein kampfunfähiges\nPokémon wiederbelebt. Die KP werden\nvollständig wiederhergestellt.", "Rimedio che rianima un Pokémon esausto\nridandogli tutti i PS.", "ひんしに　なってしまった\nポケモン　１ひきの　ＨＰを\nすべて　かいふくする。", "ひんしに　なってしまった\nポケモン　１匹の　ＨＰを\nすべて　回復する。", "기절해 버린\n포켓몬 1마리의 HP를\n모두 회복한다.", "Medicina que reanima a un Pokémon\ndebilitado y restaura todos sus PS.") },
            { PBEItem.MeadowPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Grass-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Plante.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Pflanze.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Erba.", "くさの　タイプの　せきばん。\nもたせると　くさタイプの\nわざの　いりょくが　つよまる。", "くさの　タイプの　石盤。\n持たせると　くさタイプの\n技の　威力が　強まる。", "풀타입의 석판.\n지니게 하면 풀타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Planta.\nDebe llevarla un Pokémon.") },
            { PBEItem.MentalHerb, new PBELocalizedString("An item to be held by a Pokémon. It\nsnaps the holder out of infatuation.\nIt can be used only once.", "Objet à tenir qui annule l'attirance\nd'un Pokémon. Ne peut être utilisé\nqu'une fois.", "Ein Item, das einem Pokémon zum Tragen\ngegeben werden kann. Hilft einmalig bei\neingeschränktem Handlungsspielraum.", "Strumento monouso da far tenere a un\nPokémon. Annulla l'infatuazione di chi\nlo tiene.", "もたせた　ポケモンが　じゆうに\nわざを　だせなくなったときに\nいちどだけ　かいふくする。", "持たせた　ポケモンが　自由に\n技を　だせなくなったときに\n１度だけ　回復する。", "지니게 한 포켓몬은\n헤롱헤롱 상태가 되었을 때\n한 번만 회복한다.", "El Pokémon que la lleva se libera\ndel enamoramiento. Solo puede\nusarse una vez.") },
            { PBEItem.MetalCoat, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a special metallic film that ups\nthe power of Steel-type moves.", "Objet à tenir. Pellicule métallique\nspéciale qui monte la puissance des\ncapacités de type Acier.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nverstärkt Attacken vom Typ Stahl.", "Strumento da far tenere a un Pokémon.\nSpeciale pellicola metallica che\npotenzia le mosse di tipo Acciaio.", "とくしゅな　きんぞくの　まく。\nもたせると　はがねタイプの\nわざの　いりょくが　あがる。", "特殊な　金属の　膜。\n持たせると　はがねタイプの\n技の　威力が　あがる。", "특수한 금속 장막.\n지니게 하면 강철타입\n기술의 위력이 올라간다.", "Película metálica que fortalece\nlos ataques de tipo Acero.\nDebe llevarlo un Pokémon.") },
            { PBEItem.MetalPowder, new PBELocalizedString("An item to be held by Ditto.\nExtremely fine yet hard, this odd\npowder boosts the Defense stat.", "Objet à faire tenir à Métamorph. Cette\npoudre étrange, très fine mais\nrésistante, booste sa Défense.", "Ein Item, das von Ditto getragen\nwerden kann. Es erhöht seinen\nVerteidigungs-Wert.", "Strumento da far tenere a Ditto.\nStrana polvere finissima e al tempo\nstesso dura che migliora la Difesa.", "メタモンに　もたせると　ぼうぎょが\nあがる　ふしぎなこな。\nとても　こまかくて　かたい。", "メタモンに　持たせると　防御が\nあがる　不思議な粉。\nとても　こまかくて　硬い。", "메타몽에게 지니게 하면\n방어가 올라가는 이상한 가루.\n매우 잘고 단단하다.", "Debe llevarlo Ditto. Es un polvo\nmuy fino, pero a la vez resistente,\nque aumenta su Defensa.") },
            { PBEItem.Metronome, new PBELocalizedString("A Pokémon held item that boosts a move\nused consecutively. Its effect is\nreset if another move is used.", "Objet à tenir boostant une capacité\nutilisée à répétition. L'effet s'annule\nsi une autre capacité est utilisée.", "Ein Item zum Tragen. Verstärkt eine\nAttacke, je öfter sie ununterbrochen\nausgeführt wird.", "Strumento da far tenere a un Pokémon.\nPotenzia una mossa usata di seguito.\nL'aumento svanisce al cambio di mossa.", "もたせると　おなじわざを　れんぞくで\nつかったとき　いりょくが　あがる。\nやめると　いりょくは　もどる。", "持たせると　同じ技を　連続で\n使ったとき　威力が　あがる。\nやめると　威力は　もどる。", "지니게 하면 똑같은 기술을 연속으로\n사용했을 때 위력이 올라간다.\n그만두면 위력이 되돌아간다.", "Potencia un movimiento que se use de\nforma consecutiva. Pero el efecto se\npierde si se emplea otro movimiento.") },
            { PBEItem.MicleBerry, new PBELocalizedString("If held by a Pokémon, it raises the\naccuracy of a move just once in a pinch.", "Tenue, cette Baie augmente une fois\nla Précision d'une capacité en cas de\nbesoin.", "Als getragenes Item erhöht diese Beere\ndie Genauigkeit eines Pokémon, sobald\nes in einer Notlage steckt.", "Se il Pokémon che ce l'ha è in difficoltà,\naumenta la precisione della sua mossa\nsuccessiva.", "ポケモンに　もたせると\nピンチのとき　いちどだけ\nわざの　めいちゅうりつが　あがる。", "ポケモンに　持たせると\nピンチのとき　１度だけ\n技の　命中率が　あがる。", "포켓몬에게 지니게 하면\n위급할 때 한 번만\n기술의 명중률이 올라간다.", "Cuando la lleva un Pokémon, le sube\nla Precisión de un movimiento en un\nmomento de apuro.") },
            { PBEItem.MindPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Psychic-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Psy.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Psycho.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Psico.", "エスパーの　タイプの　せきばん。\nもたせると　エスパータイプの\nわざの　いりょくが　つよまる。", "エスパーの　タイプの　石盤。\n持たせると　エスパータイプの\n技の　威力が　強まる。", "에스퍼타입의 석판.\n지니게 하면 에스퍼타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Psíquico.\nDebe llevarla un Pokémon.") },
            { PBEItem.MiracleSeed, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a seed imbued with life that ups\nthe power of Grass-type moves.", "Objet à tenir. Graine remplie de vie qui\nmonte la puissance des capacités de\ntype Plante.", "Ein Item zum Tragen. Ein Samen\nvoller Lebenskraft, der Attacken\nvom Typ Pflanze verstärkt.", "Strumento da far tenere a un Pokémon.\nSeme intriso di vita che potenzia\nle mosse di tipo Erba.", "せいめいが　やどる　タネ。\nもたせると　くさタイプの\nわざの　いりょくが　あがる。", "生命が　宿る　タネ。\n持たせると　くさタイプの\n技の　威力が　あがる。", "생명이 깃든 씨앗.\n지니게 하면 풀타입\n기술의 위력이 올라간다.", "Debe llevarla un Pokémon.\nLa vitalidad de la semilla fortalece\nlos movimientos de tipo Planta.") },
            { PBEItem.MoomooMilk, new PBELocalizedString("Milk with a very high nutrition content.\nIt restores the HP of one Pokémon by\n100 points.", "Un lait très nourrissant.\nRestaure 100 PV à un Pokémon.", "Eine äußerst nährstoffreiche Milch, die\ndie KP eines Pokémon um 100 Punkte\nauffüllt.", "Latte ricco di sostanze nutrienti.\nRestituisce 100 PS ad un Pokémon.", "えいよう　まんてんの　ミルク。\nポケモン　１ひきの　ＨＰを\n１００だけ　かいふくする。", "栄養満点の　ミルク。\nポケモン　１匹の　ＨＰを\n１００だけ　回復する。", "영양 만점의 우유.\n포켓몬 1마리의 HP를\n100만큼 회복한다.", "Leche de alto valor nutritivo que\nrestaura 100 PS de un Pokémon.") },
            { PBEItem.MoonBall, new PBELocalizedString("A Poké Ball for catching Pokémon that\nevolve using the Moon Stone.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien sur les\nPokémon évoluant avec une Pierre Lune.", "Ein Ball, der sich besonders gut eignet,\num Pokémon zu fangen, die sich durch\neinen Mondstein weiterentwickeln.", "Poké Ball un po' originale che funziona\nparticolarmente bene sui Pokémon\nche si evolvono con una Pietralunare.", "つきのいしで　しんかする　ポケモンが\nつかまえやすくなる\nちょっと　かわった　ボール。　", "つきのいしで　進化する　ポケモンが\n捕まえやすくなる\nちょっと　変わった　ボール。　", "달의돌로 진화하는 포켓몬이\n잡기 쉬워지는\n조금 특이한 볼.", "Poké Ball algo distinta que atrapa con\nmás facilidad los Pokémon que\nevolucionan con la Piedra Lunar.") },
            { PBEItem.MoonStone, new PBELocalizedString("A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt is as black as the night sky.", "Une pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle est noire comme la nuit.", "Dieser spezielle Stein löst bei\nbestimmten Pokémon die Entwicklung\naus. Er ist schwarz wie die Nacht.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nÈ nera come il cielo di notte.", "ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nよぞらのように　くろい。", "ある　特定の　ポケモンを\n進化させる　不思議な　石。\n夜空のように　黒い。", "어느 특정한 포켓몬을\n진화시키는 이상한 돌.\n밤하늘처럼 까맣다.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nEs negra como el azabache.") },
            { PBEItem.MuscleBand, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a headband that slightly boosts\nthe power of physical moves.", "Objet à tenir. Bandeau augmentant\nlégèrement la puissance des attaques\nphysiques.", "Ein Item zum Tragen.\nDieses Stirnband verstärkt\nphysische Attacken etwas.", "Strumento da far tenere a un Pokémon.\nFascia che potenzia di poco gli attacchi\nfisici.", "ちからが　わいてくる　ハチマキ。\nもたせると　ぶつりわざの\nいりょくが　すこし　あがる。", "ちからが　わいてくる　ハチマキ。\n持たせると　物理技の\n威力が　少し　あがる。", "힘이 끓어오르는 머리띠.\n지니게 하면 물리 기술의\n위력이 조금 올라간다.", "Cinta de pelo que aumenta ligeramente\nla potencia de los movimientos físicos.\nDebe llevarla un Pokémon.") },
            { PBEItem.MuscleWing, new PBELocalizedString("An item for use on a Pokémon. It\nslightly increases the base Attack stat\nof a single Pokémon.", "Objet à utiliser sur un Pokémon.\nIl augmente un peu son Attaque\nde base.", "Schenkt man einem Pokémon dieses\nItem, wird dessen Angriffs-Wert\ndauerhaft erhöht.", "Strumento da usare su un Pokémon.\nNe aumenta di poco l'Attacco di base.", "ポケモンに　つかう　どうぐ。\nポケモン　１ひきの　こうげきの\nきそポイントを　ちょっと　あげる。", "ポケモンに　使う　道具。\nポケモン　１匹の　攻撃の\n基礎ポイントを　ちょっと　あげる。", "포켓몬에게 사용하는 도구.\n포켓몬 1마리의 공격\n기초 포인트를 조금 올린다.", "Pluma que aumenta un poco el Ataque\nde un Pokémon al subir de nivel.") },
            { PBEItem.MysticWater, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a teardrop-shaped gem that ups\nthe power of Water-type moves.", "Objet à tenir. Gemme en forme de\ngoutte d'eau montant la puissance\ndes capacités de type Eau.", "Ein Item zum Tragen. Ein tränen-\nförmiger Edelstein, der Attacken\nvom Typ Wasser verstärkt.", "Strumento da far tenere a un Pokémon.\nGemma a forma di goccia che potenzia\nle mosse di tipo Acqua.", "しずくの　かたちの　ほうせき。\nもたせると　みずタイプの\nわざの　いりょくが　あがる。", "しずくの　形の　宝石。\n持たせると　みずタイプの\n技の　威力が　あがる。", "물방울 모양의 보석.\n지니게 하면 물타입\n기술의 위력이 올라간다.", "Gema con forma de gota de agua que\nfortalece los movimientos de tipo Agua.\nDebe llevarla un Pokémon.") },
            { PBEItem.NanabBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.NestBall, new PBELocalizedString("A somewhat different Poké Ball that\nworks especially well on weaker\nPokémon in the wild.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien\nsur les Pokémon les plus faibles.", "Ein Ball, dessen Erfolgsquote\nbesonders bei schwächeren\nPokémon sehr hoch ist.", "Poké Ball un po' diversa. Risulta\nparticolarmente efficace sui Pokémon\nselvatici più deboli.", "つかまえる　やせいの　ポケモンが\nよわいほど　つかまえやすくなる\nちょっと　かわった　ボール。", "捕まえる　野生の　ポケモンが\n弱いほど　捕まえやすくなる\nちょっと　変わった　ボール。", "잡으려는 야생 포켓몬이\n약할수록 잡기 쉬운\n조금 특이한 볼.", "Es algo distinta a las demás. Funciona\nespecialmente bien con los Pokémon\nsalvajes de nivel bajo.") },
            { PBEItem.NetBall, new PBELocalizedString("A somewhat different Poké Ball that\nworks especially well on Water- and\nBug-type Pokémon.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien\nsur les Pokémon Eau ou Insecte.", "Ein Ball, der sich besonders gut eignet,\num Wasser- und Käfer-Pokémon\nzu fangen.", "Poké Ball un po' diversa. Risulta\nparticolarmente efficace sui Pokémon\ndi tipo Acqua e Coleottero.", "みずタイプと　むしタイプの\nポケモンが　つかまえやすくなる\nちょっと　かわった　ボール。", "みずタイプと　むしタイプの\nポケモンが　捕まえやすくなる\nちょっと　変わった　ボール。", "물타입과 벌레타입의\n포켓몬을 잡기 쉬운\n조금 특이한 볼.", "Es algo distinta a las demás. Funciona\nespecialmente bien con los Pokémon\nde tipo Agua y Bicho.") },
            { PBEItem.NeverMeltIce, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a piece of ice that repels heat\nand boosts Ice-type moves.", "Objet à tenir. Morceau de glace qui\nréfléchit la chaleur et monte la\npuissance des capacités de type Glace.", "Ein Item zum Tragen. Ein Stück Eis,\ndas Hitze fernhält und Attacken\nvom Typ Eis verstärkt.", "Strumento da far tenere a un Pokémon.\nPezzo di ghiaccio che respinge il calore\ne potenzia le mosse di tipo Ghiaccio.", "ねつを　よせつけない　こおり。\nもたせると　こおりタイプの\nわざの　いりょくが　あがる。", "熱を　よせつけない　氷。\n持たせると　こおりタイプの\n技の　威力が　あがる。", "열을 가까이하지 않게 하는 얼음.\n지니게 하면 얼음타입\n기술의 위력이 올라간다.", "Fragmento de hielo que repele el calor y\nfortalece los movimientos de tipo Hielo.\nDebe llevarlo un Pokémon.") },
            { PBEItem.NomelBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.NormalGem, new PBELocalizedString("A gem with an ordinary essence. When\nheld, it strengthens the power of a\nNormal-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Normal.", "Ein Juwel vom Typ Normal. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Normal.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Normale.", "ノーマルの　タイプの　ジュエル。\nもたせると　いちどだけ　ノーマルの\nわざの　いりょくが　つよまる。", "ノーマルの　タイプの　ジュエル。\n持たせると　１度だけ　ノーマルの\n技の　威力が　強まる。", "노말타입의 주얼.\n지니게 하면 한 번만 노말\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Normal.\nDebe llevarla un Pokémon.") },
            { PBEItem.Nugget, new PBELocalizedString("A nugget of pure gold that gives off a\nlustrous gleam. It can be sold at a\nhigh price to shops.", "Pépite d'or pur qui brille\nmagnifiquement. Peut être vendue\nà bon prix aux magasins.", "Ein Nugget aus purem Gold, das\neinen schimmernden Glanz besitzt.\nEs ist von großem Wert.", "Pepita d'oro puro dal luccichio\nsfavillante. Si può vendere a un prezzo\npiuttosto alto nei negozi.", "キラキラと　きんいろに　ひかる\nじゅんきん　せいの　たま。\nショップで　たかく　うれる。", "キラキラと　金色に　光る\n純金製の　玉。\nショップで　高く　売れる。", "금색으로 반짝반짝 빛나는\n순금으로 만들어진 구슬.\n상점에서 비싸게 팔린다.", "Pepita de oro puro que desprende\nun brillo espectacular. Puede venderse\nmuy cara en las tiendas.") },
            { PBEItem.OccaBerry, new PBELocalizedString("Weakens a supereffective Fire-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Feu.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Feuer.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Fuoco\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　ほのお　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　ほのお技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 불꽃 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Fuego\nde un enemigo.") },
            { PBEItem.OddIncense, new PBELocalizedString("An item to be held by a Pokémon. It is\nan exotic-smelling incense that boosts\nthe power of Psychic-type moves.", "Objet à tenir. Encens au parfum\nexotique augmentant la puissance\ndes capacités de type Psy.", "Ein Item zum Tragen. Dieses\nexotische Duftkraut verstärkt\nAttacken vom Typ Psycho.", "Strumento da far tenere a un Pokémon.\nIncenso dal profumo esotico che\npotenzia le mosse di tipo Psico.", "ふしぎな　かおりの　おこう。\nもたせると　エスパータイプの\nわざの　いりょくが　あがる。", "不思議な　香りの　御香。\n持たせると　エスパータイプの\n技の　威力が　あがる。", "이상한 향기의 향로.\n지니게 하면 에스퍼타입\n기술의 위력이 올라간다.", "Incienso de exótico aroma que fortalece\nlos movimientos de tipo Psíquico.\nDebe llevarlo un Pokémon.") },
            { PBEItem.OddKeystone, new PBELocalizedString("A vital item that is needed to keep a\nstone tower from collapsing. Voices\ncan be heard from it occasionally.", "Objet très important qui empêche une\ntour en pierre de s'écrouler. Des\nbruits de voix en sortent parfois.", "Ein wichtiges Item, mit dem\nein Steinturm repariert werden\nkann.", "Strumento importante che impedisce\nil crollo di un pozzo di pietra. A volte\ndal suo interno provengono delle voci.", "これがないと　いしのとうが\nくずれてしまう　だいじないし。\nときどき　いしから　こえがきこえる。", "これがないと　石の塔が\n崩れてしまう　大事な石。\nときどき　石から　声が聞こえる。", "이것이 없으면 돌탑이\n무너져버리는 중요한 돌.\n가끔 돌에서 목소리가 들린다.", "Vital para impedir que una torre de\npiedra se derrumbe. A veces se\noyen voces en su interior.") },
            { PBEItem.OldAmber, new PBELocalizedString("A piece of amber that contains the\ngenetic material of an ancient Pokémon.\nIt is clear with a reddish tint.", "Un morceau d'ambre contenant\nl'ADN d'un ancien Pokémon. Il\nest de couleur jaune.", "Ein Stück Bernstein, in dem die Gene\neines uralten Pokémon enthalten\nsind. Es ist durchsichtig!", "Pezzo di ambra che contiene il materiale\ngenetico di un Pokémon antico. È chiaro\ne di colore giallastro.", "こだい　ポケモンの　いでんしが\nとじこめられている　コハク。\nあかみを　おびて　すけている。", "古代ポケモンの　遺伝子が\n閉じこめられている　コハク。\n赤みを　帯びて　透けている。", "고대 포켓몬의 유전자가\n갇혀 있는 호박.\n붉은 기가 돌면서 약간 투명하다.", "Fragmento de ámbar que contiene\ninformación genética de un Pokémon\nancestral. Es de color amarillo.") },
            { PBEItem.OldGateau, new PBELocalizedString("Old Chateau's hidden specialty.\nIt heals all the status problems of a\nsingle Pokémon.", "Spécialité du Vieux Château. Il\nsoigne tous les problèmes de\nstatut d'un Pokémon.", "Eine unbekannte Spezialität.\nSie behebt alle Statusprobleme\neines Pokémon.", "Specialità segreta dell'Antico Château.\nCura tutti i problemi di stato di\nun Pokémon.", "ハクタイの　かくれた　めいぶつ。\nポケモン　１ひきの　じょうたいの\nいじょうを　すべて　かいふくする。", "ハクタイの　隠れた　名物。\nポケモン　１匹の　状態の\n異常を　すべて　回復する。", "영원시티의 숨은 명물.\n포켓몬 1마리의\n상태 이상을 모두 회복한다.", "Especialidad de la Vieja Mansión.\nCura todos los problemas de estado\nde un Pokémon.") },
            { PBEItem.OranBerry, new PBELocalizedString("If held by a Pokémon,\nit heals the user by just 10 HP.", "Baie qui restaure 10 PV lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon\nzum Tragen, füllt sie seine KP\num 10 Punkte auf.", "Se tenuta da un Pokémon gli restituisce\n10 PS.", "ポケモンに　もたせると\nＨＰを　１０だけ　かいふくする。\n", "ポケモンに　持たせると\nＨＰを　１０だけ　回復する。\n", "포켓몬에게 지니게 하면\nHP를 10만큼 회복한다.\n", "Los Pokémon pueden llevarla o usarla\npara restaurar 10 PS.") },
            { PBEItem.OvalStone, new PBELocalizedString("A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt is shaped like an egg.", "Pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle a la forme d'un œuf.", "Dieser besondere, eiförmige Stein\nhilft manchen Pokémon bei\nihrer Evolution.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nÈ a forma di uovo.", "ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nたまのように　まるい。", "ある　特定の　ポケモンを\n進化させる　不思議な　石。\nたまのように　まるい。", "어느 특정 포켓몬을\n진화시키는 이상한 돌.\n구슬처럼 둥글다.", "Una piedra peculiar que hace\nevolucionar a algunos Pokémon.\nTiene forma de huevo.") },
            { PBEItem.PamtreBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.ParalyzHeal, new PBELocalizedString("A spray-type medicine.\nIt eliminates paralysis from a single\nPokémon.", "Un médicament sous forme de spray.\nSoigne un Pokémon de la paralysie.", "Ein Spray, das ein Pokémon\nvon Paralyse heilt.", "Rimedio spray.\nCura un Pokémon dalla paralisi.", "スプレーしきの　くすり。\nポケモン　１ひきの　まひの\nじょうたいを　かいふくする。", "スプレー式の　薬。\nポケモン　１匹の　まひの\n状態を　回復する。", "스프레이식의 약.\n포켓몬 1마리의\n마비 상태를 회복한다.", "Medicina en espray que cura\nun Pokémon paralizado.") },
            { PBEItem.ParkBall, new PBELocalizedString("A special Poké Ball for the Pal Park.", "Une Poké Ball spéciale utilisée au\nParc des Amis.", "Ein Ball, der nur im Park der Freunde\neingesetzt wird.", "Poké Ball speciale utilizzata solo\nall'interno del Parco Amici.", "パルパークの　なかで\nつかう　とくべつな　ボール。　\n", "パルパークの　なかで\n使う　特別な　ボール。　\n", "팔파크 내에서\n사용하는 특별한 볼.\n", "Poké Ball que se utiliza dentro del\nParque Compi.") },
            { PBEItem.PasshoBerry, new PBELocalizedString("Weakens a supereffective Water-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Eau.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Wasser.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Acqua\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　みず　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　みず技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 물 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Agua\nde un enemigo.") },
            { PBEItem.PassOrb, new PBELocalizedString("A mysterious orb containing the power\nof the Unova region, to be used when\ngenerating Pass Power.", "Sphère mystérieuse contenant\nl'Aura d'Unys et utilisable\navec l'Offri-Aura.", "Ein mysteriöser Orb, der die Kraft\nder Einall-Region enthält und zur\nErzeugung von Transferkraft dient.", "Bilia misteriosa che racchiude l'energia\ndi Unima ed è in grado di lanciare\nil Passapotere.", "デルパワーを　はっせいさせる\nときにつかう　イッシュの　パワーが\nとじこめられた　ふしぎな　たま。", "デルパワーを　発生させる\nときに使う　イッシュの　パワーが\nとじこめられた　不思議な　玉。", "딜파워를 발생시킬 때\n사용하는 하나지방의 파워가\n담긴 이상한 구슬.", "Bola misteriosa que contiene el poder\nde la región de Teselia y se utiliza para\ngenerar Poderes Regalo.") },
            { PBEItem.PayapaBerry, new PBELocalizedString("Weakens a supereffective Psychic-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Psy.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Psycho.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Psico\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　エスパー　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　エスパー技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 에스퍼 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Psíquico\nde un enemigo.") },
            { PBEItem.Pearl, new PBELocalizedString("A somewhat-small pearl that sparkles\nin a pretty silver color.\nIt can be sold cheaply to shops.", "Une petite perle qui brille d'une jolie\ncouleur argent. Peut être vendue\nà bas prix aux magasins.", "Eine relativ kleine Perle, die in\nschönem Silber funkelt. Sie ist\nvon geringem Wert.", "Perla piuttosto piccola dai graziosi\nriflessi argentei. Si può vendere\na un prezzo piuttosto basso nei negozi.", "きれいな　ぎんいろに　ひかる\nすこし　ちいさめの　しんじゅ。\nショップで　やすく　うれる。", "きれいな　銀色に　光る\n少し　ちいさめの　真珠。\nショップで　安く　売れる。", "예쁜 은색으로 빛나는\n자그마한 진주.\n상점에서 싸게 팔린다.", "Brillante perla pequeña y plateada\nque no alcanza un buen precio en\nlas tiendas.") },
            { PBEItem.PearlString, new PBELocalizedString("Very large pearls that sparkle in a\npretty silver color.\nA maniac will buy them for a high price.", "Une très grosse perle qui brille\nd'une jolie couleur argent.\nLes amateurs en donnent un bon prix.", "Eine sehr große Perle mit einem\nwunderschönen, silbernen Glanz.\nSammler geben ihr letztes Hemd dafür.", "Grossa perla dai riflessi argentei.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "きれいな　ぎんいろに　ひかる\nとても　おおつぶの　しんじゅ。\nマニアが　たかく　かってくれる。", "きれいな　銀色に　光る\nとても　大粒の　真珠。\nマニアが　高く　買ってくれる。", "예쁜 은색으로 빛나는\n매우 큰 낱알의 진주.\n마니아가 비싼 값에 산다.", "Brillantes perlas plateadas de gran\ntamaño. Muy valoradas por los\ncoleccionistas.") },
            { PBEItem.PechaBerry, new PBELocalizedString("If held by a Pokémon,\nit recovers from poison.", "Baie qui soigne l'empoisonnement\nlorsqu'elle est tenue par un Pokémon.", "Gibst du die Beere einem Pokémon zum\nTragen, heilt diese es von Vergiftung.", "Se tenuta da un Pokémon, lo guarisce\ndall'avvelenamento.", "ポケモンに　もたせると\nどくを　かいふくする。\n", "ポケモンに　持たせると\n毒を　回復する。\n", "포켓몬에게 지니게 하면\n독을 회복한다.\n", "Los Pokémon pueden llevarla o usarla\npara curarse del envenenamiento.") },
            { PBEItem.PersimBerry, new PBELocalizedString("If held by a Pokémon,\nit recovers from confusion.", "Baie qui soigne la confusion lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon zum\nTragen, heilt diese es von Verwirrung.", "Se tenuta da un Pokémon, lo libera\ndalla confusione.", "ポケモンに　もたせると\nこんらんを　かいふくする。\n", "ポケモンに　持たせると\n混乱を　回復する。\n", "포켓몬에게 지니게 하면\n혼란을 회복한다.\n", "Los Pokémon pueden llevarla o usarla\npara librarse de la confusión.") },
            { PBEItem.PetayaBerry, new PBELocalizedString("If held by a Pokémon, it raises its\nSp. Atk stat in a pinch.", "Tenue, cette Baie monte l'Atq. Spé.\nen cas de besoin.", "Als getragenes Item erhöht diese Beere\nden Spezial-Angriff eines Pokémon,\nsobald es in einer Notlage steckt.", "Aumenta l'Attacco Speciale, se\nil Pokémon che la tiene si trova\nin difficoltà.", "ポケモンに　もたせると\nピンチのとき　じぶんの\nとくこうが　あがる。", "ポケモンに　持たせると\nピンチのとき　自分の\n特攻が　あがる。", "포켓몬에게 지니게 하면\n위급할 때 자신의\n특수공격이 올라간다.", "Si la lleva un Pokémon, le sube\nel Ataque Especial en un momento\nde apuro.") },
            { PBEItem.PinapBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.PinkScarf, new PBELocalizedString("An item to be held by a Pokémon.\nIt boosts the Cute aspect of the\nholder in a Contest.", "Objet à tenir. Augmente la Grâce\nd'un Pokémon dans les Concours.", "Ein Item zum Tragen. Trainer erhöhen\ndamit in Wettbewerben die „Anmut“\nihres Pokémon.", "Strumento da far tenere a un Pokémon.\nAumenta la “Grazia\" di chi lo tiene\nin una Gara.", "もたせて　コンテストに　さんかすると\nその　ポケモンは　いつもより\nかわいく　みられる。", "持たせて　コンテストに　参加すると\nその　ポケモンは　いつもより\nかわいく　みられる。", "지니게 하여 콘테스트에 참가하면\n그 포켓몬은 여느 때보다\n귀엽게 보인다.", "Debe llevarlo un Pokémon.\nAumenta la dulzura en los concursos.") },
            { PBEItem.PlumeFossil, new PBELocalizedString("A fossil of an ancient Pokémon that\nflew in the sky in ancient times.\nIt appears to be part of its wing.", "Fossile d'un ancien Pokémon\nvolant. Il semble que ce soit\nun morceau d'aile.", "Das Fossil eines uralten Pokémon, von\ndem man annimmt, dass es der Urahn der\nVogel-Pokémon sei. Ein Teil des Flügels.", "Fossile di un Pokémon preistorico che\nsolcava i cieli. Sembra essere parte\ndella sua ala.", "とりポケモンの　そせんと　いわれる\nこだい　ポケモンの　カセキ。\nつばさの　いちぶ　らしい。", "とりポケモンの　祖先と　いわれる\n古代ポケモンの　カセキ。\nつばさの　一部らしい。", "새포켓몬의 조상이라 불리는\n고대 포켓몬의 화석.\n날개의 일부인 듯하다.", "Fósil de un Pokémon ancestral que\nsurcó los cielos. Parece ser parte\nde un ala suya.") },
            { PBEItem.PnkApricorn, new PBELocalizedString("A pink Apricorn.\nIt has a nice, sweet scent.", "Un Noigrume de couleur rose.\nIl dégage un arôme sucré.", "Eine pinkfarbene Aprikoko. Sie duftet\nsüß und appetitanregend.", "Una Ghicocca di colore rosa.\nEmana un profumo dolce.", "ももいろの　ぼんぐり。\nあまそうな　いい　においがする。\n", "ももいろの　ぼんぐり。\nあまそうな　いい　においがする。\n", "담홍색의 규토리.\n달콤한 듯한 좋은 향기가 난다.\n", "Bonguri de color rosa y aroma dulce.") },
            { PBEItem.PoisonBarb, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a small, poisonous barb that ups\nthe power of Poison-type moves.", "Objet à tenir. Petit pic empoisonné\nmontant la puissance des capacités\nde type Poison.", "Ein Item zum Tragen. Ein schmaler und\ngiftiger Widerhaken, der Attacken vom\nTyp Gift verstärkt.", "Strumento da far tenere a un Pokémon.\nAculeo piccolo e tossico che potenzia\nle mosse di tipo Veleno.", "どくのある　ちいさな　ハリ。\nもたせると　どくタイプの\nわざの　いりょくが　あがる。", "どくのある　ちいさな　ハリ。\n持たせると　どくタイプの\n技の　威力が　あがる。", "독이 있는 작은 침.\n지니게 하면 독타입\n기술의 위력이 올라간다.", "Flecha venenosa que refuerza los\nmovimientos de tipo Veneno. Debe\nllevarla un Pokémon.") },
            { PBEItem.PoisonGem, new PBELocalizedString("A gem with an essence of poison. When\nheld, it strengthens the power of a\nPoison-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Poison.", "Ein Juwel vom Typ Gift. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Gift.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Veleno.", "どくの　タイプの　ジュエル。\nもたせると　いちどだけ　どくの\nわざの　いりょくが　つよまる。", "どくの　タイプの　ジュエル。\n持たせると　１度だけ　どくの\n技の　威力が　強まる。", "독타입의 주얼.\n지니게 하면 한 번만 독\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Veneno.\nDebe llevarla un Pokémon.") },
            { PBEItem.PokeBall, new PBELocalizedString("A device for catching wild Pokémon.\nIt is thrown like a ball at the target.\nIt is designed as a capsule system.", "Un objet pareil à une capsule, qui\ncapture les Pokémon sauvages. Il suffit\npour cela de le jeter comme une balle.", "Damit fängst du wilde Pokémon.\nDu wirfst ihn wie einen normalen Ball.\nDas Design ähnelt dem einer Kapsel.", "Strumento dotato di capsula usato per\ncatturare Pokémon selvatici. Si lancia\ncontro il bersaglio come una palla.", "やせいの　ポケモンに　なげて\nつかまえる　ための　ボール。\nカプセルしきに　なっている。", "野生の　ポケモンに　投げて\n捕まえるための　ボール。\nカプセル式に　なっている。", "야생 포켓몬에게 던져서\n잡기 위한 볼.\n캡슐식으로 되어 있다.", "Dispositivo con diseño capsular que\natrapa Pokémon salvajes. Se lanza\ncomo una bola contra el blanco.") },
            { PBEItem.PokeDoll, new PBELocalizedString("A doll that attracts Pokémon.\nUse it to flee from any battle with a\nwild Pokémon.", "Poupée qui attire les Pokémon. Permet\nde s'enfuir d'un combat contre un\nPokémon sauvage.", "Eine hübsche Puppe. Sie ermöglicht\ndie Flucht im Kampf gegen wilde\nPokémon.", "Bambola che attrae i Pokémon. È utile\nper fuggire dalla lotta con un Pokémon\nselvatico.", "ポケモンの　きを　ひかせる　どうぐ。\nやせいポケモンとの　せんとうから\nぜったいに　にげられる。", "ポケモンの　気をひかせる　道具。\n野生ポケモンとの　戦闘から\n絶対に　逃げられる。", "포켓몬의 관심을 끄는 도구.\n야생 포켓몬과의 배틀에서\n반드시 도망칠 수 있다.", "Un muñeco que atrae a los Pokémon.\nEs útil para huir de un combate\ncontra un Pokémon salvaje.") },
            { PBEItem.PokeToy, new PBELocalizedString("An item that attracts Pokémon.\nUse it to flee from any battle with a\nwild Pokémon.", "Objet qui attire les Pokémon. Permet\nde s'enfuir d'un combat contre un\nPokémon sauvage.", "Ein hübscher Wedel. Er ermöglicht die\nFlucht im Kampf gegen wilde Pokémon.", "Strumento che attrae i Pokémon. Utile\nper fuggire dalla lotta con Pokémon\nselvatici.", "ポケモンの　きを　ひかせる　どうぐ。\nやせいポケモンとの　せんとうから\nぜったいに　にげられる。", "ポケモンの　気を　ひかせる　道具。\n野生ポケモンとの　戦闘から\n絶対に　逃げられる。", "포켓몬의 관심을 끄는 도구.\n야생 포켓몬과의 배틀에서\n반드시 도망칠 수 있다.", "Un objeto que atrae a los Pokémon.\nPuedes utilizarlo para huir de un\ncombate con un Pokémon salvaje.") },
            { PBEItem.PomegBerry, new PBELocalizedString("Using it on a Pokémon makes it more\nfriendly, but it also lowers its base HP.", "Rend les Pokémon plus amicaux, mais\nbaisse leurs PV de base.", "Isst ein Pokémon diese Beere,\nstärkt dies eure Freundschaft,\nverringert aber seine Basis-KP.", "Utilizzata su un Pokémon, lo rende più\namichevole, ma riduce i PS di base.", "ポケモンに　あげると\nとても　なつき　やすくなるが\nＨＰの　きそポイントは　さがる。", "ポケモンに　あげると\nとても　なつき　やすくなるが\nＨＰの　基礎ポイントは　さがる。", "포켓몬에게 주면\n매우 친밀해지기 쉬워지지만\nHP의 기초 포인트가 떨어진다.", "Al usarla con un Pokémon, se gana\nsu amistad, pero también reduce\nsus PS de base.") },
            { PBEItem.Potion, new PBELocalizedString("A spray-type medicine for wounds.\nIt restores the HP of one Pokémon by\njust 20 points.", "Un spray qui soigne les blessures.\nRestaure 20 PV à un Pokémon.", "Hilft bei Wunden. Die KP\neines Pokémon werden\num 20 Punkte aufgefüllt.", "Rimedio spray per le ferite.\nRestituisce 20 PS ad un Pokémon.", "スプレーしきの　キズぐすり。\nポケモン　１ひきの　ＨＰを\n２０だけ　かいふくする。", "スプレー式の　キズぐすり。\nポケモン　１匹の　ＨＰを\n２０だけ　回復する。", "스프레이식의 상처약.\n포켓몬 1마리의 HP를\n20만큼 회복한다.", "Medicina en espray que cura heridas\ny restaura 20 PS a un Pokémon.") },
            { PBEItem.PowerAnklet, new PBELocalizedString("A Pokémon held item that promotes\nSpeed gain on leveling, but reduces\nthe Speed stat.", "Objet à tenir permettant à la Vitesse\nde progresser plus vite, mais la\nréduisant pendant le combat.", "Ein Item zum Tragen. Erhöht bei\nLevelaufstieg den Initiative-Wert,\nsenkt aber die Initiative allgemein.", "Strumento da far tenere a un Pokémon.\nRiduce la Velocità in lotta, ma aumenta\nmolto la Velocità di base.", "もたせると　すばやさが　さがるが\nふつうよりも　ポケモンの　すばやさが\nたかく　そだちやすい。", "持たせると　素早さが　さがるが\nふつうよりも　ポケモンの　素早さが\n高く　育ちやすい。", "지니게 하면 스피드는 떨어지지만\n여느 때보다 포켓몬의 스피드를\n올리면서 키우기가 쉽다.", "Potencia la Velocidad al subir de nivel,\npero reduce la Velocidad en combate.\nDebe llevarla un Pokémon.") },
            { PBEItem.PowerBand, new PBELocalizedString("A Pokémon held item that promotes\nSp. Def gain on leveling, but reduces\nthe Speed stat.", "Objet à tenir permettant à la Défense\nSpéciale de progresser plus vite, mais\nréduisant la Vitesse pendant le combat.", "Ein Item zum Tragen. Erhöht bei Level-\naufstieg die Spezial-Verteidigung,\nsenkt aber die Initiative.", "Strumento da far tenere a un Pokémon.\nRiduce la Velocità in lotta, ma aumenta\nmolto la Difesa Speciale di base.", "もたせると　すばやさが　さがるが\nふつうよりも　ポケモンの　とくぼうが\nたかく　そだちやすい。", "持たせると　素早さが　さがるが\nふつうよりも　ポケモンの　特防が\n高く　育ちやすい。", "지니게 하면 스피드는 떨어지지만\n여느 때보다 포켓몬의 특수방어를\n올리면서 키우기가 쉽다.", "Aumenta la Defensa Especial al subir\nde nivel, pero reduce la Velocidad en\ncombate. Debe llevarla un Pokémon.") },
            { PBEItem.PowerBelt, new PBELocalizedString("A Pokémon held item that promotes\nDefense gain on leveling, but reduces\nthe Speed stat.", "Objet à tenir permettant à la Défense\nde progresser plus vite, mais\nréduisant la Vitesse pendant le combat.", "Ein Item zum Tragen. Erhöht bei\nLevelaufstieg den Verteidigungs-\nWert, senkt aber die Initiative.", "Strumento da far tenere a un Pokémon.\nRiduce la Velocità in lotta, ma aumenta\nmolto la Difesa di base.", "もたせると　すばやさが　さがるが\nふつうよりも　ポケモンの　ぼうぎょが\nたかく　そだちやすい。", "持たせると　素早さが　さがるが\nふつうよりも　ポケモンの　防御が\n高く　育ちやすい。", "지니게 하면 스피드는 떨어지지만\n여느 때보다 포켓몬의 방어를\n올리면서 키우기가 쉽다.", "Aumenta la Defensa al subir de nivel,\npero reduce la Velocidad en combate.\nDebe llevarlo un Pokémon.") },
            { PBEItem.PowerBracer, new PBELocalizedString("A Pokémon held item that promotes\nAttack gain on leveling, but reduces\nthe Speed stat.", "Objet à tenir permettant à l'Attaque\nde progresser plus vite, mais\nréduisant la Vitesse pendant le combat.", "Ein Item zum Tragen. Erhöht\nbei Levelaufstieg den Angriffs-\nWert, senkt aber die Initiative.", "Strumento da far tenere a un Pokémon.\nRiduce la Velocità in lotta, ma aumenta\nmolto l'Attacco di base.", "もたせると　すばやさが　さがるが\nふつうよりも　ポケモンの　こうげきが\nたかく　そだちやすい。", "持たせると　素早さが　さがるが\nふつうよりも　ポケモンの　攻撃が\n高く　育ちやすい。", "지니게 하면 스피드는 떨어지지만\n여느 때보다 포켓몬의 공격을\n올리면서 키우기가 쉽다.", "Potencia el Ataque al subir de nivel,\npero reduce la Velocidad en combate.\nDebe llevarlo un Pokémon.") },
            { PBEItem.PowerHerb, new PBELocalizedString("A single-use item to be held by a\nPokémon. It allows the immediate use of\na move that charges on the first turn.", "Objet à tenir et à usage unique. Permet\nd'utiliser immédiatement une capacité\nqui devrait se charger au premier tour.", "Ein getragenes Item. Der Träger kann\ndadurch eine Attacke sofort einsetzen,\ndie sonst aufgeladen werden muss.", "Strumento monouso da far tenere a un\nPokémon. Permette l'uso immediato di\nuna mossa che al primo turno si carica.", "もたせた　ポケモンは　いちどだけ\n１ターンめに　ちからを　ためるわざを\nすぐに　つかうことが　できる。", "持たせた　ポケモンは　１度だけ\n１ターン目に　ちからを　ためる技を\nすぐに　使うことが　できる。", "지니게 한 포켓몬은 한 번만\n1턴째에 힘을 모으는 기술을\n바로 사용할 수가 있다.", "Permite el uso inmediato de cualquier\nmovimiento en el primer turno. Es de un\nsolo uso y debe llevarla un Pokémon.") },
            { PBEItem.PowerLens, new PBELocalizedString("A Pokémon held item that promotes\nSp. Atk gain on leveling, but reduces\nthe Speed stat.", "Objet à tenir permettant à l'Attaque\nSpéciale de progresser plus vite, mais\nréduisant la Vitesse pendant le combat.", "Ein Item zum Tragen. Erhöht\nbei Levelaufstieg den Spezial-\nAngriff, senkt aber die Initiative.", "Strumento da far tenere a un Pokémon.\nRiduce la Velocità in lotta, ma aumenta\nmolto l'Attacco Speciale di base.", "もたせると　すばやさが　さがるが\nふつうよりも　ポケモンの　とくこうが\nたかく　そだちやすい。", "持たせると　素早さが　さがるが\nふつうよりも　ポケモンの　特攻が\n高く　育ちやすい。", "지니게 하면 스피드는 떨어지지만\n여느 때보다 포켓몬의 특수공격을\n올리면서 키우기가 쉽다.", "Potencia el Ataque Especial al subir\nde nivel, pero reduce la Velocidad en\ncombate. Debe llevarla un Pokémon.") },
            { PBEItem.PowerWeight, new PBELocalizedString("A Pokémon held item that promotes\nHP gain on leveling, but reduces the\nSpeed stat.", "Objet à tenir permettant aux PV\nde progresser plus vite, mais\nréduisant la Vitesse pendant le combat.", "Ein Item zum Tragen. Erhöht bei\nLevelaufstieg die maximalen\nKP, senkt aber die Initiative.", "Strumento da far tenere a un Pokémon.\nRiduce la Velocità in lotta, ma aumenta\nmolto i PS di base.", "もたせると　すばやさが　さがるが\nふつうよりも　ポケモンの　ＨＰが\nたかく　そだちやすい。", "持たせると　素早さが　さがるが\nふつうよりも　ポケモンの　ＨＰが\n高く　育ちやすい。", "지니게 하면 스피드는 떨어지지만\n여느 때보다 포켓몬의 HP를\n올리면서 키우기가 쉽다.", "Aumenta los PS al subir de nivel,\npero reduce la Velocidad en combate.\nDebe llevarla un Pokémon.") },
            { PBEItem.PPMax, new PBELocalizedString("It maximally raises the top PP of a\nselected move that has been learned\nby the target Pokémon.", "Monte sensiblement les PP max d'une\ncapacité sélectionnée.", "Maximiert die Anzahl der AP\neiner Attacke eines Pokémon.", "Porta al limite i PP massimi di una mossa\na scelta imparata da un Pokémon.", "おぼえている　わざのうち\n１つの　ＰＰの　さいだいちを\nさいこうまで　あげる。", "覚えている　技のうち\n１つの　ＰＰの　最大値を\n最高まで　あげる。", "기억하고 있는 기술 중\n1개의 PP 최대치를\n최고까지 올린다.", "Aumenta hasta el límite los PP máximos\ndel movimiento elegido de un Pokémon.") },
            { PBEItem.PPUp, new PBELocalizedString("It slightly raises the maximum PP of\na selected move that has been learned\nby the target Pokémon.", "Monte un peu les PP max d'une\ncapacité sélectionnée.", "Erhöht die maximale Anzahl der AP\neiner Attacke eines Pokémon.", "Aumenta di poco i PP massimi di una mossa\na scelta che è stata imparata dal\nPokémon.", "ポケモンが　おぼえている\nわざのうち　１つの　ＰＰの\nさいだいちを　すこし　あげる。", "ポケモンが　覚えている\n技のうち　１つの　ＰＰの\n最大値を　少し　あげる。", "포켓몬이 기억하고 있는\n기술 중 1개의 PP\n최대치를 조금 올린다.", "Aumenta ligeramente los PP máximos del\nmovimiento elegido de un Pokémon.") },
            { PBEItem.PremierBall, new PBELocalizedString("A somewhat rare Poké Ball that has\nbeen specially made to commemorate\nan event of some sort.", "Une Poké Ball assez rare qui fut créée\npour un événement particulier.", "Ein eher seltener Ball, der als\nReminiszenz an irgendein\nEreignis hergestellt wurde.", "Poké Ball poco comune realizzata\nappositamente per commemorare\nun particolare evento.", "なにかの　きねんの　しなとして\nとくべつに　つくられた\nちょっと　めずらしい　ボール。", "なにかの　記念の　品として\n特別に　つくられた\nちょっと　珍しい　ボール。", "무언가의 기념품으로\n특별히 만들어진\n조금 희귀한 볼.", "Es una Poké Ball algo singular\nque se creó para conmemorar\nalgún acontecimiento.") },
            { PBEItem.PrettyWing, new PBELocalizedString("Though this feather is beautiful, it's\njust a regular feather and has no\neffect on Pokémon.", "Une plume très jolie, mais ordinaire\net sans effet particulier.", "Eine ganz normale Feder, die\nrein gar nichts bewirkt.", "Piuma molto bella ma priva di qualsiasi\nutilità: non ha alcun effetto.", "きれいなだけで　なんの　こうかもない\nごくごく　ふつうの　ハネ。\n", "きれいなだけで　なんの　効果もない\nごくごく　普通の　ハネ。\n", "아름답기만 할 뿐 아무 효과도 없는\n지극히 평범한 날개.\n", "Pluma normal y corriente. Muy bonita,\npero no sirve para nada.") },
            { PBEItem.PrismScale, new PBELocalizedString("A mysterious scale that evolves\ncertain Pokémon.\nIt shines in rainbow colors.", "Une écaille étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle a une couleur arc-en-ciel.", "Eine seltsame Schuppe, durch die sich\nbestimmte Pokémon entwickeln. Sie\nfunkelt in den Farben des Regenbogens.", "Squama misteriosa che fa evolvere\ndeterminate specie di Pokémon.\nRiflette i colori dell'arcobaleno.", "ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　ウロコ。\nにじいろに　かがやいている。", "ある　特定の　ポケモンを\n進化させる　不思議な　ウロコ。\nにじ色に　輝いている。", "어느 특정한 포켓몬을\n진화시키는 이상한 비늘.\n무지갯빛으로 빛나고 있다.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nBrilla con los colores del arcoíris.") },
            { PBEItem.Protector, new PBELocalizedString("A protective item of some sort.\nIt is extremely stiff and heavy.\nIt is loved by a certain Pokémon.", "Objet protecteur très dur et très\nlourd, apprécié d'un certain Pokémon.", "Ein schützendes Item. Steif\nund schwer. Das Lieblingsstück\neines bestimmten Pokémon.", "Strumento protettivo, molto duro e\npesante, prediletto da un Pokémon\nin particolare.", "なにかの　プロテクター。\nとても　かたくて　おもい。\nある　ポケモンが　このんでいる。", "なにかの　プロテクター。\nとても　硬くて　重い。\nある　ポケモンが　好んでいる。", "무언가의 프로텍터.\n매우 단단하고 무겁다.\n어떤 포켓몬이 좋아하는 물건이다.", "Dispositivo de protección muy pesado.\nCierto Pokémon lo adora.") },
            { PBEItem.Protein, new PBELocalizedString("A nutritious drink for Pokémon.\nIt raises the base Attack stat of a\nsingle Pokémon.", "Une boisson très nutritive qui monte\nl'Attaque de base d'un Pokémon.", "Ein gehaltvolles Getränk, das den\nAngriffs-Wert eines Pokémon\ndauerhaft erhöht.", "Bevanda nutriente per Pokémon.\nAumenta l'Attacco di base.", "ポケモンの　えいようドリンク。\nポケモン　１ひきの　こうげきの\nきそポイントを　あげる。", "ポケモンの　栄養ドリンク。\nポケモン　１匹の　攻撃の\n基礎ポイントを　あげる。", "포켓몬의 영양 드링크.\n포켓몬 1마리의 공격\n기초 포인트를 올린다.", "Nutritiva bebida que aumenta el Ataque\nde base de un Pokémon.") },
            { PBEItem.PsychicGem, new PBELocalizedString("A gem with an essence of the mind. When\nheld, it strengthens the power of a\nPsychic-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Psy.", "Ein Juwel vom Typ Psycho. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Psycho.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Psico.", "エスパーの　タイプの　ジュエル。\nもたせると　いちどだけ　エスパーの\nわざの　いりょくが　つよまる。", "エスパーの　タイプの　ジュエル。\n持たせると　１度だけ　エスパーの\n技の　威力が　強まる。", "에스퍼타입의 주얼.\n지니게 하면 한 번만 에스퍼\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Psíquico.\nDebe llevarla un Pokémon.") },
            { PBEItem.PureIncense, new PBELocalizedString("An item to be held by a Pokémon.\nIt helps keep wild Pokémon away if the\nholder is the first one in the party.", "Objet à tenir. Aide à repousser les\nPokémon sauvages si le porteur est\nle premier Pokémon de l'équipe.", "Ein Item zum Tragen. Reduziert\nBegegnungen mit wilden Pokémon, wenn\nder Träger an erster Stelle steht.", "Strumento da far tenere a un Pokémon.\nAiuta ad allontanare i Pokémon selvatici\nse chi lo tiene è il primo della squadra.", "いちばん　まえの　ポケモンに\nもたせると　やせいポケモンが\nでて　きにくくなる。", "１番　前の　ポケモンに\n持たせると　野生ポケモンが\nでて　きにくくなる。", "맨 앞의 포켓몬에게\n지니게 하면 야생 포켓몬이\n나타날 확률이 낮아진다.", "Si lo lleva el primer Pokémon del\nequipo, se reduce la probabilidad de\nque se acerquen Pokémon salvajes.") },
            { PBEItem.QualotBerry, new PBELocalizedString("Using it on a Pokémon makes it more\nfriendly, but it also lowers its base\nDefense stat.", "Rend les Pokémon plus amicaux, mais\nbaisse leur stat Défense de base.", "Isst ein Pokémon diese Beere,\nstärkt dies eure Freundschaft,\nverringert aber seinen Vert.-Wert.", "Utilizzata su un Pokémon, lo rende più\namichevole, ma diminuisce la Difesa\ndi base.", "ポケモンに　あげると\nとても　なつき　やすくなるが\nぼうぎょの　きそポイントは　さがる。", "ポケモンに　あげると\nとても　なつき　やすくなるが\n防御の　基礎ポイントは　さがる。", "포켓몬에게 주면\n매우 친밀해지기 쉬워지지만\n방어의 기초 포인트가 떨어진다.", "Al usarla con un Pokémon, se gana\nsu amistad, pero también reduce\nsu Defensa de base.") },
            { PBEItem.QuickBall, new PBELocalizedString("A somewhat different Poké Ball that\nprovides a better catch rate if it is\nused at the start of a wild encounter.", "Une Poké Ball qui permet d'attraper un\nPokémon sauvage plus facilement si on\nl'utilise au début du combat.", "Ein ungewöhnlicher Ball, der\nzu Beginn eines Kampfes am\nwirkungsvollsten ist.", "Poké Ball un po' diversa che facilita\nla cattura di un Pokémon selvatico\nse usata all'inizio della lotta.", "せんとうが　はじまって　すぐに\nつかうと　つかまえやすくなる\nちょっと　かわった　ボール。", "戦闘が　はじまって　すぐに\n使うと　捕まえやすくなる\nちょっと　変わった　ボール。", "배틀이 시작되자마자\n사용하면 잡기 쉬워지는\n조금 특이한 볼.", "Una Poké Ball algo distinta que potencia\nel índice de captura si se usa al inicio\nde un encuentro con Pokémon salvajes.") },
            { PBEItem.QuickClaw, new PBELocalizedString("An item to be held by a Pokémon.\nA light, sharp claw that lets the\nbearer move first occasionally.", "Objet à tenir. Griffe légère mais\ntranchante qui permet parfois de\nfrapper le premier.", "Ein Item, das einem Pokémon zum Tragen\ngegeben werden kann. Es hebt gelegent-\nlich die Erstschlagquote.", "Strumento da far tenere a un Pokémon.\nLeggero e affilato, permette di fare\nla prima mossa ogni tanto.", "かるくて　するどい　ツメ。\nもたせると　あいてより　さきに\nこうどう　できることが　ある。", "軽くて　鋭い　ツメ。\n持たせると　相手より　先に\n行動できることが　ある。", "가볍고 날카로운 손톱.\n지니게 하면 상대보다 먼저\n행동할 수도 있다.", "Garra ligera y afilada que permite que\nel Pokémon que la lleve ataque\na veces el primero.") },
            { PBEItem.QuickPowder, new PBELocalizedString("An item to be held by Ditto.\nExtremely fine yet hard, this odd\npowder boosts the Speed stat.", "Objet à faire tenir à Métamorph. Cette\npoudre étrange, très fine mais\nrésistante, augmente sa Vitesse.", "Ein Item, das Ditto zum Tragen gegeben\nwerden kann. Fein und doch hart,\nerhöht es den Initiative-Wert.", "Strumento da far tenere a Ditto.\nQuesta strana polvere, fine e al\ncontempo dura, aumenta la Velocità.", "メタモンに　もたせると　すばやさが\nあがる　ふしぎなこな。\nとても　こまかくて　かたい。", "メタモンに　持たせると　素早さが\nあがる　不思議な粉。\nとても　こまかくて　硬い。", "메타몽에게 지니게 하면 스피드가\n올라가는 이상한 가루.\n매우 작고 단단하다.", "Debe llevarlo Ditto. Es un polvo\nmuy fino, pero a la vez resistente,\nque aumenta su Velocidad.") },
            { PBEItem.RabutaBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.RageCandyBar, new PBELocalizedString("A famous Mahogany Town candy tourists\nlike to buy and take home. It restores\nthe HP of one Pokémon by 20 points.", "Un bonbon, spécialité d'Acajou.\nIl constitue un cadeau très réputé.", "Eine Spezialität aus Mahagonia City.\nSie wird gern als Souvenir gekauft.", "Caramella tipica di Mogania.\nSouvenir con un'ottima reputazione.", "チョウジめいぶつの　まんじゅう。\nおみやげひん　として　ゆうめい。\n", "ジョウト地方　にある\nチョウジタウン名物の　まんじゅう。\nお土産品として　有名。", "황토마을의 명물인 호두과자.\n선물로 아주 유명하다.\n", "Caramelo típico de Pueblo Caoba,\nmuy popular como souvenir.") },
            { PBEItem.RareBone, new PBELocalizedString("A bone that is extremely valuable for\nPokémon archeology. It can be sold\nfor a high price to shops.", "Un os d'une grande valeur pour\nl'archéologie Pokémon. Peut être\nvendu à bon prix aux magasins.", "Dieser Knochen erfreut jeden\nPokémon-Archäologen. Er ist\nvon hohem Wert.", "Osso di grande valore per l'archeologia\nPokémon. Può essere venduto nei negozi\na un prezzo piuttosto alto.", "ポケモンこうこがくの　うえで\nとても　きちょうな　ホネ。\nショップで　たかく　うれる。", "ポケモン考古学の　うえで\nとても　貴重な　ホネ。\nショップで　高く　売れる。", "포켓몬 고고학 상에서\n매우 귀중한 뼈.\n상점에서 비싸게 팔린다.", "Un hueso de gran valor para la\narqueología Pokémon. Puede alcanzar\nun alto precio en las tiendas.") },
            { PBEItem.RareCandy, new PBELocalizedString("A candy that is packed with energy.\nIt raises the level of a single Pokémon\nby one.", "Un bonbon plein d'énergie.\nIl permet à un Pokémon de gagner\nun niveau d'expérience.", "Ein energiereiches Bonbon, das\nden Level eines Pokémon dauerhaft\num eins erhöht.", "Caramella altamente energetica.\nFa salire un Pokémon di un livello.", "エネルギーの　つまった　アメ。\nあたえると　ポケモン　１ひきの\nレベルが　１だけ　あがる。", "エネルギーの　つまった　アメ。\nあたえると　ポケモン　１匹の\nレベルが　１だけ　あがる。", "에너지가 가득한 사탕.\n먹이면 포켓몬 1마리의\n레벨이 1만큼 올라간다.", "Caramelo energético que sube\na un Pokémon de nivel.") },
            { PBEItem.RawstBerry, new PBELocalizedString("If held by a Pokémon,\nit recovers from a burn.", "Baie qui soigne les brûlures lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon\nzum Tragen, heilt diese es von\nVerbrennung.", "Se tenuta da un Pokémon, lo guarisce\nda una scottatura.", "ポケモンに　もたせると\nやけどを　かいふくする。\n", "ポケモンに　持たせると\nやけどを　回復する。\n", "포켓몬에게 지니게 하면\n화상을 회복한다.\n", "Los Pokémon pueden llevarla o usarla\npara curarse las quemaduras.") },
            { PBEItem.RazorClaw, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a sharply hooked claw that ups\nthe holder's critical-hit ratio.", "Objet à tenir. Griffe crochue\net pointue augmentant le taux\nde critiques.", "Ein Item zum Tragen.\nDie scharfe Klaue erhöht die\nVolltrefferquote des Trägers.", "Strumento da far tenere a un Pokémon.\nArtiglio che aumenta le probabilità per\nchi lo tiene di sferrare dei brutti colpi.", "するどく　とがった　ツメ。\nもたせると　わざが　きゅうしょに\nあたりやすくなる。", "鋭く　とがった　ツメ。\n持たせると　技が　急所に\n当たりやすくなる。", "날카롭고 뾰족한 손톱.\n지니게 하면 기술이\n급소에 맞기 쉬워진다.", "Aumenta la probabilidad de que\nel Pokémon que la lleve consiga\nun golpe crítico.") },
            { PBEItem.RazorFang, new PBELocalizedString("An item to be held by a Pokémon.\nIt may make foes and allies flinch\nwhen the holder inflicts damage.", "Objet à tenir pouvant apeurer l'ennemi\nquand le porteur lui inflige des dégâts.", "Ein Item zum Tragen.\nVerursacht der Träger Schaden,\nerschrickt das Ziel eventuell.", "Strumento da far tenere a un Pokémon.\nChi lo tiene può far tentennare il nemico\ncolpito.", "するどく　とがった　キバ。\nもたせると　ダメージを　あたえたとき\nひるませることが　ある。", "鋭く　とがった　キバ。\n持たせると　ダメージを　与えたとき\nひるませることが　ある。", "날카롭고 뾰족한 이빨.\n지니게 하면 데미지를 줬을 때\n풀죽게 하기도 한다.", "Si lo lleva un Pokémon cuando inflige\ndaño a otro, puede hacer retroceder\na este último.") },
            { PBEItem.RazzBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.ReaperCloth, new PBELocalizedString("A cloth imbued with horrifyingly strong\nspiritual energy.\nIt is loved by a certain Pokémon.", "Un tissu imprégné d'une énergie\nspirituelle horriblement puissante.\nApprécié d'un certain Pokémon.", "Ein Stoffumhang mit geistiger\nEnergie. Das Lieblingsstück\neines bestimmten Pokémon.", "Panno intriso di un'energia spirituale\nterribilmente forte. Un Pokémon in\nparticolare adora questo strumento.", "おそろしく　つよい　れいりょくが\nこめられている　ぬの。\nある　ポケモンが　このんでいる。", "おそろしく　強い　霊力が\nこめられている　布。\nある　ポケモンが　好んでいる。", "무섭고 강한 영력이\n담겨 있는 천.\n어떤 포켓몬이 좋아하는 물건이다.", "Tela imbuida de una energía espiritual\nmuy potente. Cierto Pokémon la adora.") },
            { PBEItem.RedApricorn, new PBELocalizedString("A red Apricorn.\nIt assails your nostrils.", "Un Noigrume de couleur rouge.\nIl dégage un puissant arôme.", "Eine rote Aprikoko. Besitzt einen\nstechenden Geruch.", "Una Ghicocca di colore rosso.\nEmana un odore molto forte.", "あかい　ぼんぐり。\nつーんと　はなにくる　においがする。\n", "あかい　ぼんぐり。\nつーんと　はなにくる　においがする。\n", "빨간색의 규토리.\n코를 톡 쏘는 향기가 난다.\n", "Bonguri de color rojo y aroma\nmuy penetrante.") },
            { PBEItem.RedCard, new PBELocalizedString("A card with a mysterious power. When\nthe holder is struck by a foe, the\nattacker is removed from battle.", "Carte au pouvoir mystérieux.\nTenue, elle force un Pokémon touchant\nle porteur à se retirer du combat.", "Eine Karte mit mysteriösen Kräften.\nWird der Träger getroffen, wird der\nAngreifer des Platzes verwiesen.", "Cartellino dal potere misterioso.\nChi lo tiene fa ritirare dalla lotta\nil Pokémon che lo ha colpito.", "ふしぎな　ちからのある　カード。\nもたせると　わざをうけた　あいてを\nたいじょう　させることが　できる。", "不思議な　ちからのある　カード。\n持たせると　技を受けた　相手を\n退場させることが　できる。", "이상한 힘이 있는 카드.\n지니게 하면 기술을 당한 상대를\n퇴장시킬 수 있다.", "Misteriosa tarjeta que permite al\nPokémon que la lleve expulsar al\nagresor cuando este le cause daño.") },
            { PBEItem.RedFlute, new PBELocalizedString("A toy flute made from red glass.\nA maniac will buy it for a high price.", "Jouet en forme de flûte en verre\nrouge. Les amateurs en donnent\nun bon prix.", "Eine Spielzeugflöte aus rotem Glas.\nSammler geben ihr letztes Hemd dafür\nher.", "Flauto giocattolo fatto di vetro rosso.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "あかい　ガラスで　できている\nおもちゃの　ふえ。\nマニアが　たかく　かってくれる。", "赤い　ガラスで　できている\nおもちゃの　笛。\nマニアが　高く　買ってくれる。", "빨간 유리로 만들어진\n장난감 피리.\n마니아가 비싼 값에 산다.", "Una flauta de juguete hecha de\ncristal rojo.\nMuy valorada por los coleccionistas.") },
            { PBEItem.RedScarf, new PBELocalizedString("An item to be held by a Pokémon.\nIt boosts the Cool aspect of the\nholder in a Contest.", "Objet à tenir. Augmente le Sang-froid\nd'un Pokémon dans les Concours.", "Ein Item zum Tragen. Trainer erhöhen\ndamit in Wettbewerben die „Coolness“\nihres Pokémon.", "Strumento da far tenere a un Pokémon.\nAumenta la “Classe\" di chi lo tiene\nin una Gara.", "もたせて　コンテストに　さんかすると\nその　ポケモンは　いつもより\nかっこよく　みられる。", "持たせて　コンテストに　参加すると\nその　ポケモンは　いつもより\nかっこよく　みられる。", "지니게 하여 콘테스트에 참가하면\n그 포켓몬은 여느 때보다\n근사하게 보인다.", "Debe llevarlo un Pokémon.\nAumenta el carisma en los concursos.") },
            { PBEItem.RedShard, new PBELocalizedString("A small red shard.\nIt appears to be from some sort of\nimplement made long ago.", "Petit tesson rouge.\nIl semble provenir d'un outil très\nancien.", "Eine rote Scherbe eines antiken\nWerkzeugs, das vor langer Zeit\nangefertigt wurde.", "Un piccolo coccio rosso che con ogni\nprobabilità è un frammento di un antico\nstrumento.", "あかい　ちいさな　かけら。\nむかしに　つくられた　なにかの\nどうぐの　いちぶ　らしい。", "赤い　小さな　かけら。\n昔に　つくられた　なにかの\n道具の　一部らしい。", "빨간 작은 조각.\n이전에 만들어진 어떤\n도구의 일부인 듯하다.", "Un pequeño fragmento rojo.\nParece formar parte de algún tipo\nde herramienta antigua.") },
            { PBEItem.RelicBand, new PBELocalizedString("A bracelet made in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.", "Un bijou d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Ein Armreif, angefertigt von einer\ngut 3 000 Jahre alten Kultur. Sammler\ngeben ihr letztes Hemd dafür.", "Braccialetto di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "３０００ねんほど　むかしの\nぶんめいで　つくられた　うでわ。\nマニアが　たかく　かってくれる。", "３０００年ほど　昔の\n文明で　つくられた　腕輪。\nマニアが　高く　買ってくれる。", "3000년 정도 된\n오랜 문명에서 만들어진 팔찌.\n마니아가 비싼 값에 산다.", "Brazalete de más de tres mil años\nde antigüedad. Muy valorado por\nlos coleccionistas.") },
            { PBEItem.RelicCopper, new PBELocalizedString("A copper coin used in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.", "Une pièce de cuivre d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Eine alte Kupfermünze. Die Währung\neiner gut 3 000 Jahre alten Kultur.\nSammler geben ihr letztes Hemd dafür.", "Moneta di rame di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "３０００ねんほど　むかしの\nぶんめいで　つかわれた　どうか。\nマニアが　たかく　かってくれる。", "３０００年ほど　昔の\n文明で　使われた　銅貨。\nマニアが　高く　買ってくれる。", "3000년 정도 된\n오랜 문명에서 사용된 동화.\n마니아가 비싼 값에 산다.", "Moneda de cobre de más de tres mil\naños de antigüedad. Muy valorada\npor los coleccionistas.") },
            { PBEItem.RelicCrown, new PBELocalizedString("A crown made in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.", "Une couronne d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Die Krone eines Königs, angefertigt von\neiner gut 3 000 Jahre alten Kultur.\nSammler geben ihr letztes Hemd dafür.", "Corona di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "３０００ねんほど　むかしの\nぶんめいで　つくられた　おうかん。\nマニアが　たかく　かってくれる。", "３０００年ほど　昔の\n文明で　つくられた　王冠。\nマニアが　高く　買ってくれる。", "3000년 정도 된\n오랜 문명에서 만들어진 왕관.\n마니아가 비싼 값에 산다.", "Corona de más de tres mil años\nde antigüedad. Muy valorada\npor los coleccionistas.") },
            { PBEItem.RelicGold, new PBELocalizedString("A gold coin used in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.", "Une pièce d'or d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Eine alte Goldmünze. Die Währung\neiner gut 3 000 Jahre alten Kultur.\nSammler geben ihr letztes Hemd dafür.", "Moneta d'oro di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "３０００ねんほど　むかしの\nぶんめいで　つかわれた　きんか。\nマニアが　たかく　かってくれる。", "３０００年ほど　昔の\n文明で　使われた　金貨。\nマニアが　高く　買ってくれる。", "3000년 정도 된\n오랜 문명에서 사용된 금화.\n마니아가 비싼 값에 산다.", "Moneda de oro de más de tres mil\naños de antigüedad. Muy valorada\npor los coleccionistas.") },
            { PBEItem.RelicSilver, new PBELocalizedString("A silver coin used in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.", "Une pièce d'argent d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Eine alte Silbermünze. Die Währung\neiner gut 3 000 Jahre alten Kultur.\nSammler geben ihr letztes Hemd dafür.", "Moneta di argento di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "３０００ねんほど　むかしの\nぶんめいで　つかわれた　ぎんか。\nマニアが　たかく　かってくれる。", "３０００年ほど　昔の\n文明で　使われた　銀貨。\nマニアが　高く　買ってくれる。", "3000년 정도 된\n오랜 문명에서 사용된 은화.\n마니아가 비싼 값에 산다.", "Moneda de plata de más de tres mil\naños de antigüedad. Muy valorada\npor los coleccionistas.") },
            { PBEItem.RelicStatue, new PBELocalizedString("A stone figure made in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.", "Une statuette d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Eine Statue, angefertigt von einer gut\n3 000 Jahre alten Kultur. Sammler\ngeben ihr letztes Hemd dafür.", "Statua di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "３０００ねんほど　むかしの\nぶんめいで　つくられた　せきぞう。\nマニアが　たかく　かってくれる。", "３０００年ほど　昔の\n文明で　つくられた　石像。\nマニアが　高く　買ってくれる。", "3000년 정도 된\n오랜 문명에서 만들어진 석상.\n마니아가 비싼 값에 산다.", "Efigie de piedra de más de tres mil\naños de antigüedad. Muy valorada\npor los coleccionistas.") },
            { PBEItem.RelicVase, new PBELocalizedString("A vase made in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.", "Un vase d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Eine Vase, angefertigt von einer\ngut 3 000 Jahre alten Kultur. Sammler\ngeben ihr letztes Hemd dafür.", "Vaso di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "３０００ねんほど　むかしの\nぶんめいで　つくられた　ツボ。\nマニアが　たかく　かってくれる。", "３０００年ほど　昔の\n文明で　つくられた　ツボ。\nマニアが　高く　買ってくれる。", "3000년 정도 된\n오랜 문명에서 만들어진 항아리.\n마니아가 비싼 값에 산다.", "Jarrón de más de tres mil años de\nantigüedad. Muy valorado por\nlos coleccionistas.") },
            { PBEItem.RepeatBall, new PBELocalizedString("A somewhat different Poké Ball that\nworks especially well on Pokémon\nspecies that were previously caught.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien sur\nles espèces de Pokémon déjà capturées.", "Ein Ball, der besonders dann sehr gut\nist, wenn man ein Pokémon fangen will,\ndessen Art bereits gefangen wurde.", "Poké Ball un po' diversa. Risulta\nparticolarmente efficace sulle specie\ndi Pokémon già catturate in precedenza.", "つかまえたことの　ある\nポケモンが　つかまえやすくなる\nちょっと　かわった　ボール。", "捕まえたことの　ある\nポケモンが　捕まえやすくなる\nちょっと　変わった　ボール。", "잡은 적이 있는\n포켓몬을 잡기 쉬운\n조금 특이한 볼.", "Es algo distinta a las demás. Funciona\nespecialmente bien con las especies\nde Pokémon que ya has capturado.") },
            { PBEItem.Repel, new PBELocalizedString("An item that prevents weak wild\nPokémon from appearing for 100 steps\nafter its use.", "Repousse les Pokémon sauvages\nfaibles pendant 100 pas.", "Hält 100 Schritte lang schwache,\nwilde Pokémon ab.", "Strumento che evita l'incontro con i\nPokémon selvatici deboli per 100 passi\ndopo l'utilizzo.", "１００ぽ　すすむ　あいだ\nよわい　やせいの　ポケモンが\nまったく　でて　こなくなる。", "１００歩　進む　あいだ\n弱い　野生の　ポケモンが\nまったく　でて　こなくなる。", "100보 걸어가는 동안\n약한 야생 포켓몬이\n전혀 나오지 않게 된다.", "Repele Pokémon salvajes débiles\nen un recorrido de 100 pasos.") },
            { PBEItem.ReplyMail, new PBELocalizedString("Stationery designed for\nwriting a reply.\nLet a Pokémon hold it for delivery.", "Un papier à lettres idéal pour répondre\nà un autre courrier. Peut être tenu\net livré par un Pokémon.", "Briefpapier, mit dem man einen\nanderen Brief beantworten kann.\nGib es einem Pokémon zum Tragen.", "Carta da lettera ideale per scrivere\nuna risposta ad un messaggio.\nUn Pokémon può tenerla e consegnarla.", "もらった　メールへの　おかえしが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "もらった　メールへの　お返しが\n書きやすい　レターペーパー。\nポケモンに　持たせて　使う。", "받은 메일에 대한 답장을\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Carta para responder a un mensaje.\nHaz que la entregue un Pokémon.") },
            { PBEItem.ResistWing, new PBELocalizedString("An item for use on a Pokémon. It\nslightly increases the base Defense\nstat of a single Pokémon.", "Objet à utiliser sur un Pokémon.\nIl augmente un peu sa Défense\nde base.", "Schenkt man einem Pokémon dieses\nItem, wird dessen Verteidigungs-Wert\ndauerhaft erhöht.", "Strumento da usare su un Pokémon.\nNe aumenta di poco la Difesa di base.", "ポケモンに　つかう　どうぐ。\nポケモン　１ひきの　ぼうぎょの\nきそポイントを　ちょっと　あげる。", "ポケモンに　使う　道具。\nポケモン　１匹の　防御の\n基礎ポイントを　ちょっと　あげる。", "포켓몬에게 사용하는 도구.\n포켓몬 1마리의 방어\n기초 포인트를 조금 올린다.", "Pluma que aumenta un poco la Defensa\nde un Pokémon al subir de nivel.") },
            { PBEItem.RevivalHerb, new PBELocalizedString("A very bitter medicinal herb.\nIt revives a fainted Pokémon, fully\nrestoring its HP.", "Une herbe médicinale très amère.\nRanime un Pokémon K.O. et restaure\ntous ses PV.", "Ein bitteres medizinisches Kraut, das\nkampfunfähige Pokémon belebt und\ndie KP vollständig auffüllt.", "Erba medicinale amarissima.\nRianima un Pokémon esausto e gli\nrestituisce tutti i PS.", "とても　にがい　やくそう。\nひんしの　ポケモン　１ひきの\nＨＰを　すべて　かいふくする。", "とても　にがい　薬草。\nひんしの　ポケモン　１匹の\nＨＰを　すべて　回復する。", "매우 쓴 약초.\n기절한 포켓몬 1마리의\nHP를 모두 회복한다.", "Hierba muy amarga que reanima a\nun Pokémon debilitado y restaura\ntodos sus PS.") },
            { PBEItem.Revive, new PBELocalizedString("A medicine that revives a fainted\nPokémon. It restores half the\nPokémon's maximum HP.", "Un médicament qui ranime un Pokémon\nK.O. et restaure la moitié de ses PV\nmax.", "Eine Medizin, die ein kampfunfähiges\nPokémon wiederbelebt. Die Hälfte der\nmaximalen KP wird wiederhergestellt.", "Rimedio che rianima un Pokémon esausto\ne gli restituisce metà dei suoi\nPS massimi.", "ひんしに　なってしまった\nポケモン　１ひきの　ＨＰを\nはんぶんまで　かいふくする。", "ひんしに　なってしまった\nポケモン　１匹の　ＨＰを\n半分まで　回復する。", "기절해 버린\n포켓몬 1마리의 HP를\n절반까지 회복한다.", "Medicina que reanima a un Pokémon\ndebilitado y restaura la mitad de\nsus PS máximos.") },
            { PBEItem.RindoBerry, new PBELocalizedString("Weakens a supereffective Grass-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Plante.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Pflanze.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Erba\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　くさ　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　くさ技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 풀 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Planta\nde un enemigo.") },
            { PBEItem.RingTarget, new PBELocalizedString("Moves that would otherwise have no\neffect will land on the Pokémon that\nholds it.", "Un Pokémon tenant cet objet devient\nsensible aux capacités autrement\ninefficaces en raison de son type.", "Attacken, die aufgrund der Wechsel-\nwirkungen zwischen den Typen keine\nWirkung haben, treffen den Träger.", "Se un Pokémon ha questo strumento, le\nmosse che di norma non hanno effetto\ncontro il suo tipo vanno a segno.", "ポケモンの　タイプの　あいしょうで\nむこう　だった　わざが\nあたるように　なってしまう。", "ポケモンの　タイプの　相性で\n無効だった　技が\n当たるように　なってしまう。", "포켓몬 타입의 상성 때문에\n무효였던 기술이\n맞게 되어 버린다.", "El Pokémon que lo lleva es vulnerable\nfrente a movimientos que normalmente\nno afectan a su tipo.") },
            { PBEItem.RockGem, new PBELocalizedString("A gem with an essence of rock. When\nheld, it strengthens the power of a\nRock-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Roche.", "Ein Juwel vom Typ Gestein. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Gestein.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Roccia.", "いわの　タイプの　ジュエル。\nもたせると　いちどだけ　いわの\nわざの　いりょくが　つよまる。", "いわの　タイプの　ジュエル。\n持たせると　１度だけ　いわの\n技の　威力が　強まる。", "바위타입의 주얼.\n지니게 하면 한 번만 바위\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Roca.\nDebe llevarla un Pokémon.") },
            { PBEItem.RockIncense, new PBELocalizedString("An item to be held by a Pokémon. It is\nan exotic-smelling incense that boosts\nthe power of Rock-type moves.", "Objet à tenir. Encens au parfum\nexotique augmentant la puissance\ndes capacités de type Roche.", "Ein Item zum Tragen. Dieses\nexotische Duftkraut verstärkt\nAttacken vom Typ Gestein.", "Strumento da far tenere a un Pokémon.\nIncenso dal profumo esotico che\npotenzia le mosse di tipo Roccia.", "ふしぎな　かおりの　おこう。\nもたせると　いわタイプの\nわざの　いりょくが　あがる。", "不思議な　香りの　御香。\n持たせると　いわタイプの\n技の　威力が　あがる。", "이상한 향기의 향로.\n지니게 하면 바위타입\n기술의 위력이 올라간다.", "Incienso de exótico aroma que fortalece\nlos movimientos de tipo Roca.\nDebe llevarlo un Pokémon.") },
            { PBEItem.RockyHelmet, new PBELocalizedString("If the holder of this item takes damage,\nthe attacker will also be damaged\nupon contact.", "Tenu, cet objet inflige des dégâts\nà l'attaquant si ce dernier utilise une\nattaque physique qui atteint son but.", "Wird der Träger von einer physischen\nAttacke getroffen, erhält der\nAngreifer ebenfalls Schaden.", "Se il Pokémon che lo tiene è colpito da\nun attacco fisico, anche l'avversario\nsubisce dei danni.", "ポケモンに　もたせると\nだげきわざを　うけたとき\nあいてにも　ダメージを　あたえる。", "ポケモンに　持たせると\n打撃技を　受けたとき\n相手にも　ダメージを　あたえる。", "포켓몬에게 지니게 하면\n타격 기술을 당했을 때\n상대에게도 데미지를 준다.", "Si el portador es alcanzado por un\nataque físico, el agresor también\nrecibe daño.") },
            { PBEItem.RootFossil, new PBELocalizedString("A fossil of an ancient Pokémon that\nlived in the sea. It appears to be\npart of a plant root.", "Fossile d'un ancien Pokémon aquatique.\nOn dirait un morceau de racine de\nplante.", "Das Fossil eines uralten Pokémon,\ndas in der Tiefsee lebte. Es sieht\nwie eine Pflanzenwurzel aus.", "Fossile di un antico Pokémon che\nviveva in mare. Sembra essere\nparte di una radice.", "おおむかし　うみに　すんでいた\nこだい　ポケモンの　カセキ。\nねっこの　いちぶ　らしい。", "大昔　海に　すんでいた\n古代ポケモンの　カセキ。\nねっこの　一部らしい。", "오랜 옛날 바다에 살았던\n고대 포켓몬의 화석.\n뿌리의 일부인 듯하다.", "Fósil de un Pokémon ancestral que\nvivió en el fondo del mar. Parece\nser parte de una raíz.") },
            { PBEItem.RoseIncense, new PBELocalizedString("An item to be held by a Pokémon. It is\nan exotic-smelling incense that boosts\nthe power of Grass-type moves.", "Objet à tenir. Encens au parfum\nexotique augmentant la puissance\ndes capacités de type Plante.", "Ein Item zum Tragen. Dieses\nexotische Duftkraut verstärkt\nAttacken vom Typ Pflanze.", "Strumento da far tenere a un Pokémon.\nIncenso dal profumo esotico che\npotenzia le mosse di tipo Erba.", "ふしぎな　かおりの　おこう。\nもたせると　くさタイプの\nわざの　いりょくが　あがる。", "不思議な　香りの　御香。\n持たせると　くさタイプの\n技の　威力が　あがる。", "이상한 향기의 향로.\n지니게 하면 풀타입\n기술의 위력이 올라간다.", "Incienso de exótico aroma que fortalece\nlos movimientos de tipo Planta.\nDebe llevarlo un Pokémon.") },
            { PBEItem.RowapBerry, new PBELocalizedString("If held by a Pokémon and a special\nattack lands, the attacker also\ntakes damage.", "Tenue, cette Baie inflige des dégâts\nà l'ennemi s'il utilise une attaque\nspéciale qui atteint son but.", "Als getragenes Item bewirkt die Beere\nSchaden bei denen, die den Träger\nmit einer Spezial-Attacke treffen.", "Se tenuta da un Pokémon, il nemico\nsubirà danni se il suo attacco speciale\nva a segno.", "ポケモンに　もたせると\nとくしゅわざを　うけたとき\nあいてにも　ダメージを　あたえる。", "ポケモンに　持たせると\n特殊技を　受けたとき\n相手にも　ダメージを　あたえる。", "포켓몬에게 지니게 하면\n특수 기술을 당했을 때\n상대에게도 데미지를 준다.", "Si la lleva un Pokémon cuando otro\nlanza un ataque especial, este último\ntambién recibe daño.") },
            { PBEItem.RSVPMail, new PBELocalizedString("Stationery designed for\ninvitations.\nLet a Pokémon hold it for delivery.", "Un papier à lettres idéal pour\nles invitations. Peut être tenu\net livré par un Pokémon.", "Briefpapier, mit dem man eine andere\nPerson zu etwas einladen kann.\nGib es einem Pokémon zum Tragen.", "Carta da lettera ideale per scrivere\nun invito a qualcuno. Un Pokémon può\ntenerla e consegnarla.", "あいてを　おさそいする　メールが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "相手を　お誘いする　メールが\n書きやすい　レターペーパー。\nポケモンに　持たせて　使う。", "상대를 초대하는 메일을\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Carta para invitar a un compañero.\nHaz que la entregue un Pokémon.") },
            { PBEItem.SacredAsh, new PBELocalizedString("It revives all fainted Pokémon.\nIn doing so, it also fully restores\ntheir HP.", "Ranime tous les Pokémon K.O. et\nrestaure tous leurs PV.", "Belebt und erfrischt alle besiegten\nPokémon und füllt deren KP\nvollständig wieder auf.", "Rianima tutti i Pokémon esausti,\nrestituendo loro anche tutti i PS.", "ひんしに　なってしまった\nポケモン　ぜんいんの　ＨＰを\nすべて　かいふくする。", "ひんしに　なってしまった\nポケモン　全員の　ＨＰを\nすべて　回復する。", "기절해 버린\n포켓몬 전원의 HP를\n모두 회복한다.", "Reanima completamente a todos los\nPokémon debilitados y restaura\ntodos sus PS.") },
            { PBEItem.SafariBall, new PBELocalizedString("A special Poké Ball that is used only in\nthe Great Marsh. It is decorated in a\ncamouflage pattern.", "Une Poké Ball spéciale dont l'usage\nest réservé au Parc Safari. Elle est\ndécorée d'un motif camouflage.", "Ein Ball, der nur in der Safari-Zone\neingesetzt wird. Er besticht durch\nsein Tarnfarbenmuster.", "Poké Ball speciale, usata soltanto\nnella Gran Palude. È decorata con\nun motivo mimetico.", "だいしつげんの　なか　だけで\nつかう　とくべつな　ボール。\nめいさいがらに　なっている。", "大湿原の　中だけで\n使う　特別な　ボール。\n迷彩柄に　なっている。", "대습초원 안에서만\n쓰는 특별한 볼.\n얼룩무늬로 되어 있다.", "Poké Ball con un diseño de camuflaje\nque se utiliza únicamente en el Gran\nPantano.") },
            { PBEItem.SalacBerry, new PBELocalizedString("If held by a Pokémon, it raises its\nSpeed stat in a pinch.", "Tenue, cette Baie monte la Vitesse\nen cas de besoin.", "Als getragenes Item erhöht diese Beere\nden Initiative-Wert eines Pokémon,\nsobald es in einer Notlage steckt.", "Aumenta la Velocità, se il Pokémon che\nla tiene si trova in difficoltà.", "ポケモンに　もたせると\nピンチのとき　じぶんの\nすばやさが　あがる。", "ポケモンに　持たせると\nピンチのとき　自分の\n素早さが　あがる。", "포켓몬에게 지니게 하면\n위급할 때 자신의\n스피드가 올라간다.", "Si la lleva un Pokémon, le sube\nla Velocidad en un momento de apuro.") },
            { PBEItem.ScopeLens, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a lens that boosts the holder's\ncritical-hit ratio.", "Objet à tenir. Lentille qui augmente le\ntaux de critiques du porteur.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nerhöht die Volltrefferquote.", "Strumento da far tenere a un Pokémon.\nLente che aumenta le probabilità per chi\nla tiene di sferrare dei brutti colpi.", "じゃくてんが　みえる　レンズ。\nもたせた　ポケモンの　わざが\nきゅうしょに　あたりやすくなる。", "弱点が　みえる　レンズ。\n持たせた　ポケモンの　技が\n急所に　当たりやすくなる。", "약점이 보이는 렌즈.\n지니게 한 포켓몬의 기술이\n급소에 맞기 쉬워진다.", "Su lente aumenta la probabilidad que\ntiene el Pokémon que lo lleva de\nconseguir un golpe crítico.") },
            { PBEItem.SeaIncense, new PBELocalizedString("An item to be held by a Pokémon. It is\nincense with a curious aroma that\nboosts the power of Water-type moves.", "Objet à tenir. Encens au parfum curieux\nqui augmente la puissance des capacités\nde type Eau.", "Ein Item zum Tragen. Sein\nausgefallenes Aroma verstärkt\nAttacken vom Typ Wasser.", "Strumento da far tenere a un Pokémon.\nIncenso dal curioso aroma che potenzia\nle mosse di tipo Acqua.", "ふしぎな　かおりの　おこう。\nもたせると　みずタイプの\nわざの　いりょくが　あがる。", "不思議な　香りの　御香。\n持たせると　みずタイプの\n技の　威力が　あがる。", "이상한 향기의 향로.\n지니게 하면 물타입\n기술의 위력이 올라간다.", "Tiene un curioso aroma que potencia\nlos movimientos de tipo Agua.\nDebe llevarlo un Pokémon.") },
            { PBEItem.SharpBeak, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a long, sharp beak that boosts\nthe power of Flying-type moves.", "Objet à tenir. Bec long et pointu\nmontant la puissance des capacités\nde type Vol.", "Ein Item zum Tragen. Ein langer,\nscharfer Schnabel verstärkt\nAttacken vom Typ Flug.", "Strumento da far tenere a un Pokémon.\nBecco lungo e affilato che potenzia\nle mosse di tipo Volante.", "ながく　とがった　くちばし。\nもたせると　ひこうタイプの\nわざの　いりょくが　あがる。", "長く　とがった　くちばし。\n持たせると　ひこうタイプの\n技の　威力が　あがる。", "길고 뾰족한 부리.\n지니게 하면 비행타입\n기술의 위력이 올라간다.", "Pico largo y afilado que aumenta la\npotencia de los movimientos de tipo\nVolador. Debe llevarlo un Pokémon.") },
            { PBEItem.ShedShell, new PBELocalizedString("A tough, discarded carapace to be held\nby a Pokémon. It enables the holder to\nswitch with a waiting Pokémon in battle.", "Objet à tenir. Carapace dure qui permet\nau porteur de se retirer même s'il est\naffecté par une attaque immobilisante.", "Ein Item zum Tragen. Dieser harte\nPanzer ermöglicht den Austausch des\nTrägers gegen ein wartendes Pokémon.", "Un Pokémon con questo robusto guscio\npuò sfuggire al nemico anche se questi\nha usato mosse o abilità intrappolanti.", "じょうぶで　かたい　ぬけがら。\nもたせた　ポケモンは　ひかえの\nポケモンと　かならず　いれかわれる。", "丈夫で　硬い　ぬけがら。\n持たせた　ポケモンは　控えの\nポケモンと　必ず　いれかわれる。", "튼튼하고 단단한 허물.\n지니게 한 포켓몬은 교대\n포켓몬과 반드시 교체할 수 있다.", "En combate, esta concha desechada\nsirve para que un Pokémon se cambie\npor otro que no esté combatiendo.") },
            { PBEItem.ShellBell, new PBELocalizedString("An item to be held by a Pokémon.\nThe holder's HP is restored a little\nevery time it inflicts damage.", "Objet à tenir. Restaure un peu les PV\ndu porteur à chaque fois qu'il inflige\ndes dégâts.", "Ein Item zum Tragen. Es stellt einige\nKP des Trägers immer dann wieder her,\nwenn dieser dem Ziel Schaden zufügt.", "Strumento da far tenere a un Pokémon.\nChi lo tiene recupera un po' di PS\nogni volta che infligge danni.", "もたせた　ポケモンが　こうげきして\nあいてに　ダメージを　あたえたとき\nＨＰを　すこし　かいふく　できる。", "持たせた　ポケモンが　攻撃して\n相手に　ダメージを　あたえたとき\nＨＰを　少し　回復できる。", "지니게 한 포켓몬이 공격하여\n상대에게 데미지를 줬을 때\nHP를 조금 회복할 수 있다.", "Debe llevarla un Pokémon.\nCada vez que este inflige daño,\nrecupera algunos PS.") },
            { PBEItem.ShinyStone, new PBELocalizedString("A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt shines with a dazzling light.", "Pierre étrange qui fait évoluer\ncertaines espèces de Pokémon. Elle\nbrille d'une lumière aveuglante.", "Dieser besondere, hell leuchtende\nStein hilft manchen Pokémon bei\nihrer Evolution.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nBrilla di una luce abbagliante.", "ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nひかりのように　まぶしい。", "ある　特定の　ポケモンを\n進化させる　不思議な　石。\nひかりのように　まぶしい。", "어느 특정 포켓몬을\n진화시키는 이상한 돌.\n빛처럼 눈부시다.", "Una piedra peculiar que hace\nevolucionar a algunos Pokémon.\nTiene un brillo espectacular.") },
            { PBEItem.ShoalSalt, new PBELocalizedString("Pure salt that can be discovered deep\ninside the Shoal Cave.\nA maniac will buy it for a high price.", "Sel pur provenant des profondeurs\nde la Grotte Tréfonds. Les amateurs\nen donnent un bon prix.", "Reine Salzkristalle, die sich in der sog.\nKüstenhöhle finden lassen. Sammler\ngeben ihr letztes Hemd dafür her.", "Sale purissimo rinvenuto nella Grotta\nOndosa. Si può vendere a un prezzo\npiuttosto alto agli intenditori.", "あさせのほらあな　という　ばしょで\nみつかる　きれいな　しお。\nマニアが　たかく　かってくれる。", "浅瀬の洞穴という　場所で\nみつかる　きれいな　塩。\nマニアが　高く　買ってくれる。", "여울의 동굴이라는 장소에서\n발견할 수 있는 깨끗한 소금.\n마니아가 비싼 값에 산다.", "Una sal muy fina encontrada en la\nCueva Cardumen.\nMuy valorada por los coleccionistas.") },
            { PBEItem.ShoalShell, new PBELocalizedString("A pretty seashell that can be found\ndeep inside the Shoal Cave.\nA maniac will buy it for a high price.", "Joli coquillage provenant des\nprofondeurs de la Grotte Tréfonds.\nLes amateurs en donnent un bon prix.", "Eine hübsche Schale, die sich in der\nKüstenhöhle finden lässt. Sammler\ngeben ihr letztes Hemd dafür her.", "Un bel guscio rinvenuto nella Grotta\nOndosa. Si può vendere a un prezzo\npiuttosto alto agli intenditori.", "あさせのほらあな　という　ばしょで\nみつかる　きれいな　かいがら。\nマニアが　たかく　かってくれる。", "浅瀬の洞穴という　場所で\nみつかる　きれいな　貝殻。\nマニアが　高く　買ってくれる。", "여울의 동굴이라는 장소에서\n발견할 수 있는 예쁜 조개껍질.\n마니아가 비싼 값에 산다.", "Concha encontrada en la Cueva\nCardumen.\nMuy valorada por los coleccionistas.") },
            { PBEItem.ShockDrive, new PBELocalizedString("A cassette to be held by Genesect.\nIt changes Techno Blast to an\nElectric-type move.", "Tenu par Genesect, ce module\nchange la capacité TechnoBuster\nen une capacité de type Électrik.", "Gibt man es Genesect zum Tragen,\nwandelt es den Typ der Attacke\nTechblaster in Elektro um.", "Modulo da far tenere a Genesect.\nTrasforma Tecnobotto in una mossa\ndi tipo Elettro.", "ゲノセクトに　もたせると\nテクノバスターという　わざが\nでんきタイプになる　カセット。", "ゲノセクトに　持たせると\nテクノバスターという　技が\nでんきタイプになる　カセット。", "게노세크트에게 지니게 하면\n테크노버스터라는 기술이\n전기타입으로 바뀌는 카세트.", "Cartucho que convierte\nTecno Shock de Genesect\nen un movimiento de tipo Eléctrico.") },
            { PBEItem.ShucaBerry, new PBELocalizedString("Weakens a supereffective Ground-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Sol.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Boden.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Terra\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　じめん　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　じめん技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 땅 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Tierra\nde un enemigo.") },
            { PBEItem.SilkScarf, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a sumptuous scarf that boosts\nthe power of Normal-type moves.", "Objet à tenir. Mouchoir somptueux\nmontant la puissance des capacités\nde type Normal.", "Ein Item zum Tragen. Ein kostbarer\nSchal, der Attacken vom Typ Normal\nverstärkt.", "Strumento da far tenere a un Pokémon.\nSciarpa raffinata che potenzia le mosse\ndi tipo Normale.", "はだざわりの　よい　スカーフ。\nもたせると　ノーマルタイプの\nわざの　いりょくが　あがる。", "はだざわりの　よい　スカーフ。\n持たせると　ノーマルタイプの\n技の　威力が　あがる。", "감촉이 좋은 스카프.\n지니게 하면 노말타입\n기술의 위력이 올라간다.", "La delicadeza del pañuelo fortalece\nlos movimientos de tipo Normal.\nDebe llevarlo un Pokémon.") },
            { PBEItem.SilverPowder, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a shiny, silver powder that ups\nthe power of Bug-type moves.", "Objet à tenir. Poudre d'argent\nbrillante qui monte la puissance\ndes capacités de type Insecte.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nverbessert Attacken vom Typ Käfer.", "Strumento da far tenere a un Pokémon.\nPolvere argentea e brillante che\npotenzia le mosse di tipo Coleottero.", "ぎんいろに　かがやく　こな。\nもたせると　むしタイプの\nわざの　いりょくが　あがる。", "銀色に　輝く　粉。\n持たせると　むしタイプの\n技の　威力が　あがる。", "은색으로 반짝이는 가루.\n지니게 하면 벌레타입\n기술의 위력이 올라간다.", "Polvo de brillo argénteo que fortalece\nlos ataques de tipo Bicho.\nDebe llevarlo un Pokémon.") },
            { PBEItem.SitrusBerry, new PBELocalizedString("If held by a Pokémon,\nit heals the user's HP a little.", "Baie qui restaure quelques PV\nlorsqu'elle est tenue par un Pokémon.", "Gibst du die Beere einem Pokémon\nzum Tragen, füllt diese seine KP\nein wenig auf.", "Se tenuta da un Pokémon restituisce\nun po' di PS.", "ポケモンに　もたせると\nＨＰを　すこしだけ　かいふくする。\n", "ポケモンに　持たせると\nＨＰを　少しだけ　回復する。\n", "포켓몬에게 지니게 하면\nHP를 조금 회복한다.\n", "Los Pokémon pueden llevarla o usarla\npara restaurar algunos PS.") },
            { PBEItem.SkullFossil, new PBELocalizedString("A fossil from a prehistoric Pokémon\nthat lived on the land. It appears to\nbe part of a head.", "Fossile d'un Pokémon préhistorique\nterrestre. On dirait une partie d'un\ncrâne.", "Das Fossil eines uralten\nPokémon, das an Land lebte.\nEs sieht wie ein Schädel aus.", "Fossile di un Pokémon preistorico che\nviveva sulla terraferma. Sembra far\nparte di un teschio.", "おおむかし　ちじょうで　くらした\nこだい　ポケモンの　カセキ。\nあたまの　いちぶ　らしい。", "大昔　地上で　暮らした\n古代ポケモンの　カセキ。\n頭の　一部らしい。", "오랜 옛날 지상에서 살았던\n고대 포켓몬의 화석.\n머리의 일부인 듯하다.", "Fósil de un Pokémon prehistórico\nterrestre. Parece ser parte de una\ncabeza.") },
            { PBEItem.SkyPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Flying-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Vol.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Flug.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Volante.", "ひこうの　タイプの　せきばん。\nもたせると　ひこうタイプの\nわざの　いりょくが　つよまる。", "ひこうの　タイプの　石盤。\n持たせると　ひこうタイプの\n技の　威力が　強まる。", "비행타입의 석판.\n지니게 하면 비행타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Volador.\nDebe llevarla un Pokémon.") },
            { PBEItem.SmokeBall, new PBELocalizedString("An item to be held by a Pokémon.\nIt enables the holder to flee from any\nwild Pokémon without fail.", "Objet à tenir. Permet au porteur de\ns'enfuir à coup sûr face à un Pokémon\nsauvage.", "Ein Pokémon kann dieses Item tragen.\nEs ermöglicht die garantierte Flucht\naus dem Kampf mit einem wilden Pokémon.", "Strumento da far tenere a un Pokémon.\nPermette a chi lo tiene di fuggire con\nsuccesso da un Pokémon selvatico.", "もたせた　ポケモンは　やせいの\nポケモンとの　せんとうから\nぜったいに　にげられる。", "持たせた　ポケモンは　野生の\nポケモンとの　戦闘から\n絶対に　逃げられる。", "지니게 한 포켓몬은\n야생 포켓몬과의 배틀에서\n반드시 도망칠 수 있다.", "Debe llevarla un Pokémon.\nPermite huir siempre de combates\ncontra Pokémon salvajes.") },
            { PBEItem.SmoothRock, new PBELocalizedString("A Pokémon held item that extends the\nduration of the move Sandstorm used\nby the holder.", "Objet à tenir augmentant la durée de\nla capacité Tempêtesable utilisée par\nle porteur.", "Ein Item zum Tragen. Verlängert\ndie Dauer von Sandsturm, wenn\nvon Träger eingesetzt.", "Strumento da far tenere a un Pokémon\nche allunga la durata della mossa\nTerrempesta se usata da chi lo tiene.", "もたせた　ポケモンが　すなあらしを\nつかうと　いつもより　すなあらしの\nじかんが　ながくなる。", "持たせた　ポケモンが　すなあらしを\n使うと　いつもより　すなあらしの\n時間が　長くなる。", "지니게 한 포켓몬이 모래바람을\n쓰면 여느 때보다 모래바람의\n시간이 길어진다.", "Prolonga la duración del movimiento\nTormenta Arena que use el Pokémon\nque la lleva.") },
            { PBEItem.SodaPop, new PBELocalizedString("A fizzy soda drink.\nIt restores the HP of one Pokémon by\n60 points.", "Une boisson pétillante.\nRestaure 60 PV à un Pokémon.", "Ein sprudelndes Mineralwasser,\ndas die KP eines Pokémon\num 60 Punkte auffüllt.", "Bevanda frizzante.\nRestituisce 60 PS ad un Pokémon.", "シュワっと　はじける　ソーダ。\nポケモン　１ひきの　ＨＰを\n６０だけ　かいふくする。", "シュワっと　はじける　ソーダ。\nポケモン　１匹の　ＨＰを\n６０だけ　回復する。", "쏴하고 톡 쏘는 사이다.\n포켓몬 1마리의 HP를\n60만큼 회복한다.", "Chispeante bebida gaseosa que\nrestaura 60 PS de un Pokémon.") },
            { PBEItem.SoftSand, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a loose, silky sand that boosts\nthe power of Ground-type moves.", "Objet à tenir. Sable fin et soyeux\nmontant la puissance des capacités\nde type Sol.", "Ein Item zum Tragen. Samtiger\nSand, der Attacken vom Typ\nBoden verstärkt.", "Strumento da far tenere a un Pokémon.\nSabbia leggera e farinosa che potenzia\nle mosse di tipo Terra.", "さわると　サラサラする　すな。\nもたせると　じめんタイプの\nわざの　いりょくが　あがる。", "さわると　サラサラする　砂。\n持たせると　じめんタイプの\n技の　威力が　あがる。", "만지면 보슬보슬한 모래.\n지니게 하면 땅타입\n기술의 위력이 올라간다.", "Debe llevarla un Pokémon.\nLa suavidad de la arena fortalece\nlos movimientos de tipo Tierra.") },
            { PBEItem.SootheBell, new PBELocalizedString("An item to be held by a Pokémon. It is\na bell with a comforting chime that\ncalms the holder and makes it friendly.", "Objet à tenir. Grelot dont le carillon\ncalme le porteur et favorise l'amitié.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nbesänftigt und fördert Sympathie.", "Strumento da far tenere a un Pokémon.\nCampanella dal suono rassicurante che\ncalma chi la usa rendendolo amichevole.", "ここちよい　ねいろの　すず。\nもたせた　ポケモンは　こころが\nやすらぎ　なつきやすくなる。", "心地よい　音色の　鈴。\n持たせた　ポケモンは　こころが\nやすらぎ　なつきやすくなる。", "기분 좋은 소리의 방울.\n지니게 한 포켓몬은 마음이\n편안해져 친밀해지기 쉬워진다.", "Una campana con un tañido calmante\nque reconforta al Pokémon que la lleva\ny lo vuelve más amistoso.") },
            { PBEItem.SoulDew, new PBELocalizedString("A wondrous orb to be held by Latios\nor Latias. It raises both the\nSp. Atk and Sp. Def stats.", "Orbe prodigieux à faire tenir à Latios\nou Latias qui monte leur Attaque\nSpéciale et leur Défense Spéciale.", "Ein Item, das von Latios oder Latias\ngetragen werden kann. Es hebt Spezial-\nAngriff und Spezial-Verteidigung.", "Sfera miracolosa da far tenere a\nLatios o Latias. Aumenta l'Attacco\nSpeciale e la Difesa Speciale.", "ラティオス　または　ラティアスに\nもたせると　とくこうと　とくぼうが\nあがる　ふしぎな　たま。", "ラティオス　または　ラティアスに\n持たせると　特攻と　特防が\nあがる　不思議な　珠。", "라티오스 혹은 라티아스에게\n지니게 하면 특수공격과 특수방어가\n올라가는 이상한 구슬.", "Un orbe fantástico que debe llevar\nLatios o Latias. Sube su Ataque\nEspecial y su Defensa Especial.") },
            { PBEItem.SpellTag, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a sinister, eerie tag that boosts\nthe power of Ghost-type moves.", "Objet à tenir. Plaque sinistre et\nétrange montant la puissance des\ncapacités de type Spectre.", "Ein Item zum Tragen. Ein fieser,\nunheimlicher Sticker, der Attacken\nvom Typ Geist verstärkt.", "Strumento da far tenere a un Pokémon.\nTarga sinistra e inquietante che\npotenzia le mosse di tipo Spettro.", "あやしくて　ぶきみな　おふだ。\nもたせると　ゴーストタイプの\nわざの　いりょくが　あがる。", "怪しくて　不気味な　お札。\n持たせると　ゴーストタイプの\n技の　威力が　あがる。", "괴상하고 으스스한 부적.\n지니게 하면 고스트타입\n기술의 위력이 올라간다.", "Misterioso y siniestro objeto que\npotencia los movimientos de tipo\nFantasma. Debe llevarlo un Pokémon.") },
            { PBEItem.SpelonBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.SplashPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Water-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Eau.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Wasser.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Acqua.", "みずの　タイプの　せきばん。\nもたせると　みずタイプの\nわざの　いりょくが　つよまる。", "みずの　タイプの　石盤。\n持たせると　みずタイプの\n技の　威力が　強まる。", "물타입의 석판.\n지니게 하면 물타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Agua.\nDebe llevarla un Pokémon.") },
            { PBEItem.SpookyPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Ghost-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Spectre.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Geist.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Spettro.", "ゴーストの　タイプの　せきばん。\nもたせると　ゴーストタイプの\nわざの　いりょくが　つよまる。", "ゴーストの　タイプの　石盤。\n持たせると　ゴーストタイプの\n技の　威力が　強まる。", "고스트타입의 석판.\n지니게 하면 고스트타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Fantasma.\nDebe llevarla un Pokémon.") },
            { PBEItem.SportBall, new PBELocalizedString("A special Poké Ball for the\nBug-Catching Contest.", "Une Poké Ball spéciale utilisée au\nConcours de Capture d'insecte.", "Ein Ball, der nur beim Käferturnier\neingesetzt wird.", "Poké Ball speciale utilizzata solo nella\nGara Pigliamosche.", "むしとりたいかいの　なかで\nつかう　とくべつな　ボール。　\n", "虫捕り大会の　なかで\n使う　特別な　ボール。　\n", "곤충채집 대회 내에서\n사용하는 특별한 볼.\n", "Poké Ball que se utiliza en el\nConcurso de Captura de Bichos.") },
            { PBEItem.StableMulch, new PBELocalizedString("A fertilizer to be spread on soft soil\nin regions where Berries are grown.\nA maniac will buy it for a high price.", "Un engrais utilisé sur sol meuble.\nLes amateurs en donnent un bon prix.", "In Regionen, wo man Beeren anbaut,\nmengt man ihn unter weiche Erde.\nSammler geben ihr letztes Hemd dafür.", "Usato per favorire la crescita delle\nBacche nelle regioni dove si coltivano.\nGli intenditori pagano caro per averlo.", "きのみを　そだてる　ちほうでは\nふかふかのつちに　まいて　つかう。\nマニアが　たかく　かってくれる。", "きのみを　育てる　地方では\nふかふかのつちに　まいて　使う。\nマニアが　高く　買ってくれる。", "나무열매를 키우는 지방에서는\n푹신푹신한 땅에 뿌려 쓴다.\n마니아가 비싼 값에 산다.", "Un fertilizante usado en suelo fértil\ndonde crecen las Bayas.\nMuy apreciado por los coleccionistas.") },
            { PBEItem.Stardust, new PBELocalizedString("Lovely, red-colored sand with a loose,\nsilky feel. It can be sold at a high\nprice to shops.", "Sable rouge ravissant, aux grains très\nfins. Peut être vendu à bon prix aux\nmagasins.", "Schöner, roter Sand, der sich\nseidenweich anfühlt. Er ist von\nhohem Wert.", "Bella sabbia rossa dalla consistenza\nleggera e farinosa. Si può vendere\na un prezzo piuttosto alto nei negozi.", "てざわりが　サラサラの\nあかくて　きれいな　すな。\nショップで　たかく　うれる。", "てざわりが　サラサラの\n赤くて　きれいな　砂。\nショップで　高く　売れる。", "감촉이 보슬보슬한\n빨갛고 예쁜 모래.\n상점에서 비싸게 팔린다.", "Bonita arena roja de tacto sedoso que\nalcanza un alto precio en las tiendas.") },
            { PBEItem.StarfBerry, new PBELocalizedString("If held by a Pokémon, it sharply raises\none of its stats in a pinch.", "Tenue, cette Baie monte beaucoup une\ndes stats en cas de besoin.", "Als getragenes Item erhöht diese Beere\neinen Statuswert des Pokémon stark,\nsobald es in einer Notlage steckt.", "Aumenta molto una statistica se\nil Pokémon che la tiene si trova\nin difficoltà.", "ポケモンに　もたせると\nピンチのとき　どれか　１つの\nのうりょくが　ぐーんと　あがる。", "ポケモンに　持たせると\nピンチのとき　どれか　１つの\n能力が　ぐーんと　あがる。", "포켓몬에게 지니게 하면\n위급할 때 능력 중의 하나가\n크게 올라간다.", "Si la lleva un Pokémon, aumenta\nmucho una característica en un\nmomento de apuro.") },
            { PBEItem.StarPiece, new PBELocalizedString("A shard of a pretty gem that sparkles\nin a red color. It can be sold at a\nhigh price to shops.", "Fragment de gemme qui brille d'un éclat\nrouge. Peut se vendre à bon prix aux\nmagasins.", "Teil eines hübschen Edelsteins, der in\nrötlicher Farbe schimmert. Erzielt einen\nhohen Preis, wenn man ihn verkauft.", "Frammento rosso acceso di una gemma.\nSi può vendere a un prezzo piuttosto\nalto nei negozi.", "キラキラと　あかく　ひかる\nきれいな　ほうせきの　かけら。\nショップで　たかく　うれる。", "キラキラと　赤く　光る\nきれいな　宝石の　かけら。\nショップで　高く　売れる。", "반짝반짝 빨갛게 빛나는\n예쁜 보석 조각.\n상점에서 비싸게 팔린다.", "Fragmento de una bonita gema roja\nque puede venderse muy caro en las\ntiendas.") },
            { PBEItem.SteelGem, new PBELocalizedString("A gem with an essence of steel. When\nheld, it strengthens the power of a\nSteel-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Acier.", "Ein Juwel vom Typ Stahl. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Stahl.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Acciaio.", "はがねの　タイプの　ジュエル。\nもたせると　いちどだけ　はがねの\nわざの　いりょくが　つよまる。", "はがねの　タイプの　ジュエル。\n持たせると　１度だけ　はがねの\n技の　威力が　強まる。", "강철타입의 주얼.\n지니게 하면 한 번만 강철\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Acero.\nDebe llevarla un Pokémon.") },
            { PBEItem.Stick, new PBELocalizedString("An item to be held by Farfetch'd. It is\na very long and stiff stalk of leek\nthat boosts the critical-hit ratio.", "Objet à faire tenir à Canarticho.\nPoireau très long et solide qui\nmonte son taux de critiques.", "Ein Item, das von Porenta getragen\nwerden kann. Diese lange Lauchstange\nerhöht die Volltrefferquote stark.", "Strumento da far tenere a Farfetch'd.\nGambo di porro lungo che aumenta\ndi molto la probabilità di brutti colpi.", "とても　ながくて　かたい　クキ。\nカモネギに　もたせると　わざが\nきゅうしょに　あたりやすくなる。", "とても　長くて　硬い　クキ。\nカモネギに　持たせると　技が\n急所に　当たりやすくなる。", "매우 길고 단단한 줄기.\n파오리에게 지니게 하면 기술이\n급소에 맞기 쉬워진다.", "Es un puerro muy largo que debe llevar\nFarfetch'd. Aumenta la probabilidad de\nque logre un golpe crítico mucho.") },
            { PBEItem.StickyBarb, new PBELocalizedString("A held item that damages the holder on\nevery turn. It may latch on to foes and\nallies that touch the holder.", "Objet à tenir blessant le porteur à\nchaque tour. Peut s'accrocher aux\nennemis qui attaquent le porteur.", "Schadet dem Träger in jeder Runde,\nkann aber an Angreifern haften\nbleiben, die ihn berühren.", "Strumento che danneggia chi lo tiene\nad ogni turno. Può attaccarsi al nemico\nche lancia un attacco fisico.", "もたせると　まいターン　ダメージを\nうける。　じぶんに　ふれてきた\nあいてに　くっつくことも　ある。", "持たせると　毎ターン　ダメージを\n受ける。　自分に　ふれてきた\n相手に　くっつくことも　ある。", "지니게 하면 매 턴 데미지를 입는다.\n자신에게 접촉해온 상대에게\n들러붙기도 한다.", "Daña en cada turno al Pokémon que\nla lleva. Puede adherirse al Pokémon\nque alcance con su ataque al portador.") },
            { PBEItem.StonePlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Rock-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Roche.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Gestein.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Roccia.", "いわの　タイプの　せきばん。\nもたせると　いわタイプの\nわざの　いりょくが　つよまる。", "いわの　タイプの　石盤。\n持たせると　いわタイプの\n技の　威力が　強まる。", "바위타입의 석판.\n지니게 하면 바위타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Roca.\nDebe llevarla un Pokémon.") },
            { PBEItem.SunStone, new PBELocalizedString("A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt is as red as the sun.", "Une pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle est rouge comme le soleil.", "Dieser spezielle Stein löst bei\nbestimmten Pokémon die Entwicklung\naus. Er ist rot wie die Sonne.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nÈ rossa come il sole.", "ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nたいようのように　あかい。", "ある　特定の　ポケモンを\n進化させる　不思議な　石。\n太陽のように　赤い。", "어느 특정한 포켓몬을\n진화시키는 이상한 돌.\n태양처럼 빨갛다.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nEs roja como el núcleo del sol.") },
            { PBEItem.SuperPotion, new PBELocalizedString("A spray-type medicine for wounds.\nIt restores the HP of one Pokémon by\n50 points.", "Un spray qui soigne les blessures.\nRestaure 50 PV à un Pokémon.", "Hilft bei Wunden. Es füllt\ndie KP eines Pokémon\num 50 Punkte auf.", "Rimedio spray per le ferite.\nRestituisce 50 PS ad un Pokémon.", "スプレーしきの　キズぐすり。\nポケモン　１ひきの　ＨＰを\n５０だけ　かいふくする。", "スプレー式の　キズぐすり。\nポケモン　１匹の　ＨＰを\n５０だけ　回復する。", "스프레이식의 상처약.\n포켓몬 1마리의 HP를\n50만큼 회복한다.", "Medicina en espray que cura heridas\ny restaura 50 PS de un Pokémon.") },
            { PBEItem.SuperRepel, new PBELocalizedString("An item that prevents weak wild\nPokémon from appearing for 200 steps\nafter its use.", "Repousse les Pokémon sauvages\nfaibles pendant 200 pas.", "Hält 200 Schritte lang schwache,\nwilde Pokémon ab.", "Repellente che evita l'incontro con i\nPokémon selvatici deboli per 200 passi\ndopo l'utilizzo.", "２００ぽ　すすむ　あいだ\nよわい　やせいの　ポケモンが\nまったく　でて　こなくなる。", "２００歩　進む　あいだ\n弱い　野生の　ポケモンが\nまったく　でて　こなくなる。", "200보 걸어가는 동안\n약한 야생 포켓몬이\n전혀 나오지 않게 된다.", "Repele Pokémon salvajes débiles\nen un recorrido de 200 pasos.") },
            { PBEItem.SweetHeart, new PBELocalizedString("Very sweet chocolate.\nIt restores the HP of one Pokémon\nby only 20 points.", "Un chocolat extrêmement\nsucré. Restaure 20 PV\nà un Pokémon.", "Ein süßer Schokosnack, der die KP\neines Pokémon um 20 Punkte auffüllt.", "Cioccolato molto dolce.\nRestituisce 20 PS ad un Pokémon.", "とても　あまったるい　チョコレート。\nポケモン　１ひきの　ＨＰを\n２０だけ　かいふくする。", "とても　甘ったるい　チョコレート。\nポケモン　１匹の　ＨＰを\n２０だけ　回復する。", "지나칠 정도로 달콤한 초콜릿.\n포켓몬 1마리의 HP를\n20만큼 회복한다.", "Chocolatina de sabor dulce.\nRestaura 20 PS de un Pokémon.") },
            { PBEItem.SwiftWing, new PBELocalizedString("An item for use on a Pokémon. It\nslightly increases the base Speed stat\nof a single Pokémon.", "Objet à utiliser sur un Pokémon.\nIl augmente un peu sa Vitesse\nde base.", "Schenkt man einem Pokémon dieses\nItem, wird dessen Initiative-Wert\ndauerhaft erhöht.", "Strumento da usare su un Pokémon.\nNe aumenta di poco la Velocità di base.", "ポケモンに　つかう　どうぐ。\nポケモン　１ひきの　すばやさの\nきそポイントを　ちょっと　あげる。", "ポケモンに　使う　道具。\nポケモン　１匹の　素早さの\n基礎ポイントを　ちょっと　あげる。", "포켓몬에게 사용하는 도구.\n포켓몬 1마리의 스피드\n기초 포인트를 조금 올린다.", "Pluma que aumenta un poco\nla Velocidad de un Pokémon\nal subir de nivel.") },
            { PBEItem.TamatoBerry, new PBELocalizedString("Using it on a Pokémon makes it more\nfriendly, but it also lowers its base\nSpeed stat.", "Rend les Pokémon plus amicaux, mais\nbaisse leur stat Vitesse de base.", "Isst ein Pokémon diese Beere,\nstärkt dies eure Freundschaft, ver-\nringert aber seinen Initiative-Wert.", "Utilizzata su un Pokémon, lo rende più\namichevole, ma diminuisce la Velocità\ndi base.", "ポケモンに　あげると\nとても　なつき　やすくなるが\nすばやさの　きそポイントは　さがる。", "ポケモンに　あげると\nとても　なつき　やすくなるが\n素早さの　基礎ポイントは　さがる。", "포켓몬에게 주면\n매우 친밀해지기 쉬워지지만\n스피드의 기초 포인트가 떨어진다.", "Al usarla con un Pokémon, se gana\nsu amistad, pero también reduce\nsu Velocidad de base.") },
            { PBEItem.TangaBerry, new PBELocalizedString("Weakens a supereffective Bug-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Insecte.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Käfer.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo\nColeottero del nemico.", "ポケモンに　もたせると\nこうかばつぐんの　むし　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　むし技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 벌레 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Bicho\nde un enemigo.") },
            { PBEItem.ThanksMail, new PBELocalizedString("Stationery designed for\na thank-you note.\nLet a Pokémon hold it for delivery.", "Un papier à lettres idéal pour les\nremerciements. Peut être tenu\net livré par un Pokémon.", "Briefpapier, mit dem man sich\nbei jemandem bedanken kann.\nGib es einem Pokémon zum Tragen.", "Carta da lettera ideale per scrivere\ndei ringraziamenti. Un Pokémon può\ntenerla e consegnarla.", "かんしゃを　つたえる　メールが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "感謝を　伝える　メールが\n書きやすい　レターペーパー。\nポケモンに　持たせて　使う。", "감사의 마음을 전하는 메일을\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Carta de agradecimiento.\nHaz que la entregue un Pokémon.") },
            { PBEItem.ThickClub, new PBELocalizedString("An item to be held by Cubone or\nMarowak. It is a hard bone of some\nsort that boosts the Attack stat.", "Objet à faire tenir à Osselait ou\nOssatueur. Os dur qui monte leur\nAttaque.", "Ein harter Knochen, der den Angriffs-\nWert von Tragosso oder Knogga\nerhöht, wenn sie ihn tragen.", "Strumento da far tenere a Cubone o\nMarowak. Osso duro che migliora\nl'Attacco.", "なにかの　かたい　ホネ。\nカラカラ　または　ガラガラに\nもたせると　こうげきが　あがる。", "なにかの　硬い　ホネ。\nカラカラ　または　ガラガラに\n持たせると　攻撃が　あがる。", "무언가의 단단한 뼈.\n탕구리 혹은 텅구리에게\n지니게 하면 공격이 올라간다.", "Un extraño tipo de hueso que potencia\nel Ataque. Deben llevarlo Cubone o\nMarowak.") },
            { PBEItem.Thunderstone, new PBELocalizedString("A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt has a thunderbolt pattern.", "Une pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nUn éclair est dessiné dessus.", "Dieser spezielle Stein löst bei\nbestimmten Pokémon die Entwicklung\naus. Er hat ein Blitzmuster.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nRiporta l'immagine di un fulmine.", "ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nいなずまの　もようが　ある。", "ある　特定の　ポケモンを\n進化させる　不思議な　石。\n稲妻の　模様が　ある。", "어느 특정한 포켓몬을\n진화시키는 이상한 돌.\n천둥번개 무늬가 있다.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nTiene grabado un rayo.") },
            { PBEItem.TimerBall, new PBELocalizedString("A somewhat different Ball that\nbecomes progressively better the\nmore turns there are in a battle.", "Une Poké Ball un peu spéciale qui\nfonctionne de mieux en mieux à\nmesure que le combat s'éternise.", "Ein Ball, der mit zunehmender\nKampfdauer effektiver wird.", "Poké Ball un po' diversa che diventa\nprogressivamente più efficace con\nl'aumentare dei turni in lotta.", "ターンすうが　かかれば　かかるほど\nポケモンが　つかまえやすくなる\nちょっと　かわった　ボール。", "ターン数が　かかれば　かかるほど\nポケモンが　捕まえやすくなる\nちょっと　変わった　ボール。", "턴 수가 길어지면 길어질수록\n포켓몬을 잡기 쉬운\n조금 특이한 볼.", "Una Poké Ball algo distinta que va\nmejorando su rendimiento con cada\nturno de combate.") },
            { PBEItem.TinyMushroom, new PBELocalizedString("A small and rare mushroom.\nIt is sought after by collectors.", "Un petit champignon plutôt rare.\nIl est plutôt populaire parmi\ncertains fans dévoués.", "Ein kleiner und seltener Pilz.\nSeine Fans sind ganz aus dem\nHäuschen, wenn sie einen finden.", "Fungo piccolo e raro. È parecchio\napprezzato da alcuni intenditori.", "ちいさな　めずらしい　キノコ。\nいちぶの　マニアの　あいだでは\nけっこう　にんきが　たかい。", "ちいさな　珍しい　キノコ。\n一部の　マニアの　あいだでは\nけっこう　人気が　高い。", "작고 진귀한 버섯.\n일부 마니아 사이에서는\n매우 인기가 높다.", "Una pequeña seta que es poco común\ny bastante popular entre determinados\ngrupos de gourmets.") },
            { PBEItem.ToxicOrb, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a bizarre orb that badly poisons\nthe holder in battle.", "Objet à tenir. Orbe bizarre\nempoisonnant gravement son\nporteur durant le combat.", "Ein Item zum Tragen. Dieser\nbizarre Orb vergiftet seinen\nTräger im Kampf schwer.", "Strumento da far tenere a un Pokémon.\nSfera bizzarra che iperavvelena chi la\ntiene in lotta.", "さわると　どくをだす　ふしぎなたま。\nもたせると　せんとうちゅうに\nもうどくの　じょうたいに　なる。", "触ると　毒をだす　不思議な玉。\n持たせると　戦闘中に\n猛毒の　状態に　なる。", "만지면 독을 발산하는 이상한 구슬.\n지니게 하면 배틀 중에\n맹독 상태가 된다.", "Extraña esfera que envenena\ngravemente al Pokémon que\nla lleva en combate.") },
            { PBEItem.ToxicPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Poison-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Poison.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Gift.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Veleno.", "どくの　タイプの　せきばん。\nもたせると　どくタイプの\nわざの　いりょくが　つよまる。", "どくの　タイプの　石盤。\n持たせると　どくタイプの\n技の　威力が　強まる。", "독타입의 석판.\n지니게 하면 독타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Veneno.\nDebe llevarla un Pokémon.") },
            { PBEItem.TwistedSpoon, new PBELocalizedString("An item to be held by a Pokémon. It is\na spoon imbued with telekinetic power\nthat boosts Psychic-type moves.", "Objet à tenir. Cuillère contenant un\npouvoir télékinésique montant la\npuissance des capacités de type Psy.", "Ein Item zum Tragen. Ein Löffel voller\ntelekinetischer Energie, der Attacken\nvom Typ Psycho verstärkt.", "Strumento da far tenere a un Pokémon.\nCucchiaio dotato di potere telecinetico\nche potenzia le mosse di tipo Psico.", "ねんりきを　こめた　スプーン。\nもたせると　エスパータイプの\nわざの　いりょくが　あがる。", "念力を　こめた　スプーン。\n持たせると　エスパータイプの\n技の　威力が　あがる。", "염동력을 담은 숟가락.\n지니게 하면 에스퍼타입\n기술의 위력이 올라간다.", "Cuchara llena de energía telequinética\nque potencia los movimientos de tipo\nPsíquico. Debe llevarla un Pokémon.") },
            { PBEItem.UltraBall, new PBELocalizedString("An ultra-performance Ball that\nprovides a higher Pokémon catch rate\nthan a Great Ball.", "Une Ball ultraperformante dont le taux\nde réussite est supérieur à celui de la\nSuper Ball.", "Ein Ball mit hoher Erfolgsquote.\nDem Superball in allen\nPunkten überlegen.", "Ball dalle prestazioni eccellenti:\nfornisce maggiori probabilità di\ncattura rispetto alla Mega Ball.", "スーパーボールよりも　さらに\nポケモンを　つかまえやすくなった\nすごく　せいのうのいい　ボール。", "スーパーボールよりも　さらに\nポケモンを　捕まえやすくなった\nすごく　性能のいい　ボール。", "수퍼볼보다도 더욱\n포켓몬을 잡기 쉬워진\n매우 성능이 좋은 볼.", "Poké Ball de rendimiento superior.\nTiene un índice de éxito mayor\nal de la Superball.") },
            { PBEItem.UpGrade, new PBELocalizedString("A transparent device filled with all\nsorts of data.\nIt was produced by Silph Co.", "Dispositif transparent rempli de\ndonnées diverses et variées.\nFabriqué par la Sylphe SARL.", "Ein durchsichtiges Gerät. Es\nenthält alle möglichen Daten.\nHergestellt von Silph Co.", "Apparecchio trasparente pieno zeppo\ndi dati di ogni tipo. È un prodotto\ndella Silph SpA.", "なかに　いろんな　じょうほうが\nつまった　とうめいな　きかい。\nシルフ　カンパニーせい。", "中に　いろんな　情報が\nつまった　透明な　機械。\nシルフ　カンパニー製。", "안에 여러 가지 정보가\n가득한 투명한 기계다.\n실프주식회사 제품.", "Peculiar dispositivo fabricado en\nSilph S. A. que contiene todo tipo\nde datos.") },
            { PBEItem.WacanBerry, new PBELocalizedString("Weakens a supereffective Electric-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une\nattaque ennemie super efficace\nde type Électrik.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Elektro.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Elettro\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　でんき　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　でんき技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 전기 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Eléctrico\nde un enemigo.") },
            { PBEItem.WaterGem, new PBELocalizedString("A gem with an essence of water. When\nheld, it strengthens the power of a\nWater-type move only once.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Eau.", "Ein Juwel vom Typ Wasser. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Wasser.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Acqua.", "みずの　タイプの　ジュエル。\nもたせると　いちどだけ　みずの\nわざの　いりょくが　つよまる。", "みずの　タイプの　ジュエル。\n持たせると　１度だけ　みずの\n技の　威力が　強まる。", "물타입의 주얼.\n지니게 하면 한 번만 물\n기술의 위력이 강해진다.", "Joya de un solo uso que potencia\nlos movimientos de tipo Agua.\nDebe llevarla un Pokémon.") },
            { PBEItem.WaterStone, new PBELocalizedString("A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt is a clear, light blue.", "Une pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle est de couleur bleue.", "Dieser spezielle Stein löst bei\nbestimmten Pokémon die Entwicklung\naus. Er ist hellblau.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nHa delle macchioline azzurre.", "ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nすんだ　みずいろを　している。", "ある　特定の　ポケモンを\n進化させる　不思議な　石。\n澄んだ　水色を　している。", "어느 특정한 포켓몬을\n진화시키는 이상한 돌.\n맑고 푸른 색을 띠고 있다.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nEs de color azul.") },
            { PBEItem.WatmelBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.WaveIncense, new PBELocalizedString("An item to be held by a Pokémon. It is\nincense with a curious aroma that\nboosts the power of Water-type moves.", "Objet à tenir. Encens au parfum\nexotique augmentant la puissance\ndes capacités de type Eau.", "Ein Item zum Tragen. Sein\nausgefallenes Aroma verstärkt\nAttacken vom Typ Wasser.", "Strumento da far tenere a un Pokémon.\nIncenso dal curioso aroma che potenzia\nle mosse di tipo Acqua.", "ふしぎな　かおりの　おこう。\nもたせると　みずタイプの\nわざの　いりょくが　あがる。", "不思議な　香りの　御香。\n持たせると　みずタイプの\n技の　威力が　あがる。", "이상한 향기의 향로.\n지니게 하면 물타입\n기술의 위력이 올라간다.", "Incienso que tiene un curioso aroma\nque potencia los movimientos de\ntipo Agua. Debe llevarlo un Pokémon.") },
            { PBEItem.WepearBerry, new PBELocalizedString("A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "イッシュ地方では　とても\n珍しい　きのみ。\nマニアが　高く　買ってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.") },
            { PBEItem.WhiteFlute, new PBELocalizedString("A toy flute made from white glass.\nA maniac will buy it for a high price.", "Jouet en forme de flûte en verre\nblanc. Les amateurs en donnent\nun bon prix.", "Eine Spielzeugflöte aus weißem Glas.\nSammler geben ihr letztes Hemd dafür\nher.", "Flauto giocattolo fatto di vetro bianco.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "しろい　ガラスで　できている\nおもちゃの　ふえ。\nマニアが　たかく　かってくれる。", "白い　ガラスで　できている\nおもちゃの　笛。\nマニアが　高く　買ってくれる。", "하얀 유리로 만들어진\n장난감 피리.\n마니아가 비싼 값에 산다.", "Una flauta de juguete hecha de\ncristal blanco.\nMuy valorada por los coleccionistas.") },
            { PBEItem.WhiteHerb, new PBELocalizedString("An item to be held by a Pokémon.\nIt restores any lowered stat in\nbattle. It can be used only once.", "Objet à tenir qui restaure les stats\nbaissées. Ne peut être utilisé qu'une\nfois.", "Ein Item, das einem Pokémon zum Tragen\ngegeben werden kann. Es hebt einmalig\njede negative Statusveränderung auf.", "Strumento da far tenere a un Pokémon.\nRipristina le statistiche ridotte in\nlotta. Si può usare una sola volta.", "もたせた　ポケモンの　のうりょくが\nさがったとき　いちどだけ　もとの\nじょうたいに　もどす。", "持たせた　ポケモンの　能力が\nさがったとき　１度だけ　もとの\n状態に　もどす。", "지니게 한 포켓몬의 능력이\n떨어졌을 때 한 번만 원래\n상태로 돌아온다.", "Restaura cualquier característica\nreducida en combate. Solo se puede\nusar una vez. Debe llevarla un Pokémon.") },
            { PBEItem.WhtApricorn, new PBELocalizedString("A white Apricorn.\nIt doesn't smell like anything.", "Un Noigrume de couleur blanche.\nIl ne dégage aucun arôme.", "Eine weiße Aprikoko.\nRiecht nach überhaupt nichts.", "Una Ghicocca di colore bianco.\nNon emana alcun odore.", "しろい　ぼんぐり。\nなんの　においも　しない。\n", "しろい　ぼんぐり。\nなんの　においも　しない。\n", "하얀색의 규토리.\n어떠한 향기도 나지 않는다.\n", "Bonguri de color blanco que no\ndesprende aroma alguno.") },
            { PBEItem.WideLens, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a magnifying lens that slightly\nboosts the accuracy of moves.", "Objet à tenir. Magnifique loupe\naugmentant un peu la Précision\ndes capacités.", "Ein Item zum Tragen.\nDieses Vergrößerungsglas\nerhöht die Genauigkeit etwas.", "Strumento da far tenere a un Pokémon.\nLente d'ingrandimento che aumenta\ndi poco la precisione delle mosse.", "ものが　おおきく　みえる　レンズ。\nもたせると　わざの　めいちゅうりつが\nすこし　あがる。", "ものが　大きく　みえる　レンズ。\n持たせると　技の　命中率が\n少し　あがる。", "물건이 크게 보이는 렌즈.\n지니게 하면 기술의 명중률이\n조금 올라간다.", "Debe llevarla un Pokémon.\nAumenta ligeramente la Precisión de\nlos movimientos.") },
            { PBEItem.WikiBerry, new PBELocalizedString("If held by a Pokémon, it restores the\nuser's HP in a pinch, but will cause\nconfusion if it hates the taste.", "Tenue, cette Baie restaure des PV en\ncas de besoin, mais rend confus si\nle Pokémon n'aime pas son goût.", "Als getragenes Item füllt die Beere die\nKP eines Pokémon auf, doch ihr Aroma\nverwirrt jene, die es nicht mögen.", "Se tenuta da un Pokémon in difficoltà,\nrestituisce PS. Se al Pokémon non piace\nil suo sapore, può causare confusione.", "ポケモンに　もたせると\nピンチのとき　ＨＰを　かいふくする。\nきらいな　あじだと　こんらん　する。", "ポケモンに　持たせると\nピンチのとき　ＨＰを　回復する。\nきらいな味だと　混乱する。", "포켓몬에게 지니게 하면\n위급할 때 HP를 회복한다.\n싫어하는 맛이면 혼란에 빠진다.", "Los Pokémon pueden llevarla o usarla\npara restaurar algunos PS. Su sabor\npuede causar confusión.") },
            { PBEItem.WiseGlasses, new PBELocalizedString("An item to be held by a Pokémon. It is\na thick pair of glasses that slightly\nboosts the power of special moves.", "Objet à tenir. Paire de lunettes\népaisses augmentant un peu la\npuissance des attaques spéciales.", "Ein Item zum Tragen. Diese\ndicke Brille verstärkt auf Spezial-\nAngriff basierende Attacken.", "Strumento da far tenere a un Pokémon.\nPaio di occhiali dalle lenti spesse che\npotenzia di poco le mosse speciali.", "ぶあつい　レンズの　ついた　メガネ。\nもたせると　とくしゅわざの\nいりょくが　すこし　あがる。", "ぶあつい　レンズの　ついた　メガネ。\n持たせると　特殊技の\n威力が　少し　あがる。", "두꺼운 렌즈를 붙인 안경.\n지니게 하면 특수 기술의\n위력이 조금 올라간다.", "Potencian ligeramente los movimientos\nespeciales. Debe llevarlas un Pokémon.") },
            { PBEItem.XAccuracy, new PBELocalizedString("An item that raises the accuracy\nof a Pokémon in battle. It wears off if\nthe Pokémon is withdrawn.", "Monte la Précision pendant un combat.\nL'effet s'estompe si le Pokémon se\nretire.", "Hebt die Genauigkeit eines Pokémon\nim Kampf. Die Wirkung wird mit dem\nAustausch des Pokémon beendet.", "Aumenta la precisione di un Pokémon\nin una lotta. L'effetto svanisce se\nil Pokémon è sostituito.", "せんとうちゅうの　ポケモンの\nめいちゅうを　あげる　どうぐ。\nひっこめると　もとに　もどる。", "戦闘中の　ポケモンの\n命中を　あげる　道具。\nひっこめると　元に　戻る。", "배틀 중인 포켓몬의 명중률을 올리는\n도구. 포켓몬을 볼에 넣어버리면\n원래대로 되돌아간다.", "Aumenta la Precisión de los ataques\nen combate. Al cambiar de Pokémon,\nel efecto desaparece.") },
            { PBEItem.XAttack, new PBELocalizedString("An item that raises the Attack stat\nof a Pokémon in battle. It wears off if\nthe Pokémon is withdrawn.", "Monte l'Attaque pendant un combat.\nL'effet s'estompe si le Pokémon se\nretire.", "Hebt den Angriffs-Wert eines Pokémon\nim Kampf. Die Wirkung wird mit dem\nTausch des Pokémon beendet.", "Aumenta l'Attacco di un Pokémon\nin una lotta. L'effetto svanisce se\nil Pokémon è sostituito.", "せんとうちゅうの　ポケモンの\nこうげきを　あげる　どうぐ。\nひっこめると　もとに　もどる。", "戦闘中の　ポケモンの\n攻撃を　あげる　道具。\nひっこめると　元に　戻る。", "배틀 중인 포켓몬의 공격을 올리는\n도구. 포켓몬을 볼에 넣어버리면\n원래대로 되돌아간다.", "Aumenta el Ataque en combate.\nAl cambiar de Pokémon, el efecto\ndesaparece.") },
            { PBEItem.XDefend, new PBELocalizedString("An item that raises the Defense stat\nof a Pokémon in battle. It wears off if\nthe Pokémon is withdrawn.", "Monte la Défense pendant un combat.\nL'effet s'estompe si le Pokémon se\nretire.", "Hebt den Verteidigungs-Wert eines\nPokémon im Kampf. Die Wirkung wird\nmit dem Austausch des Pokémon beendet.", "Aumenta la Difesa di un Pokémon\nin una lotta. L'effetto svanisce se\nil Pokémon è sostituito.", "せんとうちゅうの　ポケモンの\nぼうぎょを　あげる　どうぐ。\nひっこめると　もとに　もどる。", "戦闘中の　ポケモンの\n防御を　あげる　道具。\nひっこめると　元に　戻る。", "배틀 중인 포켓몬의 방어를 올리는\n도구. 포켓몬을 볼에 넣어버리면\n원래대로 되돌아간다.", "Aumenta la Defensa en combate.\nAl cambiar de Pokémon, el efecto\ndesaparece.") },
            { PBEItem.XSpDef, new PBELocalizedString("An item that raises the Sp. Def stat\nof a Pokémon in battle. It wears off if\nthe Pokémon is withdrawn.", "Monte la Défense Spéciale pendant\nun combat. L'effet s'estompe si le\nPokémon se retire.", "Hebt die Spezial-Verteidigung eines\nPokémon im Kampf. Die Wirkung wird\nmit dem Austausch des Pokémon beendet.", "Aumenta la Difesa Speciale di un\nPokémon in una lotta. L'effetto\nsvanisce se il Pokémon è sostituito.", "せんとうちゅうの　ポケモンの\nとくぼうを　あげる　どうぐ。\nひっこめると　もとに　もどる。", "戦闘中の　ポケモンの\n特防を　あげる　道具。\nひっこめると　元に　戻る。", "배틀 중인 포켓몬의 특수방어를 올리는\n도구. 포켓몬을 볼에 넣어버리면\n원래대로 되돌아간다.", "Aumenta la Defensa Especial en\ncombate. Al cambiar de Pokémon,\nel efecto desaparece.") },
            { PBEItem.XSpecial, new PBELocalizedString("An item that raises the Sp. Atk stat\nof a Pokémon in battle. It wears off if\nthe Pokémon is withdrawn.", "Monte l'Attaque Spéciale pendant\nun combat. L'effet s'estompe si\nle Pokémon se retire.", "Hebt den Spezial-Angriff eines Pokémon\nim Kampf. Die Wirkung wird mit dem\nAustausch des Pokémon beendet.", "Aumenta l'Attacco Speciale di un\nPokémon in una lotta. L'effetto\nsvanisce se il Pokémon è sostituito.", "せんとうちゅうの　ポケモンの\nとくこうを　あげる　どうぐ。\nひっこめると　もとに　もどる。", "戦闘中の　ポケモンの\n特攻を　あげる　道具。\nひっこめると　元に　戻る。", "배틀 중인 포켓몬의 특수공격을 올리는\n도구. 포켓몬을 볼에 넣어버리면\n원래대로 되돌아간다.", "Aumenta el Ataque Especial\nen combate. Al cambiar de Pokémon,\nel efecto desaparece.") },
            { PBEItem.XSpeed, new PBELocalizedString("An item that raises the Speed stat\nof a Pokémon in battle. It wears off if\nthe Pokémon is withdrawn.", "Monte la Vitesse pendant un combat.\nL'effet s'estompe si le Pokémon se\nretire.", "Hebt den Initiative-Wert eines Pokémon\nim Kampf. Die Wirkung wird mit dem\nAustausch des Pokémon beendet.", "Aumenta la Velocità di un Pokémon\nin una lotta. L'effetto svanisce se\nil Pokémon è sostituito.", "せんとうちゅうの　ポケモンの\nすばやさを　あげる　どうぐ。\nひっこめると　もとに　もどる。", "戦闘中の　ポケモンの\n素早さを　あげる　道具。\nひっこめると　元に　戻る。", "배틀 중인 포켓몬의 스피드를 올리는\n도구. 포켓몬을 볼에 넣어버리면\n원래대로 되돌아간다.", "Aumenta la Velocidad en combate.\nAl cambiar de Pokémon, el efecto\ndesaparece.") },
            { PBEItem.YacheBerry, new PBELocalizedString("Weakens a supereffective Ice-type\nattack against the holding Pokémon.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Glace.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Eis.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Ghiaccio\ndel nemico.", "ポケモンに　もたせると\nこうかばつぐんの　こおり　わざを\nうけたとき　いりょくが　よわまる。", "ポケモンに　持たせると\n効果バツグンの　こおり技を\n受けたとき　威力が　弱まる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 얼음 기술을\n당했을 때 위력이 약해진다.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Hielo\nde un enemigo.") },
            { PBEItem.YellowFlute, new PBELocalizedString("A toy flute made from yellow glass.\nA maniac will buy it for a high price.", "Jouet en forme de flûte en verre\njaune. Les amateurs en donnent\nun bon prix.", "Eine Spielzeugflöte aus gelbem Glas.\nSammler geben ihr letztes Hemd dafür\nher.", "Flauto giocattolo fatto di vetro giallo.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "きいろい　ガラスで　できている\nおもちゃの　ふえ。\nマニアが　たかく　かってくれる。", "黄色い　ガラスで　できている\nおもちゃの　笛。\nマニアが　高く　買ってくれる。", "노란 유리로 만들어진\n장난감 피리.\n마니아가 비싼 값에 산다.", "Una flauta de juguete hecha de\ncristal amarillo.\nMuy valorada por los coleccionistas.") },
            { PBEItem.YellowScarf, new PBELocalizedString("An item to be held by a Pokémon.\nIt boosts the Tough aspect of the\nholder in a Contest.", "Objet à tenir. Augmente la Robustesse\nd'un Pokémon dans les Concours.", "Ein Item zum Tragen. Trainer erhöhen\ndamit in Wettbewerben die „Stärke“\nihres Pokémon.", "Strumento da far tenere a un Pokémon.\nAumenta la “Grinta\" di chi lo tiene\nin una Gara.", "もたせて　コンテストに　さんかすると\nその　ポケモンは　いつもより\nたくましく　みられる。", "持たせて　コンテストに　参加すると\nその　ポケモンは　いつもより\nたくましく　みられる。", "지니게 하여 콘테스트에 참가하면\n그 포켓몬은 여느 때보다\n강인하게 보인다.", "Debe llevarlo un Pokémon.\nAumenta la dureza en los concursos.") },
            { PBEItem.YellowShard, new PBELocalizedString("A small yellow shard.\nIt appears to be from some sort of\nimplement made long ago.", "Petit tesson jaune.\nIl semble provenir d'un outil très\nancien.", "Eine gelbe Scherbe eines antiken\nWerkzeugs, das vor langer Zeit\nangefertigt wurde.", "Un piccolo coccio giallo che con ogni\nprobabilità è un frammento di un antico\nstrumento.", "きいろい　ちいさな　かけら。\nむかしに　つくられた　なにかの\nどうぐの　いちぶ　らしい。", "黄色い　小さな　かけら。\n昔に　つくられた　なにかの\n道具の　一部らしい。", "노란 작은 조각.\n옛날에 만들어진 어떤\n도구의 일부인 듯하다.", "Un pequeño fragmento amarillo.\nParece formar parte de algún tipo\nde herramienta antigua.") },
            { PBEItem.YlwApricorn, new PBELocalizedString("A yellow Apricorn.\nIt has an invigorating scent.", "Un Noigrume de couleur jaune.\nIl dégage un arôme délicat.", "Eine gelbe Aprikoko.\nSie riecht ein wenig unreif.", "Una Ghicocca di colore giallo.\nEmana un profumo delicato.", "きいろの　ぼんぐり。\nさわやかな　かおりが　する。\n", "きいろの　ぼんぐり。\nさわやかな　かおりが　する。\n", "노란색의 규토리.\n산뜻한 향기가 난다.\n", "Bonguri de color amarillo y aroma\nrefrescante.") },
            { PBEItem.ZapPlate, new PBELocalizedString("An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Electric-type moves.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Électrik.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Elektro.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Elettro.", "でんきの　タイプの　せきばん。\nもたせると　でんきタイプの\nわざの　いりょくが　つよまる。", "でんきの　タイプの　石盤。\n持たせると　でんきタイプの\n技の　威力が　強まる。", "전기타입의 석판.\n지니게 하면 전기타입\n기술의 위력이 강해진다.", "Tabla de piedra que potencia\nlos movimientos de tipo Eléctrico.\nDebe llevarla un Pokémon.") },
            { PBEItem.Zinc, new PBELocalizedString("A nutritious drink for Pokémon.\nIt raises the base Sp. Def (Special\nDefense) stat of a single Pokémon.", "Une boisson très nutritive qui monte\nla Défense Spéciale d'un Pokémon.", "Ein gehaltvolles Getränk, das die\nSpezial-Verteidigung eines Pokémon\ndauerhaft erhöht.", "Bevanda nutriente per Pokémon.\nAumenta la Difesa Speciale di base.", "ポケモンの　えいようドリンク。\nポケモン　１ひきの　とくぼうの\nきそポイントを　あげる。", "ポケモンの　栄養ドリンク。\nポケモン　１匹の　特防の\n基礎ポイントを　あげる。", "포켓몬의 영양 드링크.\n포켓몬 1마리의 특수방어\n기초 포인트를 올린다.", "Nutritiva bebida que aumenta la Defensa\nEspecial de base de un Pokémon.") },
            { PBEItem.ZoomLens, new PBELocalizedString("An item to be held by a Pokémon.\nIf the holder moves after its\ntarget, its accuracy will be boosted.", "Objet à tenir. Augmente la Précision\ndu porteur s'il attaque après l'ennemi.", "Ein Item zum Tragen. Agiert das\nZiel zuerst, erhöht sich die\nGenauigkeit des Trägers.", "Strumento da far tenere a un Pokémon.\nAumenta la precisione se chi lo tiene\nattacca dopo il nemico.", "もたせた　ポケモンが　あいてよりも\nこうどうするのが　おそいとき\nわざが　めいちゅう　しやすくなる。", "持たせた　ポケモンが　相手よりも\n行動するのが　遅いとき\n技が　命中しやすくなる。", "지니게 한 포켓몬이 상대보다도\n행동하는 것이 늦을 때\n기술이 명중하기 쉬워진다.", "Debe llevarlo un Pokémon. Si se mueve\ndespués del enemigo, aumenta la\nPrecisión del Pokémon.") }
        });
    }
}
