        public static ReadOnlyDictionary<PBEItem, PBELocalizedString> Descriptions { get; } = new ReadOnlyDictionary<PBEItem, PBELocalizedString>(new Dictionary<PBEItem, PBELocalizedString>()
        {
            { PBEItem.None, new PBELocalizedString("\xFF1F\xFF1F\xFF1F", "???", "???", "???", "???", "???", "???") },
            { PBEItem.AbsorbBulb, new PBELocalizedString("つかいすての　きゅうこん。\nもたせて　みずの　わざを　うけると\nとくこうが　あがる。", "단 한 번 쓰면 없어지는 구근.\n지니게 하고 물 기술을 받으면\n특수공격이 올라간다.", "Bulbe jetable. Tenu, il augmente\nl'Attaque Spéciale lorsque le Pokémon\nsubit une attaque de type Eau.", "Eine einmalig einsetzbare Knolle.\nErhöht den Spez.-Angr. des Trägers,\nwenn ihn eine Wasser-Attacke trifft.", "Raíz de usar y tirar que sube el Ataque\nEspecial cuando se recibe un ataque\nde tipo Agua.", "Bulbo monouso. Se il Pokémon che\nlo tiene subisce una mossa di tipo\nAcqua, il suo Attacco Speciale aumenta.", "A consumable bulb. If the holder is hit\nby a Water-type move, its Sp. Atk\nwill rise.") },
            { PBEItem.AdamantOrb, new PBELocalizedString("ディアルガに　もたせると　ドラゴンと\nはがねタイプの　わざの　いりょくが\nあがる　ひかり　かがやく　たま。", "디아루가에게 지니게 하면 드래곤과\n강철타입 기술의 위력이 올라가는\n반짝반짝 빛나는 구슬.", "Orbe très brillant destiné à Dialga.\nAugmente la puissance des capacités\nde type Dragon et Acier.", "Ein hell leuchtender Orb.\nVerstärkt Attacken vom Typ Drachen\nund Stahl, wenn Dialga ihn trägt.", "Una esfera brillante que potencia los\nmovimientos de tipo Acero y Dragón.\nDebe llevarla Dialga.", "Sfera molto luminosa da far tenere\na Dialga. Potenzia le mosse di tipo\nDrago e Acciaio.", "A brightly gleaming orb to be held by\nDialga. It boosts the power of Dragon-\nand Steel-type moves.") },
            { PBEItem.AguavBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　\xFF28\xFF30を　かいふくする。\nきらいな　あじだと　こんらん　する。", "포켓몬에게 지니게 하면\n위급할 때 HP를 회복한다.\n싫어하는 맛이면 혼란에 빠진다.", "Tenue, cette Baie restaure des PV en\ncas de besoin, mais rend confus si\nle Pokémon n'aime pas son goût.", "Als getragenes Item füllt die Beere die\nKP eines Pokémon auf, doch ihr Aroma\nverwirrt jene, die es nicht mögen.", "Los Pokémon pueden llevarla o usarla\npara restaurar algunos PS. Su sabor\npuede causar confusión.", "Se tenuta da un Pokémon in difficoltà,\nrestituisce PS. Se al Pokémon non piace\nil suo sapore, può causare confusione.", "If held by a Pokémon, it restores the\nuser's HP in a pinch, but will cause\nconfusion if it hates the taste.") },
            { PBEItem.AirBalloon, new PBELocalizedString("ポケモンに　もたせると\nポケモンが　ちゅうに　うく。\nこうげきを　うけると　われてしまう。", "포켓몬에게 지니게 하면\n포켓몬이 허공에 뜬다.\n공격을 받으면 터져버린다.", "Tenu par un Pokémon, cet objet lui\npermet de flotter dans les airs.\nIl éclate en cas d'attaque.", "Gibt man es einem Pokémon zum Tragen,\nfängt es an zu schweben. Wird es\ngetroffen, platzt das Item.", "El Pokémon que lo lleve flotará en el\naire. Si recibe un golpe, estallará.", "Permette al Pokémon che lo tiene di\nfluttuare in aria. Scoppia se è colpito\nda un attacco.", "When held by a Pokémon, the Pokémon\nwill float into the air. When the holder\nis attacked, this item will burst.") },
            { PBEItem.AmuletCoin, new PBELocalizedString("もたせた　ポケモンが　いちどでも\nせんとうに　かおを　だすと\nおかねが　\xFF12ばい　もらえる。", "지니게 한 포켓몬이\n한 번이라도 배틀에 나가면\n돈을 2배로 받을 수 있다.", "Objet à tenir. Double l'argent gagné\nsi le porteur se joint au combat.", "Ein Item, das ein Pokémon trägt und\ndas doppeltes Preisgeld bringt, wenn\nder Träger am Kampf teilnimmt.", "Si el Pokémon que la lleva lucha en\nun combate, duplica las ganancias.", "Strumento da far tenere a un Pokémon.\nRaddoppia i guadagni nella lotta se\nchi lo tiene vi partecipa.", "An item to be held by a Pokémon.\nIt doubles a battle's prize money if\nthe holding Pokémon joins in.") },
            { PBEItem.Antidote, new PBELocalizedString("スプレーしきの　くすり。\nポケモン　\xFF11ひきの　どくの\nじょうたいを　かいふくする。", "스프레이식의 약.\n포켓몬 1마리의\n독 상태를 회복한다.", "Un médicament sous forme de spray.\nGuérit un Pokémon empoisonné.", "Ein Spray, das Vergiftungen\neines Pokémon heilt.", "Medicina en espray que contrarresta\nlos efectos del veneno en un Pokémon.", "Rimedio spray.\nCura un Pokémon dall'avvelenamento.", "A spray-type medicine.\nIt lifts the effect of poison from one\nPokémon.") },
            { PBEItem.ApicotBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　じぶんの\nとくぼうが　あがる。", "포켓몬에게 지니게 하면\n위급할 때 자신의\n특수방어가 올라간다.", "Tenue, cette Baie monte la Déf. Spé.\nen cas de besoin.", "Als getragenes Item erhöht diese Beere\ndie Spezial-Verteidigung eines Pokémon,\nsobald es in einer Notlage steckt.", "Si la lleva un Pokémon, le sube\nla Defensa Especial en un momento\nde apuro.", "Aumenta la Difesa Speciale, se\nil Pokémon che la tiene si trova\nin difficoltà.", "If held by a Pokémon, it raises its\nSp. Def stat in a pinch.") },
            { PBEItem.ArmorFossil, new PBELocalizedString("おおむかし　ちじょうで　くらした\nこだい　ポケモンの　カセキ。\nえりかざりの　いちぶ　らしい。", "오랜 옛날 지상에서 살았던\n고대 포켓몬의 화석.\n깃 장식의 일부인 듯하다.", "Fossile d'un Pokémon préhistorique\nterrestre. On dirait une partie d'un\ncollier.", "Das Fossil eines uralten Pokémon,\ndas an Land lebte. Es sieht wie\nein Schlüsselbein aus.", "Fósil de un Pokémon prehistórico\nterrestre. Parece ser parte\nde un cuello.", "Fossile di un Pokémon preistorico che\nviveva sulla terraferma. Sembra far\nparte di un collare.", "A fossil from a prehistoric Pokémon\nthat lived on the land. It appears to\nbe part of a collar.") },
            { PBEItem.AspearBerry, new PBELocalizedString("ポケモンに　もたせると\nこおりを　かいふくする。\n", "포켓몬에게 지니게 하면\n얼음을 회복한다.\n", "Baie qui soigne le gel lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon zum\nTragen, heilt diese es von Frost.", "Los Pokémon pueden llevarla o usarla\npara descongelarse.", "Se tenuta da un Pokémon, lo cura\ndal congelamento.", "If held by a Pokémon,\nit defrosts it.") },
            { PBEItem.Awakening, new PBELocalizedString("スプレーしきの　くすり。\nポケモン　\xFF11ひきの　ねむりの\nじょうたいを　かいふくする。", "스프레이식의 약.\n포켓몬 1마리의\n잠듦 상태를 회복한다.", "Un médicament sous forme de spray.\nRéveille un Pokémon endormi.", "Ein Spray, das ein schlafendes\nPokémon aufweckt.", "Medicina en espray que libera un\nPokémon de las garras del sueño.", "Rimedio spray.\nSveglia un Pokémon addormentato.", "A spray-type medicine.\nIt awakens a Pokémon from the\nclutches of sleep.") },
            { PBEItem.BabiriBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　はがね　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 강철 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Acier.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Stahl.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Acero\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Acciaio\ndel nemico.", "Weakens a supereffective Steel-type\nattack against the holding Pokémon.") },
            { PBEItem.BalmMushroom, new PBELocalizedString("あたりいったいに　いい　かおりが\nひろがる　めずらしい　キノコ。\nマニアが　たかく　かってくれる。", "주변 일대에 좋은 향기가\n퍼지는 희귀한 버섯.\n마니아가 비싼 값에 산다.", "Champignon rare qui répand un\nagréable parfum autour de lui.\nLes amateurs en donnent un bon prix.", "Ein seltener Pilz, der ein angenehmes\nAroma verbreitet. Kenner geben ihr\nletztes Hemd dafür.", "Extraña seta que desprende un\nagradable aroma. Muy apreciada\npor los gourmets.", "Fungo raro che emana un profumo\ngradevolissimo. Si può vendere a un\nprezzo piuttosto alto agli intenditori.", "A rare mushroom which gives off a nice\nfragrance.\nA maniac will buy it for a high price.") },
            { PBEItem.BelueBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.BerryJuice, new PBELocalizedString("きのみ　\xFF11\xFF10\xFF10\xFF05の　ジュース。\nポケモン　\xFF11ひきの　\xFF28\xFF30を\n\xFF12\xFF10だけ　かいふくする。", "나무열매 100% 쥬스.\n포켓몬 1마리의 HP를\n20만큼 회복한다.", "Une boisson 100% pur jus de Baies.\nRestaure 20 PV à un Pokémon.", "100 %iger Fruchtsaft, aus Beeren\ngekeltert. Er stellt 20 KP eines\nPokémon wieder her.", "Zumo de Bayas 100% natural que\nrestaura 20 PS de un Pokémon.", "Bevanda a base di puro succo di Bacche.\nRestituisce 20 PS ad un Pokémon.", "A 100% pure juice made of Berries.\nIt restores the HP of one Pokémon by\njust 20 points.") },
            { PBEItem.BigMushroom, new PBELocalizedString("おおきな　めずらしい　キノコ。\nいちぶの　マニアの　あいだでは\nとても　にんきが　たかい。", "크고 진귀한 버섯.\n일부 마니아 사이에서는\n매우 인기가 높다.", "Un grand champignon plutôt rare.\nIl est très populaire parmi\ncertains fans dévoués.", "Ein großer und seltener Pilz. Liebhaber\ndieses Pilzes freuen sich ein Loch in\nden Bauch, wenn sie einen finden.", "Una gran seta que es poco común\ny muy popular entre determinados\ngrupos de gourmets.", "Fungo grande e raro. È molto\napprezzato da alcuni intenditori.", "A large and rare mushroom.\nIt is sought after by collectors.") },
            { PBEItem.BigNugget, new PBELocalizedString("キラキラと　きんいろに　ひかる\nじゅんきんせいの　おおきな　たま。\nマニアが　たかく　かってくれる。", "금색으로 반짝반짝 빛나는\n순금으로 만들어진 큰 구슬.\n마니아가 비싼 값에 산다.", "Grosse pépite d'or pur qui brille\nmagnifiquement.\nLes amateurs en donnent un bon prix.", "Ein großes Nugget aus purem Gold, das\neinen schimmernden Glanz besitzt.\nSammler zahlen einen hohen Preis dafür.", "Pepita grande de oro puro que\ndesprende un brillo espectacular.\nMuy valorada por los coleccionistas.", "Grossa pepita d'oro puro dal luccichio\nsfavillante. Si può vendere a un prezzo\npiuttosto alto agli intenditori.", "A big nugget of pure gold that gives off\na lustrous gleam.\nA maniac will buy it for a high price.") },
            { PBEItem.BigPearl, new PBELocalizedString("きれいな　ぎんいろに　ひかる\nかなり　おおつぶの　しんじゅ。\nショップで　たかく　うれる。", "예쁜 은색으로 빛나는\n상당히 큰 낱알의 진주.\n상점에서 비싸게 팔린다.", "Une grande perle qui brille d'une jolie\ncouleur argent. Peut être vendue à\nbon prix aux magasins.", "Eine relativ große Perle, die in\nschönem Silber funkelt. Sie ist\nvon hohem Wert.", "Brillante perla plateada de gran\ntamaño que puede venderse\na buen precio en las tiendas.", "Perla piuttosto grande dai graziosi\nriflessi argentei. Si può vendere\na un prezzo piuttosto alto nei negozi.", "A quite-large pearl that sparkles in a\npretty silver color. It can be sold at\na high price to shops.") },
            { PBEItem.BigRoot, new PBELocalizedString("もたせると　\xFF28\xFF30を　すいとる　わざで\nいつもより　おおく　じぶんの　\xFF28\xFF30を\nかいふくできる。", "지니게 하면 HP 흡수 기술을 썼을 때\n여느 때보다 많은 HP를\n자신이 회복할 수 있다.", "Objet à tenir augmentant la puissance\ndes attaques volant des PV.", "Ein Item zum Tragen. Verstärkt\nKP-stehlende Attacken.", "El Pokémon que la lleva recupera\nmás PS al usar movimientos que\ndrenan al objetivo.", "Strumento da far tenere a un Pokémon.\nPotenzia le mosse ruba-PS facendo\nrecuperare più PS a chi lo usa.", "A Pokémon held item that boosts the\npower of HP-stealing moves to let the\nholder recover more HP.") },
            { PBEItem.BindingBand, new PBELocalizedString("しめつけるわざを　ほじょする　バンド。\nもたせると　しめつけるわざの\nいりょくが　つよく　なる。", "조이기 기술을 보조하는 밴드.\n지니게 하면 조이기 기술의\n위력이 강해진다.", "Objet à tenir augmentant la puissance\ndes attaques immobilisantes telles\nque Ligotage ou Étreinte.", "Ein Band, das die Stärke aller\nKlammerattacken des Trägers\nerhöht.", "Banda que potencia los movimientos\nque estrujan al rival.", "Strumento da far tenere a un Pokémon\nche aumenta la potenza di mosse come\nAvvolgibotta e Legatutto.", "A band that increases the power of\nbinding moves when held.") },
            { PBEItem.BlackBelt, new PBELocalizedString("きが　ひきしまる　おび。\nもたせると　かくとうタイプの\nわざの　いりょくが　あがる。", "마음을 긴장시키는 띠.\n지니게 하면 격투타입\n기술의 위력이 올라간다.", "Objet à tenir. Ceinture qui augmente\nla détermination et la puissance des\ncapacités de type Combat.", "Ein Item zum Tragen. Der Gürtel\nverstärkt die Entschlossenheit\nund Attacken vom Typ Kampf.", "Debe llevarlo un Pokémon.\nAumenta la decisión y fortalece\nlos movimientos de tipo Lucha.", "Strumento da far tenere a un Pokémon.\nCintura che potenzia la determinazione\ne le mosse di tipo Lotta.", "An item to be held by a Pokémon.\nIt is a belt that boosts determination\nand Fighting-type moves.") },
            { PBEItem.BlackFlute, new PBELocalizedString("くろい　ガラスで　できている\nおもちゃの　ふえ。\nマニアが　たかく　かってくれる。", "검은 유리로 만들어진\n장난감 피리.\n마니아가 비싼 값에 산다.", "Jouet en forme de flûte en verre\nnoir. Les amateurs en donnent\nun bon prix.", "Eine Spielzeugflöte aus schwarzem Glas.\nSammler geben ihr letztes Hemd dafür\nher.", "Una flauta de juguete hecha de\ncristal negro.\nMuy valorada por los coleccionistas.", "Flauto giocattolo fatto di vetro nero.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A toy flute made from black glass.\nA maniac will buy it for a high price.") },
            { PBEItem.BlackGlasses, new PBELocalizedString("あやしく　みえる　メガネ。\nもたせると　あくタイプの\nわざの　いりょくが　あがる。", "괴상하게 보이는 안경.\n지니게 하면 악타입\n기술의 위력이 올라간다.", "Objet à tenir. Paire de lunettes à\nl'aspect louche qui monte la puissance\ndes capacités de type Ténèbres.", "Ein Item zum Tragen. Eine\ndunkle Brille, die Attacken\nvom Typ Unlicht verstärkt.", "Debe llevarlas un Pokémon.\nFortalecen los movimientos de\ntipo Siniestro.", "Strumento da far tenere a un Pokémon.\nInquietante paio di occhiali che\npotenzia le mosse di tipo Buio.", "An item to be held by a Pokémon.\nIt is a shady-looking pair of glasses\nthat boosts Dark-type moves.") },
            { PBEItem.BlackSludge, new PBELocalizedString("もたせると　どくタイプの　ポケモンは\nすこしずつ　\xFF28\xFF30を　かいふくする。\nそれいがいは　\xFF28\xFF30が　へってしまう。", "지니게 하면 독타입의 포켓몬은\n조금씩 HP를 회복한다.\n그 이외의 타입은 HP가 줄어 버린다.", "Objet à tenir restaurant peu à peu les\nPV des Pokémon de type Poison. Inflige\ndes dégâts à tous les autres types.", "Ein Item zum Tragen, das einem\nGift-Pokémon KP zurückgibt, anderen\nPokémon aber Schaden zufügt.", "Restaura gradualmente los PS\nde los Pokémon de tipo Veneno\ny daña a todos los demás.", "Strumento da far tenere a un Pokémon:\nridà gradualmente PS ai Pokémon di\ntipo Veleno. Danneggia tutti gli altri.", "A held item that gradually restores\nthe HP of Poison-type Pokémon.\nIt inflicts damage on all other types.") },
            { PBEItem.BlkApricorn, new PBELocalizedString("くろい　ぼんぐり。\nなんともいえない　においがする。\n", "검은색의 규토리.\n말로는 표현할 수 없는 향기가 난다.\n", "Un Noigrume de couleur noire.\nIl dégage un arôme indéfinissable.", "Eine schwarze Aprikoko. Sie riecht\nirgendwie unbeschreiblich.", "Bonguri de color negro y aroma\nindescriptible.", "Una Ghicocca di colore nero.\nEmana un odore indefinibile.", "A black Apricorn\nIt has an indescribable scent.") },
            { PBEItem.BluApricorn, new PBELocalizedString("あおい　ぼんぐり。\nすこし　あおくさい　かおりがする。\n", "파란색의 규토리.\n약간 풋내가 난다.\n", "Un Noigrume de couleur bleue.\nIl dégage un arôme un peu aigre.", "Eine blaue Aprikoko. Ein frischer\nGeruch geht von ihr aus.", "Bonguri de color azul y aroma tierno.", "Una Ghicocca di colore blu.\nEmana un odore un po' aspro.", "A blue Apricorn.\nIt smells a bit like grass.") },
            { PBEItem.BlueFlute, new PBELocalizedString("あおい　ガラスで　できている\nおもちゃの　ふえ。\nマニアが　たかく　かってくれる。", "파란 유리로 만들어진\n장난감 피리.\n마니아가 비싼 값에 산다.", "Jouet en forme de flûte en verre\nbleu. Les amateurs en donnent\nun bon prix.", "Eine Spielzeugflöte aus blauem Glas.\nSammler geben ihr letztes Hemd dafür\nher.", "Una flauta de juguete hecha\nde cristal azul.\nMuy valorada por los coleccionistas.", "Flauto giocattolo fatto di vetro blu.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A toy flute made from blue glass.\nA maniac will buy it for a high price.") },
            { PBEItem.BlueScarf, new PBELocalizedString("もたせて　コンテストに　さんかすると\nその　ポケモンは　いつもより\nうつくしく　みられる。", "지니게 하여 콘테스트에 참가하면\n그 포켓몬은 여느 때보다\n아름답게 보인다.", "Objet à tenir. Augmente la Beauté\nd'un Pokémon dans les Concours.", "Ein Item zum Tragen. Trainer erhöhen\ndamit in Wettbewerben die „Schönheit“\nihres Pokémon.", "Debe llevarlo un Pokémon.\nAumenta la belleza en los concursos.", "Strumento da far tenere a un Pokémon.\nAumenta la “Bellezza\" di chi lo tiene\nin una Gara.", "An item to be held by a Pokémon.\nIt boosts the Beauty aspect of the\nholder in a Contest.") },
            { PBEItem.BlueShard, new PBELocalizedString("あおい　ちいさな　かけら。\nむかしに　つくられた　なにかの\nどうぐの　いちぶ　らしい。", "파란 작은 조각.\n옛날에 만들어진 어떤\n도구의 일부인 듯하다.", "Petit tesson bleu.\nIl semble provenir d'un outil très\nancien.", "Eine blaue Scherbe eines antiken\nWerkzeugs, das vor langer Zeit\nangefertigt wurde.", "Un pequeño fragmento azul.\nParece formar parte de algún tipo\nde herramienta antigua.", "Un piccolo coccio blu che con ogni\nprobabilità è un frammento di un antico\nstrumento.", "A small blue shard.\nIt appears to be from some sort of\nimplement made long ago.") },
            { PBEItem.BlukBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.BridgeMailD, new PBELocalizedString("まっかな　はねばしの　えがらが\nプリントされた　びんせん。\nポケモンに　もたせて　つかう。", "새빨간 도개교의 무늬가\n프린트된 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Un papier à lettres avec un motif de\npont mobile rouge. Peut être tenu\net livré par un Pokémon.", "Das Briefpapier hat eine knallrote\nZugbrücke als Hintergrund.\nGib es einem Pokémon zum Tragen.", "Carta con la imagen de un puente\nlevadizo de color rojo. Haz que\nla entregue un Pokémon.", "Carta da lettera che riporta l'immagine\ndi un ponte mobile di color rosso fuoco.\nUn Pokémon può tenerla e consegnarla.", "Stationery featuring a print of\na red drawbridge.\nLet a Pokémon hold it for use.") },
            { PBEItem.BridgeMailM, new PBELocalizedString("アーチを　えがいた　はしの　えがらが\nプリントされた　びんせん。\nポケモンに　もたせて　つかう。", "아치가 그려진 다리 무늬가\n프린트된 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Un papier à lettres avec un motif de\npont bâti sur des arches. Peut être\ntenu et livré par un Pokémon.", "Das Briefpapier hat eine geschwungene\nBrücke als Hintergrundmotiv.\nGib es einem Pokémon zum Tragen.", "Carta con la imagen de un puente\ncon forma de varios arcos. Haz\nque la entregue un Pokémon.", "Carta da lettera che riporta l'immagine\ndi un ponte ad archi.\nUn Pokémon può tenerla e consegnarla.", "Stationery featuring a print of\nan arched bridge.\nLet a Pokémon hold it for use.") },
            { PBEItem.BridgeMailS, new PBELocalizedString("そらを　つらぬく　はしの　えがらが\nプリントされた　びんせん。\nポケモンに　もたせて　つかう。", "하늘을 관통하는 다리의 무늬가\n프린트된 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Un papier à lettres avec un motif de\npont traversant le ciel. Peut être\ntenu et livré par un Pokémon.", "Das Briefpapier hat eine weit in den\nHimmel ragende Brücke als Hintergrund.\nGib es einem Pokémon zum Tragen.", "Carta con la imagen de un puente\nque se alza hacia el cielo. Haz que\nla entregue un Pokémon.", "Carta da lettera che riporta l'immagine\ndi un ponte che attraversa il cielo.\nUn Pokémon può tenerla e consegnarla.", "Stationery featuring a print of\na sky-piercing bridge.\nLet a Pokémon hold it for use.") },
            { PBEItem.BridgeMailT, new PBELocalizedString("こうてつの　つりばしの　えがらが\nプリントされた　びんせん。\nポケモンに　もたせて　つかう。", "강철의 현수교 무늬가\n프린트된 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Un papier à lettres avec un motif de\npont suspendu en acier. Peut être\ntenu et livré par un Pokémon.", "Das Briefpapier hat eine stählerne\nHängebrücke als Hintergrund.\nGib es einem Pokémon zum Tragen.", "Carta con la imagen de un puente\ncolgante de acero. Haz que la\nentregue un Pokémon.", "Carta da lettera che riporta l'immagine\ndi un ponte sospeso in acciaio.\nUn Pokémon può tenerla e consegnarla.", "Stationery featuring a print of\na steel suspension bridge.\nLet a Pokémon hold it for use.") },
            { PBEItem.BridgeMailV, new PBELocalizedString("レンガづくりの　はしの　えがらが\nプリントされた　びんせん。\nポケモンに　もたせて　つかう。", "벽돌로 만든 다리 무늬가\n프린트된 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Un papier à lettres avec un motif\nde pont en briques. Peut être tenu\net livré par un Pokémon.", "Das Briefpapier hat eine Brücke\naus Ziegelsteinen als Hintergrund.\nGib es einem Pokémon zum Tragen.", "Carta con la imagen de un puente\nde ladrillos. Haz que la entregue\nun Pokémon.", "Carta da lettera che riporta l'immagine\ndi un ponte fatto di mattoni.\nUn Pokémon può tenerla e consegnarla.", "Stationery featuring a print of\na brick bridge.\nLet a Pokémon hold it for use.") },
            { PBEItem.BrightPowder, new PBELocalizedString("キラキラ　ひかるこな。　もたせると\nひかりが　あいてを　まどわして\nわざが　めいちゅう　しにくくなる。", "반짝반짝 빛나는 가루. 지니게 하면\n빛이 상대를 혼란시켜\n기술이 명중되기 어려워진다.", "Objet à tenir qui intimide et baisse\nla Précision de l'ennemi.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nsenkt die Genauigkeit des Gegners.", "Lanza un destello que baja la Precisión\ndel enemigo. Debe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nEmette un bagliore insidioso che riduce\nla precisione dell'avversario.", "An item to be held by a Pokémon.\nIt casts a tricky glare that lowers\nthe opponent's accuracy.") },
            { PBEItem.BugGem, new PBELocalizedString("むしの　タイプの　ジュエル。\nもたせると　いちどだけ　むしの\nわざの　いりょくが　つよまる。", "벌레타입의 주얼.\n지니게 하면 한 번만 벌레\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Insecte.", "Ein Juwel vom Typ Käfer. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Käfer.", "Joya de un solo uso que potencia\nlos movimientos de tipo Bicho.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Coleottero.", "A gem with an insect-like essence.\nWhen held, it strengthens the power of\na Bug-type move only once.") },
            { PBEItem.BurnDrive, new PBELocalizedString("ゲノセクトに　もたせると\nテクノバスターという　わざが\nほのおタイプになる　カセット。", "게노세크트에게 지니게 하면\n테크노버스터라는 기술이\n불꽃타입으로 바뀌는 카세트.", "Tenu par Genesect, ce module\nchange la capacité TechnoBuster\nen une capacité de type Feu.", "Gibt man es Genesect zum Tragen,\nwandelt es den Typ der Attacke\nTechblaster in Feuer um.", "Cartucho que convierte\nTecno Shock de Genesect\nen un movimiento de tipo Fuego.", "Modulo da far tenere a Genesect.\nTrasforma Tecnobotto in una mossa\ndi tipo Fuoco.", "A cassette to be held by Genesect.\nIt changes Techno Blast to a\nFire-type move.") },
            { PBEItem.BurnHeal, new PBELocalizedString("スプレーしきの　くすり。\nポケモン　\xFF11ひきの　やけどの\nじょうたいを　かいふくする。", "스프레이식의 약.\n포켓몬 1마리의\n화상 상태를 회복한다.", "Un médicament sous forme de spray.\nSoigne les brûlures d'un Pokémon.", "Ein Spray, das Verbrennungen\neines Pokémon heilt.", "Medicina en espray que cura\nlas quemaduras a un Pokémon.", "Rimedio spray.\nCura un Pokémon dalla scottatura.", "A spray-type medicine.\nIt heals a single Pokémon that is\nsuffering from a burn.") },
            { PBEItem.Calcium, new PBELocalizedString("ポケモンの　えいようドリンク。\nポケモン　\xFF11ひきの　とくこうの\nきそポイントを　あげる。", "포켓몬의 영양 드링크.\n포켓몬 1마리의 특수공격\n기초 포인트를 올린다.", "Une boisson très nutritive qui\nmonte l'Attaque Spéciale de\nbase d'un Pokémon.", "Ein gehaltvolles Getränk, das den\nSpezial-Angriff eines Pokémon\ndauerhaft erhöht.", "Nutritiva bebida que aumenta el Ataque\nEspecial de base de un Pokémon.", "Bevanda nutriente per Pokémon.\nAumenta l'Attacco Speciale di base.", "A nutritious drink for Pokémon.\nIt raises the base Sp. Atk (Special\nAttack) stat of a single Pokémon.") },
            { PBEItem.Carbos, new PBELocalizedString("ポケモンの　えいようドリンク。\nポケモン　\xFF11ひきの　すばやさの\nきそポイントを　あげる。", "포켓몬의 영양 드링크.\n포켓몬 1마리의 스피드\n기초 포인트를 올린다.", "Une boisson très nutritive qui monte\nla Vitesse de base d'un Pokémon.", "Ein gehaltvolles Getränk, das den\nInitiative-Wert eines Pokémon\ndauerhaft erhöht.", "Nutritiva bebida que aumenta la\nVelocidad de base de un Pokémon.", "Bevanda nutriente per Pokémon.\nAumenta la Velocità di base.", "A nutritious drink for Pokémon.\nIt raises the base Speed stat of a\nsingle Pokémon.") },
            { PBEItem.Casteliacone, new PBELocalizedString("ヒウンシティの　めいぶつアイス。\nポケモン　\xFF11ひきの　じょうたいの\nいじょうを　すべて　かいふくする。", "구름시티의 명물 아이스.\n포켓몬 1마리의 상태 이상을\n모두 회복한다.", "Spécialité de Volucité. Elle\nsoigne tous les problèmes\nde statut d'un Pokémon.", "Dieses Eis ist eine Spezialität\naus Stratos City. Es behebt alle\nStatusprobleme eines Pokémon.", "Especialidad de Ciudad Porcelana.\nCura los problemas de estado de\nun Pokémon.", "Il famoso gelato di Austropoli. Cura\ntutti i problemi di stato di un Pokémon.", "Castelia City's specialty, soft-serve\nice cream. It heals all the status\nproblems of a single Pokémon.") },
            { PBEItem.CellBattery, new PBELocalizedString("つかいすての　じゅうでんち。\nもたせて　でんきの　わざを　うけると\nこうげきが　あがる。", "단 한 번 쓰면 없어지는 충전지.\n지니게 하고 전기 기술을 받으면\n공격이 올라간다.", "Pile jetable. Tenue, elle augmente\nl'Attaque lorsque le Pokémon subit\nune attaque de type Électrik.", "Ein einmalig aufladbarer Akku.\nErhöht den Angriff des Trägers, wenn\nihn eine Elektro-Attacke trifft.", "Batería de usar y tirar que sube\nel Ataque de quien la lleva cuando\nrecibe un golpe de tipo Eléctrico.", "Pila monouso. Se il Pokémon che la\ntiene subisce un attacco di tipo\nElettro, il suo Attacco aumenta.", "A consumable battery. If the\nholder is hit by an Electric-type\nmove, its Attack will rise.") },
            { PBEItem.Charcoal, new PBELocalizedString("ものを　もやす　ねんりょう。\nもたせると　ほのおタイプの\nわざの　いりょくが　あがる。", "물건을 태우는 연료.\n지니게 하면 불꽃타입\n기술의 위력이 올라간다.", "Objet à tenir. Combustible montant la\npuissance des capacités de type Feu.", "Ein Item zum Tragen. Ein Brennstoff,\nder Attacken vom Typ Feuer verstärkt.", "Actúa como combustible y potencia\nlos movimientos de tipo Fuego.\nDebe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nSostanza combustibile che potenzia\nle mosse di tipo Fuoco.", "An item to be held by a Pokémon.\nIt is a combustible fuel that boosts\nthe power of Fire-type moves.") },
            { PBEItem.ChartiBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　いわ　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 바위 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Roche.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Gestein.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Roca\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Roccia\ndel nemico.", "Weakens a supereffective Rock-type\nattack against the holding Pokémon.") },
            { PBEItem.CheriBerry, new PBELocalizedString("ポケモンに　もたせると\nまひを　かいふくする。\n", "포켓몬에게 지니게 하면\n마비를 회복한다.\n", "Baie qui soigne la paralysie lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon zum\nTragen, heilt diese es von Paralyse.", "Los Pokémon pueden llevarla o usarla\npara recuperarse de la parálisis.", "Se tenuta da un Pokémon, lo cura\ndalla paralisi.", "If held by a Pokémon,\nit recovers from paralysis.") },
            { PBEItem.CherishBall, new PBELocalizedString("なにかの　きねんの　しなとして\nとくべつに　つくられた\nかなり　めずらしい　ボール。", "무언가의 기념품으로\n특별히 만들어진\n상당히 진귀한 볼.", "Une Poké Ball plutôt rare qui fut créée\npour une occasion spéciale.", "Ein seltener Ball, der als\nReminiszenz an irgendein\nEreignis hergestellt wurde.", "Es una Poké Ball bastante singular\nque se creó para conmemorar\nalguna ocasión especial.", "Poké Ball molto esclusiva creata per\ncommemorare un evento speciale.", "A quite rare Poké Ball that has been\nspecially crafted to commemorate\nan occasion of some sort.") },
            { PBEItem.ChestoBerry, new PBELocalizedString("ポケモンに　もたせると\nねむりを　かいふくする。\n", "포켓몬에게 지니게 하면\n잠듦을 회복한다.\n", "Baie qui soigne le sommeil lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon\nzum Tragen, erwacht es dadurch\naus dem Schlaf.", "Los Pokémon pueden llevarla o usarla\npara despertar del sueño.", "Se tenuta da un Pokémon, lo risveglia\ndal sonno.", "If held by a Pokémon,\nit recovers from sleep.") },
            { PBEItem.ChilanBerry, new PBELocalizedString("ポケモンに　もたせると\nノーマル　わざを　うけたとき\nいりょくが　よわまる。", "포켓몬에게 지니게 하면\n노말 기술을 당했을 때\n위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie de type Normal.", "Als getragenes Item schwächt diese\nBeere Attacken des Angreifers vom\nTyp Normal.", "Si la lleva un Pokémon, debilita un\nataque de tipo Normal de un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco di tipo Normale del nemico.", "Weakens a Normal-type attack against\nthe Pokémon holding this berry.") },
            { PBEItem.ChillDrive, new PBELocalizedString("ゲノセクトに　もたせると\nテクノバスターという　わざが\nこおりタイプになる　カセット。", "게노세크트에게 지니게 하면\n테크노버스터라는 기술이\n얼음타입으로 바뀌는 카세트.", "Tenu par Genesect, ce module\nchange la capacité TechnoBuster\nen une capacité de type Glace.", "Gibt man es Genesect zum Tragen,\nwandelt es den Typ der Attacke\nTechblaster in Eis um.", "Cartucho que convierte\nTecno Shock de Genesect\nen un movimiento de tipo Hielo.", "Modulo da far tenere a Genesect.\nTrasforma Tecnobotto in una mossa\ndi tipo Ghiaccio.", "A cassette to be held by Genesect.\nIt changes Techno Blast to an\nIce-type move.") },
            { PBEItem.ChoiceBand, new PBELocalizedString("ちょっと　こだわった　ハチマキ。\nもたせると　こうげきは　あがるが\nおなじ　わざしか　だせなくなる。", "기술을 구애받는 머리띠.\n지니게 하면 공격은 올라가지만\n똑같은 기술밖에 쓰지 못한다.", "Objet à tenir. Ce bandeau monte\nl'Attaque, mais ne permet d'utiliser\nqu'une seule capacité par combat.", "Ein Item, das ein Pokémon tragen\nkann. Es stärkt den Angriffs-Wert,\naber nur eine Attacke ist einsetzbar.", "Aumenta el Ataque, pero solo permite\nusar un movimiento.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nQuesta benda aumenta l'Attacco, ma\npermette l'uso di una sola mossa.", "An item to be held by a Pokémon.\nThis headband ups Attack, but allows\nthe use of only one of its moves.") },
            { PBEItem.ChoiceScarf, new PBELocalizedString("ちょっと　こだわった　スカーフ。\nもたせると　すばやさは　あがるが\nおなじ　わざしか　だせなくなる。", "기술을 구애받는 스카프.\n지니게 하면 스피드는 올라가지만\n같은 기술밖에 쓸 수 없다.", "Objet à tenir augmentant la Vitesse,\nmais ne permettant d'utiliser qu'une\nseule capacité par combat.", "Ein Item zum Tragen. Der Schal\nerhöht den Initiative-Wert, aber nur\neine Attacke ist einsetzbar.", "Debe llevarlo un Pokémon. Potencia\nla Velocidad, pero solo permite usar\nun movimiento.", "Strumento da far tenere a un Pokémon.\nStola che aumenta la Velocità, ma\nconsente l'uso di una sola mossa.", "An item to be held by a Pokémon.\nThis scarf boosts Speed, but allows\nthe use of only one of its moves.") },
            { PBEItem.ChoiceSpecs, new PBELocalizedString("ちょっと　こだわった　メガネ。\nもたせると　とくこうは　あがるが\nおなじ　わざしか　だせなくなる。", "기술을 구애받는 안경.\n지니게 하면 특수공격이 올라가지만\n같은 기술밖에 쓸 수 없다.", "Objet à tenir augmentant l'Attaque\nSpéciale, mais ne permettant d'utiliser\nqu'une seule capacité par combat.", "Ein Item zum Tragen. Die Brille\nerhöht den Spezial-Angriff, aber\nnur eine Attacke ist einsetzbar.", "Potencian el Ataque Especial, pero solo\npermiten usar un movimiento.\nDebe llevarlas un Pokémon.", "Strumento da far tenere a un Pokémon.\nQuesti occhiali aumentano l'Att. Sp., ma\npermettono l'uso di una sola mossa.", "An item to be held by a Pokémon. These\ndistinctive glasses boost Sp. Atk but\nallow the use of only one of its moves.") },
            { PBEItem.ChopleBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　かくとう　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 격투 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Combat.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Kampf.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Lucha\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Lotta\ndel nemico.", "Weakens a supereffective Fighting-type\nattack against the holding Pokémon.") },
            { PBEItem.ClawFossil, new PBELocalizedString("おおむかし　うみに　すんでいた\nこだい　ポケモンの　カセキ。\nツメの　いちぶ　らしい。", "오랜 옛날 바다에 살았던\n고대 포켓몬의 화석.\n발톱의 일부인 듯하다.", "Fossile d'un ancien Pokémon aquatique.\nOn dirait un morceau de griffe.", "Das Fossil eines uralten Pokémon,\ndas in der Tiefsee lebte. Es sieht\nwie eine Klaue aus.", "Fósil de un Pokémon ancestral que\nvivió en el fondo del mar. Parece\nser parte de una garra.", "Fossile di un antico Pokémon che\nviveva in mare. Sembra essere\nparte di un artiglio.", "A fossil of an ancient Pokémon that\nlived in the sea. It appears to be\npart of a claw.") },
            { PBEItem.CleanseTag, new PBELocalizedString("いちばん　まえの　ポケモンに\nもたせると　やせいポケモンが\nでて　きにくくなる。", "맨 앞의 포켓몬에게\n지니게 하면 야생 포켓몬이\n나타날 확률이 낮아진다.", "Objet à tenir. Aide à repousser les\nPokémon sauvages si le porteur est\nle premier Pokémon de l'équipe.", "Ein Item zum Tragen. Reduziert\nBegegnungen mit wilden Pokémon, wenn\nder Träger an erster Stelle steht.", "Si lo lleva el primer Pokémon del\nequipo, se reduce la probabilidad de\nque se acerquen Pokémon salvajes.", "Strumento da far tenere a un Pokémon.\nAiuta ad allontanare i Pokémon selvatici\nse chi lo tiene è il primo della squadra.", "An item to be held by a Pokémon.\nIt helps keep wild Pokémon away if the\nholder is the first one in the party.") },
            { PBEItem.CleverWing, new PBELocalizedString("ポケモンに　つかう　どうぐ。\nポケモン　\xFF11ひきの　とくぼうの\nきそポイントを　ちょっと　あげる。", "포켓몬에게 사용하는 도구.\n포켓몬 1마리의 특수방어\n기초 포인트를 조금 올린다.", "Objet à utiliser sur un Pokémon.\nIl augmente un peu sa\nDéfense Spéciale de base.", "Schenkt man einem Pokémon dieses\nItem, wird dessen Spezial-Verteidigung\ndauerhaft erhöht.", "Pluma que aumenta un poco\nla Defensa Especial de un Pokémon\nal subir de nivel.", "Strumento da usare su un Pokémon.\nNe aumenta di poco la Difesa Speciale\ndi base.", "An item for use on a Pokémon. It\nslightly increases the base Sp. Def\nstat of a single Pokémon.") },
            { PBEItem.CobaBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　ひこう　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 비행 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Vol.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Flug.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Volador\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Volante\ndel nemico.", "Weakens a supereffective Flying-type\nattack against the holding Pokémon.") },
            { PBEItem.ColburBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　あく　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 악 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une\nattaque ennemie super efficace\nde type Ténèbres.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Unlicht.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Siniestro\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Buio\ndel nemico.", "Weakens a supereffective Dark-type\nattack against the holding Pokémon.") },
            { PBEItem.CometShard, new PBELocalizedString("すいせいが　ちかづいたとき\nちひょうに　おちた　かけら。\nマニアが　たかく　かってくれる。", "혜성이 가까워졌을 때\n지표면에 떨어진 조각.\n마니아가 비싼 값에 산다.", "Un fragment de comète, tombé au sol\nquand elle traversait l'atmosphère.\nLes amateurs en donnent un bon prix.", "Ein Kometensplitter, der aus dem All\nherabgefallen ist. Sammler geben ihr\nletztes Hemd dafür.", "Fragmento de un cometa caído al\nsuelo al atravesar la atmósfera.\nMuy valorado por los coleccionistas.", "Scheggia di cometa caduta al suolo.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A shard which fell to the ground when\na comet approached.\nA maniac will buy it for a high price.") },
            { PBEItem.CornnBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.CoverFossil, new PBELocalizedString("おおむかし　うみに　すんでいた\nこだい　ポケモンの　カセキ。\nせなかの　いちぶ　らしい。", "오랜 옛날 바다에 살았던\n고대 포켓몬의 화석.\n등의 일부인 듯하다.", "Fossile d'un ancien Pokémon\naquatique. Il semble que ce\nsoit une partie de son dos.", "Das Fossil eines uralten Pokémon,\ndas in der Tiefsee lebte. Offenbar\nhandelt sich um einen Teil des Rückens.", "Fósil de un Pokémon ancestral que\nvivió en el fondo del mar. Parece ser\nun fragmento de concha marina.", "Fossile di un Pokémon preistorico che\nviveva nel mare. Sembra essere parte\ndel suo dorso.", "A fossil of an ancient Pokémon that\nlived in the sea in ancient times.\nIt appears to be part of its back.") },
            { PBEItem.CustapBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　いちどだけ\nこうどうが　はやくなる。", "포켓몬에게 지니게 하면\n위급할 때 한 번만\n행동이 빨라진다.", "Tenue, cette Baie permet d'attaquer\nen premier une fois en cas de besoin.", "Als getragenes Item ermöglicht\ndiese Beere dem Träger in einer\nNotlage den Erstschlag.", "El Pokémon que la lleva puede actuar\nen primer lugar una vez cuando se\nencuentra en un momento de apuro.", "Se il Pokémon che ce l'ha è in difficoltà,\ngli consente di fare per primo la sua\nmossa. Funziona una sola volta.", "If held by a Pokémon, it gets to move\nfirst just once in a pinch.") },
            { PBEItem.DampMulch, new PBELocalizedString("きのみを　そだてる　ちほうでは\nふかふかのつちに　まいて　つかう。\nマニアが　たかく　かってくれる。", "나무열매를 키우는 지방에서는\n푹신푹신한 땅에 뿌려 쓴다.\n마니아가 비싼 값에 산다.", "Un engrais utilisé sur sol meuble.\nLes amateurs en donnent un bon prix.", "In Regionen, wo man Beeren anbaut,\nmengt man ihn unter weiche Erde.\nSammler geben ihr letztes Hemd dafür.", "Un fertilizante usado en suelo fértil\ndonde crecen las Bayas.\nMuy apreciado por los coleccionistas.", "Usato per favorire la crescita delle\nBacche nelle regioni dove si coltivano.\nGli intenditori pagano caro per averlo.", "A fertilizer to be spread on soft soil\nin regions where Berries are grown.\nA maniac will buy it for a high price.") },
            { PBEItem.DampRock, new PBELocalizedString("もたせた　ポケモンが　あまごいを\nつかうと　いつもより　あめの\nじかんが　ながくなる。", "지니게 한 포켓몬이 비바라기를\n쓰면 여느 때보다 비내리는\n시간이 길어진다.", "Objet à tenir augmentant la durée\nde la capacité Danse Pluie utilisée\npar le porteur.", "Ein Item zum Tragen. Verlängert\ndie Dauer von Regentanz, wenn\nvon Träger eingesetzt.", "Prolonga la duración del movimiento\nDanza Lluvia que use el Pokémon\nque la lleva.", "Strumento da far tenere a un Pokémon\nche allunga la durata della mossa\nPioggiadanza se usata da chi lo tiene.", "A Pokémon held item that extends the\nduration of the move Rain Dance used\nby the holder.") },
            { PBEItem.DarkGem, new PBELocalizedString("あくの　タイプの　ジュエル。\nもたせると　いちどだけ　あくの\nわざの　いりょくが　つよまる。", "악타입의 주얼.\n지니게 하면 한 번만 악\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Ténèbres.", "Ein Juwel vom Typ Unlicht. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Unlicht.", "Joya de un solo uso que potencia\nlos movimientos de tipo Siniestro.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Buio.", "A gem with an essence of darkness.\nWhen held, it strengthens the power of\na Dark-type move only once.") },
            { PBEItem.DawnStone, new PBELocalizedString("ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nひとみのように　まばゆい。", "어느 특정 포켓몬을\n진화시키는 이상한 돌.\n눈동자처럼 아름답다.", "Pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle brille comme un œil.", "Dieser besondere, glitzernde Stein\nhilft manchen Pokémon bei\nihrer Evolution.", "Una piedra peculiar que hace\nevolucionar a algunos Pokémon.\nBrilla como un lucero.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nÈ luccicante come gli occhi.", "A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt sparkles like eyes.") },
            { PBEItem.DeepSeaScale, new PBELocalizedString("パールルに　もたせると\nとくぼうが　あがる　ウロコ。\nうすい　ピンクいろに　ひかる。", "진주몽에게 지니게 하면\n특수방어가 올라가는 비늘.\n연한 분홍색으로 빛난다.", "Objet à faire tenir à Coquiperl.\nÉcaille de couleur rose montant\nsa Défense Spéciale.", "Ein Item, das von Perlu getragen\nwerden kann. Es schimmert pink\nund hebt die Spezial-Verteidigung.", "Tiene un débil brillo rosado\ny debe llevarla Clamperl.\nSube la Defensa Especial.", "Strumento da far tenere a Clamperl.\nSquama dai bagliori rosei che aumenta\nla Difesa Speciale.", "An item to be held by Clamperl.\nA scale that shines a faint pink, it\nraises the Sp. Def stat.") },
            { PBEItem.DeepSeaTooth, new PBELocalizedString("パールルに　もたせると\nとくこうが　あがる　キバ。\nするどく　ぎんいろに　ひかる。", "진주몽에게 지니게 하면\n특수공격이 올라가는 이빨.\n날카로운 은색으로 빛난다.", "Objet à faire tenir à Coquiperl.\nDent de couleur argent montant\nson Attaque Spéciale.", "Ein Item, das von Perlu getragen\nwerden kann. Es schimmert silbern\nund hebt den Spezial-Angriff.", "Tiene el brillo afilado de la plata\ny debe llevarlo Clamperl.\nSube el Ataque Especial.", "Strumento da far tenere a Clamperl.\nDente dai bagliori argentei che aumenta\nl'Attacco Speciale.", "An item to be held by Clamperl.\nA fang that gleams a sharp silver, it\nraises the Sp. Atk stat.") },
            { PBEItem.DestinyKnot, new PBELocalizedString("ほそくて　ながい　まっかな　いと。\nもたせると　メロメロに　なったとき\nあいても　メロメロに　できる。", "가늘고 긴 새빨간 실.\n지니게 하면 자신이 헤롱헤롱해졌을 때\n상대도 헤롱헤롱해지게 된다.", "Un long fil rouge vif à faire tenir à un\nPokémon. Si le porteur succombe à\nl'attirance, l'ennemi aussi.", "Ein Item zum Tragen. Wird\nder Träger betört, gilt\ndies auch für den Angreifer.", "Un hilo largo y delgado de color rojo\nque transmite el enamoramiento del\nPokémon que lo lleva a su objetivo.", "Gomitolo di filo rosso sottile da dare\na un Pokémon. Se il Pokémon rimane\ninfatuato, contagia anche il nemico.", "A long, thin, bright-red string to\nbe held by a Pokémon. If the holder\nbecomes infatuated, the foe does too.") },
            { PBEItem.DireHit, new PBELocalizedString("きゅうしょりつが　おおきく　あがる。\nいちどしか　つかうことが　できない。\nひっこめると　もとに　もどる。", "급소 명중률이 크게 올라간다.\n한 번밖에 쓸 수 없다. 포켓몬을 볼에\n넣어버리면 원래대로 되돌아간다.", "Monte fortement le taux de critiques.\nUtilisable une seule fois. L'effet\ndisparaît si le Pokémon se retire.", "Hebt einmalig die Volltrefferquote des\nPokémon im Kampf stark an. Die Wirkung\nendet mit dem Austausch des Pokémon.", "Aumenta el índice de golpe crítico\nen combate. Al cambiar de\nPokémon, el efecto desaparece.", "Brutti colpi molto più probabili.\nSi può usare una sola volta e l'effetto\nsvanisce se il Pokémon è sostituito.", "It raises the critical-hit ratio greatly.\nIt can be used only once and wears off\nif the Pokémon is withdrawn.") },
            { PBEItem.DiveBall, new PBELocalizedString("みずの　せかいで　くらしている\nポケモンが　つかまえやすくなる\nちょっと　かわった　ボール。", "물의 세계에서 사는\n포켓몬을 잡기 쉬운\n조금 특이한 볼.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien\nsur les Pokémon sous-marins.", "Ein Ball, mit dem sich Pokémon,\ndie im Wasser leben, besser\nfangen lassen.", "Es algo distinta a las demás. Funciona\nespecialmente bien con los Pokémon\nque viven bajo el agua.", "Poké Ball un po' diversa. Risulta\nparticolarmente efficace sui Pokémon\nche vivono in acqua.", "A somewhat different Poké Ball that\nworks especially well on Pokémon that\nlive underwater.") },
            { PBEItem.DomeFossil, new PBELocalizedString("おおむかし　うみに　すんでいた\nこだい　ポケモンの　カセキ。\nこうらの　いちぶ　らしい。", "오랜 옛날 바다에 살았던\n고대 포켓몬의 화석.\n등 껍질의 일부인 듯하다.", "Fossile d'un ancien Pokémon aquatique.\nOn dirait un morceau de carapace.", "Das Fossil eines uralten Pokémon,\ndas in der Tiefsee lebte. Es sieht\nwie das Stück einer Muschel aus.", "Fósil de un Pokémon ancestral\nque vivió en el fondo del mar.\nParece ser un fragmento de concha.", "Fossile di un Pokémon antico che\nviveva in mare. Sembra essere\nparte di una corazza.", "A fossil of an ancient Pokémon that\nlived in the sea. It appears to be\npart of a shell.") },
            { PBEItem.DouseDrive, new PBELocalizedString("ゲノセクトに　もたせると\nテクノバスターという　わざが\nみずタイプになる　カセット。", "게노세크트에게 지니게 하면\n테크노버스터라는 기술이\n물타입으로 바뀌는 카세트.", "Tenu par Genesect, ce module\nchange la capacité TechnoBuster\nen une capacité de type Eau.", "Gibt man es Genesect zum Tragen,\nwandelt es den Typ der Attacke\nTechblaster in Wasser um.", "Cartucho que convierte\nTecno Shock de Genesect\nen un movimiento de tipo Agua.", "Modulo da far tenere a Genesect.\nTrasforma Tecnobotto in una mossa\ndi tipo Acqua.", "A cassette to be held by Genesect.\nIt changes Techno Blast to a\nWater-type move.") },
            { PBEItem.DracoPlate, new PBELocalizedString("ドラゴンの　タイプの　せきばん。\nもたせると　ドラゴンタイプの\nわざの　いりょくが　つよまる。", "드래곤타입의 석판.\n지니게 하면 드래곤타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Dragon.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Drachen.", "Tabla de piedra que potencia\nlos movimientos de tipo Dragón.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Drago.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Dragon-type moves.") },
            { PBEItem.DragonFang, new PBELocalizedString("かたくて　するどい　キバ。\nもたせると　ドラゴンタイプの\nわざの　いりょくが　あがる。", "단단하고 날카로운 이빨.\n지니게 하면 드래곤타입\n기술의 위력이 올라간다.", "Objet à tenir. Croc dur et pointu\nmontant la puissance des capacités\nde type Dragon.", "Ein Item zum Tragen. Ein harter und\nscharfer Reißzahn, der Attacken vom\nTyp Drachen verstärkt.", "Colmillo de gran dureza que refuerza\nlos movimientos de tipo Dragón.\nDebe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nDente duro e affilato che potenzia\nle mosse di tipo Drago.", "An item to be held by a Pokémon.\nIt is a hard and sharp fang that ups\nthe power of Dragon-type moves.") },
            { PBEItem.DragonGem, new PBELocalizedString("ドラゴンの　タイプの　ジュエル。\nもたせると　いちどだけ　ドラゴンの\nわざの　いりょくが　つよまる。", "드래곤타입의 주얼.\n지니게 하면 한 번만 드래곤\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Dragon.", "Ein Juwel vom Typ Drachen. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Drachen.", "Joya de un solo uso que potencia\nlos movimientos de tipo Dragón.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Drago.", "A gem with a draconic essence.\nWhen held, it strengthens the power\nof a Dragon-type move only once.") },
            { PBEItem.DragonScale, new PBELocalizedString("かたくて　じょうぶな　ウロコ。\nドラゴンタイプの　ポケモンが\nもっていることが　ある。", "단단하고 튼튼한 비늘.\n드래곤타입의 포켓몬이\n지니고 있는 경우가 있다.", "Une écaille épaisse et dure tenue\nparfois par les Pokémon de type Dragon\nquand ils sont attrapés.", "Eine dicke und starke Haut.\nDrachen-Pokémon tragen\ndieses Item gern bei sich.", "Una gruesa y resistente escama que\npueden llevar los Pokémon de tipo\nDragón cuando son capturados.", "Squama spessa e dura.\nPuò essere tenuta da un Pokémon\ndi tipo Drago quando viene catturato.", "A thick and tough scale.\nDragon-type Pokémon may be holding\nthis item when caught.") },
            { PBEItem.DreadPlate, new PBELocalizedString("あくの　タイプの　せきばん。\nもたせると　あくタイプの\nわざの　いりょくが　つよまる。", "악타입의 석판.\n지니게 하면 악타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Ténèbres.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Unlicht.", "Tabla de piedra que potencia\nlos movimientos de tipo Siniestro.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Buio.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Dark-type moves.") },
            { PBEItem.DreamBall, new PBELocalizedString("ハイリンクのもりで　いつのまにか\nバッグに　あらわれる　ゆめのボール。\nどんな　ポケモンも　つかまえられる。", "하일링크의 숲에서 어느 사이엔지\n가방 속에 나타나는 꿈의 볼.\n어떤 포켓몬이라도 잡을 수 있다.", "Poké Ball du rêve. Apparue dans le\nSac à la Forêt du Heylink. Permet\nd'attraper n'importe quel Pokémon.", "Ein Ball wie aus einem Traum, der im\nKontaktebenenhain plötzlich im Beutel\nauftaucht. Fängt jedes Pokémon.", "Bola especial que aparece de pronto\nen tu Bolsa en el Bosque Nexo y\npuede atrapar a cualquier Pokémon.", "Ball onirica. Appare all'improvviso nella\nBorsa quando si è nel Bosco Intramondo.\nPuò acchiappare qualsiasi Pokémon.", "A special Poké Ball that appears out of\nnowhere in a bag at the Entree Forest.\nIt can catch any Pokémon.") },
            { PBEItem.DubiousDisc, new PBELocalizedString("なかに　あやしい　じょうほうが\nつまった　とうめいな　きかい。\nせいさくしゃは　ふめい。", "안에 괴상한 정보가\n가득한 투명한 기계다.\n제작자 불명.", "Appareil transparent rempli de données\ndouteuses. Son fabricant n'est pas\nconnu.", "Eine Scheibe mit seltsamen Daten.\nIhr Ursprung ist unbekannt.", "Dispositivo transparente que contiene\ndatos misteriosos. Es de fabricante\ndesconocido.", "Dispositivo trasparente strapieno\ndi dati di dubbia entità. Non se ne\nconosce il produttore.", "A transparent device overflowing with\ndubious data.\nIts producer is unknown.") },
            { PBEItem.DurinBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.DuskBall, new PBELocalizedString("よるや　どうくつなど　くらいところで\nポケモンが　つかまえやすくなる\nちょっと　かわった　ボール。", "밤이나 동굴 등 어두운 곳에서\n포켓몬을 잡기 쉬운\n조금 특이한 볼.", "Une Poké Ball efficace pour attraper\nles Pokémon de nuit ou dans des\nendroits sombres tels que les grottes.", "Ein mysteriöser Ball,\ngeeignet für Einsätze in der\nNacht oder an dunklen Orten.", "Es algo distinta. Hace que sea más fácil\ncapturar a los Pokémon salvajes por la\nnoche o en lugares oscuros.", "Poké Ball un po' diversa che rende più\nfacile catturare Pokémon selvatici di\nnotte o in luoghi bui come le grotte.", "A somewhat different Poké Ball that\nmakes it easier to catch wild Pokémon\nat night or in dark places like caves.") },
            { PBEItem.DuskStone, new PBELocalizedString("ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nやみのように　くらい。", "어느 특정 포켓몬을\n진화시키는 이상한 돌.\n칠흑처럼 어둡다.", "Pierre étrange qui fait évoluer\ncertaines espèces de Pokémon. Elle\nest sombre comme une nuit sans lune.", "Dieser besondere, düstere Stein\nhilft manchen Pokémon bei\nihrer Evolution.", "Una piedra peculiar que hace\nevolucionar a algunos Pokémon.\nEs oscura como la noche.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nÈ scura come le tenebre.", "A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt is as dark as dark can be.") },
            { PBEItem.EarthPlate, new PBELocalizedString("じめんの　タイプの　せきばん。\nもたせると　じめんタイプの\nわざの　いりょくが　つよまる。", "땅타입의 석판.\n지니게 하면 땅타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Sol.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Boden.", "Tabla de piedra que potencia\nlos movimientos de tipo Tierra.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Terra.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Ground-type moves.") },
            { PBEItem.EjectButton, new PBELocalizedString("もたせて　わざを　うけると\nせんとうから　だっしゅつして\nてもちと　いれかわる　ことができる。", "지니게 하고 기술을 받으면\n배틀에서 탈출하여\n지닌 포켓몬과 교체할 수 있다.", "Si le Pokémon qui le tient subit une\nattaque, il s'enfuit pour être remplacé\npar un autre membre de l'équipe.", "Wird der Träger getroffen, flieht er\naus dem Kampf und wird mit einem\nTeam-Pokémon ausgetauscht.", "Si el portador es alcanzado por un\nataque, saldrá del combate y será\nsustituido por otro Pokémon del equipo.", "Se il Pokémon che lo tiene è colpito da\nun attacco, viene immediatamente\nsostituito da un compagno.", "If the holder is hit by an attack, it will\nswitch with another Pokémon in\nyour party.") },
            { PBEItem.Electirizer, new PBELocalizedString("とてつもない　でんきの　エネルギーが\nたくわえられている　はこ。\nある　ポケモンが　このんでいる。", "엄청난 전기의 에너지가\n비축되어 있는 상자.\n어떤 포켓몬이 좋아하는 물건이다.", "Une boîte remplie d'une énorme quantité\nd'énergie électrique. Appréciée d'un\ncertain Pokémon.", "Eine Schachtel mit elektrischer\nEnergie. Das Lieblingsstück\neines bestimmten Pokémon.", "Este dispositivo contiene una gran\ncantidad de energía eléctrica.\nCierto Pokémon lo adora.", "Scatola strapiena di energia elettrica.\nUn Pokémon in particolare adora questo\nstrumento.", "A box packed with a tremendous\namount of electric energy.\nIt is loved by a certain Pokémon.") },
            { PBEItem.ElectricGem, new PBELocalizedString("でんきの　タイプの　ジュエル。\nもたせると　いちどだけ　でんきの\nわざの　いりょくが　つよまる。", "전기타입의 주얼.\n지니게 하면 한 번만 전기\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Électrik.", "Ein Juwel vom Typ Elektro. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Elektro.", "Joya de un solo uso que potencia\nlos movimientos de tipo Eléctrico.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Elettro.", "A gem with an essence of electricity.\nWhen held, it strengthens the power of\nan Electric-type move only once.") },
            { PBEItem.Elixir, new PBELocalizedString("ポケモンが　おぼえている\n\xFF14つの　わざの　\xFF30\xFF30を\n\xFF11\xFF10ずつ　かいふくする。", "포켓몬이 기억하고 있는\n4개 기술의 PP를\n10씩 회복한다.", "Restaure 10 PP de toutes les\ncapacités d'un Pokémon.", "Füllt die AP aller Attacken eines\nPokémon um 10 Punkte auf.", "Restaura 10 PP de todos los movimientos\naprendidos por el Pokémon elegido.", "Restituisce 10 PP a ciascuna delle\nmosse imparate dal Pokémon.", "It restores the PP of all the\nmoves learned by the targeted Pokémon\nby 10 points each.") },
            { PBEItem.EnergyPowder, new PBELocalizedString("とても　にがい　こなぐすり。\nポケモン　\xFF11ひきの　\xFF28\xFF30を\n\xFF15\xFF10だけ　かいふくする。", "매우 쓴 가루약.\n포켓몬 1마리의 HP를\n50만큼 회복한다.", "Une poudre médicinale très amère.\nRestaure 50 PV à un Pokémon.", "Ein bitteres medizinisches Pulver,\ndas die KP eines Pokémon um\n50 Punkte auffüllt.", "Polvo medicinal muy amargo que\nrestaura 50 PS de un Pokémon.", "Rimedio amarissimo in polvere.\nRestituisce 50 PS ad un Pokémon.", "A very bitter medicine powder.\nIt restores the HP of one Pokémon by\n50 points.") },
            { PBEItem.EnergyRoot, new PBELocalizedString("とても　にがい　ねっこ。\nポケモン　\xFF11ひきの　\xFF28\xFF30を\n\xFF12\xFF10\xFF10だけ　かいふくする。", "매우 쓴 뿌리.\n포켓몬 1마리의 HP를\n200만큼 회복한다.", "Une racine très amère.\nRestaure 200 PV à un Pokémon.", "Eine bittere Wurzel. Sie füllt die\nKP eines Pokémon um 200 Punkte auf.", "Raíz muy amarga que restaura\n200 PS de un Pokémon.", "Radice amarissima.\nRestituisce 200 PS ad un Pokémon.", "A very bitter root.\nIt restores the HP of one Pokémon by\n200 points.") },
            { PBEItem.EnigmaBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　わざを\nうけたとき　\xFF28\xFF30を　かいふくする。", "포켓몬에게 지니게 하면\n효과가 뛰어난 공격을\n받았을 때 HP를 회복한다.", "Tenue, cette Baie restaure les PV\ndu Pokémon s'il subit une attaque\nsuper efficace.", "Als getragenes Item füllt die Beere die\nKP eines Pokémon auf, wenn es von sehr\neffektiven Attacken getroffen wird.", "Restaura los PS de un Pokémon\nsi la lleva cuando le alcanza\nun ataque supereficaz.", "Ripristina i PS, se il Pokémon che\nla tiene è colpito da un attacco\nsuperefficace.", "If held by a Pokémon, it restores\nits HP if it is hit by any\nsupereffective attack.") },
            { PBEItem.EscapeRope, new PBELocalizedString("ながくて　じょうぶな　ヒモ。\nどうくつや　ダンジョンから\nぬけだすことが　できる。", "길고 튼튼한 끈.\n동굴이나 던전에서\n빠져나올 수 있다.", "Une corde longue et solide permettant\nde sortir rapidement d'une grotte ou\nd'un donjon.", "Ein langes, festes Seil, das die\nsofortige Flucht aus Höhlen oder\nÄhnlichem ermöglicht.", "Cuerda larga y resistente que sirve\npara huir de cuevas y sitios cerrados\nen general.", "Corda lunga e resistente che si usa\nper fuggire in un batter d'occhio\ndalle grotte o da certi luoghi chiusi.", "A long, durable rope.\nUse it to escape instantly from a cave\nor a dungeon.") },
            { PBEItem.Ether, new PBELocalizedString("ポケモンが　おぼえている\nわざのうち　\xFF11つの　\xFF30\xFF30を\n\xFF11\xFF10だけ　かいふくする。", "포켓몬이 기억하고 있는\n기술 중 1개의 PP를\n10만큼 회복한다.", "Restaure jusqu'à 10 PP d'une\ncapacité sélectionnée.", "Füllt die AP einer ausgewählten Attacke\neines Pokémon um 10 Punkte auf.", "Restaura un máximo de 10 PP del\nmovimiento elegido de un Pokémon.", "Restituisce un massimo di 10 PP\na una mossa a scelta che è stata\nimparata dal Pokémon.", "It restores the PP of a Pokémon's\nselected move by a maximum of\n10 points.") },
            { PBEItem.Everstone, new PBELocalizedString("ポケモンに　もたせると\nそのあいだ　しんか　しなくなる\nふしぎな　いし。", "포켓몬에게 지니게 하면\n지닌 동안에는 진화하지 않게 되는\n이상한 돌.", "Objet à tenir. Pierre merveilleuse\nempêchant l'évolution.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nbewirkt einen Entwicklungsstopp.", "El Pokémon que lleva esta piedra\nmágica no evoluciona.", "Strumento da far tenere a un Pokémon.\nChi tiene questa pietra particolare\nevita l'evoluzione.", "An item to be held by a Pokémon.\nThe Pokémon holding this peculiar stone\nis prevented from evolving.") },
            { PBEItem.Eviolite, new PBELocalizedString("しんかの　ふしぎな　かたまり。\nもたせると　しんかまえ　ポケモンの\nぼうぎょと　とくぼうが　あがる。", "진화의 이상한 덩어리.\n지니게 하면 진화 전 포켓몬의\n방어와 특수방어가 올라간다.", "Un étrange concentré d'évolution qui\naugmente la Défense et la Déf. Spé.\nd'un Pokémon pouvant évoluer.", "Ein mysteriöser Klumpen, der die Vert.\nu. Spez.-Vert. von Pokémon erhöht,\ndie sich noch entwickeln können.", "Piedra evolutiva. El Pokémon portador\naumentará su Defensa y su Defensa\nEspecial si aún puede evolucionar.", "Misteriosa sfera evolutiva. Aumenta la\nDifesa e la Difesa Speciale di un Pokémon\nche può ancora evolversi.", "A mysterious evolutionary lump. When\nheld, it raises the Defense and Sp. Def\nof a Pokémon that can still evolve.") },
            { PBEItem.ExpertBelt, new PBELocalizedString("つかいこまれた　くろい　おび。\nもたせると　こうかばつぐんの　とき\nわざの　いりょくが　すこし　あがる。", "오래 사용한 까만 띠.\n지니게 하면 효과가 뛰어날 때\n기술의 위력이 조금 올라간다.", "Objet à tenir. Ceinture usée\naugmentant légèrement la puissance\ndes capacités super efficaces.", "Ein Item zum Tragen.\nDieser alte Gürtel verstärkt\nsehr effektive Attacken.", "Potencia ligeramente los movimientos\nsupereficaces.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nCintura consumata che potenzia di poco\nle mosse superefficaci.", "An item to be held by a Pokémon. It is\na well-worn belt that slightly boosts\nthe power of supereffective moves.") },
            { PBEItem.ExpShare, new PBELocalizedString("もたせた　ポケモンは　せんとうに\nでなくても　けいけんちを\nわけてもらえる　べんりな　そうち。", "지니게 한 포켓몬은 배틀에\n나가지 않아도 경험치를\n나누어 받을 수 있는 편리한 장치.", "Objet à tenir. Le porteur reçoit une\npartie des points Exp. du combat sans\navoir à combattre.", "Ein Item, das einem Pokémon zum Tragen\ngegeben werden kann. Es teilt die\nErfahrungs-Punkte aus einem Kampf auf.", "El Pokémon que lo lleva obtiene parte\nde los Puntos de Exp. del combate\nsin participar en él.", "Strumento da far tenere a un Pokémon.\nChi lo tiene ottiene Punti Esperienza\ndalla lotta anche se non vi partecipa.", "An item to be held by a Pokémon.\nThe holder gets a share of a battle's\nExp. Points without battling.") },
            { PBEItem.FastBall, new PBELocalizedString("にげあしの　はやい　ポケモンが\nつかまえやすくなる\nちょっと　かわった　ボール。　", "도주가 빠른 포켓몬이\n잡기 쉬워지는\n조금 특이한 볼.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien sur\nles Pokémon qui ont tendance à fuir.", "Ein Ball, der sich besonders gut eignet,\num flinke Pokémon zu fangen.", "Poké Ball algo distinta, especialmente\nindicada para capturar Pokémon\nevasivos.", "Poké Ball un po' originale che funziona\nbenissimo sui Pokémon bravi a scappare.", "A Poké Ball that makes it easier to\ncatch Pokémon which are quick to\nrun away.") },
            { PBEItem.FavoredMail, new PBELocalizedString("すきなものを　つたえる　メールが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "좋아하는 것을 전하는 메일을\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Un papier à lettres idéal pour parler\nde ce que l'on aime. Peut être tenu\net livré par un Pokémon.", "Briefpapier, mit dem man jemandem\nverraten kann, was man gern mag.\nGib es einem Pokémon zum Tragen.", "Carta para escribir sobre tus cosas\nfavoritas. Haz que la entregue\nun Pokémon.", "Carta da lettera ideale per comunicare\nciò che si preferisce. Un Pokémon può\ntenerla e consegnarla.", "Stationery designed for\nwriting about your favorite things.\nLet a Pokémon hold it for delivery.") },
            { PBEItem.FightingGem, new PBELocalizedString("かくとうの　タイプの　ジュエル。\nもたせると　いちどだけ　かくとうの\nわざの　いりょくが　つよまる。", "격투타입의 주얼.\n지니게 하면 한 번만 격투\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Combat.", "Ein Juwel vom Typ Kampf. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Kampf.", "Joya de un solo uso que potencia\nlos movimientos de tipo Lucha.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Lotta.", "A gem with an essence of combat. When\nheld, it strengthens the power of a\nFighting-type move only once.") },
            { PBEItem.FigyBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　\xFF28\xFF30を　かいふくする。\nきらいな　あじだと　こんらん　する。", "포켓몬에게 지니게 하면\n위급할 때 HP를 회복한다.\n싫어하는 맛이면 혼란에 빠진다.", "Tenue, cette Baie restaure des PV en\ncas de besoin, mais rend confus si\nle Pokémon n'aime pas son goût.", "Als getragenes Item füllt die Beere die\nKP eines Pokémon auf, doch ihr Aroma\nverwirrt jene, die es nicht mögen.", "Los Pokémon pueden llevarla o usarla\npara restaurar algunos PS. Su sabor\npuede causar confusión.", "Se tenuta da un Pokémon in difficoltà,\nrestituisce PS. Se al Pokémon non piace\nil suo sapore, può causare confusione.", "If held by a Pokémon, it restores the\nuser's HP in a pinch, but will cause\nconfusion if it hates the taste.") },
            { PBEItem.FireGem, new PBELocalizedString("ほのおの　タイプの　ジュエル。\nもたせると　いちどだけ　ほのおの\nわざの　いりょくが　つよまる。", "불꽃타입의 주얼.\n지니게 하면 한 번만 불꽃\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Feu.", "Ein Juwel vom Typ Feuer. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Feuer.", "Joya de un solo uso que potencia\nlos movimientos de tipo Fuego.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Fuoco.", "A gem with an essence of fire. When\nheld, it strengthens the power of a\nFire-type move only once.") },
            { PBEItem.FireStone, new PBELocalizedString("ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nだいだいいろを　している。", "어느 특정한 포켓몬을\n진화시키는 이상한 돌.\n주황색을 띠고 있다.", "Une pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle est jaune et orange.", "Dieser spezielle Stein löst bei\nbestimmten Pokémon die Entwicklung\naus. Er ist orange.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nEs amarilla con una marca naranja.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nÈ gialla ed arancione.", "A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt is colored orange.") },
            { PBEItem.FistPlate, new PBELocalizedString("かくとうの　タイプの　せきばん。\nもたせると　かくとうタイプの\nわざの　いりょくが　つよまる。", "격투타입의 석판.\n지니게 하면 격투타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Combat.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Kampf.", "Tabla de piedra que potencia\nlos movimientos de tipo Lucha.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Lotta.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Fighting-type moves.") },
            { PBEItem.FlameOrb, new PBELocalizedString("さわると　ねつをだす　ふしぎなたま。\nもたせると　せんとうちゅうに\nやけどの　じょうたいに　なる。", "만지면 열을 내는 이상한 구슬.\n지니게 하면 배틀 중에\n화상 상태가 된다.", "Objet à tenir. Orbe bizarre brûlant\nson porteur durant le combat.", "Ein Item zum Tragen.\nDieser bizarre Orb fügt seinem\nTräger Verbrennungen zu.", "Extraña esfera que causa quemaduras\nal Pokémon que la lleva en combate.", "Strumento da far tenere a un Pokémon.\nSfera bizzarra che procura una\nscottatura a chi la tiene in lotta.", "An item to be held by a Pokémon.\nIt is a bizarre orb that inflicts a\nburn on the holder in battle.") },
            { PBEItem.FlamePlate, new PBELocalizedString("ほのおの　タイプの　せきばん。\nもたせると　ほのおタイプの\nわざの　いりょくが　つよまる。", "불꽃타입의 석판.\n지니게 하면 불꽃타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Feu.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Feuer.", "Tabla de piedra que potencia\nlos movimientos de tipo Fuego.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Fuoco.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Fire-type moves.") },
            { PBEItem.FloatStone, new PBELocalizedString("とても　かるい　いし。\nもたせると　ポケモンの\nたいじゅうが　かるくなる。", "매우 가벼운 돌.\n지니게 하면 포켓몬의\n체중이 가벼워진다.", "Une pierre très légère.\nElle réduit le poids du\nPokémon qui la tient.", "Ein federleichter Stein, der das\nGewicht des Trägers verringert.", "Piedra muy ligera que reduce el peso\ndel Pokémon que la lleve.", "Pietra leggerissima che riduce il peso\ndel Pokémon che la tiene.", "A very light stone. It reduces the\nweight of a Pokémon when held.") },
            { PBEItem.FluffyTail, new PBELocalizedString("ポケモンの　きを　ひかせる　どうぐ。\nやせいポケモンとの　せんとうから\nぜったいに　にげられる。", "포켓몬의 관심을 끄는 도구.\n야생 포켓몬과의 배틀에서\n반드시 도망칠 수 있다.", "Objet qui attire les Pokémon. Permet\nde s'enfuir d'un combat contre un\nPokémon sauvage.", "Ein hübsches Item. Es ermöglicht\ndie Flucht im Kampf gegen wilde\nPokémon.", "Un objeto que atrae a los Pokémon.\nEs útil para huir de un combate\ncontra un Pokémon salvaje.", "Strumento che attrae i Pokémon. È utile\nper fuggire dalla lotta con Pokémon\nselvatici.", "An item that attracts Pokémon.\nUse it to flee from any battle with a\nwild Pokémon.") },
            { PBEItem.FlyingGem, new PBELocalizedString("ひこうの　タイプの　ジュエル。\nもたせると　いちどだけ　ひこうの\nわざの　いりょくが　つよまる。", "비행타입의 주얼.\n지니게 하면 한 번만 비행\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Vol.", "Ein Juwel vom Typ Flug. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Flug.", "Joya de un solo uso que potencia\nlos movimientos de tipo Volador.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Volante.", "A gem with an essence of air. When held,\nit strengthens the power of a\nFlying-type move only once.") },
            { PBEItem.FocusBand, new PBELocalizedString("もたせると　ひんしに　なりそうな\nわざを　うけても　\xFF28\xFF30を　\xFF11だけ\nのこして　たえることが　ある。", "지니게 하면\n기절할 듯한 기술을 받아도\nHP를 1 남겨서 견디기도 한다.", "Objet à tenir pouvant parfois empêcher\nd'être mis K.O., ne laissant qu'un PV.", "Ein Item, das die Kampfunfähigkeit\nverhindern kann, da dem Träger\n1 KP bleibt.", "El Pokémon que la lleva puede resistir\nun golpe que potencialmente lo deja\nK.O. con un PS.", "Strumento da far tenere a un Pokémon.\nChi lo tiene può evitare un potenziale\nKO e rimanere con un solo PS.", "An item to be held by a Pokémon.\nThe holder may endure a potential KO\nattack, leaving it with just 1 HP.") },
            { PBEItem.FocusSash, new PBELocalizedString("もたせると　\xFF28\xFF30が　まんたんのとき\nひんしになりそうな　わざを　うけても　\n\xFF28\xFF30\xFF11で　いちどだけ　たえる。", "지니게 하면 HP가 꽉 찼을 때\n기절할 듯한 기술을 당해도\nHP1로 한 번은 버틴다.", "Objet à tenir permettant au porteur,\ns'il a ses PV pleins, d'éviter un\npotentiel K.O. en conservant un PV.", "Ein Item zum Tragen. Bei vollen KP\nreduziert eine K.O.-Attacke die\nKP nur auf 1.", "Si el Pokémon que la lleva tiene los PS\nal máximo, puede resistir un golpe que\npotencialmente lo deja K.O. con un PS.", "Strumento da far tenere a un Pokémon.\nSe chi lo tiene ha tutti i PS, eviterà\nun potenziale KO, rimanendo con un PS.", "An item to be held by a Pokémon. If it\nhas full HP, the holder will endure one\npotential KO attack, leaving 1 HP.") },
            { PBEItem.FreshWater, new PBELocalizedString("ミネラル　たっぷりの　みず。\nポケモン　\xFF11ひきの　\xFF28\xFF30を\n\xFF15\xFF10だけ　かいふくする。", "미네랄이 가득한 물.\n포켓몬 1마리의 HP를\n50만큼 회복한다.", "Une eau riche en minéraux.\nRestaure 50 PV à un Pokémon.", "Ein stilles Wasser mit hohem\nMineralienanteil, das die KP eines\nPokémon um 50 Punkte auffüllt.", "Agua de alto contenido mineral que\nrestaura 50 PS de un Pokémon.", "Acqua ricca di sostanze minerali.\nRestituisce 50 PS ad un Pokémon.", "Water with a high mineral content.\nIt restores the HP of one Pokémon by\n50 points.") },
            { PBEItem.FriendBall, new PBELocalizedString("つかまえた　やせいポケモンが\nすぐに　なついて　くれる\nちょっと　かわった　ボール。　", "잡은 야생 포켓몬이\n바로 친밀해지는\n조금 특이한 볼.", "Une Poké Ball un peu spéciale qui\npermet de rendre rapidement amicaux\nles Pokémon sauvages attrapés avec.", "Ein besonderer Ball, der die\nSympathie von gefangenen Pokémon\nschneller ansteigen lässt.", "Poké Ball algo distinta que hace más\namistosos a los Pokémon salvajes que\nse capturan con ella.", "Poké Ball un po' originale che fa sì che\ni Pokémon selvatici con essa catturati\nsi affezionino più rapidamente.", "A Poké Ball that makes caught Pokémon\nmore friendly.") },
            { PBEItem.FullHeal, new PBELocalizedString("スプレーしきの　くすり。\nポケモン　\xFF11ひきの　じょうたいの\nいじょうを　すべて　かいふくする。", "스프레이식의 약.\n포켓몬 1마리의\n상태 이상을 모두 회복한다.", "Un médicament sous forme de spray.\nSoigne tous les problèmes de statut\nd'un Pokémon.", "Ein Spray, das alle Statusprobleme\neines Pokémon behebt.", "Medicina en espray que cura todos los\nproblemas de estado de un Pokémon.", "Rimedio spray.\nRisolve tutti i problemi di stato\ndi un Pokémon.", "A spray-type medicine.\nIt heals all the status problems of a\nsingle Pokémon.") },
            { PBEItem.FullIncense, new PBELocalizedString("ふしぎな　かおりの　おこう。\nもたせた　ポケモンは　いつもより\nこうどうが　おそくなる。", "이상한 향기의 향로.\n지니게 한 포켓몬은 여느 때보다\n행동이 느려진다.", "Objet à tenir. Encens au parfum\nexotique gonflant son porteur et\nle faisant se déplacer lentement.", "Ein Item zum Tragen. Dieses exotische\nDuftkraut macht den Träger benommen\nund langsam.", "Incienso de exótico aroma que hace\nque el Pokémon que lo lleve esté\naturdido y se mueva más despacio.", "Strumento da far tenere a un Pokémon.\nIncenso dal profumo esotico che\nrende gonfio e lento chi lo tiene.", "An item to be held by a Pokémon. It is\nan exotic-smelling incense that makes\nthe holder bloated and slow moving.") },
            { PBEItem.FullRestore, new PBELocalizedString("ポケモン　\xFF11ひきの　\xFF28\xFF30と\nじょうたい　いじょうを　すべて\nかいふくする。", "포켓몬 1마리의 HP와\n상태 이상을 모두\n회복한다.", "Un médicament qui restaure tous les\nPV d'un Pokémon et soigne tous ses\nproblèmes de statut.", "Dieses Item stellt alle KP eines\nPokémon wieder her und behebt\nalle Statusprobleme.", "Medicina que restaura todos los PS\ny cura todos los problemas de\nestado de un Pokémon.", "Rimedio che restituisce tutti i PS ad\nun Pokémon e risolve i suoi problemi\ndi stato.", "A medicine that fully restores the HP\nand heals any status problems of a\nsingle Pokémon.") },
            { PBEItem.GanlonBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　じぶんの\nぼうぎょが　あがる。", "포켓몬에게 지니게 하면\n위급할 때 자신의\n방어가 올라간다.", "Tenue, cette Baie monte la Défense\nen cas de besoin.", "Als getragenes Item erhöht diese Beere\nden Verteidigungs-Wert eines Pokémon,\nsobald es in einer Notlage steckt.", "Si la lleva un Pokémon, le sube\nla Defensa en un momento de apuro.", "Aumenta la Difesa, se il Pokémon che\nla tiene si trova in difficoltà.", "If held by a Pokémon, it raises its\nDefense stat in a pinch.") },
            { PBEItem.GeniusWing, new PBELocalizedString("ポケモンに　つかう　どうぐ。\nポケモン　\xFF11ひきの　とくこうの\nきそポイントを　ちょっと　あげる。", "포켓몬에게 사용하는 도구.\n포켓몬 1마리의 특수공격\n기초 포인트를 조금 올린다.", "Objet à utiliser sur un Pokémon.\nIl augmente un peu son\nAttaque Spéciale de base.", "Schenkt man einem Pokémon dieses\nItem, wird dessen Spezial-Angriff\ndauerhaft erhöht.", "Pluma que aumenta un poco\nel Ataque Especial de un Pokémon\nal subir de nivel.", "Strumento da usare su un Pokémon.\nNe aumenta di poco l'Attacco Speciale\ndi base.", "An item for use on a Pokémon. It\nslightly increases the base Sp. Atk\nstat of a single Pokémon.") },
            { PBEItem.GhostGem, new PBELocalizedString("ゴーストの　タイプの　ジュエル。\nもたせると　いちどだけ　ゴーストの\nわざの　いりょくが　つよまる。", "고스트타입의 주얼.\n지니게 하면 한 번만 고스트\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Spectre.", "Ein Juwel vom Typ Geist. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Geist.", "Joya de un solo uso que potencia\nlos movimientos de tipo Fantasma.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Spettro.", "A gem with a spectral essence. When\nheld, it strengthens the power of a\nGhost-type move only once.") },
            { PBEItem.GooeyMulch, new PBELocalizedString("きのみを　そだてる　ちほうでは\nふかふかのつちに　まいて　つかう。\nマニアが　たかく　かってくれる。", "나무열매를 키우는 지방에서는\n푹신푹신한 땅에 뿌려 쓴다.\n마니아가 비싼 값에 산다.", "Un engrais utilisé sur sol meuble.\nLes amateurs en donnent un bon prix.", "In Regionen, wo man Beeren anbaut,\nmengt man ihn unter weiche Erde.\nSammler geben ihr letztes Hemd dafür.", "Un fertilizante usado en suelo fértil\ndonde crecen las Bayas.\nMuy apreciado por los coleccionistas.", "Usata per favorire la crescita delle\nBacche nelle regioni dove si coltivano.\nGli intenditori pagano caro per averla.", "A fertilizer to be spread on soft soil\nin regions where Berries are grown.\nA maniac will buy it for a high price.") },
            { PBEItem.GrassGem, new PBELocalizedString("くさの　タイプの　ジュエル。\nもたせると　いちどだけ　くさの\nわざの　いりょくが　つよまる。", "풀타입의 주얼.\n지니게 하면 한 번만 풀\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Plante.", "Ein Juwel vom Typ Pflanze. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Pflanze.", "Joya de un solo uso que potencia\nlos movimientos de tipo Planta.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Erba.", "A gem with an essence of nature. When\nheld, it strengthens the power of a\nGrass-type move only once.") },
            { PBEItem.GreatBall, new PBELocalizedString("モンスターボールよりも　さらに\nポケモンを　つかまえやすくなった\nすこし　せいのうのいい　ボール。", "몬스터볼보다도 더욱\n포켓몬을 잡기 쉬워진\n약간 성능이 좋은 볼.", "Une Ball très performante dont le taux\nde réussite est supérieur à celui de la\nPoké Ball.", "Ein Ball mit guter Erfolgsquote.\nDem Pokéball in allen\nPunkten überlegen.", "Poké Ball de alto rendimiento.\nTiene un índice de éxito superior\nal de la Poké Ball.", "Ball dalle ottime prestazioni: fornisce\nmaggiori probabilità di cattura rispetto\nalla Poké Ball.", "A good, high-performance Ball that\nprovides a higher Pokémon catch rate\nthan a standard Poké Ball.") },
            { PBEItem.GreenScarf, new PBELocalizedString("もたせて　コンテストに　さんかすると\nその　ポケモンは　いつもより\nかしこく　みられる。", "지니게 하여 콘테스트에 참가하면\n그 포켓몬은 여느 때보다\n슬기롭게 보인다.", "Objet à tenir. Augmente l'Intelligence\nd'un Pokémon dans les Concours.", "Ein Item zum Tragen. Trainer erhöhen\ndamit in Wettbewerben die „Klugheit“\nihres Pokémon.", "Debe llevarlo un Pokémon.\nAumenta el ingenio en los concursos.", "Strumento da far tenere a un Pokémon.\nAumenta l'“Acume\" di chi lo tiene\nin una Gara.", "An item to be held by a Pokémon.\nIt boosts the Smart aspect of the\nholder in a Contest.") },
            { PBEItem.GreenShard, new PBELocalizedString("みどりの　ちいさな　かけら。\nむかしに　つくられた　なにかの\nどうぐの　いちぶ　らしい。", "초록색 작은 조각.\n옛날에 만들어진 어떤\n도구의 일부인 듯하다.", "Petit tesson vert.\nIl semble provenir d'un outil très\nancien.", "Eine grüne Scherbe eines antiken\nWerkzeugs, das vor langer Zeit\nangefertigt wurde.", "Un pequeño fragmento verde.\nParece formar parte de algún tipo\nde herramienta antigua.", "Un piccolo coccio verde che con ogni\nprobabilità è un frammento di un antico\nstrumento.", "A small green shard.\nIt appears to be from some sort of\nimplement made long ago.") },
            { PBEItem.GreetMail, new PBELocalizedString("はじめましての　あいさつが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "첫인사를\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Un papier à lettres idéal pour\nse présenter. Peut être tenu\net livré par un Pokémon.", "Briefpapier, mit dem man sich\nbei jemandem vorstellen kann.\nGib es einem Pokémon zum Tragen.", "Carta para presentarse a alguien.\nHaz que la entregue un Pokémon.", "Carta da lettera ideale per presentarsi\na qualcuno. Un Pokémon può tenerla e\nconsegnarla.", "Stationery designed for\nintroductory greetings.\nLet a Pokémon hold it for delivery.") },
            { PBEItem.GrepaBerry, new PBELocalizedString("ポケモンに　あげると\nとても　なつき　やすくなるが\nとくぼうの　きそポイントは　さがる。", "포켓몬에게 주면\n매우 친밀해지기 쉬워지지만\n특수방어의 기초 포인트가 떨어진다.", "Rend les Pokémon plus amicaux, mais\nbaisse leur stat Défense Spéciale de\nbase.", "Isst ein Pokémon diese Beere,\nstärkt dies eure Freundschaft, ver-\nringert aber seinen Spez.-Vert.-Wert.", "Al usarla con un Pokémon, se gana\nsu amistad, pero también reduce\nsu Defensa Especial de base.", "Utilizzata su un Pokémon, lo rende più\namichevole, ma diminuisce la Difesa\nSpeciale di base.", "Using it on a Pokémon makes it more\nfriendly, but it also lowers its base\nSp. Def stat.") },
            { PBEItem.GripClaw, new PBELocalizedString("もたせると　しめつける　まきつくなど\nダメージを　あたえつづける　わざの\nターンすうが　ふえる。", "지니게 하면 조이기나 김밥말이 등\n데미지를 계속 주는 기술의\n턴 수가 늘어난다.", "Objet à tenir augmentant la durée des\nattaques à tours multiples telles que\nLigotage et Étreinte.", "Ein Item zum Tragen.\nVerlängert die Dauer von\nKlammergriff, Wickel etc.", "Prolonga la duración de ataques\nmultiturno como Atadura y Repetición.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon\nche prolunga gli attacchi multiturno\ncome Avvolgibotta e Legatutto.", "A Pokémon held item that extends the\nduration of multiturn attacks like\nBind and Wrap.") },
            { PBEItem.GriseousOrb, new PBELocalizedString("ギラティナに　もたせると　ドラゴンと\nゴーストタイプの　わざの　いりょくが\nあがる　ひかり　かがやく　たま。", "기라티나에게 지니게 하면 드래곤과\n고스트타입 기술의 위력이 올라가는\n반짝반짝 빛나는 구슬.", "Mystérieux objet destiné à Giratina.\nAméliore les capacités de type Dragon\net Spectre.", "Ein glühender Orb. Verstärkt\nAttacken vom Typ Drachen und\nGeist, wenn Giratina ihn trägt.", "Una esfera brillante que potencia los\nmovimientos de tipo Dragón y Fantasma.\nDebe llevarla Giratina.", "Poliedro splendente da far tenere a\nGiratina. Aumenta la potenza delle\nmosse di tipo Drago e Spettro.", "A glowing orb to be held by Giratina.\nIt boosts the power of Dragon- and\nGhost-type moves.") },
            { PBEItem.GrnApricorn, new PBELocalizedString("みどりの　ぼんぐり。\nふしぎと　こうばしい　かおりがする。\n", "초록색의 규토리.\n이상하게도 고소한 향기가 난다.\n", "Un Noigrume de couleur verte.\nIl dégage un arôme étonnamment riche.", "Eine grüne Aprikoko. Sie verströmt\neinen seltsam aromatischen Duft.", "Bonguri de color verde y aroma peculiar\nque recuerda al café tostado.", "Una Ghicocca di colore verde.\nEmana un profumo intenso.", "A green Apricorn.\nIt has a mysterious, aromatic scent.") },
            { PBEItem.GroundGem, new PBELocalizedString("じめんの　タイプの　ジュエル。\nもたせると　いちどだけ　じめんの\nわざの　いりょくが　つよまる。", "땅타입의 주얼.\n지니게 하면 한 번만 땅\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Sol.", "Ein Juwel vom Typ Boden. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Boden.", "Joya de un solo uso que potencia\nlos movimientos de tipo Tierra.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Terra.", "A gem with an essence of land. When\nheld, it strengthens the power of a\nGround-type move only once.") },
            { PBEItem.GrowthMulch, new PBELocalizedString("きのみを　そだてる　ちほうでは\nふかふかのつちに　まいて　つかう。\nマニアが　たかく　かってくれる。", "나무열매를 키우는 지방에서는\n푹신푹신한 땅에 뿌려 쓴다.\n마니아가 비싼 값에 산다.", "Un engrais utilisé sur sol meuble.\nLes amateurs en donnent un bon prix.", "In Regionen, wo man Beeren anbaut,\nmengt man ihn unter weiche Erde.\nSammler geben ihr letztes Hemd dafür.", "Un fertilizante usado en suelo fértil\ndonde crecen las Bayas.\nMuy apreciado por los coleccionistas.", "Usato per favorire la crescita delle\nBacche nelle regioni dove si coltivano.\nGli intenditori pagano caro per averlo.", "A fertilizer to be spread on soft soil\nin regions where Berries are grown.\nA maniac will buy it for a high price.") },
            { PBEItem.GuardSpec, new PBELocalizedString("せんとうちゅう　\xFF15ターンの　あいだ\nみかたの　のうりょくを\nさげられなくする　どうぐ。", "배틀 중 5턴 동안\n같은 편의 능력을\n떨어뜨리지 않게 하는 도구.", "Empêche la réduction des stats\nde tous les Pokémon de l'équipe\npendant 5 tours.", "Eine mögliche Statusveränderung\nder Pokémon im Team wird für\n5 Runden verhindert.", "Evita que bajen las características\ndel equipo Pokémon durante\ncinco turnos.", "Per cinque turni dopo l'uso, evita\nla riduzione delle statistiche dei\nPokémon della squadra dell'Allenatore.", "An item that prevents stat reduction\namong the Trainer's party Pokémon\nfor five turns after use.") },
            { PBEItem.HabanBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　ドラゴン　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 드래곤 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Dragon.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Drachen.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Dragón\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Drago\ndel nemico.", "Weakens a supereffective Dragon-type\nattack against the holding Pokémon.") },
            { PBEItem.HardStone, new PBELocalizedString("ぜったいに　われない　いし。\nもたせると　いわタイプの\nわざの　いりょくが　あがる。", "절대 깨지지 않는 돌.\n지니게 하면 바위타입\n기술의 위력이 올라간다.", "Objet à tenir. Pierre incassable qui\nmonte la puissance des capacités de\ntype Roche.", "Ein Item zum Tragen. Ein unzer-\nbrechlicher Stein, der Attacken\nvom Typ Gestein verstärkt.", "Debe llevarla un Pokémon.\nLa robustez de la piedra fortalece\nlos movimientos de tipo Roca.", "Strumento da far tenere a un Pokémon.\nPietra indistruttibile che potenzia\nle mosse di tipo Roccia.", "An item to be held by a Pokémon.\nIt is an unbreakable stone that ups\nthe power of Rock-type moves.") },
            { PBEItem.HealBall, new PBELocalizedString("つかまえた　ポケモンの　\xFF28\xFF30と\nじょうたい　いじょうを　かいふくする\nちょっと　やさしい　ボール。", "잡은 포켓몬의 HP와\n상태 이상을 회복시키는\n조금 다정한 볼.", "Une Poké Ball de soin qui rend tous les\nPV du Pokémon attrapé et qui soigne\ntous les changements de statut.", "Ein wohltuender Ball. Er füllt alle KP\ndes gefangenen Pokémon auf und\nbehebt alle Statusprobleme.", "Poké Ball curativa que restaura los PS\ny cura todos los problemas de estado\nde un Pokémon después de capturarlo.", "Poké Ball curativa che ridà PS\nal Pokémon catturato e risolve ogni\nproblema di stato.", "A remedial Poké Ball that restores the\ncaught Pokémon's HP and eliminates\nany status problem.") },
            { PBEItem.HealPowder, new PBELocalizedString("とても　にがい　こなぐすり。\nポケモン　\xFF11ひきの　じょうたいの\nいじょうを　すべて　かいふくする。", "매우 쓴 가루약.\n포켓몬 1마리의\n상태 이상을 모두 회복한다.", "Une poudre médicinale très amère.\nSoigne tous les problèmes de statut\nd'un Pokémon.", "Ein bitteres medizinisches Pulver,\ndas alle Statusprobleme eines\nPokémon aufhebt.", "Polvos medicinales muy amargos que\ncuran todos los problemas de estado\nde un Pokémon.", "Rimedio amarissimo in polvere.\nRisolve tutti i problemi di stato\ndi un Pokémon.", "A very bitter medicine powder.\nIt heals all the status problems of a\nsingle Pokémon.") },
            { PBEItem.HealthWing, new PBELocalizedString("ポケモンに　つかう　どうぐ。\nポケモン　\xFF11ひきの　\xFF28\xFF30の\nきそポイントを　ちょっと　あげる。", "포켓몬에게 사용하는 도구.\n포켓몬 1마리의 HP\n기초 포인트를 조금 올린다.", "Objet à utiliser sur un Pokémon.\nIl augmente un peu ses PV de base.", "Schenkt man einem Pokémon dieses\nItem, wird dessen Basis-KP-Anzahl\ndauerhaft erhöht.", "Pluma que aumenta un poco los PS\nde un Pokémon al subir de nivel.", "Strumento da usare su un Pokémon.\nNe aumenta di poco i PS di base.", "An item for use on a Pokémon. It\nslightly increases the base HP of a\nsingle Pokémon.") },
            { PBEItem.HeartScale, new PBELocalizedString("きれいな　ハートの　かたちの\nとても　めずらしい　ウロコ。\nにじいろに　うすく　かがやく。", "예쁜 하트 모양의\n무척 진귀한 비늘.\n무지개색으로 연하게 반짝인다.", "Une jolie écaille en forme de cœur qui\nest très rare. Elle brille légèrement\nd'un éclat arc-en-ciel.", "Eine hübsche, herzförmige, sehr\nseltene Schuppe. Sie erstrahlt\nin den Farben des Regenbogens.", "Bella escama con forma de corazón\nque brilla ligeramente como los colores\ndel arcoíris. Es muy poco común.", "Squama graziosa, a forma di cuore e\nmolto rara. Riflette tutti i colori\ndell'iride.", "A pretty, heart-shaped scale that is\nextremely rare. It glows faintly in the\ncolors of the rainbow.") },
            { PBEItem.HeatRock, new PBELocalizedString("もたせた　ポケモンが　にほんばれを\nつかうと　いつもより　はれの\nじかんが　ながくなる。", "지니게 한 포켓몬이 쾌청을\n쓰면 여느 때보다 쾌청의\n시간이 길어진다.", "Objet à tenir augmentant la durée\nde la capacité Zénith utilisée par le\nporteur.", "Ein Item zum Tragen. Verlängert\ndie Dauer von Sonnentag, wenn\nvon Träger eingesetzt.", "Prolonga la duración del movimiento\nDía Soleado que use el Pokémon\nque la lleva.", "Strumento da far tenere a un Pokémon\nche allunga la durata della mossa\nGiornodisole se usata da chi lo tiene.", "A Pokémon held item that extends the\nduration of the move Sunny Day used\nby the holder.") },
            { PBEItem.HeavyBall, new PBELocalizedString("たいじゅうの　おもい　ポケモンが\nつかまえやすくなる\nちょっと　かわった　ボール。　", "체중이 무거운 포켓몬이\n잡기 쉬워지는\n조금 특이한 볼.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien sur\nles Pokémon lourds.", "Ein Ball, der sich besonders gut eignet,\num Pokémon mit hohem Körpergewicht\nzu fangen.", "Poké Ball algo distinta que funciona\nespecialmente bien a la hora de atrapar\nPokémon de mucho peso.", "Poké Ball un po' originale che funziona\nparticolarmente bene sui Pokémon\npesanti.", "A Poké Ball for catching very heavy\nPokémon.") },
            { PBEItem.HelixFossil, new PBELocalizedString("おおむかし　うみに　すんでいた\nこだい　ポケモンの　カセキ。\nかいがらの　いちぶ　らしい。", "오랜 옛날 바다에 살았던\n고대 포켓몬의 화석.\n조개껍질의 일부인 듯하다.", "Fossile d'un ancien Pokémon aquatique.\nOn dirait un morceau de coquillage.", "Das Fossil eines uralten Pokémon,\ndas in der Tiefsee lebte. Es sieht\nwie ein Muschelstück aus.", "Fósil de un Pokémon ancestral que\nvivió en el fondo del mar. Parece ser\nun fragmento de concha marina.", "Fossile di un Pokémon antico che\nviveva in mare. Sembra essere\nparte di una conchiglia.", "A fossil of an ancient Pokémon that\nlived in the sea. It appears to be\npart of a seashell.") },
            { PBEItem.HondewBerry, new PBELocalizedString("ポケモンに　あげると\nとても　なつき　やすくなるが\nとくこうの　きそポイントは　さがる。", "포켓몬에게 주면\n매우 친밀해지기 쉬워지지만\n특수공격의 기초 포인트가 떨어진다.", "Rend les Pokémon plus amicaux, mais\nbaisse leur stat Attaque Spéciale de\nbase.", "Isst ein Pokémon diese Beere,\nstärkt dies eure Freundschaft, ver-\nringert aber seinen Spez.-Angr.-Wert.", "Al usarla con un Pokémon, se gana\nsu amistad, pero también reduce\nsu Ataque Especial de base.", "Utilizzata su un Pokémon, lo rende più\namichevole, ma diminuisce l'Attacco\nSpeciale di base.", "Using it on a Pokémon makes it more\nfriendly, but it also lowers its base\nSp. Atk stat.") },
            { PBEItem.Honey, new PBELocalizedString("くさむらや　どうくつ　などで\nつかうと　あまいにおいに　さそわれて\nやせいポケモンが　あらわれる。", "풀밭이나 동굴 등에서 사용하면\n달콤한 냄새에 이끌려서\n야생 포켓몬이 나타난다.", "Un miel au parfum riche qui, utilisé dans\nl'herbe, une grotte ou sur des arbres\nspéciaux, attire les Pokémon sauvages.", "Der Duft dieses Honigs lockt wilde\nPokémon an. Verwende ihn in hohem\nGras oder an besonderen Bäumen.", "Su delicioso aroma atrae a Pokémon\nsalvajes si se usa en zonas de hierba\nalta, cuevas o árboles especiales.", "Miele dolce dall'aroma intenso che\nattrae i Pokémon selvatici. Usalo nelle\ngrotte, nell'erba o su alberi speciali.", "A sweet honey with a lush aroma that\nattracts wild Pokémon when it is used\nin grass, caves, or on special trees.") },
            { PBEItem.HPUp, new PBELocalizedString("ポケモンの　えいようドリンク。\nポケモン　\xFF11ひきの　\xFF28\xFF30の\nきそポイントを　あげる。", "포켓몬의 영양 드링크.\n포켓몬 1마리의 HP\n기초 포인트를 올린다.", "Une boisson très nutritive qui monte\nles PV de base d'un Pokémon.", "Ein gehaltvolles Getränk, das die\nBasis-KP-Anzahl eines Pokémon\ndauerhaft erhöht.", "Nutritiva bebida que aumenta los PS\nde base de un Pokémon.", "Bevanda nutriente per Pokémon.\nAumenta i PS di base.", "A nutritious drink for Pokémon.\nIt raises the base HP of a single\nPokémon.") },
            { PBEItem.HyperPotion, new PBELocalizedString("スプレーしきの　キズぐすり。\nポケモン　\xFF11ひきの　\xFF28\xFF30を\n\xFF12\xFF10\xFF10だけ　かいふくする。", "스프레이식의 상처약.\n포켓몬 1마리의 HP를\n200만큼 회복한다.", "Un spray qui soigne les blessures.\nRestaure 200 PV à un Pokémon.", "Hilft bei Wunden. Füllt\ndie KP eines Pokémon\num 200 Punkte auf.", "Medicina en espray que cura heridas\ny restaura 200 PS de un Pokémon.", "Rimedio spray per le ferite.\nRestituisce 200 PS ad un Pokémon.", "A spray-type medicine for wounds.\nIt restores the HP of one Pokémon by\n200 points.") },
            { PBEItem.IapapaBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　\xFF28\xFF30を　かいふくする。\nきらいな　あじだと　こんらん　する。", "포켓몬에게 지니게 하면\n위급할 때 HP를 회복한다.\n싫어하는 맛이면 혼란에 빠진다.", "Tenue, cette Baie restaure des PV en\ncas de besoin, mais rend confus si\nle Pokémon n'aime pas son goût.", "Als getragenes Item füllt die Beere die\nKP eines Pokémon auf, doch ihr Aroma\nverwirrt jene, die es nicht mögen.", "Los Pokémon pueden llevarla o usarla\npara restaurar algunos PS. Su sabor\npuede causar confusión.", "Se tenuta da un Pokémon in difficoltà,\nrestituisce PS. Se al Pokémon non piace\nil suo sapore, può causare confusione.", "If held by a Pokémon, it restores the\nuser's HP in a pinch, but will cause\nconfusion if it hates the taste.") },
            { PBEItem.IceGem, new PBELocalizedString("こおりの　タイプの　ジュエル。\nもたせると　いちどだけ　こおりの\nわざの　いりょくが　つよまる。", "얼음타입의 주얼.\n지니게 하면 한 번만 얼음\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Glace.", "Ein Juwel vom Typ Eis. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Eis.", "Joya de un solo uso que potencia\nlos movimientos de tipo Hielo.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Ghiaccio.", "A gem with an essence of ice. When held,\nit strengthens the power of an\nIce-type move only once.") },
            { PBEItem.IceHeal, new PBELocalizedString("スプレーしきの　くすり。\nポケモン　\xFF11ひきの　こおりの\nじょうたいを　かいふくする。", "스프레이식의 약.\n포켓몬 1마리의\n얼음 상태를 회복한다.", "Un médicament sous forme de spray.\nRéchauffe un Pokémon gelé.", "Ein Spray, das ein eingefrorenes\nPokémon auftaut.", "Medicina en espray que descongela\nun Pokémon.", "Rimedio spray.\nCura un Pokémon dal congelamento.", "A spray-type medicine.\nIt defrosts a Pokémon that has been\nfrozen solid.") },
            { PBEItem.IciclePlate, new PBELocalizedString("こおりの　タイプの　せきばん。\nもたせると　こおりタイプの\nわざの　いりょくが　つよまる。", "얼음타입의 석판.\n지니게 하면 얼음타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Glace.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Eis.", "Tabla de piedra que potencia\nlos movimientos de tipo Hielo.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Ghiaccio.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Ice-type moves.") },
            { PBEItem.IcyRock, new PBELocalizedString("もたせた　ポケモンが　あられを\nつかうと　いつもより　あられの\nじかんが　ながくなる。", "지니게 한 포켓몬이 싸라기눈을\n쓰면 여느 때보다 싸라기눈의\n시간이 길어진다.", "Objet à tenir augmentant la durée\nde la capacité Grêle utilisée par le\nporteur.", "Ein Item zum Tragen. Verlängert\ndie Dauer von Hagelsturm, wenn\nvon Träger eingesetzt.", "Prolonga la duración del movimiento\nGranizo que use el Pokémon que\nla lleva.", "Strumento da far tenere a un Pokémon\nche allunga la durata della mossa\nGrandine se usata da chi lo tiene.", "A Pokémon held item that extends the\nduration of the move Hail used by the\nholder.") },
            { PBEItem.InquiryMail, new PBELocalizedString("あいてへの　しつもんの　メールが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "상대에게 질문하는 메일을\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Un papier à lettres idéal pour\nposer des questions. Peut être\ntenu et livré par un Pokémon.", "Briefpapier, mit dem man jemandem\neine Frage stellen kann.\nGib es einem Pokémon zum Tragen.", "Carta para preguntar a un compañero.\nHaz que la entregue un Pokémon.", "Carta da lettera ideale per scrivere\nuna domanda a qualcuno. Un Pokémon\npuò tenerla e consegnarla.", "Stationery designed for\nwriting questions.\nLet a Pokémon hold it for delivery.") },
            { PBEItem.InsectPlate, new PBELocalizedString("むしの　タイプの　せきばん。\nもたせると　むしタイプの\nわざの　いりょくが　つよまる。", "벌레타입의 석판.\n지니게 하면 벌레타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Insecte.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Käfer.", "Tabla de piedra que potencia\nlos movimientos de tipo Bicho.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Coleottero.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Bug-type moves.") },
            { PBEItem.Iron, new PBELocalizedString("ポケモンの　えいようドリンク。\nポケモン　\xFF11ひきの　ぼうぎょの\nきそポイントを　あげる。", "포켓몬의 영양 드링크.\n포켓몬 1마리의 방어\n기초 포인트를 올린다.", "Une boisson très nutritive qui monte\nla Défense de base d'un Pokémon.", "Ein gehaltvolles Getränk, das den\nVerteidigungs-Wert eines Pokémon\ndauerhaft erhöht.", "Nutritiva bebida que aumenta la Defensa\nde base de un Pokémon.", "Bevanda nutriente per Pokémon.\nAumenta la Difesa di base.", "A nutritious drink for Pokémon.\nIt raises the base Defense stat of a\nsingle Pokémon.") },
            { PBEItem.IronBall, new PBELocalizedString("もたせると　すばやさが　さがる。\nひこうタイプや　ふゆう　ポケモンは\nじめんわざが　あたって　しまう。", "지니게 하면 스피드가 떨어진다.\n비행타입이나 부유포켓몬은\n땅 기술에 맞아 버린다.", "Objet à tenir réduisant la Vitesse. Rend\nles porteurs de type Vol et lévitant\nsensibles aux capacités de type Sol.", "Ein Item zum Tragen, das Flug-Pokémon\nanfällig für Attacken vom Typ Boden\nmacht. Senkt den Initiative-Wert.", "Reduce la Velocidad. Los Pokémon que\nlevitan y los de tipo Volador se tornan\nsensibles a movimientos de tipo Tierra.", "Riduce la Velocità e rende vulnerabili\nalle mosse terra se chi la tiene è di tipo\nVolante o ha l'abilità Levitazione.", "A Pokémon held item that cuts Speed.\nIt makes Flying-type and levitating\nholders susceptible to Ground moves.") },
            { PBEItem.IronPlate, new PBELocalizedString("はがねの　タイプの　せきばん。\nもたせると　はがねタイプの\nわざの　いりょくが　つよまる。", "강철타입의 석판.\n지니게 하면 강철타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Acier.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Stahl.", "Tabla de piedra que potencia\nlos movimientos de tipo Acero.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Acciaio.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Steel-type moves.") },
            { PBEItem.JabocaBerry, new PBELocalizedString("ポケモンに　もたせると\nぶつりわざを　うけたとき\nあいてにも　ダメージを　あたえる。", "포켓몬에게 지니게 하면\n물리 기술을 당했을 때\n상대에게도 데미지를 준다.", "Tenue, cette Baie inflige des dégâts\nà l'ennemi s'il utilise une attaque\nphysique qui atteint son but.", "Als getragenes Item bewirkt die Beere\nSchaden bei denen, die den Träger\nmit einer physischen Attacke treffen.", "Si la lleva un Pokémon cuando otro\nlanza un ataque físico, este último\ntambién recibe daño.", "Se tenuta da un Pokémon, il nemico\nsubirà danni se il suo attacco fisico\nva a segno.", "If held by a Pokémon and a physical\nattack lands, the attacker also\ntakes damage.") },
            { PBEItem.KasibBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　ゴースト　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 고스트 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Spectre.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Geist.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Fantasma\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Spettro\ndel nemico.", "Weakens a supereffective Ghost-type\nattack against the holding Pokémon.") },
            { PBEItem.KebiaBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　どく　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 독 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Poison.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Gift.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Veneno\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Veleno\ndel nemico.", "Weakens a supereffective Poison-type\nattack against the holding Pokémon.") },
            { PBEItem.KelpsyBerry, new PBELocalizedString("ポケモンに　あげると\nとても　なつき　やすくなるが\nこうげきの　きそポイントは　さがる。", "포켓몬에게 주면\n매우 친밀해지기 쉬워지지만\n공격의 기초 포인트가 떨어진다.", "Rend les Pokémon plus amicaux, mais\nbaisse leur stat Attaque de base.", "Isst ein Pokémon diese Beere,\nstärkt dies eure Freundschaft,\nverringert aber seinen Angriffs-Wert.", "Al usarla con un Pokémon, se gana\nsu amistad, pero también reduce\nsu Ataque de base.", "Utilizzata su un Pokémon, lo rende più\namichevole, ma diminuisce l'Attacco\ndi base.", "Using it on a Pokémon makes it more\nfriendly, but it also lowers its base\nAttack stat.") },
            { PBEItem.KingsRock, new PBELocalizedString("もたせると　こうげきして\nダメージを　あたえたときに\nあいてを　ひるませることが　ある。", "지니게 하면\n공격해서 데미지를 줄 때\n상대를 풀죽이기도 한다.", "Objet à tenir. Peut apeurer l'ennemi\nlorsque le porteur lui inflige des\ndégâts.", "Gibst du es einem Pokémon zum Tragen,\nlässt es mit seinem Angriff das Ziel\ngelegentlich zurückschrecken.", "El Pokémon que la lleva puede\nhacer retroceder al Pokémon\nal que le inflige daño.", "Strumento da far tenere a un Pokémon.\nSe chi lo usa arreca danni, può far\ntentennare il nemico.", "An item to be held by a Pokémon.\nWhen the holder inflicts damage,\nthe target may flinch.") },
            { PBEItem.LaggingTail, new PBELocalizedString("とても　おもたい　なにかの　しっぽ。\nもたせると　いつもより　こうどうが\nおそくなる。", "매우 무거운 무언가의 꼬리.\n지니게 하면 여느 때보다\n행동이 느려진다.", "Objet à tenir extrêmement lourd qui\nralentit le porteur.", "Ein Item zum Tragen. Es ist immens\nschwer und senkt die Geschwindigkeit\ndes Trägers.", "Es muy pesada y hace que el Pokémon\nque la lleva se mueva más lento de\nlo normal.", "Strumento da far tenere a un Pokémon.\nÈ tremendamente pesante e rende chi\nlo tiene più lento del normale.", "An item to be held by a Pokémon.\nIt is tremendously heavy and makes\nthe holder move slower than usual.") },
            { PBEItem.LansatBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　こうげきが\nきゅうしょに　あたりやすくなる。", "포켓몬에게 지니게 하면\n위급할 때 공격이\n급소를 맞히기 쉬워진다.", "Tenue, cette Baie monte le taux de\ncritiques en cas de besoin.", "Als getragenes Item erhöht diese Beere\ndie Volltrefferquote eines Pokémon,\nsobald es in einer Notlage steckt.", "Si la lleva un Pokémon, aumenta\nla probabilidad de dar un golpe\ncrítico en un momento de apuro.", "Brutti colpi più probabili se il Pokémon\nche la tiene si trova in difficoltà.", "If held by a Pokémon, it raises its\ncritical-hit ratio in a pinch.") },
            { PBEItem.LavaCookie, new PBELocalizedString("フエンめいぶつの　せんべい。\nポケモン　\xFF11ひきの　じょうたいの\nいじょうを　すべて　かいふくする。", "용암마을의 명물인 전병.\n포켓몬 1마리의\n상태 이상을 회복한다.", "La spécialité de la ville de Vermilava.\nSoigne tous les problèmes de statut\nd'un Pokémon.", "Eine Spezialität aus Bad Lavastadt,\ndie alle Statusprobleme eines\nPokémon heilt.", "Dulce típico de Pueblo Lavacalda\nque cura todos los problemas de\nestado de un Pokémon.", "Specialità tipica di Cuordilava.\nRisolve tutti i problemi di stato\ndi un Pokémon.", "Lavaridge Town's local specialty.\nIt heals all the status problems of\none Pokémon.") },
            { PBEItem.LaxIncense, new PBELocalizedString("もたせると　おこうの　ふしぎな\nかおりが　あいてを　まどわせて\nわざが　めいちゅう　しにくくなる。", "지니게 하면 향로의 이상한\n향기가 상대를 혼란시켜\n기술이 명중하기 어려워진다.", "Objet à tenir. Le parfum trompeur\nde cet encens baisse la Précision de\nl'ennemi.", "Ein Item zum Tragen. Das\nverwirrende Aroma senkt die\nGenauigkeit des Gegners.", "Debe llevarlo un Pokémon.\nSu peculiar aroma reduce la Precisión\ndel objetivo.", "Strumento da far tenere a un Pokémon.\nIl suo aroma intenso riduce\nla precisione del nemico.", "An item to be held by a Pokémon. The\ntricky aroma of this incense may make\nattacks miss the holder.") },
            { PBEItem.LeafStone, new PBELocalizedString("ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nはっぱの　もようが　ある。", "어느 특정한 포켓몬을\n진화시키는 이상한 돌.\n잎사귀 무늬가 있다.", "Une pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nUne feuille est dessinée dessus.", "Dieser spezielle Stein löst bei\nbestimmten Pokémon die Entwicklung\naus. Er hat ein Blattmuster.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nTiene grabada una hoja.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nRiporta l'immagine di una foglia.", "A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt has a leaf pattern.") },
            { PBEItem.Leftovers, new PBELocalizedString("もたせると　ポケモンの　\xFF28\xFF30が\nせんとうの　あいだ　すこしずつ\nかいふくする。", "지니게 하면 포켓몬의 HP가\n배틀을 하는 동안 조금씩\n회복된다.", "Objet à tenir permettant de restaurer\nles PV du porteur graduellement durant\nle combat.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nfüllt im Kampf stetig KP auf.", "Restaura gradualmente los PS\ndel Pokémon que lo lleva\ndurante el combate.", "Strumento da far tenere a un Pokémon.\nFa recuperare gradualmente i PS\ndurante la lotta.", "An item to be held by a Pokémon.\nThe holder's HP is gradually restored\nduring battle.") },
            { PBEItem.Lemonade, new PBELocalizedString("とても　あまい　ジュース。\nポケモン　\xFF11ひきの　\xFF28\xFF30を\n\xFF18\xFF10だけ　かいふくする。", "매우 달콤한 쥬스.\n포켓몬 1마리의 HP를\n80만큼 회복한다.", "Une boisson très sucrée.\nRestaure 80 PV à un Pokémon.", "Ein sehr süßes Getränk, das die KP\neines Pokémon um 80 Punkte auffüllt.", "Bebida muy dulce que restaura 80 PS\nde un Pokémon.", "Bevanda molto dolce.\nRestituisce 80 PS ad un Pokémon.", "A very sweet drink.\nIt restores the HP of one Pokémon by\n80 points.") },
            { PBEItem.LeppaBerry, new PBELocalizedString("ポケモンに　もたせると\n\xFF30\xFF30を　\xFF11\xFF10だけ　かいふくする。\n", "포켓몬에게 지니게 하면\nPP를 10만큼 회복한다.\n", "Baie qui restaure 10 PP lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon\nzum Tragen, füllt sie die AP einer\nAttacke um 10 Punkte auf.", "Los Pokémon pueden llevarla o usarla\npara restaurar 10 PP de un movimiento.", "Se tenuta da un Pokémon restituisce\n10 PP a una mossa.", "If held by a Pokémon,\nit restores a move's PP by 10.") },
            { PBEItem.LevelBall, new PBELocalizedString("じぶんの　ポケモンより　レベルが\nひくいほど　つかまえやすくなる\nちょっと　かわった　ボール。　", "자신의 포켓몬보다 레벨이\n낮을수록 잡기 쉬워지는\n조금 특이한 볼.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien sur\nles Pokémon de niveau inférieur au sien.", "Ein Ball, mit dem sich Pokémon umso\nleichter fangen lassen, je niedriger ihr\nLevel im Vergleich zu deinem Team ist.", "Poké Ball algo distinta que funciona\nespecialmente bien con Pokémon\nde nivel inferior a los que se posee.", "Poké Ball un po' originale che funziona\nparticolarmente bene sui Pokémon\ndi livello inferiore al proprio.", "A Poké Ball for catching Pokémon that\nare a lower level than your own.") },
            { PBEItem.LiechiBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　じぶんの\nこうげきが　あがる。", "포켓몬에게 지니게 하면\n위급할 때 자신의\n공격이 올라간다.", "Tenue, cette Baie monte l'Attaque\nen cas de besoin.", "Als getragenes Item erhöht diese Beere\nden Angriffs-Wert eines Pokémon, sobald\nes in einer Notlage steckt.", "Si la lleva un Pokémon, le sube\nel Ataque en un momento de apuro.", "Aumenta l'Attacco, se il Pokémon che\nla tiene si trova in difficoltà.", "If held by a Pokémon, it raises its\nAttack stat in a pinch.") },
            { PBEItem.LifeOrb, new PBELocalizedString("もたせると　こうげきする　たびに\n\xFF28\xFF30が　すこし　へってしまうが\nわざの　いりょくが　あがる。", "지니게 하면 공격할 때마다\nHP가 조금씩 줄지만\n기술의 위력이 올라간다.", "Objet à tenir augmentant la puissance\ndes capacités, mais qui dépense des\nPV à chaque coup.", "Ein Item zum Tragen.\nVerstärkt Attacken, aber jede\nAttacke kostet den Träger KP.", "Potencia los movimientos, pero\nconsume PS con cada ataque.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nPotenzia le mosse, a scapito di alcuni\nPS per ogni colpo.", "An item to be held by a Pokémon.\nIt boosts the power of moves, but at\nthe cost of some HP on each hit.") },
            { PBEItem.LightBall, new PBELocalizedString("ピカチュウに　もたせると\nこうげきと　とくこうの　いりょくが\nあがる　ふしぎな　たま。", "피카츄에게 지니게 하면\n공격과 특수공격의 위력이\n올라가는 이상한 구슬.", "Objet à faire tenir par Pikachu. Orbe\nénigmatique qui monte son Attaque et\nson Attaque Spéciale.", "Ein Item, das von Pikachu getragen\nwerden kann. Es erhöht den Angriffs-\nWert und Spezial-Angriff.", "Asombrosa esfera que debe llevar\nPikachu y que aumenta su Ataque\ny su Ataque Especial.", "Strumento da far tenere a Pikachu.\nSfera insolita che aumenta l'Attacco e\nl'Attacco Speciale.", "An item to be held by Pikachu.\nIt is a puzzling orb that raises\nthe Attack and Sp. Atk stat.") },
            { PBEItem.LightClay, new PBELocalizedString("もたせた　ポケモンが　ひかりのかべや\nリフレクターを　つかったとき\nいつもよりも　ながく　のこる。", "지니게 한 포켓몬이 빛의장막이나\n리플렉터를 사용했을 때\n여느 때보다 긴 시간동안 지속된다.", "Objet à tenir augmentant la durée\nde capacités barrières telles que\nMur Lumière et Protection.", "Ein Item zum Tragen.\nVerlängert die Dauer von\nLichtschild, Reflektor etc.", "Prolonga la duración de barreras\ncomo Reflejo y Pantalla de Luz.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nAllunga la durata delle mosse barriera\ncome Schermoluce e Riflesso.", "A Pokémon held item that extends the\nduration of barrier moves like Light\nScreen and Reflect used by the holder.") },
            { PBEItem.LikeMail, new PBELocalizedString("なにかを　おすすめする　メールが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "뭔가 추천하는 메일을\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Un papier à lettres idéal pour\ndonner des conseils. Peut être\ntenu et livré par un Pokémon.", "Briefpapier, mit dem man jemandem\neinen Tipp geben kann.\nGib es einem Pokémon zum Tragen.", "Carta para recomendar algo.\nHaz que la entregue un Pokémon.", "Carta da lettera ideale per consigliare\nqualcosa a qualcuno. Un Pokémon può\ntenerla e consegnarla.", "Stationery designed for\nwriting recommendations.\nLet a Pokémon hold it for delivery.") },
            { PBEItem.LoveBall, new PBELocalizedString("じぶんの　ポケモンと　せいべつが\nちがうと　つかまえやすくなる\nちょっと　かわった　ボール。　", "자신의 포켓몬과 성별이\n다르면 잡기 쉬워지는\n조금 특이한 볼.", "Une Poké Ball un peu spéciale qui\nfonctionne mieux sur un Pokémon de\nla même espèce mais de sexe opposé.", "Ein Ball, der sich besonders gut eignet,\num Pokémon zu fangen, die ein anderes\nGeschlecht haben als dein Pokémon.", "Poké Ball algo distinta que atrapa mejor\nPokémon de la misma especie que los\nque se posee, pero de sexo diferente.", "Poké Ball speciale che funziona meglio\nsui Pokémon di sesso opposto a quello\ndel proprio e della stessa specie.", "Poké Ball for catching Pokémon that are\nthe opposite gender of your Pokémon.") },
            { PBEItem.LuckIncense, new PBELocalizedString("もたせた　ポケモンが　いちどでも\nせんとうに　かおを　だすと\nおかねが　\xFF12ばい　もらえる。", "지니게 한 포켓몬이\n한 번이라도 배틀에 나가면\n돈을 2배로 받을 수 있다.", "Objet à tenir. Double l'argent gagné\nsi le porteur se joint au combat.", "Ein Item, das ein Pokémon trägt und\ndas doppelte Preisgeld bringt, wenn\nder Träger am Kampf teilnimmt.", "Si el Pokémon que lo lleva lucha en\nun combate, duplica las ganancias.", "Strumento da far tenere a un Pokémon.\nRaddoppia i guadagni nella lotta se\nchi lo tiene vi partecipa.", "An item to be held by a Pokémon.\nIt doubles a battle's prize money if\nthe holding Pokémon joins in.") },
            { PBEItem.LuckyEgg, new PBELocalizedString("しあわせが　つまった　タマゴ。\nもたせた　ポケモンは　もらえる\nけいけんちが　すこし　ふえる。", "행복이 가득한 알.\n지니게 한 포켓몬은 받을 수 있는\n경험치가 조금 늘어난다.", "Objet à tenir. Œuf rempli de joie qui\naugmente le nombre de points Exp.\ngagnés au combat.", "Ein getragenes Item, das mit Glück\ngefüllt ist und für zusätzliche\nE.-Punkte im Kampf sorgt.", "Es un huevo lleno de felicidad que te\nhace ganar Puntos de Experiencia extra\nen combate. Debe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nUovo pieno di felicità che aumenta\ni Punti Esperienza in lotta.", "An item to be held by a Pokémon.\nIt is an egg filled with happiness that\nearns extra Exp. Points in battle.") },
            { PBEItem.LuckyPunch, new PBELocalizedString("こううんを　よぶ　グローブ。\nラッキーに　もたせると　わざが\nきゅうしょに　あたりやすくなる。", "행운을 부르는 글러브다.\n럭키에게 지니게 하면 기술이\n급소에 맞기 쉬워진다.", "Objet à faire tenir à Leveinard. Paire\nde gants montant son taux de critiques.", "Ein Item, das von Chaneira getragen\nwerden kann. Diese Handschuhe erhöhen\ndie Volltrefferquote von Chaneira.", "Un par de guantes que debe llevar\nChansey. Potencian su índice\nde golpe crítico mucho.", "Strumento da far tenere a Chansey.\nGuanti che aumentano di molto la\nprobabilità di sferrare brutti colpi.", "An item to be held by Chansey. It is a\npair of gloves that boosts Chansey's\ncritical-hit ratio.") },
            { PBEItem.LumBerry, new PBELocalizedString("ポケモンに　もたせると\nすべての　じょうたい　いじょうを\nかいふくする。", "포켓몬에게 지니게 하면\n모든 상태 이상을\n회복한다.", "Baie qui soigne tous les problèmes\nde statut lorsqu'elle est tenue\npar un Pokémon.", "Gibst du die Beere einem Pokémon\nzum Tragen, hebt sie alle seine\nStatusprobleme auf.", "Los Pokémon pueden llevarla o usarla\npara recuperarse de problemas\nde estado.", "Se tenuta da un Pokémon risolve\nqualsiasi problema di stato.", "If held by a Pokémon,\nit recovers from any status problem.") },
            { PBEItem.LureBall, new PBELocalizedString("つりざおで　つりあげた　ポケモンだと\nつかまえやすくなる\nちょっと　かわった　ボール。　", "낚싯대로 낚아올린 포켓몬이라면\n잡기 쉬워지는\n조금 특이한 볼.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien sur les\nPokémon ferrés avec une canne à pêche.", "Ein Ball, der sich besonders gut\neignet, um Pokémon zu fangen,\ndie an deiner Angel hängen.", "Poké Ball algo distinta, especialmente\nindicada para capturar los Pokémon\nque se han pescado con una Caña.", "Poké Ball un po' originale che funziona\nparticolarmente bene sui Pokémon\npescati con un Amo.", "A Poké Ball for catching Pokémon hooked\nby a Rod when fishing.") },
            { PBEItem.LustrousOrb, new PBELocalizedString("パルキアに　もたせると　ドラゴンと\nみずタイプの　わざの　いりょくが\nあがる　うつくしく　かがやく　たま。", "펄기아에게 지니게 하면 드래곤과\n물타입 기술의 위력이 올라가는\n아름답게 반짝이는 구슬.", "Bel orbe luisant destiné à Palkia.\nAugmente la puissance des capacités\nde type Dragon et Eau.", "Ein hell leuchtender Orb.\nVerstärkt Attacken vom Typ Drachen\nund Wasser, wenn Palkia ihn trägt.", "Una bonita esfera que potencia\nlos movimientos de tipo Dragón y Agua.\nDebe llevarla Palkia.", "Sfera splendente da far tenere\na Palkia. Potenzia le mosse di tipo\nDrago e Acqua.", "A beautifully glowing orb to be held by\nPalkia. It boosts the power of Dragon-\nand Water-type moves.") },
            { PBEItem.LuxuryBall, new PBELocalizedString("つかまえた　やせいポケモンが\nとても　なつきやすくなる\nいごこちの　いい　ボール。", "잡은 야생 포켓몬이\n매우 친밀해지기 쉬운\n편안한 볼.", "Une Poké Ball pratique qui permet\nde gagner rapidement l'amitié d'un\nPokémon sauvage attrapé.", "Ein behaglicher Ball, der Pokémon\nveranlasst, nach dem Fang schneller\nfreundlicher zu werden.", "Acogedora Poké Ball que hace más\namistosos a los Pokémon salvajes\ncapturados.", "Poké Ball utile che rende più amichevoli\ni Pokémon selvatici catturati.", "A comfortable Poké Ball that makes a\ncaught wild Pokémon quickly grow\nfriendly.") },
            { PBEItem.MachoBrace, new PBELocalizedString("かたくて　おもい　ギプス。\nもたせると　すばやさは　さがるが\nふつうより　つよく　そだちやすい。", "단단하고 무거운 깁스.\n지니게 하면 스피드는 떨어지지만\n여느 때보다 강하게 키우기 쉽다.", "Objet à tenir. C'est un bracelet dur\net lourd qui rend le porteur plus fort,\nmais baisse sa Vitesse.", "Ein Item, das ein Pokémon tragen\nkann. Der Initiative-Wert sinkt zwar,\naber es wird dafür umso stärker.", "Sólido y pesado brazal. Sube más las\ncaracterísticas al subir nivel, pero\nbaja la Velocidad de quien lo lleva.", "Strumento da far tenere a un Pokémon.\nAttrezzo pesante che riduce la Velocità\nma aiuta la crescita delle statistiche.", "An item to be held by a Pokémon. It is\na stiff and heavy brace that promotes\nstrong growth but lowers Speed.") },
            { PBEItem.Magmarizer, new PBELocalizedString("とてつもない　マグマの　エネルギーが\nたくわえられている　はこ。\nある　ポケモンが　このんでいる。", "엄청난 마그마의 에너지가\n비축되어 있는 상자.\n어떤 포켓몬이 좋아하는 물건이다.", "Une boîte remplie d'une énorme quantité\nd'énergie magmatique. Appréciée d'un\ncertain Pokémon.", "Eine Schachtel mit Magmaenergie.\nDas Lieblingsstück eines bestimmten\nPokémon.", "Esta caja contiene una enorme\ncantidad de energía magmática.\nCierto Pokémon lo adora.", "Scatola strapiena di energia lavica.\nUn Pokémon in particolare adora\nquesto strumento.", "A box packed with a tremendous\namount of magma energy.\nIt is loved by a certain Pokémon.") },
            { PBEItem.Magnet, new PBELocalizedString("きょうりょくな　じしゃく。\nもたせると　でんきタイプの\nわざの　いりょくが　あがる。", "강력한 자석.\n지니게 하면 전기타입\n기술의 위력이 올라간다.", "Objet à tenir. Aimant puissant montant\nla puissance des capacités de type\nÉlectrik.", "Ein Item zum Tragen, dessen\nmagnetische Kraft Attacken\nvom Typ Elektro verstärkt.", "Debe llevarlo un Pokémon.\nSu potente magnetismo fortalece\nlos movimientos de tipo Eléctrico.", "Strumento da far tenere a un Pokémon.\nPotente magnete che potenzia le mosse\ndi tipo Elettro.", "An item to be held by a Pokémon.\nIt is a powerful magnet that boosts\nthe power of Electric-type moves.") },
            { PBEItem.MagoBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　\xFF28\xFF30を　かいふくする。\nきらいな　あじだと　こんらん　する。", "포켓몬에게 지니게 하면\n위급할 때 HP를 회복한다.\n싫어하는 맛이면 혼란에 빠진다.", "Tenue, cette Baie restaure des PV en\ncas de besoin, mais rend confus si\nle Pokémon n'aime pas son goût.", "Als getragenes Item füllt die Beere die\nKP eines Pokémon auf, doch ihr Aroma\nverwirrt jene, die es nicht mögen.", "Los Pokémon pueden llevarla o usarla\npara restaurar algunos PS. Su sabor\npuede causar confusión.", "Se tenuta da un Pokémon in difficoltà,\nrestituisce PS. Se al Pokémon non piace\nil suo sapore, può causare confusione.", "If held by a Pokémon, it restores the\nuser's HP in a pinch, but will cause\nconfusion if it hates the taste.") },
            { PBEItem.MagostBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.MasterBall, new PBELocalizedString("やせいの　ポケモンを　かならず\nつかまえることが　できる\nさいこう　せいのうの　ボール。", "야생 포켓몬을 반드시\n잡을 수 있는\n최고 성능의 볼.", "Assurément la Ball la plus performante.\nElle permet de capturer à coup sûr un\nPokémon sauvage.", "Der beste Ball!\nDamit fängst du garantiert\njedes wilde Pokémon.", "La Poké Ball definitiva. Atrapa\ncualquier Pokémon salvaje y no\nfalla nunca.", "La Ball dalle prestazioni migliori:\ncattura qualsiasi Pokémon selvatico\nsenza mai fallire.", "The best Ball with the ultimate level of\nperformance. It will catch any wild\nPokémon without fail.") },
            { PBEItem.MaxElixir, new PBELocalizedString("ポケモンが　おぼえている\n\xFF14つの　わざの　\xFF30\xFF30を\nすべて　かいふくする。", "포켓몬이 기억하고 있는\n4개 기술의 PP를\n모두 회복한다.", "Restaure tous les PP de toutes\nles capacités d'un Pokémon.", "Füllt alle AP aller Attacken eines\nPokémon vollständig auf.", "Restaura completamente los PP de\ntodos los movimientos aprendidos\npor el Pokémon elegido.", "Restituisce tutti i PP a ogni mossa\nimparata dal Pokémon.", "It fully restores the PP of all the\nmoves learned by the targeted\nPokémon.") },
            { PBEItem.MaxEther, new PBELocalizedString("ポケモンが　おぼえている\nわざのうち　\xFF11つの　\xFF30\xFF30を\nすべて　かいふくする。", "포켓몬이 기억하고 있는\n기술 중 1개의 PP를\n모두 회복한다.", "Restaure tous les PP d'une capacité\nsélectionnée.", "Füllt die AP einer ausgewählten Attacke\neines Pokémon vollständig auf.", "Restaura completamente los PP del\nmovimiento elegido de un Pokémon.", "Restituisce tutti i PP a una mossa\na scelta che è stata imparata\ndal Pokémon.", "It fully restores the PP of a single\nselected move that has been learned\nby the target Pokémon.") },
            { PBEItem.MaxPotion, new PBELocalizedString("スプレーしきの　キズぐすり。\nポケモン　\xFF11ひきの　\xFF28\xFF30を\nすべて　かいふくする。", "스프레이식의 상처약.\n포켓몬 1마리의 HP를\n모두 회복한다.", "Un spray qui soigne les blessures.\nRestaure tous les PV d'un Pokémon.", "Hilft bei Wunden. Füllt\ndie KP eines Pokémon\nvollständig wieder auf.", "Medicina en espray que cura heridas\ny restaura todos los PS de un Pokémon.", "Rimedio spray per le ferite.\nRestituisce tutti i PS ad un Pokémon.", "A spray-type medicine for wounds.\nIt completely restores the HP of a\nsingle Pokémon.") },
            { PBEItem.MaxRepel, new PBELocalizedString("\xFF12\xFF15\xFF10ぽ　すすむ　あいだ\nよわい　やせいの　ポケモンが\nまったく　でて　こなくなる。", "250보 걸어가는 동안\n약한 야생 포켓몬이\n전혀 나오지 않게 된다.", "Repousse les Pokémon sauvages\nfaibles pendant 250 pas.", "Hält 250 Schritte lang schwache,\nwilde Pokémon ab.", "Repele Pokémon salvajes débiles\nen un recorrido de 250 pasos.", "Repellente che evita l'incontro con i\nPokémon selvatici deboli per 250 passi\ndopo l'utilizzo.", "An item that prevents weak wild\nPokémon from appearing for 250 steps\nafter its use.") },
            { PBEItem.MaxRevive, new PBELocalizedString("ひんしに　なってしまった\nポケモン　\xFF11ひきの　\xFF28\xFF30を\nすべて　かいふくする。", "기절해 버린\n포켓몬 1마리의 HP를\n모두 회복한다.", "Un médicament qui ranime un Pokémon\nK.O. et restaure tous ses PV.", "Eine Medizin, die ein kampfunfähiges\nPokémon wiederbelebt. Die KP werden\nvollständig wiederhergestellt.", "Medicina que reanima a un Pokémon\ndebilitado y restaura todos sus PS.", "Rimedio che rianima un Pokémon esausto\nridandogli tutti i PS.", "A medicine that revives a fainted\nPokémon. It fully restores the\nPokémon's HP.") },
            { PBEItem.MeadowPlate, new PBELocalizedString("くさの　タイプの　せきばん。\nもたせると　くさタイプの\nわざの　いりょくが　つよまる。", "풀타입의 석판.\n지니게 하면 풀타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Plante.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Pflanze.", "Tabla de piedra que potencia\nlos movimientos de tipo Planta.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Erba.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Grass-type moves.") },
            { PBEItem.MentalHerb, new PBELocalizedString("もたせた　ポケモンが　じゆうに\nわざを　だせなくなったときに\nいちどだけ　かいふくする。", "지니게 한 포켓몬은\n헤롱헤롱 상태가 되었을 때\n한 번만 회복한다.", "Objet à tenir qui annule l'attirance\nd'un Pokémon. Ne peut être utilisé\nqu'une fois.", "Ein Item, das einem Pokémon zum Tragen\ngegeben werden kann. Hilft einmalig bei\neingeschränktem Handlungsspielraum.", "El Pokémon que la lleva se libera\ndel enamoramiento. Solo puede\nusarse una vez.", "Strumento monouso da far tenere a un\nPokémon. Annulla l'infatuazione di chi\nlo tiene.", "An item to be held by a Pokémon. It\nsnaps the holder out of infatuation.\nIt can be used only once.") },
            { PBEItem.MetalCoat, new PBELocalizedString("とくしゅな　きんぞくの　まく。\nもたせると　はがねタイプの\nわざの　いりょくが　あがる。", "특수한 금속 장막.\n지니게 하면 강철타입\n기술의 위력이 올라간다.", "Objet à tenir. Pellicule métallique\nspéciale qui monte la puissance des\ncapacités de type Acier.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nverstärkt Attacken vom Typ Stahl.", "Película metálica que fortalece\nlos ataques de tipo Acero.\nDebe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nSpeciale pellicola metallica che\npotenzia le mosse di tipo Acciaio.", "An item to be held by a Pokémon.\nIt is a special metallic film that ups\nthe power of Steel-type moves.") },
            { PBEItem.MetalPowder, new PBELocalizedString("メタモンに　もたせると　ぼうぎょが\nあがる　ふしぎなこな。\nとても　こまかくて　かたい。", "메타몽에게 지니게 하면\n방어가 올라가는 이상한 가루.\n매우 잘고 단단하다.", "Objet à faire tenir à Métamorph. Cette\npoudre étrange, très fine mais\nrésistante, booste sa Défense.", "Ein Item, das von Ditto getragen\nwerden kann. Es erhöht seinen\nVerteidigungs-Wert.", "Debe llevarlo Ditto. Es un polvo\nmuy fino, pero a la vez resistente,\nque aumenta su Defensa.", "Strumento da far tenere a Ditto.\nStrana polvere finissima e al tempo\nstesso dura che migliora la Difesa.", "An item to be held by Ditto.\nExtremely fine yet hard, this odd\npowder boosts the Defense stat.") },
            { PBEItem.Metronome, new PBELocalizedString("もたせると　おなじわざを　れんぞくで\nつかったとき　いりょくが　あがる。\nやめると　いりょくは　もどる。", "지니게 하면 똑같은 기술을 연속으로\n사용했을 때 위력이 올라간다.\n그만두면 위력이 되돌아간다.", "Objet à tenir boostant une capacité\nutilisée à répétition. L'effet s'annule\nsi une autre capacité est utilisée.", "Ein Item zum Tragen. Verstärkt eine\nAttacke, je öfter sie ununterbrochen\nausgeführt wird.", "Potencia un movimiento que se use de\nforma consecutiva. Pero el efecto se\npierde si se emplea otro movimiento.", "Strumento da far tenere a un Pokémon.\nPotenzia una mossa usata di seguito.\nL'aumento svanisce al cambio di mossa.", "A Pokémon held item that boosts a move\nused consecutively. Its effect is\nreset if another move is used.") },
            { PBEItem.MicleBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　いちどだけ\nわざの　めいちゅうりつが　あがる。", "포켓몬에게 지니게 하면\n위급할 때 한 번만\n기술의 명중률이 올라간다.", "Tenue, cette Baie augmente une fois\nla Précision d'une capacité en cas de\nbesoin.", "Als getragenes Item erhöht diese Beere\ndie Genauigkeit eines Pokémon, sobald\nes in einer Notlage steckt.", "Cuando la lleva un Pokémon, le sube\nla Precisión de un movimiento en un\nmomento de apuro.", "Se il Pokémon che ce l'ha è in difficoltà,\naumenta la precisione della sua mossa\nsuccessiva.", "If held by a Pokémon, it raises the\naccuracy of a move just once in a pinch.") },
            { PBEItem.MindPlate, new PBELocalizedString("エスパーの　タイプの　せきばん。\nもたせると　エスパータイプの\nわざの　いりょくが　つよまる。", "에스퍼타입의 석판.\n지니게 하면 에스퍼타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Psy.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Psycho.", "Tabla de piedra que potencia\nlos movimientos de tipo Psíquico.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Psico.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Psychic-type moves.") },
            { PBEItem.MiracleSeed, new PBELocalizedString("せいめいが　やどる　タネ。\nもたせると　くさタイプの\nわざの　いりょくが　あがる。", "생명이 깃든 씨앗.\n지니게 하면 풀타입\n기술의 위력이 올라간다.", "Objet à tenir. Graine remplie de vie qui\nmonte la puissance des capacités de\ntype Plante.", "Ein Item zum Tragen. Ein Samen\nvoller Lebenskraft, der Attacken\nvom Typ Pflanze verstärkt.", "Debe llevarla un Pokémon.\nLa vitalidad de la semilla fortalece\nlos movimientos de tipo Planta.", "Strumento da far tenere a un Pokémon.\nSeme intriso di vita che potenzia\nle mosse di tipo Erba.", "An item to be held by a Pokémon.\nIt is a seed imbued with life that ups\nthe power of Grass-type moves.") },
            { PBEItem.MoomooMilk, new PBELocalizedString("えいよう　まんてんの　ミルク。\nポケモン　\xFF11ひきの　\xFF28\xFF30を\n\xFF11\xFF10\xFF10だけ　かいふくする。", "영양 만점의 우유.\n포켓몬 1마리의 HP를\n100만큼 회복한다.", "Un lait très nourrissant.\nRestaure 100 PV à un Pokémon.", "Eine äußerst nährstoffreiche Milch, die\ndie KP eines Pokémon um 100 Punkte\nauffüllt.", "Leche de alto valor nutritivo que\nrestaura 100 PS de un Pokémon.", "Latte ricco di sostanze nutrienti.\nRestituisce 100 PS ad un Pokémon.", "Milk with a very high nutrition content.\nIt restores the HP of one Pokémon by\n100 points.") },
            { PBEItem.MoonBall, new PBELocalizedString("つきのいしで　しんかする　ポケモンが\nつかまえやすくなる\nちょっと　かわった　ボール。　", "달의돌로 진화하는 포켓몬이\n잡기 쉬워지는\n조금 특이한 볼.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien sur les\nPokémon évoluant avec une Pierre Lune.", "Ein Ball, der sich besonders gut eignet,\num Pokémon zu fangen, die sich durch\neinen Mondstein weiterentwickeln.", "Poké Ball algo distinta que atrapa con\nmás facilidad los Pokémon que\nevolucionan con la Piedra Lunar.", "Poké Ball un po' originale che funziona\nparticolarmente bene sui Pokémon\nche si evolvono con una Pietralunare.", "A Poké Ball for catching Pokémon that\nevolve using the Moon Stone.") },
            { PBEItem.MoonStone, new PBELocalizedString("ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nよぞらのように　くろい。", "어느 특정한 포켓몬을\n진화시키는 이상한 돌.\n밤하늘처럼 까맣다.", "Une pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle est noire comme la nuit.", "Dieser spezielle Stein löst bei\nbestimmten Pokémon die Entwicklung\naus. Er ist schwarz wie die Nacht.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nEs negra como el azabache.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nÈ nera come il cielo di notte.", "A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt is as black as the night sky.") },
            { PBEItem.MuscleBand, new PBELocalizedString("ちからが　わいてくる　ハチマキ。\nもたせると　ぶつりわざの\nいりょくが　すこし　あがる。", "힘이 끓어오르는 머리띠.\n지니게 하면 물리 기술의\n위력이 조금 올라간다.", "Objet à tenir. Bandeau augmentant\nlégèrement la puissance des attaques\nphysiques.", "Ein Item zum Tragen.\nDieses Stirnband verstärkt\nphysische Attacken etwas.", "Cinta de pelo que aumenta ligeramente\nla potencia de los movimientos físicos.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nFascia che potenzia di poco gli attacchi\nfisici.", "An item to be held by a Pokémon.\nIt is a headband that slightly boosts\nthe power of physical moves.") },
            { PBEItem.MuscleWing, new PBELocalizedString("ポケモンに　つかう　どうぐ。\nポケモン　\xFF11ひきの　こうげきの\nきそポイントを　ちょっと　あげる。", "포켓몬에게 사용하는 도구.\n포켓몬 1마리의 공격\n기초 포인트를 조금 올린다.", "Objet à utiliser sur un Pokémon.\nIl augmente un peu son Attaque\nde base.", "Schenkt man einem Pokémon dieses\nItem, wird dessen Angriffs-Wert\ndauerhaft erhöht.", "Pluma que aumenta un poco el Ataque\nde un Pokémon al subir de nivel.", "Strumento da usare su un Pokémon.\nNe aumenta di poco l'Attacco di base.", "An item for use on a Pokémon. It\nslightly increases the base Attack stat\nof a single Pokémon.") },
            { PBEItem.MysticWater, new PBELocalizedString("しずくの　かたちの　ほうせき。\nもたせると　みずタイプの\nわざの　いりょくが　あがる。", "물방울 모양의 보석.\n지니게 하면 물타입\n기술의 위력이 올라간다.", "Objet à tenir. Gemme en forme de\ngoutte d'eau montant la puissance\ndes capacités de type Eau.", "Ein Item zum Tragen. Ein tränen-\nförmiger Edelstein, der Attacken\nvom Typ Wasser verstärkt.", "Gema con forma de gota de agua que\nfortalece los movimientos de tipo Agua.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGemma a forma di goccia che potenzia\nle mosse di tipo Acqua.", "An item to be held by a Pokémon.\nIt is a teardrop-shaped gem that ups\nthe power of Water-type moves.") },
            { PBEItem.NanabBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.NestBall, new PBELocalizedString("つかまえる　やせいの　ポケモンが\nよわいほど　つかまえやすくなる\nちょっと　かわった　ボール。", "잡으려는 야생 포켓몬이\n약할수록 잡기 쉬운\n조금 특이한 볼.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien\nsur les Pokémon les plus faibles.", "Ein Ball, dessen Erfolgsquote\nbesonders bei schwächeren\nPokémon sehr hoch ist.", "Es algo distinta a las demás. Funciona\nespecialmente bien con los Pokémon\nsalvajes de nivel bajo.", "Poké Ball un po' diversa. Risulta\nparticolarmente efficace sui Pokémon\nselvatici più deboli.", "A somewhat different Poké Ball that\nworks especially well on weaker\nPokémon in the wild.") },
            { PBEItem.NetBall, new PBELocalizedString("みずタイプと　むしタイプの\nポケモンが　つかまえやすくなる\nちょっと　かわった　ボール。", "물타입과 벌레타입의\n포켓몬을 잡기 쉬운\n조금 특이한 볼.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien\nsur les Pokémon Eau ou Insecte.", "Ein Ball, der sich besonders gut eignet,\num Wasser- und Käfer-Pokémon\nzu fangen.", "Es algo distinta a las demás. Funciona\nespecialmente bien con los Pokémon\nde tipo Agua y Bicho.", "Poké Ball un po' diversa. Risulta\nparticolarmente efficace sui Pokémon\ndi tipo Acqua e Coleottero.", "A somewhat different Poké Ball that\nworks especially well on Water- and\nBug-type Pokémon.") },
            { PBEItem.NeverMeltIce, new PBELocalizedString("ねつを　よせつけない　こおり。\nもたせると　こおりタイプの\nわざの　いりょくが　あがる。", "열을 가까이하지 않게 하는 얼음.\n지니게 하면 얼음타입\n기술의 위력이 올라간다.", "Objet à tenir. Morceau de glace qui\nréfléchit la chaleur et monte la\npuissance des capacités de type Glace.", "Ein Item zum Tragen. Ein Stück Eis,\ndas Hitze fernhält und Attacken\nvom Typ Eis verstärkt.", "Fragmento de hielo que repele el calor y\nfortalece los movimientos de tipo Hielo.\nDebe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nPezzo di ghiaccio che respinge il calore\ne potenzia le mosse di tipo Ghiaccio.", "An item to be held by a Pokémon.\nIt is a piece of ice that repels heat\nand boosts Ice-type moves.") },
            { PBEItem.NomelBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.NormalGem, new PBELocalizedString("ノーマルの　タイプの　ジュエル。\nもたせると　いちどだけ　ノーマルの\nわざの　いりょくが　つよまる。", "노말타입의 주얼.\n지니게 하면 한 번만 노말\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Normal.", "Ein Juwel vom Typ Normal. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Normal.", "Joya de un solo uso que potencia\nlos movimientos de tipo Normal.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Normale.", "A gem with an ordinary essence. When\nheld, it strengthens the power of a\nNormal-type move only once.") },
            { PBEItem.Nugget, new PBELocalizedString("キラキラと　きんいろに　ひかる\nじゅんきん　せいの　たま。\nショップで　たかく　うれる。", "금색으로 반짝반짝 빛나는\n순금으로 만들어진 구슬.\n상점에서 비싸게 팔린다.", "Pépite d'or pur qui brille\nmagnifiquement. Peut être vendue\nà bon prix aux magasins.", "Ein Nugget aus purem Gold, das\neinen schimmernden Glanz besitzt.\nEs ist von großem Wert.", "Pepita de oro puro que desprende\nun brillo espectacular. Puede venderse\nmuy cara en las tiendas.", "Pepita d'oro puro dal luccichio\nsfavillante. Si può vendere a un prezzo\npiuttosto alto nei negozi.", "A nugget of pure gold that gives off a\nlustrous gleam. It can be sold at a\nhigh price to shops.") },
            { PBEItem.OccaBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　ほのお　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 불꽃 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Feu.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Feuer.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Fuego\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Fuoco\ndel nemico.", "Weakens a supereffective Fire-type\nattack against the holding Pokémon.") },
            { PBEItem.OddIncense, new PBELocalizedString("ふしぎな　かおりの　おこう。\nもたせると　エスパータイプの\nわざの　いりょくが　あがる。", "이상한 향기의 향로.\n지니게 하면 에스퍼타입\n기술의 위력이 올라간다.", "Objet à tenir. Encens au parfum\nexotique augmentant la puissance\ndes capacités de type Psy.", "Ein Item zum Tragen. Dieses\nexotische Duftkraut verstärkt\nAttacken vom Typ Psycho.", "Incienso de exótico aroma que fortalece\nlos movimientos de tipo Psíquico.\nDebe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nIncenso dal profumo esotico che\npotenzia le mosse di tipo Psico.", "An item to be held by a Pokémon. It is\nan exotic-smelling incense that boosts\nthe power of Psychic-type moves.") },
            { PBEItem.OddKeystone, new PBELocalizedString("これがないと　いしのとうが\nくずれてしまう　だいじないし。\nときどき　いしから　こえがきこえる。", "이것이 없으면 돌탑이\n무너져버리는 중요한 돌.\n가끔 돌에서 목소리가 들린다.", "Objet très important qui empêche une\ntour en pierre de s'écrouler. Des\nbruits de voix en sortent parfois.", "Ein wichtiges Item, mit dem\nein Steinturm repariert werden\nkann.", "Vital para impedir que una torre de\npiedra se derrumbe. A veces se\noyen voces en su interior.", "Strumento importante che impedisce\nil crollo di un pozzo di pietra. A volte\ndal suo interno provengono delle voci.", "A vital item that is needed to keep a\nstone tower from collapsing. Voices\ncan be heard from it occasionally.") },
            { PBEItem.OldAmber, new PBELocalizedString("こだい　ポケモンの　いでんしが\nとじこめられている　コハク。\nあかみを　おびて　すけている。", "고대 포켓몬의 유전자가\n갇혀 있는 호박.\n붉은 기가 돌면서 약간 투명하다.", "Un morceau d'ambre contenant\nl'ADN d'un ancien Pokémon. Il\nest de couleur jaune.", "Ein Stück Bernstein, in dem die Gene\neines uralten Pokémon enthalten\nsind. Es ist durchsichtig!", "Fragmento de ámbar que contiene\ninformación genética de un Pokémon\nancestral. Es de color amarillo.", "Pezzo di ambra che contiene il materiale\ngenetico di un Pokémon antico. È chiaro\ne di colore giallastro.", "A piece of amber that contains the\ngenetic material of an ancient Pokémon.\nIt is clear with a reddish tint.") },
            { PBEItem.OldGateau, new PBELocalizedString("ハクタイの　かくれた　めいぶつ。\nポケモン　\xFF11ひきの　じょうたいの\nいじょうを　すべて　かいふくする。", "영원시티의 숨은 명물.\n포켓몬 1마리의\n상태 이상을 모두 회복한다.", "Spécialité du Vieux Château. Il\nsoigne tous les problèmes de\nstatut d'un Pokémon.", "Eine unbekannte Spezialität.\nSie behebt alle Statusprobleme\neines Pokémon.", "Especialidad de la Vieja Mansión.\nCura todos los problemas de estado\nde un Pokémon.", "Specialità segreta dell'Antico Château.\nCura tutti i problemi di stato di\nun Pokémon.", "Old Chateau's hidden specialty.\nIt heals all the status problems of a\nsingle Pokémon.") },
            { PBEItem.OranBerry, new PBELocalizedString("ポケモンに　もたせると\n\xFF28\xFF30を　\xFF11\xFF10だけ　かいふくする。\n", "포켓몬에게 지니게 하면\nHP를 10만큼 회복한다.\n", "Baie qui restaure 10 PV lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon\nzum Tragen, füllt sie seine KP\num 10 Punkte auf.", "Los Pokémon pueden llevarla o usarla\npara restaurar 10 PS.", "Se tenuta da un Pokémon gli restituisce\n10 PS.", "If held by a Pokémon,\nit heals the user by just 10 HP.") },
            { PBEItem.OvalStone, new PBELocalizedString("ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nたまのように　まるい。", "어느 특정 포켓몬을\n진화시키는 이상한 돌.\n구슬처럼 둥글다.", "Pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle a la forme d'un œuf.", "Dieser besondere, eiförmige Stein\nhilft manchen Pokémon bei\nihrer Evolution.", "Una piedra peculiar que hace\nevolucionar a algunos Pokémon.\nTiene forma de huevo.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nÈ a forma di uovo.", "A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt is shaped like an egg.") },
            { PBEItem.PamtreBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.ParalyzHeal, new PBELocalizedString("スプレーしきの　くすり。\nポケモン　\xFF11ひきの　まひの\nじょうたいを　かいふくする。", "스프레이식의 약.\n포켓몬 1마리의\n마비 상태를 회복한다.", "Un médicament sous forme de spray.\nSoigne un Pokémon de la paralysie.", "Ein Spray, das ein Pokémon\nvon Paralyse heilt.", "Medicina en espray que cura\nun Pokémon paralizado.", "Rimedio spray.\nCura un Pokémon dalla paralisi.", "A spray-type medicine.\nIt eliminates paralysis from a single\nPokémon.") },
            { PBEItem.ParkBall, new PBELocalizedString("パルパークの　なかで\nつかう　とくべつな　ボール。　\n", "팔파크 내에서\n사용하는 특별한 볼.\n", "Une Poké Ball spéciale utilisée au\nParc des Amis.", "Ein Ball, der nur im Park der Freunde\neingesetzt wird.", "Poké Ball que se utiliza dentro del\nParque Compi.", "Poké Ball speciale utilizzata solo\nall'interno del Parco Amici.", "A special Poké Ball for the Pal Park.") },
            { PBEItem.PasshoBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　みず　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 물 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Eau.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Wasser.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Agua\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Acqua\ndel nemico.", "Weakens a supereffective Water-type\nattack against the holding Pokémon.") },
            { PBEItem.PassOrb, new PBELocalizedString("デルパワーを　はっせいさせる\nときにつかう　イッシュの　パワーが\nとじこめられた　ふしぎな　たま。", "딜파워를 발생시킬 때\n사용하는 하나지방의 파워가\n담긴 이상한 구슬.", "Sphère mystérieuse contenant\nl'Aura d'Unys et utilisable\navec l'Offri-Aura.", "Ein mysteriöser Orb, der die Kraft\nder Einall-Region enthält und zur\nErzeugung von Transferkraft dient.", "Bola misteriosa que contiene el poder\nde la región de Teselia y se utiliza para\ngenerar Poderes Regalo.", "Bilia misteriosa che racchiude l'energia\ndi Unima ed è in grado di lanciare\nil Passapotere.", "A mysterious orb containing the power\nof the Unova region, to be used when\ngenerating Pass Power.") },
            { PBEItem.PayapaBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　エスパー　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 에스퍼 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Psy.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Psycho.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Psíquico\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Psico\ndel nemico.", "Weakens a supereffective Psychic-type\nattack against the holding Pokémon.") },
            { PBEItem.Pearl, new PBELocalizedString("きれいな　ぎんいろに　ひかる\nすこし　ちいさめの　しんじゅ。\nショップで　やすく　うれる。", "예쁜 은색으로 빛나는\n자그마한 진주.\n상점에서 싸게 팔린다.", "Une petite perle qui brille d'une jolie\ncouleur argent. Peut être vendue\nà bas prix aux magasins.", "Eine relativ kleine Perle, die in\nschönem Silber funkelt. Sie ist\nvon geringem Wert.", "Brillante perla pequeña y plateada\nque no alcanza un buen precio en\nlas tiendas.", "Perla piuttosto piccola dai graziosi\nriflessi argentei. Si può vendere\na un prezzo piuttosto basso nei negozi.", "A somewhat-small pearl that sparkles\nin a pretty silver color.\nIt can be sold cheaply to shops.") },
            { PBEItem.PearlString, new PBELocalizedString("きれいな　ぎんいろに　ひかる\nとても　おおつぶの　しんじゅ。\nマニアが　たかく　かってくれる。", "예쁜 은색으로 빛나는\n매우 큰 낱알의 진주.\n마니아가 비싼 값에 산다.", "Une très grosse perle qui brille\nd'une jolie couleur argent.\nLes amateurs en donnent un bon prix.", "Eine sehr große Perle mit einem\nwunderschönen, silbernen Glanz.\nSammler geben ihr letztes Hemd dafür.", "Brillantes perlas plateadas de gran\ntamaño. Muy valoradas por los\ncoleccionistas.", "Grossa perla dai riflessi argentei.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "Very large pearls that sparkle in a\npretty silver color.\nA maniac will buy them for a high price.") },
            { PBEItem.PechaBerry, new PBELocalizedString("ポケモンに　もたせると\nどくを　かいふくする。\n", "포켓몬에게 지니게 하면\n독을 회복한다.\n", "Baie qui soigne l'empoisonnement\nlorsqu'elle est tenue par un Pokémon.", "Gibst du die Beere einem Pokémon zum\nTragen, heilt diese es von Vergiftung.", "Los Pokémon pueden llevarla o usarla\npara curarse del envenenamiento.", "Se tenuta da un Pokémon, lo guarisce\ndall'avvelenamento.", "If held by a Pokémon,\nit recovers from poison.") },
            { PBEItem.PersimBerry, new PBELocalizedString("ポケモンに　もたせると\nこんらんを　かいふくする。\n", "포켓몬에게 지니게 하면\n혼란을 회복한다.\n", "Baie qui soigne la confusion lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon zum\nTragen, heilt diese es von Verwirrung.", "Los Pokémon pueden llevarla o usarla\npara librarse de la confusión.", "Se tenuta da un Pokémon, lo libera\ndalla confusione.", "If held by a Pokémon,\nit recovers from confusion.") },
            { PBEItem.PetayaBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　じぶんの\nとくこうが　あがる。", "포켓몬에게 지니게 하면\n위급할 때 자신의\n특수공격이 올라간다.", "Tenue, cette Baie monte l'Atq. Spé.\nen cas de besoin.", "Als getragenes Item erhöht diese Beere\nden Spezial-Angriff eines Pokémon,\nsobald es in einer Notlage steckt.", "Si la lleva un Pokémon, le sube\nel Ataque Especial en un momento\nde apuro.", "Aumenta l'Attacco Speciale, se\nil Pokémon che la tiene si trova\nin difficoltà.", "If held by a Pokémon, it raises its\nSp. Atk stat in a pinch.") },
            { PBEItem.PinapBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.PinkScarf, new PBELocalizedString("もたせて　コンテストに　さんかすると\nその　ポケモンは　いつもより\nかわいく　みられる。", "지니게 하여 콘테스트에 참가하면\n그 포켓몬은 여느 때보다\n귀엽게 보인다.", "Objet à tenir. Augmente la Grâce\nd'un Pokémon dans les Concours.", "Ein Item zum Tragen. Trainer erhöhen\ndamit in Wettbewerben die „Anmut“\nihres Pokémon.", "Debe llevarlo un Pokémon.\nAumenta la dulzura en los concursos.", "Strumento da far tenere a un Pokémon.\nAumenta la “Grazia\" di chi lo tiene\nin una Gara.", "An item to be held by a Pokémon.\nIt boosts the Cute aspect of the\nholder in a Contest.") },
            { PBEItem.PlumeFossil, new PBELocalizedString("とりポケモンの　そせんと　いわれる\nこだい　ポケモンの　カセキ。\nつばさの　いちぶ　らしい。", "새포켓몬의 조상이라 불리는\n고대 포켓몬의 화석.\n날개의 일부인 듯하다.", "Fossile d'un ancien Pokémon\nvolant. Il semble que ce soit\nun morceau d'aile.", "Das Fossil eines uralten Pokémon, von\ndem man annimmt, dass es der Urahn der\nVogel-Pokémon sei. Ein Teil des Flügels.", "Fósil de un Pokémon ancestral que\nsurcó los cielos. Parece ser parte\nde un ala suya.", "Fossile di un Pokémon preistorico che\nsolcava i cieli. Sembra essere parte\ndella sua ala.", "A fossil of an ancient Pokémon that\nflew in the sky in ancient times.\nIt appears to be part of its wing.") },
            { PBEItem.PnkApricorn, new PBELocalizedString("ももいろの　ぼんぐり。\nあまそうな　いい　においがする。\n", "담홍색의 규토리.\n달콤한 듯한 좋은 향기가 난다.\n", "Un Noigrume de couleur rose.\nIl dégage un arôme sucré.", "Eine pinkfarbene Aprikoko. Sie duftet\nsüß und appetitanregend.", "Bonguri de color rosa y aroma dulce.", "Una Ghicocca di colore rosa.\nEmana un profumo dolce.", "A pink Apricorn.\nIt has a nice, sweet scent.") },
            { PBEItem.PoisonBarb, new PBELocalizedString("どくのある　ちいさな　ハリ。\nもたせると　どくタイプの\nわざの　いりょくが　あがる。", "독이 있는 작은 침.\n지니게 하면 독타입\n기술의 위력이 올라간다.", "Objet à tenir. Petit pic empoisonné\nmontant la puissance des capacités\nde type Poison.", "Ein Item zum Tragen. Ein schmaler und\ngiftiger Widerhaken, der Attacken vom\nTyp Gift verstärkt.", "Flecha venenosa que refuerza los\nmovimientos de tipo Veneno. Debe\nllevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nAculeo piccolo e tossico che potenzia\nle mosse di tipo Veleno.", "An item to be held by a Pokémon.\nIt is a small, poisonous barb that ups\nthe power of Poison-type moves.") },
            { PBEItem.PoisonGem, new PBELocalizedString("どくの　タイプの　ジュエル。\nもたせると　いちどだけ　どくの\nわざの　いりょくが　つよまる。", "독타입의 주얼.\n지니게 하면 한 번만 독\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Poison.", "Ein Juwel vom Typ Gift. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Gift.", "Joya de un solo uso que potencia\nlos movimientos de tipo Veneno.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Veleno.", "A gem with an essence of poison. When\nheld, it strengthens the power of a\nPoison-type move only once.") },
            { PBEItem.PokeBall, new PBELocalizedString("やせいの　ポケモンに　なげて\nつかまえる　ための　ボール。\nカプセルしきに　なっている。", "야생 포켓몬에게 던져서\n잡기 위한 볼.\n캡슐식으로 되어 있다.", "Un objet pareil à une capsule, qui\ncapture les Pokémon sauvages. Il suffit\npour cela de le jeter comme une balle.", "Damit fängst du wilde Pokémon.\nDu wirfst ihn wie einen normalen Ball.\nDas Design ähnelt dem einer Kapsel.", "Dispositivo con diseño capsular que\natrapa Pokémon salvajes. Se lanza\ncomo una bola contra el blanco.", "Strumento dotato di capsula usato per\ncatturare Pokémon selvatici. Si lancia\ncontro il bersaglio come una palla.", "A device for catching wild Pokémon.\nIt is thrown like a ball at the target.\nIt is designed as a capsule system.") },
            { PBEItem.PokeDoll, new PBELocalizedString("ポケモンの　きを　ひかせる　どうぐ。\nやせいポケモンとの　せんとうから\nぜったいに　にげられる。", "포켓몬의 관심을 끄는 도구.\n야생 포켓몬과의 배틀에서\n반드시 도망칠 수 있다.", "Poupée qui attire les Pokémon. Permet\nde s'enfuir d'un combat contre un\nPokémon sauvage.", "Eine hübsche Puppe. Sie ermöglicht\ndie Flucht im Kampf gegen wilde\nPokémon.", "Un muñeco que atrae a los Pokémon.\nEs útil para huir de un combate\ncontra un Pokémon salvaje.", "Bambola che attrae i Pokémon. È utile\nper fuggire dalla lotta con un Pokémon\nselvatico.", "A doll that attracts Pokémon.\nUse it to flee from any battle with a\nwild Pokémon.") },
            { PBEItem.PokeToy, new PBELocalizedString("ポケモンの　きを　ひかせる　どうぐ。\nやせいポケモンとの　せんとうから\nぜったいに　にげられる。", "포켓몬의 관심을 끄는 도구.\n야생 포켓몬과의 배틀에서\n반드시 도망칠 수 있다.", "Objet qui attire les Pokémon. Permet\nde s'enfuir d'un combat contre un\nPokémon sauvage.", "Ein hübscher Wedel. Er ermöglicht die\nFlucht im Kampf gegen wilde Pokémon.", "Un objeto que atrae a los Pokémon.\nPuedes utilizarlo para huir de un\ncombate con un Pokémon salvaje.", "Strumento che attrae i Pokémon. Utile\nper fuggire dalla lotta con Pokémon\nselvatici.", "An item that attracts Pokémon.\nUse it to flee from any battle with a\nwild Pokémon.") },
            { PBEItem.PomegBerry, new PBELocalizedString("ポケモンに　あげると\nとても　なつき　やすくなるが\n\xFF28\xFF30の　きそポイントは　さがる。", "포켓몬에게 주면\n매우 친밀해지기 쉬워지지만\nHP의 기초 포인트가 떨어진다.", "Rend les Pokémon plus amicaux, mais\nbaisse leurs PV de base.", "Isst ein Pokémon diese Beere,\nstärkt dies eure Freundschaft,\nverringert aber seine Basis-KP.", "Al usarla con un Pokémon, se gana\nsu amistad, pero también reduce\nsus PS de base.", "Utilizzata su un Pokémon, lo rende più\namichevole, ma riduce i PS di base.", "Using it on a Pokémon makes it more\nfriendly, but it also lowers its base HP.") },
            { PBEItem.Potion, new PBELocalizedString("スプレーしきの　キズぐすり。\nポケモン　\xFF11ひきの　\xFF28\xFF30を\n\xFF12\xFF10だけ　かいふくする。", "스프레이식의 상처약.\n포켓몬 1마리의 HP를\n20만큼 회복한다.", "Un spray qui soigne les blessures.\nRestaure 20 PV à un Pokémon.", "Hilft bei Wunden. Die KP\neines Pokémon werden\num 20 Punkte aufgefüllt.", "Medicina en espray que cura heridas\ny restaura 20 PS a un Pokémon.", "Rimedio spray per le ferite.\nRestituisce 20 PS ad un Pokémon.", "A spray-type medicine for wounds.\nIt restores the HP of one Pokémon by\njust 20 points.") },
            { PBEItem.PowerAnklet, new PBELocalizedString("もたせると　すばやさが　さがるが\nふつうよりも　ポケモンの　すばやさが\nたかく　そだちやすい。", "지니게 하면 스피드는 떨어지지만\n여느 때보다 포켓몬의 스피드를\n올리면서 키우기가 쉽다.", "Objet à tenir permettant à la Vitesse\nde progresser plus vite, mais la\nréduisant pendant le combat.", "Ein Item zum Tragen. Erhöht bei\nLevelaufstieg den Initiative-Wert,\nsenkt aber die Initiative allgemein.", "Potencia la Velocidad al subir de nivel,\npero reduce la Velocidad en combate.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nRiduce la Velocità in lotta, ma aumenta\nmolto la Velocità di base.", "A Pokémon held item that promotes\nSpeed gain on leveling, but reduces\nthe Speed stat.") },
            { PBEItem.PowerBand, new PBELocalizedString("もたせると　すばやさが　さがるが\nふつうよりも　ポケモンの　とくぼうが\nたかく　そだちやすい。", "지니게 하면 스피드는 떨어지지만\n여느 때보다 포켓몬의 특수방어를\n올리면서 키우기가 쉽다.", "Objet à tenir permettant à la Défense\nSpéciale de progresser plus vite, mais\nréduisant la Vitesse pendant le combat.", "Ein Item zum Tragen. Erhöht bei Level-\naufstieg die Spezial-Verteidigung,\nsenkt aber die Initiative.", "Aumenta la Defensa Especial al subir\nde nivel, pero reduce la Velocidad en\ncombate. Debe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nRiduce la Velocità in lotta, ma aumenta\nmolto la Difesa Speciale di base.", "A Pokémon held item that promotes\nSp. Def gain on leveling, but reduces\nthe Speed stat.") },
            { PBEItem.PowerBelt, new PBELocalizedString("もたせると　すばやさが　さがるが\nふつうよりも　ポケモンの　ぼうぎょが\nたかく　そだちやすい。", "지니게 하면 스피드는 떨어지지만\n여느 때보다 포켓몬의 방어를\n올리면서 키우기가 쉽다.", "Objet à tenir permettant à la Défense\nde progresser plus vite, mais\nréduisant la Vitesse pendant le combat.", "Ein Item zum Tragen. Erhöht bei\nLevelaufstieg den Verteidigungs-\nWert, senkt aber die Initiative.", "Aumenta la Defensa al subir de nivel,\npero reduce la Velocidad en combate.\nDebe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nRiduce la Velocità in lotta, ma aumenta\nmolto la Difesa di base.", "A Pokémon held item that promotes\nDefense gain on leveling, but reduces\nthe Speed stat.") },
            { PBEItem.PowerBracer, new PBELocalizedString("もたせると　すばやさが　さがるが\nふつうよりも　ポケモンの　こうげきが\nたかく　そだちやすい。", "지니게 하면 스피드는 떨어지지만\n여느 때보다 포켓몬의 공격을\n올리면서 키우기가 쉽다.", "Objet à tenir permettant à l'Attaque\nde progresser plus vite, mais\nréduisant la Vitesse pendant le combat.", "Ein Item zum Tragen. Erhöht\nbei Levelaufstieg den Angriffs-\nWert, senkt aber die Initiative.", "Potencia el Ataque al subir de nivel,\npero reduce la Velocidad en combate.\nDebe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nRiduce la Velocità in lotta, ma aumenta\nmolto l'Attacco di base.", "A Pokémon held item that promotes\nAttack gain on leveling, but reduces\nthe Speed stat.") },
            { PBEItem.PowerHerb, new PBELocalizedString("もたせた　ポケモンは　いちどだけ\n\xFF11ターンめに　ちからを　ためるわざを\nすぐに　つかうことが　できる。", "지니게 한 포켓몬은 한 번만\n1턴째에 힘을 모으는 기술을\n바로 사용할 수가 있다.", "Objet à tenir et à usage unique. Permet\nd'utiliser immédiatement une capacité\nqui devrait se charger au premier tour.", "Ein getragenes Item. Der Träger kann\ndadurch eine Attacke sofort einsetzen,\ndie sonst aufgeladen werden muss.", "Permite el uso inmediato de cualquier\nmovimiento en el primer turno. Es de un\nsolo uso y debe llevarla un Pokémon.", "Strumento monouso da far tenere a un\nPokémon. Permette l'uso immediato di\nuna mossa che al primo turno si carica.", "A single-use item to be held by a\nPokémon. It allows the immediate use of\na move that charges on the first turn.") },
            { PBEItem.PowerLens, new PBELocalizedString("もたせると　すばやさが　さがるが\nふつうよりも　ポケモンの　とくこうが\nたかく　そだちやすい。", "지니게 하면 스피드는 떨어지지만\n여느 때보다 포켓몬의 특수공격을\n올리면서 키우기가 쉽다.", "Objet à tenir permettant à l'Attaque\nSpéciale de progresser plus vite, mais\nréduisant la Vitesse pendant le combat.", "Ein Item zum Tragen. Erhöht\nbei Levelaufstieg den Spezial-\nAngriff, senkt aber die Initiative.", "Potencia el Ataque Especial al subir\nde nivel, pero reduce la Velocidad en\ncombate. Debe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nRiduce la Velocità in lotta, ma aumenta\nmolto l'Attacco Speciale di base.", "A Pokémon held item that promotes\nSp. Atk gain on leveling, but reduces\nthe Speed stat.") },
            { PBEItem.PowerWeight, new PBELocalizedString("もたせると　すばやさが　さがるが\nふつうよりも　ポケモンの　\xFF28\xFF30が\nたかく　そだちやすい。", "지니게 하면 스피드는 떨어지지만\n여느 때보다 포켓몬의 HP를\n올리면서 키우기가 쉽다.", "Objet à tenir permettant aux PV\nde progresser plus vite, mais\nréduisant la Vitesse pendant le combat.", "Ein Item zum Tragen. Erhöht bei\nLevelaufstieg die maximalen\nKP, senkt aber die Initiative.", "Aumenta los PS al subir de nivel,\npero reduce la Velocidad en combate.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nRiduce la Velocità in lotta, ma aumenta\nmolto i PS di base.", "A Pokémon held item that promotes\nHP gain on leveling, but reduces the\nSpeed stat.") },
            { PBEItem.PPMax, new PBELocalizedString("おぼえている　わざのうち\n\xFF11つの　\xFF30\xFF30の　さいだいちを\nさいこうまで　あげる。", "기억하고 있는 기술 중\n1개의 PP 최대치를\n최고까지 올린다.", "Monte sensiblement les PP max d'une\ncapacité sélectionnée.", "Maximiert die Anzahl der AP\neiner Attacke eines Pokémon.", "Aumenta hasta el límite los PP máximos\ndel movimiento elegido de un Pokémon.", "Porta al limite i PP massimi di una mossa\na scelta imparata da un Pokémon.", "It maximally raises the top PP of a\nselected move that has been learned\nby the target Pokémon.") },
            { PBEItem.PPUp, new PBELocalizedString("ポケモンが　おぼえている\nわざのうち　\xFF11つの　\xFF30\xFF30の\nさいだいちを　すこし　あげる。", "포켓몬이 기억하고 있는\n기술 중 1개의 PP\n최대치를 조금 올린다.", "Monte un peu les PP max d'une\ncapacité sélectionnée.", "Erhöht die maximale Anzahl der AP\neiner Attacke eines Pokémon.", "Aumenta ligeramente los PP máximos del\nmovimiento elegido de un Pokémon.", "Aumenta di poco i PP massimi di una mossa\na scelta che è stata imparata dal\nPokémon.", "It slightly raises the maximum PP of\na selected move that has been learned\nby the target Pokémon.") },
            { PBEItem.PremierBall, new PBELocalizedString("なにかの　きねんの　しなとして\nとくべつに　つくられた\nちょっと　めずらしい　ボール。", "무언가의 기념품으로\n특별히 만들어진\n조금 희귀한 볼.", "Une Poké Ball assez rare qui fut créée\npour un événement particulier.", "Ein eher seltener Ball, der als\nReminiszenz an irgendein\nEreignis hergestellt wurde.", "Es una Poké Ball algo singular\nque se creó para conmemorar\nalgún acontecimiento.", "Poké Ball poco comune realizzata\nappositamente per commemorare\nun particolare evento.", "A somewhat rare Poké Ball that has\nbeen specially made to commemorate\nan event of some sort.") },
            { PBEItem.PrettyWing, new PBELocalizedString("きれいなだけで　なんの　こうかもない\nごくごく　ふつうの　ハネ。\n", "아름답기만 할 뿐 아무 효과도 없는\n지극히 평범한 날개.\n", "Une plume très jolie, mais ordinaire\net sans effet particulier.", "Eine ganz normale Feder, die\nrein gar nichts bewirkt.", "Pluma normal y corriente. Muy bonita,\npero no sirve para nada.", "Piuma molto bella ma priva di qualsiasi\nutilità: non ha alcun effetto.", "Though this feather is beautiful, it's\njust a regular feather and has no\neffect on Pokémon.") },
            { PBEItem.PrismScale, new PBELocalizedString("ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　ウロコ。\nにじいろに　かがやいている。", "어느 특정한 포켓몬을\n진화시키는 이상한 비늘.\n무지갯빛으로 빛나고 있다.", "Une écaille étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle a une couleur arc-en-ciel.", "Eine seltsame Schuppe, durch die sich\nbestimmte Pokémon entwickeln. Sie\nfunkelt in den Farben des Regenbogens.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nBrilla con los colores del arcoíris.", "Squama misteriosa che fa evolvere\ndeterminate specie di Pokémon.\nRiflette i colori dell'arcobaleno.", "A mysterious scale that evolves\ncertain Pokémon.\nIt shines in rainbow colors.") },
            { PBEItem.Protector, new PBELocalizedString("なにかの　プロテクター。\nとても　かたくて　おもい。\nある　ポケモンが　このんでいる。", "무언가의 프로텍터.\n매우 단단하고 무겁다.\n어떤 포켓몬이 좋아하는 물건이다.", "Objet protecteur très dur et très\nlourd, apprécié d'un certain Pokémon.", "Ein schützendes Item. Steif\nund schwer. Das Lieblingsstück\neines bestimmten Pokémon.", "Dispositivo de protección muy pesado.\nCierto Pokémon lo adora.", "Strumento protettivo, molto duro e\npesante, prediletto da un Pokémon\nin particolare.", "A protective item of some sort.\nIt is extremely stiff and heavy.\nIt is loved by a certain Pokémon.") },
            { PBEItem.Protein, new PBELocalizedString("ポケモンの　えいようドリンク。\nポケモン　\xFF11ひきの　こうげきの\nきそポイントを　あげる。", "포켓몬의 영양 드링크.\n포켓몬 1마리의 공격\n기초 포인트를 올린다.", "Une boisson très nutritive qui monte\nl'Attaque de base d'un Pokémon.", "Ein gehaltvolles Getränk, das den\nAngriffs-Wert eines Pokémon\ndauerhaft erhöht.", "Nutritiva bebida que aumenta el Ataque\nde base de un Pokémon.", "Bevanda nutriente per Pokémon.\nAumenta l'Attacco di base.", "A nutritious drink for Pokémon.\nIt raises the base Attack stat of a\nsingle Pokémon.") },
            { PBEItem.PsychicGem, new PBELocalizedString("エスパーの　タイプの　ジュエル。\nもたせると　いちどだけ　エスパーの\nわざの　いりょくが　つよまる。", "에스퍼타입의 주얼.\n지니게 하면 한 번만 에스퍼\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Psy.", "Ein Juwel vom Typ Psycho. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Psycho.", "Joya de un solo uso que potencia\nlos movimientos de tipo Psíquico.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Psico.", "A gem with an essence of the mind. When\nheld, it strengthens the power of a\nPsychic-type move only once.") },
            { PBEItem.PureIncense, new PBELocalizedString("いちばん　まえの　ポケモンに\nもたせると　やせいポケモンが\nでて　きにくくなる。", "맨 앞의 포켓몬에게\n지니게 하면 야생 포켓몬이\n나타날 확률이 낮아진다.", "Objet à tenir. Aide à repousser les\nPokémon sauvages si le porteur est\nle premier Pokémon de l'équipe.", "Ein Item zum Tragen. Reduziert\nBegegnungen mit wilden Pokémon, wenn\nder Träger an erster Stelle steht.", "Si lo lleva el primer Pokémon del\nequipo, se reduce la probabilidad de\nque se acerquen Pokémon salvajes.", "Strumento da far tenere a un Pokémon.\nAiuta ad allontanare i Pokémon selvatici\nse chi lo tiene è il primo della squadra.", "An item to be held by a Pokémon.\nIt helps keep wild Pokémon away if the\nholder is the first one in the party.") },
            { PBEItem.QualotBerry, new PBELocalizedString("ポケモンに　あげると\nとても　なつき　やすくなるが\nぼうぎょの　きそポイントは　さがる。", "포켓몬에게 주면\n매우 친밀해지기 쉬워지지만\n방어의 기초 포인트가 떨어진다.", "Rend les Pokémon plus amicaux, mais\nbaisse leur stat Défense de base.", "Isst ein Pokémon diese Beere,\nstärkt dies eure Freundschaft,\nverringert aber seinen Vert.-Wert.", "Al usarla con un Pokémon, se gana\nsu amistad, pero también reduce\nsu Defensa de base.", "Utilizzata su un Pokémon, lo rende più\namichevole, ma diminuisce la Difesa\ndi base.", "Using it on a Pokémon makes it more\nfriendly, but it also lowers its base\nDefense stat.") },
            { PBEItem.QuickBall, new PBELocalizedString("せんとうが　はじまって　すぐに\nつかうと　つかまえやすくなる\nちょっと　かわった　ボール。", "배틀이 시작되자마자\n사용하면 잡기 쉬워지는\n조금 특이한 볼.", "Une Poké Ball qui permet d'attraper un\nPokémon sauvage plus facilement si on\nl'utilise au début du combat.", "Ein ungewöhnlicher Ball, der\nzu Beginn eines Kampfes am\nwirkungsvollsten ist.", "Una Poké Ball algo distinta que potencia\nel índice de captura si se usa al inicio\nde un encuentro con Pokémon salvajes.", "Poké Ball un po' diversa che facilita\nla cattura di un Pokémon selvatico\nse usata all'inizio della lotta.", "A somewhat different Poké Ball that\nprovides a better catch rate if it is\nused at the start of a wild encounter.") },
            { PBEItem.QuickClaw, new PBELocalizedString("かるくて　するどい　ツメ。\nもたせると　あいてより　さきに\nこうどう　できることが　ある。", "가볍고 날카로운 손톱.\n지니게 하면 상대보다 먼저\n행동할 수도 있다.", "Objet à tenir. Griffe légère mais\ntranchante qui permet parfois de\nfrapper le premier.", "Ein Item, das einem Pokémon zum Tragen\ngegeben werden kann. Es hebt gelegent-\nlich die Erstschlagquote.", "Garra ligera y afilada que permite que\nel Pokémon que la lleve ataque\na veces el primero.", "Strumento da far tenere a un Pokémon.\nLeggero e affilato, permette di fare\nla prima mossa ogni tanto.", "An item to be held by a Pokémon.\nA light, sharp claw that lets the\nbearer move first occasionally.") },
            { PBEItem.QuickPowder, new PBELocalizedString("メタモンに　もたせると　すばやさが\nあがる　ふしぎなこな。\nとても　こまかくて　かたい。", "메타몽에게 지니게 하면 스피드가\n올라가는 이상한 가루.\n매우 작고 단단하다.", "Objet à faire tenir à Métamorph. Cette\npoudre étrange, très fine mais\nrésistante, augmente sa Vitesse.", "Ein Item, das Ditto zum Tragen gegeben\nwerden kann. Fein und doch hart,\nerhöht es den Initiative-Wert.", "Debe llevarlo Ditto. Es un polvo\nmuy fino, pero a la vez resistente,\nque aumenta su Velocidad.", "Strumento da far tenere a Ditto.\nQuesta strana polvere, fine e al\ncontempo dura, aumenta la Velocità.", "An item to be held by Ditto.\nExtremely fine yet hard, this odd\npowder boosts the Speed stat.") },
            { PBEItem.RabutaBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.RageCandyBar, new PBELocalizedString("チョウジめいぶつの　まんじゅう。\nおみやげひん　として　ゆうめい。\n", "황토마을의 명물인 호두과자.\n선물로 아주 유명하다.\n", "Un bonbon, spécialité d'Acajou.\nIl constitue un cadeau très réputé.", "Eine Spezialität aus Mahagonia City.\nSie wird gern als Souvenir gekauft.", "Caramelo típico de Pueblo Caoba,\nmuy popular como souvenir.", "Caramella tipica di Mogania.\nSouvenir con un'ottima reputazione.", "A famous Mahogany Town candy tourists\nlike to buy and take home. It restores\nthe HP of one Pokémon by 20 points.") },
            { PBEItem.RareBone, new PBELocalizedString("ポケモンこうこがくの　うえで\nとても　きちょうな　ホネ。\nショップで　たかく　うれる。", "포켓몬 고고학 상에서\n매우 귀중한 뼈.\n상점에서 비싸게 팔린다.", "Un os d'une grande valeur pour\nl'archéologie Pokémon. Peut être\nvendu à bon prix aux magasins.", "Dieser Knochen erfreut jeden\nPokémon-Archäologen. Er ist\nvon hohem Wert.", "Un hueso de gran valor para la\narqueología Pokémon. Puede alcanzar\nun alto precio en las tiendas.", "Osso di grande valore per l'archeologia\nPokémon. Può essere venduto nei negozi\na un prezzo piuttosto alto.", "A bone that is extremely valuable for\nPokémon archeology. It can be sold\nfor a high price to shops.") },
            { PBEItem.RareCandy, new PBELocalizedString("エネルギーの　つまった　アメ。\nあたえると　ポケモン　\xFF11ひきの\nレベルが　\xFF11だけ　あがる。", "에너지가 가득한 사탕.\n먹이면 포켓몬 1마리의\n레벨이 1만큼 올라간다.", "Un bonbon plein d'énergie.\nIl permet à un Pokémon de gagner\nun niveau d'expérience.", "Ein energiereiches Bonbon, das\nden Level eines Pokémon dauerhaft\num eins erhöht.", "Caramelo energético que sube\na un Pokémon de nivel.", "Caramella altamente energetica.\nFa salire un Pokémon di un livello.", "A candy that is packed with energy.\nIt raises the level of a single Pokémon\nby one.") },
            { PBEItem.RawstBerry, new PBELocalizedString("ポケモンに　もたせると\nやけどを　かいふくする。\n", "포켓몬에게 지니게 하면\n화상을 회복한다.\n", "Baie qui soigne les brûlures lorsqu'elle\nest tenue par un Pokémon.", "Gibst du die Beere einem Pokémon\nzum Tragen, heilt diese es von\nVerbrennung.", "Los Pokémon pueden llevarla o usarla\npara curarse las quemaduras.", "Se tenuta da un Pokémon, lo guarisce\nda una scottatura.", "If held by a Pokémon,\nit recovers from a burn.") },
            { PBEItem.RazorClaw, new PBELocalizedString("するどく　とがった　ツメ。\nもたせると　わざが　きゅうしょに\nあたりやすくなる。", "날카롭고 뾰족한 손톱.\n지니게 하면 기술이\n급소에 맞기 쉬워진다.", "Objet à tenir. Griffe crochue\net pointue augmentant le taux\nde critiques.", "Ein Item zum Tragen.\nDie scharfe Klaue erhöht die\nVolltrefferquote des Trägers.", "Aumenta la probabilidad de que\nel Pokémon que la lleve consiga\nun golpe crítico.", "Strumento da far tenere a un Pokémon.\nArtiglio che aumenta le probabilità per\nchi lo tiene di sferrare dei brutti colpi.", "An item to be held by a Pokémon.\nIt is a sharply hooked claw that ups\nthe holder's critical-hit ratio.") },
            { PBEItem.RazorFang, new PBELocalizedString("するどく　とがった　キバ。\nもたせると　ダメージを　あたえたとき\nひるませることが　ある。", "날카롭고 뾰족한 이빨.\n지니게 하면 데미지를 줬을 때\n풀죽게 하기도 한다.", "Objet à tenir pouvant apeurer l'ennemi\nquand le porteur lui inflige des dégâts.", "Ein Item zum Tragen.\nVerursacht der Träger Schaden,\nerschrickt das Ziel eventuell.", "Si lo lleva un Pokémon cuando inflige\ndaño a otro, puede hacer retroceder\na este último.", "Strumento da far tenere a un Pokémon.\nChi lo tiene può far tentennare il nemico\ncolpito.", "An item to be held by a Pokémon.\nIt may make foes and allies flinch\nwhen the holder inflicts damage.") },
            { PBEItem.RazzBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.ReaperCloth, new PBELocalizedString("おそろしく　つよい　れいりょくが\nこめられている　ぬの。\nある　ポケモンが　このんでいる。", "무섭고 강한 영력이\n담겨 있는 천.\n어떤 포켓몬이 좋아하는 물건이다.", "Un tissu imprégné d'une énergie\nspirituelle horriblement puissante.\nApprécié d'un certain Pokémon.", "Ein Stoffumhang mit geistiger\nEnergie. Das Lieblingsstück\neines bestimmten Pokémon.", "Tela imbuida de una energía espiritual\nmuy potente. Cierto Pokémon la adora.", "Panno intriso di un'energia spirituale\nterribilmente forte. Un Pokémon in\nparticolare adora questo strumento.", "A cloth imbued with horrifyingly strong\nspiritual energy.\nIt is loved by a certain Pokémon.") },
            { PBEItem.RedApricorn, new PBELocalizedString("あかい　ぼんぐり。\nつーんと　はなにくる　においがする。\n", "빨간색의 규토리.\n코를 톡 쏘는 향기가 난다.\n", "Un Noigrume de couleur rouge.\nIl dégage un puissant arôme.", "Eine rote Aprikoko. Besitzt einen\nstechenden Geruch.", "Bonguri de color rojo y aroma\nmuy penetrante.", "Una Ghicocca di colore rosso.\nEmana un odore molto forte.", "A red Apricorn.\nIt assails your nostrils.") },
            { PBEItem.RedCard, new PBELocalizedString("ふしぎな　ちからのある　カード。\nもたせると　わざをうけた　あいてを\nたいじょう　させることが　できる。", "이상한 힘이 있는 카드.\n지니게 하면 기술을 당한 상대를\n퇴장시킬 수 있다.", "Carte au pouvoir mystérieux.\nTenue, elle force un Pokémon touchant\nle porteur à se retirer du combat.", "Eine Karte mit mysteriösen Kräften.\nWird der Träger getroffen, wird der\nAngreifer des Platzes verwiesen.", "Misteriosa tarjeta que permite al\nPokémon que la lleve expulsar al\nagresor cuando este le cause daño.", "Cartellino dal potere misterioso.\nChi lo tiene fa ritirare dalla lotta\nil Pokémon che lo ha colpito.", "A card with a mysterious power. When\nthe holder is struck by a foe, the\nattacker is removed from battle.") },
            { PBEItem.RedFlute, new PBELocalizedString("あかい　ガラスで　できている\nおもちゃの　ふえ。\nマニアが　たかく　かってくれる。", "빨간 유리로 만들어진\n장난감 피리.\n마니아가 비싼 값에 산다.", "Jouet en forme de flûte en verre\nrouge. Les amateurs en donnent\nun bon prix.", "Eine Spielzeugflöte aus rotem Glas.\nSammler geben ihr letztes Hemd dafür\nher.", "Una flauta de juguete hecha de\ncristal rojo.\nMuy valorada por los coleccionistas.", "Flauto giocattolo fatto di vetro rosso.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A toy flute made from red glass.\nA maniac will buy it for a high price.") },
            { PBEItem.RedScarf, new PBELocalizedString("もたせて　コンテストに　さんかすると\nその　ポケモンは　いつもより\nかっこよく　みられる。", "지니게 하여 콘테스트에 참가하면\n그 포켓몬은 여느 때보다\n근사하게 보인다.", "Objet à tenir. Augmente le Sang-froid\nd'un Pokémon dans les Concours.", "Ein Item zum Tragen. Trainer erhöhen\ndamit in Wettbewerben die „Coolness“\nihres Pokémon.", "Debe llevarlo un Pokémon.\nAumenta el carisma en los concursos.", "Strumento da far tenere a un Pokémon.\nAumenta la “Classe\" di chi lo tiene\nin una Gara.", "An item to be held by a Pokémon.\nIt boosts the Cool aspect of the\nholder in a Contest.") },
            { PBEItem.RedShard, new PBELocalizedString("あかい　ちいさな　かけら。\nむかしに　つくられた　なにかの\nどうぐの　いちぶ　らしい。", "빨간 작은 조각.\n이전에 만들어진 어떤\n도구의 일부인 듯하다.", "Petit tesson rouge.\nIl semble provenir d'un outil très\nancien.", "Eine rote Scherbe eines antiken\nWerkzeugs, das vor langer Zeit\nangefertigt wurde.", "Un pequeño fragmento rojo.\nParece formar parte de algún tipo\nde herramienta antigua.", "Un piccolo coccio rosso che con ogni\nprobabilità è un frammento di un antico\nstrumento.", "A small red shard.\nIt appears to be from some sort of\nimplement made long ago.") },
            { PBEItem.RelicBand, new PBELocalizedString("\xFF13\xFF10\xFF10\xFF10ねんほど　むかしの\nぶんめいで　つくられた　うでわ。\nマニアが　たかく　かってくれる。", "3000년 정도 된\n오랜 문명에서 만들어진 팔찌.\n마니아가 비싼 값에 산다.", "Un bijou d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Ein Armreif, angefertigt von einer\ngut 3 000 Jahre alten Kultur. Sammler\ngeben ihr letztes Hemd dafür.", "Brazalete de más de tres mil años\nde antigüedad. Muy valorado por\nlos coleccionistas.", "Braccialetto di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A bracelet made in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.") },
            { PBEItem.RelicCopper, new PBELocalizedString("\xFF13\xFF10\xFF10\xFF10ねんほど　むかしの\nぶんめいで　つかわれた　どうか。\nマニアが　たかく　かってくれる。", "3000년 정도 된\n오랜 문명에서 사용된 동화.\n마니아가 비싼 값에 산다.", "Une pièce de cuivre d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Eine alte Kupfermünze. Die Währung\neiner gut 3 000 Jahre alten Kultur.\nSammler geben ihr letztes Hemd dafür.", "Moneda de cobre de más de tres mil\naños de antigüedad. Muy valorada\npor los coleccionistas.", "Moneta di rame di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A copper coin used in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.") },
            { PBEItem.RelicCrown, new PBELocalizedString("\xFF13\xFF10\xFF10\xFF10ねんほど　むかしの\nぶんめいで　つくられた　おうかん。\nマニアが　たかく　かってくれる。", "3000년 정도 된\n오랜 문명에서 만들어진 왕관.\n마니아가 비싼 값에 산다.", "Une couronne d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Die Krone eines Königs, angefertigt von\neiner gut 3 000 Jahre alten Kultur.\nSammler geben ihr letztes Hemd dafür.", "Corona de más de tres mil años\nde antigüedad. Muy valorada\npor los coleccionistas.", "Corona di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A crown made in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.") },
            { PBEItem.RelicGold, new PBELocalizedString("\xFF13\xFF10\xFF10\xFF10ねんほど　むかしの\nぶんめいで　つかわれた　きんか。\nマニアが　たかく　かってくれる。", "3000년 정도 된\n오랜 문명에서 사용된 금화.\n마니아가 비싼 값에 산다.", "Une pièce d'or d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Eine alte Goldmünze. Die Währung\neiner gut 3 000 Jahre alten Kultur.\nSammler geben ihr letztes Hemd dafür.", "Moneda de oro de más de tres mil\naños de antigüedad. Muy valorada\npor los coleccionistas.", "Moneta d'oro di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A gold coin used in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.") },
            { PBEItem.RelicSilver, new PBELocalizedString("\xFF13\xFF10\xFF10\xFF10ねんほど　むかしの\nぶんめいで　つかわれた　ぎんか。\nマニアが　たかく　かってくれる。", "3000년 정도 된\n오랜 문명에서 사용된 은화.\n마니아가 비싼 값에 산다.", "Une pièce d'argent d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Eine alte Silbermünze. Die Währung\neiner gut 3 000 Jahre alten Kultur.\nSammler geben ihr letztes Hemd dafür.", "Moneda de plata de más de tres mil\naños de antigüedad. Muy valorada\npor los coleccionistas.", "Moneta di argento di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A silver coin used in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.") },
            { PBEItem.RelicStatue, new PBELocalizedString("\xFF13\xFF10\xFF10\xFF10ねんほど　むかしの\nぶんめいで　つくられた　せきぞう。\nマニアが　たかく　かってくれる。", "3000년 정도 된\n오랜 문명에서 만들어진 석상.\n마니아가 비싼 값에 산다.", "Une statuette d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Eine Statue, angefertigt von einer gut\n3 000 Jahre alten Kultur. Sammler\ngeben ihr letztes Hemd dafür.", "Efigie de piedra de más de tres mil\naños de antigüedad. Muy valorada\npor los coleccionistas.", "Statua di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A stone figure made in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.") },
            { PBEItem.RelicVase, new PBELocalizedString("\xFF13\xFF10\xFF10\xFF10ねんほど　むかしの\nぶんめいで　つくられた　ツボ。\nマニアが　たかく　かってくれる。", "3000년 정도 된\n오랜 문명에서 만들어진 항아리.\n마니아가 비싼 값에 산다.", "Un vase d'une civilisation\nvieille de plus de 3 000 ans.\nLes amateurs en donnent un bon prix.", "Eine Vase, angefertigt von einer\ngut 3 000 Jahre alten Kultur. Sammler\ngeben ihr letztes Hemd dafür.", "Jarrón de más de tres mil años de\nantigüedad. Muy valorado por\nlos coleccionistas.", "Vaso di circa 3000 anni fa.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A vase made in a civilization\nabout 3,000 years ago.\nA maniac will buy it for a high price.") },
            { PBEItem.RepeatBall, new PBELocalizedString("つかまえたことの　ある\nポケモンが　つかまえやすくなる\nちょっと　かわった　ボール。", "잡은 적이 있는\n포켓몬을 잡기 쉬운\n조금 특이한 볼.", "Une Poké Ball un peu spéciale qui\nfonctionne particulièrement bien sur\nles espèces de Pokémon déjà capturées.", "Ein Ball, der besonders dann sehr gut\nist, wenn man ein Pokémon fangen will,\ndessen Art bereits gefangen wurde.", "Es algo distinta a las demás. Funciona\nespecialmente bien con las especies\nde Pokémon que ya has capturado.", "Poké Ball un po' diversa. Risulta\nparticolarmente efficace sulle specie\ndi Pokémon già catturate in precedenza.", "A somewhat different Poké Ball that\nworks especially well on Pokémon\nspecies that were previously caught.") },
            { PBEItem.Repel, new PBELocalizedString("\xFF11\xFF10\xFF10ぽ　すすむ　あいだ\nよわい　やせいの　ポケモンが\nまったく　でて　こなくなる。", "100보 걸어가는 동안\n약한 야생 포켓몬이\n전혀 나오지 않게 된다.", "Repousse les Pokémon sauvages\nfaibles pendant 100 pas.", "Hält 100 Schritte lang schwache,\nwilde Pokémon ab.", "Repele Pokémon salvajes débiles\nen un recorrido de 100 pasos.", "Strumento che evita l'incontro con i\nPokémon selvatici deboli per 100 passi\ndopo l'utilizzo.", "An item that prevents weak wild\nPokémon from appearing for 100 steps\nafter its use.") },
            { PBEItem.ReplyMail, new PBELocalizedString("もらった　メールへの　おかえしが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "받은 메일에 대한 답장을\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Un papier à lettres idéal pour répondre\nà un autre courrier. Peut être tenu\net livré par un Pokémon.", "Briefpapier, mit dem man einen\nanderen Brief beantworten kann.\nGib es einem Pokémon zum Tragen.", "Carta para responder a un mensaje.\nHaz que la entregue un Pokémon.", "Carta da lettera ideale per scrivere\nuna risposta ad un messaggio.\nUn Pokémon può tenerla e consegnarla.", "Stationery designed for\nwriting a reply.\nLet a Pokémon hold it for delivery.") },
            { PBEItem.ResistWing, new PBELocalizedString("ポケモンに　つかう　どうぐ。\nポケモン　\xFF11ひきの　ぼうぎょの\nきそポイントを　ちょっと　あげる。", "포켓몬에게 사용하는 도구.\n포켓몬 1마리의 방어\n기초 포인트를 조금 올린다.", "Objet à utiliser sur un Pokémon.\nIl augmente un peu sa Défense\nde base.", "Schenkt man einem Pokémon dieses\nItem, wird dessen Verteidigungs-Wert\ndauerhaft erhöht.", "Pluma que aumenta un poco la Defensa\nde un Pokémon al subir de nivel.", "Strumento da usare su un Pokémon.\nNe aumenta di poco la Difesa di base.", "An item for use on a Pokémon. It\nslightly increases the base Defense\nstat of a single Pokémon.") },
            { PBEItem.RevivalHerb, new PBELocalizedString("とても　にがい　やくそう。\nひんしの　ポケモン　\xFF11ひきの\n\xFF28\xFF30を　すべて　かいふくする。", "매우 쓴 약초.\n기절한 포켓몬 1마리의\nHP를 모두 회복한다.", "Une herbe médicinale très amère.\nRanime un Pokémon K.O. et restaure\ntous ses PV.", "Ein bitteres medizinisches Kraut, das\nkampfunfähige Pokémon belebt und\ndie KP vollständig auffüllt.", "Hierba muy amarga que reanima a\nun Pokémon debilitado y restaura\ntodos sus PS.", "Erba medicinale amarissima.\nRianima un Pokémon esausto e gli\nrestituisce tutti i PS.", "A very bitter medicinal herb.\nIt revives a fainted Pokémon, fully\nrestoring its HP.") },
            { PBEItem.Revive, new PBELocalizedString("ひんしに　なってしまった\nポケモン　\xFF11ひきの　\xFF28\xFF30を\nはんぶんまで　かいふくする。", "기절해 버린\n포켓몬 1마리의 HP를\n절반까지 회복한다.", "Un médicament qui ranime un Pokémon\nK.O. et restaure la moitié de ses PV\nmax.", "Eine Medizin, die ein kampfunfähiges\nPokémon wiederbelebt. Die Hälfte der\nmaximalen KP wird wiederhergestellt.", "Medicina que reanima a un Pokémon\ndebilitado y restaura la mitad de\nsus PS máximos.", "Rimedio che rianima un Pokémon esausto\ne gli restituisce metà dei suoi\nPS massimi.", "A medicine that revives a fainted\nPokémon. It restores half the\nPokémon's maximum HP.") },
            { PBEItem.RindoBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　くさ　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 풀 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Plante.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Pflanze.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Planta\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Erba\ndel nemico.", "Weakens a supereffective Grass-type\nattack against the holding Pokémon.") },
            { PBEItem.RingTarget, new PBELocalizedString("ポケモンの　タイプの　あいしょうで\nむこう　だった　わざが\nあたるように　なってしまう。", "포켓몬 타입의 상성 때문에\n무효였던 기술이\n맞게 되어 버린다.", "Un Pokémon tenant cet objet devient\nsensible aux capacités autrement\ninefficaces en raison de son type.", "Attacken, die aufgrund der Wechsel-\nwirkungen zwischen den Typen keine\nWirkung haben, treffen den Träger.", "El Pokémon que lo lleva es vulnerable\nfrente a movimientos que normalmente\nno afectan a su tipo.", "Se un Pokémon ha questo strumento, le\nmosse che di norma non hanno effetto\ncontro il suo tipo vanno a segno.", "Moves that would otherwise have no\neffect will land on the Pokémon that\nholds it.") },
            { PBEItem.RockGem, new PBELocalizedString("いわの　タイプの　ジュエル。\nもたせると　いちどだけ　いわの\nわざの　いりょくが　つよまる。", "바위타입의 주얼.\n지니게 하면 한 번만 바위\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Roche.", "Ein Juwel vom Typ Gestein. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Gestein.", "Joya de un solo uso que potencia\nlos movimientos de tipo Roca.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Roccia.", "A gem with an essence of rock. When\nheld, it strengthens the power of a\nRock-type move only once.") },
            { PBEItem.RockIncense, new PBELocalizedString("ふしぎな　かおりの　おこう。\nもたせると　いわタイプの\nわざの　いりょくが　あがる。", "이상한 향기의 향로.\n지니게 하면 바위타입\n기술의 위력이 올라간다.", "Objet à tenir. Encens au parfum\nexotique augmentant la puissance\ndes capacités de type Roche.", "Ein Item zum Tragen. Dieses\nexotische Duftkraut verstärkt\nAttacken vom Typ Gestein.", "Incienso de exótico aroma que fortalece\nlos movimientos de tipo Roca.\nDebe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nIncenso dal profumo esotico che\npotenzia le mosse di tipo Roccia.", "An item to be held by a Pokémon. It is\nan exotic-smelling incense that boosts\nthe power of Rock-type moves.") },
            { PBEItem.RockyHelmet, new PBELocalizedString("ポケモンに　もたせると\nだげきわざを　うけたとき\nあいてにも　ダメージを　あたえる。", "포켓몬에게 지니게 하면\n타격 기술을 당했을 때\n상대에게도 데미지를 준다.", "Tenu, cet objet inflige des dégâts\nà l'attaquant si ce dernier utilise une\nattaque physique qui atteint son but.", "Wird der Träger von einer physischen\nAttacke getroffen, erhält der\nAngreifer ebenfalls Schaden.", "Si el portador es alcanzado por un\nataque físico, el agresor también\nrecibe daño.", "Se il Pokémon che lo tiene è colpito da\nun attacco fisico, anche l'avversario\nsubisce dei danni.", "If the holder of this item takes damage,\nthe attacker will also be damaged\nupon contact.") },
            { PBEItem.RootFossil, new PBELocalizedString("おおむかし　うみに　すんでいた\nこだい　ポケモンの　カセキ。\nねっこの　いちぶ　らしい。", "오랜 옛날 바다에 살았던\n고대 포켓몬의 화석.\n뿌리의 일부인 듯하다.", "Fossile d'un ancien Pokémon aquatique.\nOn dirait un morceau de racine de\nplante.", "Das Fossil eines uralten Pokémon,\ndas in der Tiefsee lebte. Es sieht\nwie eine Pflanzenwurzel aus.", "Fósil de un Pokémon ancestral que\nvivió en el fondo del mar. Parece\nser parte de una raíz.", "Fossile di un antico Pokémon che\nviveva in mare. Sembra essere\nparte di una radice.", "A fossil of an ancient Pokémon that\nlived in the sea. It appears to be\npart of a plant root.") },
            { PBEItem.RoseIncense, new PBELocalizedString("ふしぎな　かおりの　おこう。\nもたせると　くさタイプの\nわざの　いりょくが　あがる。", "이상한 향기의 향로.\n지니게 하면 풀타입\n기술의 위력이 올라간다.", "Objet à tenir. Encens au parfum\nexotique augmentant la puissance\ndes capacités de type Plante.", "Ein Item zum Tragen. Dieses\nexotische Duftkraut verstärkt\nAttacken vom Typ Pflanze.", "Incienso de exótico aroma que fortalece\nlos movimientos de tipo Planta.\nDebe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nIncenso dal profumo esotico che\npotenzia le mosse di tipo Erba.", "An item to be held by a Pokémon. It is\nan exotic-smelling incense that boosts\nthe power of Grass-type moves.") },
            { PBEItem.RowapBerry, new PBELocalizedString("ポケモンに　もたせると\nとくしゅわざを　うけたとき\nあいてにも　ダメージを　あたえる。", "포켓몬에게 지니게 하면\n특수 기술을 당했을 때\n상대에게도 데미지를 준다.", "Tenue, cette Baie inflige des dégâts\nà l'ennemi s'il utilise une attaque\nspéciale qui atteint son but.", "Als getragenes Item bewirkt die Beere\nSchaden bei denen, die den Träger\nmit einer Spezial-Attacke treffen.", "Si la lleva un Pokémon cuando otro\nlanza un ataque especial, este último\ntambién recibe daño.", "Se tenuta da un Pokémon, il nemico\nsubirà danni se il suo attacco speciale\nva a segno.", "If held by a Pokémon and a special\nattack lands, the attacker also\ntakes damage.") },
            { PBEItem.RSVPMail, new PBELocalizedString("あいてを　おさそいする　メールが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "상대를 초대하는 메일을\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Un papier à lettres idéal pour\nles invitations. Peut être tenu\net livré par un Pokémon.", "Briefpapier, mit dem man eine andere\nPerson zu etwas einladen kann.\nGib es einem Pokémon zum Tragen.", "Carta para invitar a un compañero.\nHaz que la entregue un Pokémon.", "Carta da lettera ideale per scrivere\nun invito a qualcuno. Un Pokémon può\ntenerla e consegnarla.", "Stationery designed for\ninvitations.\nLet a Pokémon hold it for delivery.") },
            { PBEItem.SacredAsh, new PBELocalizedString("ひんしに　なってしまった\nポケモン　ぜんいんの　\xFF28\xFF30を\nすべて　かいふくする。", "기절해 버린\n포켓몬 전원의 HP를\n모두 회복한다.", "Ranime tous les Pokémon K.O. et\nrestaure tous leurs PV.", "Belebt und erfrischt alle besiegten\nPokémon und füllt deren KP\nvollständig wieder auf.", "Reanima completamente a todos los\nPokémon debilitados y restaura\ntodos sus PS.", "Rianima tutti i Pokémon esausti,\nrestituendo loro anche tutti i PS.", "It revives all fainted Pokémon.\nIn doing so, it also fully restores\ntheir HP.") },
            { PBEItem.SafariBall, new PBELocalizedString("だいしつげんの　なか　だけで\nつかう　とくべつな　ボール。\nめいさいがらに　なっている。", "대습초원 안에서만\n쓰는 특별한 볼.\n얼룩무늬로 되어 있다.", "Une Poké Ball spéciale dont l'usage\nest réservé au Parc Safari. Elle est\ndécorée d'un motif camouflage.", "Ein Ball, der nur in der Safari-Zone\neingesetzt wird. Er besticht durch\nsein Tarnfarbenmuster.", "Poké Ball con un diseño de camuflaje\nque se utiliza únicamente en el Gran\nPantano.", "Poké Ball speciale, usata soltanto\nnella Gran Palude. È decorata con\nun motivo mimetico.", "A special Poké Ball that is used only in\nthe Great Marsh. It is decorated in a\ncamouflage pattern.") },
            { PBEItem.SalacBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　じぶんの\nすばやさが　あがる。", "포켓몬에게 지니게 하면\n위급할 때 자신의\n스피드가 올라간다.", "Tenue, cette Baie monte la Vitesse\nen cas de besoin.", "Als getragenes Item erhöht diese Beere\nden Initiative-Wert eines Pokémon,\nsobald es in einer Notlage steckt.", "Si la lleva un Pokémon, le sube\nla Velocidad en un momento de apuro.", "Aumenta la Velocità, se il Pokémon che\nla tiene si trova in difficoltà.", "If held by a Pokémon, it raises its\nSpeed stat in a pinch.") },
            { PBEItem.ScopeLens, new PBELocalizedString("じゃくてんが　みえる　レンズ。\nもたせた　ポケモンの　わざが\nきゅうしょに　あたりやすくなる。", "약점이 보이는 렌즈.\n지니게 한 포켓몬의 기술이\n급소에 맞기 쉬워진다.", "Objet à tenir. Lentille qui augmente le\ntaux de critiques du porteur.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nerhöht die Volltrefferquote.", "Su lente aumenta la probabilidad que\ntiene el Pokémon que lo lleva de\nconseguir un golpe crítico.", "Strumento da far tenere a un Pokémon.\nLente che aumenta le probabilità per chi\nla tiene di sferrare dei brutti colpi.", "An item to be held by a Pokémon.\nIt is a lens that boosts the holder's\ncritical-hit ratio.") },
            { PBEItem.SeaIncense, new PBELocalizedString("ふしぎな　かおりの　おこう。\nもたせると　みずタイプの\nわざの　いりょくが　あがる。", "이상한 향기의 향로.\n지니게 하면 물타입\n기술의 위력이 올라간다.", "Objet à tenir. Encens au parfum curieux\nqui augmente la puissance des capacités\nde type Eau.", "Ein Item zum Tragen. Sein\nausgefallenes Aroma verstärkt\nAttacken vom Typ Wasser.", "Tiene un curioso aroma que potencia\nlos movimientos de tipo Agua.\nDebe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nIncenso dal curioso aroma che potenzia\nle mosse di tipo Acqua.", "An item to be held by a Pokémon. It is\nincense with a curious aroma that\nboosts the power of Water-type moves.") },
            { PBEItem.SharpBeak, new PBELocalizedString("ながく　とがった　くちばし。\nもたせると　ひこうタイプの\nわざの　いりょくが　あがる。", "길고 뾰족한 부리.\n지니게 하면 비행타입\n기술의 위력이 올라간다.", "Objet à tenir. Bec long et pointu\nmontant la puissance des capacités\nde type Vol.", "Ein Item zum Tragen. Ein langer,\nscharfer Schnabel verstärkt\nAttacken vom Typ Flug.", "Pico largo y afilado que aumenta la\npotencia de los movimientos de tipo\nVolador. Debe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nBecco lungo e affilato che potenzia\nle mosse di tipo Volante.", "An item to be held by a Pokémon.\nIt is a long, sharp beak that boosts\nthe power of Flying-type moves.") },
            { PBEItem.ShedShell, new PBELocalizedString("じょうぶで　かたい　ぬけがら。\nもたせた　ポケモンは　ひかえの\nポケモンと　かならず　いれかわれる。", "튼튼하고 단단한 허물.\n지니게 한 포켓몬은 교대\n포켓몬과 반드시 교체할 수 있다.", "Objet à tenir. Carapace dure qui permet\nau porteur de se retirer même s'il est\naffecté par une attaque immobilisante.", "Ein Item zum Tragen. Dieser harte\nPanzer ermöglicht den Austausch des\nTrägers gegen ein wartendes Pokémon.", "En combate, esta concha desechada\nsirve para que un Pokémon se cambie\npor otro que no esté combatiendo.", "Un Pokémon con questo robusto guscio\npuò sfuggire al nemico anche se questi\nha usato mosse o abilità intrappolanti.", "A tough, discarded carapace to be held\nby a Pokémon. It enables the holder to\nswitch with a waiting Pokémon in battle.") },
            { PBEItem.ShellBell, new PBELocalizedString("もたせた　ポケモンが　こうげきして\nあいてに　ダメージを　あたえたとき\n\xFF28\xFF30を　すこし　かいふく　できる。", "지니게 한 포켓몬이 공격하여\n상대에게 데미지를 줬을 때\nHP를 조금 회복할 수 있다.", "Objet à tenir. Restaure un peu les PV\ndu porteur à chaque fois qu'il inflige\ndes dégâts.", "Ein Item zum Tragen. Es stellt einige\nKP des Trägers immer dann wieder her,\nwenn dieser dem Ziel Schaden zufügt.", "Debe llevarla un Pokémon.\nCada vez que este inflige daño,\nrecupera algunos PS.", "Strumento da far tenere a un Pokémon.\nChi lo tiene recupera un po' di PS\nogni volta che infligge danni.", "An item to be held by a Pokémon.\nThe holder's HP is restored a little\nevery time it inflicts damage.") },
            { PBEItem.ShinyStone, new PBELocalizedString("ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nひかりのように　まぶしい。", "어느 특정 포켓몬을\n진화시키는 이상한 돌.\n빛처럼 눈부시다.", "Pierre étrange qui fait évoluer\ncertaines espèces de Pokémon. Elle\nbrille d'une lumière aveuglante.", "Dieser besondere, hell leuchtende\nStein hilft manchen Pokémon bei\nihrer Evolution.", "Una piedra peculiar que hace\nevolucionar a algunos Pokémon.\nTiene un brillo espectacular.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nBrilla di una luce abbagliante.", "A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt shines with a dazzling light.") },
            { PBEItem.ShoalSalt, new PBELocalizedString("あさせのほらあな　という　ばしょで\nみつかる　きれいな　しお。\nマニアが　たかく　かってくれる。", "여울의 동굴이라는 장소에서\n발견할 수 있는 깨끗한 소금.\n마니아가 비싼 값에 산다.", "Sel pur provenant des profondeurs\nde la Grotte Tréfonds. Les amateurs\nen donnent un bon prix.", "Reine Salzkristalle, die sich in der sog.\nKüstenhöhle finden lassen. Sammler\ngeben ihr letztes Hemd dafür her.", "Una sal muy fina encontrada en la\nCueva Cardumen.\nMuy valorada por los coleccionistas.", "Sale purissimo rinvenuto nella Grotta\nOndosa. Si può vendere a un prezzo\npiuttosto alto agli intenditori.", "Pure salt that can be discovered deep\ninside the Shoal Cave.\nA maniac will buy it for a high price.") },
            { PBEItem.ShoalShell, new PBELocalizedString("あさせのほらあな　という　ばしょで\nみつかる　きれいな　かいがら。\nマニアが　たかく　かってくれる。", "여울의 동굴이라는 장소에서\n발견할 수 있는 예쁜 조개껍질.\n마니아가 비싼 값에 산다.", "Joli coquillage provenant des\nprofondeurs de la Grotte Tréfonds.\nLes amateurs en donnent un bon prix.", "Eine hübsche Schale, die sich in der\nKüstenhöhle finden lässt. Sammler\ngeben ihr letztes Hemd dafür her.", "Concha encontrada en la Cueva\nCardumen.\nMuy valorada por los coleccionistas.", "Un bel guscio rinvenuto nella Grotta\nOndosa. Si può vendere a un prezzo\npiuttosto alto agli intenditori.", "A pretty seashell that can be found\ndeep inside the Shoal Cave.\nA maniac will buy it for a high price.") },
            { PBEItem.ShockDrive, new PBELocalizedString("ゲノセクトに　もたせると\nテクノバスターという　わざが\nでんきタイプになる　カセット。", "게노세크트에게 지니게 하면\n테크노버스터라는 기술이\n전기타입으로 바뀌는 카세트.", "Tenu par Genesect, ce module\nchange la capacité TechnoBuster\nen une capacité de type Électrik.", "Gibt man es Genesect zum Tragen,\nwandelt es den Typ der Attacke\nTechblaster in Elektro um.", "Cartucho que convierte\nTecno Shock de Genesect\nen un movimiento de tipo Eléctrico.", "Modulo da far tenere a Genesect.\nTrasforma Tecnobotto in una mossa\ndi tipo Elettro.", "A cassette to be held by Genesect.\nIt changes Techno Blast to an\nElectric-type move.") },
            { PBEItem.ShucaBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　じめん　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 땅 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Sol.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Boden.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Tierra\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Terra\ndel nemico.", "Weakens a supereffective Ground-type\nattack against the holding Pokémon.") },
            { PBEItem.SilkScarf, new PBELocalizedString("はだざわりの　よい　スカーフ。\nもたせると　ノーマルタイプの\nわざの　いりょくが　あがる。", "감촉이 좋은 스카프.\n지니게 하면 노말타입\n기술의 위력이 올라간다.", "Objet à tenir. Mouchoir somptueux\nmontant la puissance des capacités\nde type Normal.", "Ein Item zum Tragen. Ein kostbarer\nSchal, der Attacken vom Typ Normal\nverstärkt.", "La delicadeza del pañuelo fortalece\nlos movimientos de tipo Normal.\nDebe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nSciarpa raffinata che potenzia le mosse\ndi tipo Normale.", "An item to be held by a Pokémon.\nIt is a sumptuous scarf that boosts\nthe power of Normal-type moves.") },
            { PBEItem.SilverPowder, new PBELocalizedString("ぎんいろに　かがやく　こな。\nもたせると　むしタイプの\nわざの　いりょくが　あがる。", "은색으로 반짝이는 가루.\n지니게 하면 벌레타입\n기술의 위력이 올라간다.", "Objet à tenir. Poudre d'argent\nbrillante qui monte la puissance\ndes capacités de type Insecte.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nverbessert Attacken vom Typ Käfer.", "Polvo de brillo argénteo que fortalece\nlos ataques de tipo Bicho.\nDebe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nPolvere argentea e brillante che\npotenzia le mosse di tipo Coleottero.", "An item to be held by a Pokémon.\nIt is a shiny, silver powder that ups\nthe power of Bug-type moves.") },
            { PBEItem.SitrusBerry, new PBELocalizedString("ポケモンに　もたせると\n\xFF28\xFF30を　すこしだけ　かいふくする。\n", "포켓몬에게 지니게 하면\nHP를 조금 회복한다.\n", "Baie qui restaure quelques PV\nlorsqu'elle est tenue par un Pokémon.", "Gibst du die Beere einem Pokémon\nzum Tragen, füllt diese seine KP\nein wenig auf.", "Los Pokémon pueden llevarla o usarla\npara restaurar algunos PS.", "Se tenuta da un Pokémon restituisce\nun po' di PS.", "If held by a Pokémon,\nit heals the user's HP a little.") },
            { PBEItem.SkullFossil, new PBELocalizedString("おおむかし　ちじょうで　くらした\nこだい　ポケモンの　カセキ。\nあたまの　いちぶ　らしい。", "오랜 옛날 지상에서 살았던\n고대 포켓몬의 화석.\n머리의 일부인 듯하다.", "Fossile d'un Pokémon préhistorique\nterrestre. On dirait une partie d'un\ncrâne.", "Das Fossil eines uralten\nPokémon, das an Land lebte.\nEs sieht wie ein Schädel aus.", "Fósil de un Pokémon prehistórico\nterrestre. Parece ser parte de una\ncabeza.", "Fossile di un Pokémon preistorico che\nviveva sulla terraferma. Sembra far\nparte di un teschio.", "A fossil from a prehistoric Pokémon\nthat lived on the land. It appears to\nbe part of a head.") },
            { PBEItem.SkyPlate, new PBELocalizedString("ひこうの　タイプの　せきばん。\nもたせると　ひこうタイプの\nわざの　いりょくが　つよまる。", "비행타입의 석판.\n지니게 하면 비행타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Vol.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Flug.", "Tabla de piedra que potencia\nlos movimientos de tipo Volador.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Volante.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Flying-type moves.") },
            { PBEItem.SmokeBall, new PBELocalizedString("もたせた　ポケモンは　やせいの\nポケモンとの　せんとうから\nぜったいに　にげられる。", "지니게 한 포켓몬은\n야생 포켓몬과의 배틀에서\n반드시 도망칠 수 있다.", "Objet à tenir. Permet au porteur de\ns'enfuir à coup sûr face à un Pokémon\nsauvage.", "Ein Pokémon kann dieses Item tragen.\nEs ermöglicht die garantierte Flucht\naus dem Kampf mit einem wilden Pokémon.", "Debe llevarla un Pokémon.\nPermite huir siempre de combates\ncontra Pokémon salvajes.", "Strumento da far tenere a un Pokémon.\nPermette a chi lo tiene di fuggire con\nsuccesso da un Pokémon selvatico.", "An item to be held by a Pokémon.\nIt enables the holder to flee from any\nwild Pokémon without fail.") },
            { PBEItem.SmoothRock, new PBELocalizedString("もたせた　ポケモンが　すなあらしを\nつかうと　いつもより　すなあらしの\nじかんが　ながくなる。", "지니게 한 포켓몬이 모래바람을\n쓰면 여느 때보다 모래바람의\n시간이 길어진다.", "Objet à tenir augmentant la durée de\nla capacité Tempêtesable utilisée par\nle porteur.", "Ein Item zum Tragen. Verlängert\ndie Dauer von Sandsturm, wenn\nvon Träger eingesetzt.", "Prolonga la duración del movimiento\nTormenta Arena que use el Pokémon\nque la lleva.", "Strumento da far tenere a un Pokémon\nche allunga la durata della mossa\nTerrempesta se usata da chi lo tiene.", "A Pokémon held item that extends the\nduration of the move Sandstorm used\nby the holder.") },
            { PBEItem.SodaPop, new PBELocalizedString("シュワっと　はじける　ソーダ。\nポケモン　\xFF11ひきの　\xFF28\xFF30を\n\xFF16\xFF10だけ　かいふくする。", "쏴하고 톡 쏘는 사이다.\n포켓몬 1마리의 HP를\n60만큼 회복한다.", "Une boisson pétillante.\nRestaure 60 PV à un Pokémon.", "Ein sprudelndes Mineralwasser,\ndas die KP eines Pokémon\num 60 Punkte auffüllt.", "Chispeante bebida gaseosa que\nrestaura 60 PS de un Pokémon.", "Bevanda frizzante.\nRestituisce 60 PS ad un Pokémon.", "A fizzy soda drink.\nIt restores the HP of one Pokémon by\n60 points.") },
            { PBEItem.SoftSand, new PBELocalizedString("さわると　サラサラする　すな。\nもたせると　じめんタイプの\nわざの　いりょくが　あがる。", "만지면 보슬보슬한 모래.\n지니게 하면 땅타입\n기술의 위력이 올라간다.", "Objet à tenir. Sable fin et soyeux\nmontant la puissance des capacités\nde type Sol.", "Ein Item zum Tragen. Samtiger\nSand, der Attacken vom Typ\nBoden verstärkt.", "Debe llevarla un Pokémon.\nLa suavidad de la arena fortalece\nlos movimientos de tipo Tierra.", "Strumento da far tenere a un Pokémon.\nSabbia leggera e farinosa che potenzia\nle mosse di tipo Terra.", "An item to be held by a Pokémon.\nIt is a loose, silky sand that boosts\nthe power of Ground-type moves.") },
            { PBEItem.SootheBell, new PBELocalizedString("ここちよい　ねいろの　すず。\nもたせた　ポケモンは　こころが\nやすらぎ　なつきやすくなる。", "기분 좋은 소리의 방울.\n지니게 한 포켓몬은 마음이\n편안해져 친밀해지기 쉬워진다.", "Objet à tenir. Grelot dont le carillon\ncalme le porteur et favorise l'amitié.", "Ein Item, das einem Pokémon zum\nTragen gegeben werden kann. Es\nbesänftigt und fördert Sympathie.", "Una campana con un tañido calmante\nque reconforta al Pokémon que la lleva\ny lo vuelve más amistoso.", "Strumento da far tenere a un Pokémon.\nCampanella dal suono rassicurante che\ncalma chi la usa rendendolo amichevole.", "An item to be held by a Pokémon. It is\na bell with a comforting chime that\ncalms the holder and makes it friendly.") },
            { PBEItem.SoulDew, new PBELocalizedString("ラティオス　または　ラティアスに\nもたせると　とくこうと　とくぼうが\nあがる　ふしぎな　たま。", "라티오스 혹은 라티아스에게\n지니게 하면 특수공격과 특수방어가\n올라가는 이상한 구슬.", "Orbe prodigieux à faire tenir à Latios\nou Latias qui monte leur Attaque\nSpéciale et leur Défense Spéciale.", "Ein Item, das von Latios oder Latias\ngetragen werden kann. Es hebt Spezial-\nAngriff und Spezial-Verteidigung.", "Un orbe fantástico que debe llevar\nLatios o Latias. Sube su Ataque\nEspecial y su Defensa Especial.", "Sfera miracolosa da far tenere a\nLatios o Latias. Aumenta l'Attacco\nSpeciale e la Difesa Speciale.", "A wondrous orb to be held by Latios\nor Latias. It raises both the\nSp. Atk and Sp. Def stats.") },
            { PBEItem.SpellTag, new PBELocalizedString("あやしくて　ぶきみな　おふだ。\nもたせると　ゴーストタイプの\nわざの　いりょくが　あがる。", "괴상하고 으스스한 부적.\n지니게 하면 고스트타입\n기술의 위력이 올라간다.", "Objet à tenir. Plaque sinistre et\nétrange montant la puissance des\ncapacités de type Spectre.", "Ein Item zum Tragen. Ein fieser,\nunheimlicher Sticker, der Attacken\nvom Typ Geist verstärkt.", "Misterioso y siniestro objeto que\npotencia los movimientos de tipo\nFantasma. Debe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nTarga sinistra e inquietante che\npotenzia le mosse di tipo Spettro.", "An item to be held by a Pokémon.\nIt is a sinister, eerie tag that boosts\nthe power of Ghost-type moves.") },
            { PBEItem.SpelonBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.SplashPlate, new PBELocalizedString("みずの　タイプの　せきばん。\nもたせると　みずタイプの\nわざの　いりょくが　つよまる。", "물타입의 석판.\n지니게 하면 물타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Eau.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Wasser.", "Tabla de piedra que potencia\nlos movimientos de tipo Agua.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Acqua.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Water-type moves.") },
            { PBEItem.SpookyPlate, new PBELocalizedString("ゴーストの　タイプの　せきばん。\nもたせると　ゴーストタイプの\nわざの　いりょくが　つよまる。", "고스트타입의 석판.\n지니게 하면 고스트타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Spectre.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Geist.", "Tabla de piedra que potencia\nlos movimientos de tipo Fantasma.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Spettro.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Ghost-type moves.") },
            { PBEItem.SportBall, new PBELocalizedString("むしとりたいかいの　なかで\nつかう　とくべつな　ボール。　\n", "곤충채집 대회 내에서\n사용하는 특별한 볼.\n", "Une Poké Ball spéciale utilisée au\nConcours de Capture d'insecte.", "Ein Ball, der nur beim Käferturnier\neingesetzt wird.", "Poké Ball que se utiliza en el\nConcurso de Captura de Bichos.", "Poké Ball speciale utilizzata solo nella\nGara Pigliamosche.", "A special Poké Ball for the\nBug-Catching Contest.") },
            { PBEItem.StableMulch, new PBELocalizedString("きのみを　そだてる　ちほうでは\nふかふかのつちに　まいて　つかう。\nマニアが　たかく　かってくれる。", "나무열매를 키우는 지방에서는\n푹신푹신한 땅에 뿌려 쓴다.\n마니아가 비싼 값에 산다.", "Un engrais utilisé sur sol meuble.\nLes amateurs en donnent un bon prix.", "In Regionen, wo man Beeren anbaut,\nmengt man ihn unter weiche Erde.\nSammler geben ihr letztes Hemd dafür.", "Un fertilizante usado en suelo fértil\ndonde crecen las Bayas.\nMuy apreciado por los coleccionistas.", "Usato per favorire la crescita delle\nBacche nelle regioni dove si coltivano.\nGli intenditori pagano caro per averlo.", "A fertilizer to be spread on soft soil\nin regions where Berries are grown.\nA maniac will buy it for a high price.") },
            { PBEItem.Stardust, new PBELocalizedString("てざわりが　サラサラの\nあかくて　きれいな　すな。\nショップで　たかく　うれる。", "감촉이 보슬보슬한\n빨갛고 예쁜 모래.\n상점에서 비싸게 팔린다.", "Sable rouge ravissant, aux grains très\nfins. Peut être vendu à bon prix aux\nmagasins.", "Schöner, roter Sand, der sich\nseidenweich anfühlt. Er ist von\nhohem Wert.", "Bonita arena roja de tacto sedoso que\nalcanza un alto precio en las tiendas.", "Bella sabbia rossa dalla consistenza\nleggera e farinosa. Si può vendere\na un prezzo piuttosto alto nei negozi.", "Lovely, red-colored sand with a loose,\nsilky feel. It can be sold at a high\nprice to shops.") },
            { PBEItem.StarfBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　どれか　\xFF11つの\nのうりょくが　ぐーんと　あがる。", "포켓몬에게 지니게 하면\n위급할 때 능력 중의 하나가\n크게 올라간다.", "Tenue, cette Baie monte beaucoup une\ndes stats en cas de besoin.", "Als getragenes Item erhöht diese Beere\neinen Statuswert des Pokémon stark,\nsobald es in einer Notlage steckt.", "Si la lleva un Pokémon, aumenta\nmucho una característica en un\nmomento de apuro.", "Aumenta molto una statistica se\nil Pokémon che la tiene si trova\nin difficoltà.", "If held by a Pokémon, it sharply raises\none of its stats in a pinch.") },
            { PBEItem.StarPiece, new PBELocalizedString("キラキラと　あかく　ひかる\nきれいな　ほうせきの　かけら。\nショップで　たかく　うれる。", "반짝반짝 빨갛게 빛나는\n예쁜 보석 조각.\n상점에서 비싸게 팔린다.", "Fragment de gemme qui brille d'un éclat\nrouge. Peut se vendre à bon prix aux\nmagasins.", "Teil eines hübschen Edelsteins, der in\nrötlicher Farbe schimmert. Erzielt einen\nhohen Preis, wenn man ihn verkauft.", "Fragmento de una bonita gema roja\nque puede venderse muy caro en las\ntiendas.", "Frammento rosso acceso di una gemma.\nSi può vendere a un prezzo piuttosto\nalto nei negozi.", "A shard of a pretty gem that sparkles\nin a red color. It can be sold at a\nhigh price to shops.") },
            { PBEItem.SteelGem, new PBELocalizedString("はがねの　タイプの　ジュエル。\nもたせると　いちどだけ　はがねの\nわざの　いりょくが　つよまる。", "강철타입의 주얼.\n지니게 하면 한 번만 강철\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Acier.", "Ein Juwel vom Typ Stahl. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Stahl.", "Joya de un solo uso que potencia\nlos movimientos de tipo Acero.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Acciaio.", "A gem with an essence of steel. When\nheld, it strengthens the power of a\nSteel-type move only once.") },
            { PBEItem.Stick, new PBELocalizedString("とても　ながくて　かたい　クキ。\nカモネギに　もたせると　わざが\nきゅうしょに　あたりやすくなる。", "매우 길고 단단한 줄기.\n파오리에게 지니게 하면 기술이\n급소에 맞기 쉬워진다.", "Objet à faire tenir à Canarticho.\nPoireau très long et solide qui\nmonte son taux de critiques.", "Ein Item, das von Porenta getragen\nwerden kann. Diese lange Lauchstange\nerhöht die Volltrefferquote stark.", "Es un puerro muy largo que debe llevar\nFarfetch'd. Aumenta la probabilidad de\nque logre un golpe crítico mucho.", "Strumento da far tenere a Farfetch'd.\nGambo di porro lungo che aumenta\ndi molto la probabilità di brutti colpi.", "An item to be held by Farfetch'd. It is\na very long and stiff stalk of leek\nthat boosts the critical-hit ratio.") },
            { PBEItem.StickyBarb, new PBELocalizedString("もたせると　まいターン　ダメージを\nうける。　じぶんに　ふれてきた\nあいてに　くっつくことも　ある。", "지니게 하면 매 턴 데미지를 입는다.\n자신에게 접촉해온 상대에게\n들러붙기도 한다.", "Objet à tenir blessant le porteur à\nchaque tour. Peut s'accrocher aux\nennemis qui attaquent le porteur.", "Schadet dem Träger in jeder Runde,\nkann aber an Angreifern haften\nbleiben, die ihn berühren.", "Daña en cada turno al Pokémon que\nla lleva. Puede adherirse al Pokémon\nque alcance con su ataque al portador.", "Strumento che danneggia chi lo tiene\nad ogni turno. Può attaccarsi al nemico\nche lancia un attacco fisico.", "A held item that damages the holder on\nevery turn. It may latch on to foes and\nallies that touch the holder.") },
            { PBEItem.StonePlate, new PBELocalizedString("いわの　タイプの　せきばん。\nもたせると　いわタイプの\nわざの　いりょくが　つよまる。", "바위타입의 석판.\n지니게 하면 바위타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Roche.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Gestein.", "Tabla de piedra que potencia\nlos movimientos de tipo Roca.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Roccia.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Rock-type moves.") },
            { PBEItem.SunStone, new PBELocalizedString("ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nたいようのように　あかい。", "어느 특정한 포켓몬을\n진화시키는 이상한 돌.\n태양처럼 빨갛다.", "Une pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle est rouge comme le soleil.", "Dieser spezielle Stein löst bei\nbestimmten Pokémon die Entwicklung\naus. Er ist rot wie die Sonne.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nEs roja como el núcleo del sol.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nÈ rossa come il sole.", "A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt is as red as the sun.") },
            { PBEItem.SuperPotion, new PBELocalizedString("スプレーしきの　キズぐすり。\nポケモン　\xFF11ひきの　\xFF28\xFF30を\n\xFF15\xFF10だけ　かいふくする。", "스프레이식의 상처약.\n포켓몬 1마리의 HP를\n50만큼 회복한다.", "Un spray qui soigne les blessures.\nRestaure 50 PV à un Pokémon.", "Hilft bei Wunden. Es füllt\ndie KP eines Pokémon\num 50 Punkte auf.", "Medicina en espray que cura heridas\ny restaura 50 PS de un Pokémon.", "Rimedio spray per le ferite.\nRestituisce 50 PS ad un Pokémon.", "A spray-type medicine for wounds.\nIt restores the HP of one Pokémon by\n50 points.") },
            { PBEItem.SuperRepel, new PBELocalizedString("\xFF12\xFF10\xFF10ぽ　すすむ　あいだ\nよわい　やせいの　ポケモンが\nまったく　でて　こなくなる。", "200보 걸어가는 동안\n약한 야생 포켓몬이\n전혀 나오지 않게 된다.", "Repousse les Pokémon sauvages\nfaibles pendant 200 pas.", "Hält 200 Schritte lang schwache,\nwilde Pokémon ab.", "Repele Pokémon salvajes débiles\nen un recorrido de 200 pasos.", "Repellente che evita l'incontro con i\nPokémon selvatici deboli per 200 passi\ndopo l'utilizzo.", "An item that prevents weak wild\nPokémon from appearing for 200 steps\nafter its use.") },
            { PBEItem.SweetHeart, new PBELocalizedString("とても　あまったるい　チョコレート。\nポケモン　\xFF11ひきの　\xFF28\xFF30を\n\xFF12\xFF10だけ　かいふくする。", "지나칠 정도로 달콤한 초콜릿.\n포켓몬 1마리의 HP를\n20만큼 회복한다.", "Un chocolat extrêmement\nsucré. Restaure 20 PV\nà un Pokémon.", "Ein süßer Schokosnack, der die KP\neines Pokémon um 20 Punkte auffüllt.", "Chocolatina de sabor dulce.\nRestaura 20 PS de un Pokémon.", "Cioccolato molto dolce.\nRestituisce 20 PS ad un Pokémon.", "Very sweet chocolate.\nIt restores the HP of one Pokémon\nby only 20 points.") },
            { PBEItem.SwiftWing, new PBELocalizedString("ポケモンに　つかう　どうぐ。\nポケモン　\xFF11ひきの　すばやさの\nきそポイントを　ちょっと　あげる。", "포켓몬에게 사용하는 도구.\n포켓몬 1마리의 스피드\n기초 포인트를 조금 올린다.", "Objet à utiliser sur un Pokémon.\nIl augmente un peu sa Vitesse\nde base.", "Schenkt man einem Pokémon dieses\nItem, wird dessen Initiative-Wert\ndauerhaft erhöht.", "Pluma que aumenta un poco\nla Velocidad de un Pokémon\nal subir de nivel.", "Strumento da usare su un Pokémon.\nNe aumenta di poco la Velocità di base.", "An item for use on a Pokémon. It\nslightly increases the base Speed stat\nof a single Pokémon.") },
            { PBEItem.TamatoBerry, new PBELocalizedString("ポケモンに　あげると\nとても　なつき　やすくなるが\nすばやさの　きそポイントは　さがる。", "포켓몬에게 주면\n매우 친밀해지기 쉬워지지만\n스피드의 기초 포인트가 떨어진다.", "Rend les Pokémon plus amicaux, mais\nbaisse leur stat Vitesse de base.", "Isst ein Pokémon diese Beere,\nstärkt dies eure Freundschaft, ver-\nringert aber seinen Initiative-Wert.", "Al usarla con un Pokémon, se gana\nsu amistad, pero también reduce\nsu Velocidad de base.", "Utilizzata su un Pokémon, lo rende più\namichevole, ma diminuisce la Velocità\ndi base.", "Using it on a Pokémon makes it more\nfriendly, but it also lowers its base\nSpeed stat.") },
            { PBEItem.TangaBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　むし　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 벌레 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une attaque\nennemie super efficace de type Insecte.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Käfer.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Bicho\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo\nColeottero del nemico.", "Weakens a supereffective Bug-type\nattack against the holding Pokémon.") },
            { PBEItem.ThanksMail, new PBELocalizedString("かんしゃを　つたえる　メールが\nかきやすい　びんせん。\nポケモンに　もたせて　つかう。", "감사의 마음을 전하는 메일을\n쉽게 쓸 수 있는 편지지.\n포켓몬에게 지니게 해서 쓴다.", "Un papier à lettres idéal pour les\nremerciements. Peut être tenu\net livré par un Pokémon.", "Briefpapier, mit dem man sich\nbei jemandem bedanken kann.\nGib es einem Pokémon zum Tragen.", "Carta de agradecimiento.\nHaz que la entregue un Pokémon.", "Carta da lettera ideale per scrivere\ndei ringraziamenti. Un Pokémon può\ntenerla e consegnarla.", "Stationery designed for\na thank-you note.\nLet a Pokémon hold it for delivery.") },
            { PBEItem.ThickClub, new PBELocalizedString("なにかの　かたい　ホネ。\nカラカラ　または　ガラガラに\nもたせると　こうげきが　あがる。", "무언가의 단단한 뼈.\n탕구리 혹은 텅구리에게\n지니게 하면 공격이 올라간다.", "Objet à faire tenir à Osselait ou\nOssatueur. Os dur qui monte leur\nAttaque.", "Ein harter Knochen, der den Angriffs-\nWert von Tragosso oder Knogga\nerhöht, wenn sie ihn tragen.", "Un extraño tipo de hueso que potencia\nel Ataque. Deben llevarlo Cubone o\nMarowak.", "Strumento da far tenere a Cubone o\nMarowak. Osso duro che migliora\nl'Attacco.", "An item to be held by Cubone or\nMarowak. It is a hard bone of some\nsort that boosts the Attack stat.") },
            { PBEItem.Thunderstone, new PBELocalizedString("ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nいなずまの　もようが　ある。", "어느 특정한 포켓몬을\n진화시키는 이상한 돌.\n천둥번개 무늬가 있다.", "Une pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nUn éclair est dessiné dessus.", "Dieser spezielle Stein löst bei\nbestimmten Pokémon die Entwicklung\naus. Er hat ein Blitzmuster.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nTiene grabado un rayo.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nRiporta l'immagine di un fulmine.", "A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt has a thunderbolt pattern.") },
            { PBEItem.TimerBall, new PBELocalizedString("ターンすうが　かかれば　かかるほど\nポケモンが　つかまえやすくなる\nちょっと　かわった　ボール。", "턴 수가 길어지면 길어질수록\n포켓몬을 잡기 쉬운\n조금 특이한 볼.", "Une Poké Ball un peu spéciale qui\nfonctionne de mieux en mieux à\nmesure que le combat s'éternise.", "Ein Ball, der mit zunehmender\nKampfdauer effektiver wird.", "Una Poké Ball algo distinta que va\nmejorando su rendimiento con cada\nturno de combate.", "Poké Ball un po' diversa che diventa\nprogressivamente più efficace con\nl'aumentare dei turni in lotta.", "A somewhat different Ball that\nbecomes progressively better the\nmore turns there are in a battle.") },
            { PBEItem.TinyMushroom, new PBELocalizedString("ちいさな　めずらしい　キノコ。\nいちぶの　マニアの　あいだでは\nけっこう　にんきが　たかい。", "작고 진귀한 버섯.\n일부 마니아 사이에서는\n매우 인기가 높다.", "Un petit champignon plutôt rare.\nIl est plutôt populaire parmi\ncertains fans dévoués.", "Ein kleiner und seltener Pilz.\nSeine Fans sind ganz aus dem\nHäuschen, wenn sie einen finden.", "Una pequeña seta que es poco común\ny bastante popular entre determinados\ngrupos de gourmets.", "Fungo piccolo e raro. È parecchio\napprezzato da alcuni intenditori.", "A small and rare mushroom.\nIt is sought after by collectors.") },
            { PBEItem.ToxicOrb, new PBELocalizedString("さわると　どくをだす　ふしぎなたま。\nもたせると　せんとうちゅうに\nもうどくの　じょうたいに　なる。", "만지면 독을 발산하는 이상한 구슬.\n지니게 하면 배틀 중에\n맹독 상태가 된다.", "Objet à tenir. Orbe bizarre\nempoisonnant gravement son\nporteur durant le combat.", "Ein Item zum Tragen. Dieser\nbizarre Orb vergiftet seinen\nTräger im Kampf schwer.", "Extraña esfera que envenena\ngravemente al Pokémon que\nla lleva en combate.", "Strumento da far tenere a un Pokémon.\nSfera bizzarra che iperavvelena chi la\ntiene in lotta.", "An item to be held by a Pokémon.\nIt is a bizarre orb that badly poisons\nthe holder in battle.") },
            { PBEItem.ToxicPlate, new PBELocalizedString("どくの　タイプの　せきばん。\nもたせると　どくタイプの\nわざの　いりょくが　つよまる。", "독타입의 석판.\n지니게 하면 독타입\n기술의 위력이 강해진다.", "Objet à tenir. Tablette en pierre\naugmentant la puissance des capacités\nde type Poison.", "Ein Item zum Tragen. Diese Steintafel\nverstärkt Attacken vom Typ Gift.", "Tabla de piedra que potencia\nlos movimientos de tipo Veneno.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nLastra di pietra che potenzia le mosse\ndi tipo Veleno.", "An item to be held by a Pokémon.\nIt is a stone tablet that boosts the\npower of Poison-type moves.") },
            { PBEItem.TwistedSpoon, new PBELocalizedString("ねんりきを　こめた　スプーン。\nもたせると　エスパータイプの\nわざの　いりょくが　あがる。", "염동력을 담은 숟가락.\n지니게 하면 에스퍼타입\n기술의 위력이 올라간다.", "Objet à tenir. Cuillère contenant un\npouvoir télékinésique montant la\npuissance des capacités de type Psy.", "Ein Item zum Tragen. Ein Löffel voller\ntelekinetischer Energie, der Attacken\nvom Typ Psycho verstärkt.", "Cuchara llena de energía telequinética\nque potencia los movimientos de tipo\nPsíquico. Debe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nCucchiaio dotato di potere telecinetico\nche potenzia le mosse di tipo Psico.", "An item to be held by a Pokémon. It is\na spoon imbued with telekinetic power\nthat boosts Psychic-type moves.") },
            { PBEItem.UltraBall, new PBELocalizedString("スーパーボールよりも　さらに\nポケモンを　つかまえやすくなった\nすごく　せいのうのいい　ボール。", "수퍼볼보다도 더욱\n포켓몬을 잡기 쉬워진\n매우 성능이 좋은 볼.", "Une Ball ultraperformante dont le taux\nde réussite est supérieur à celui de la\nSuper Ball.", "Ein Ball mit hoher Erfolgsquote.\nDem Superball in allen\nPunkten überlegen.", "Poké Ball de rendimiento superior.\nTiene un índice de éxito mayor\nal de la Superball.", "Ball dalle prestazioni eccellenti:\nfornisce maggiori probabilità di\ncattura rispetto alla Mega Ball.", "An ultra-performance Ball that\nprovides a higher Pokémon catch rate\nthan a Great Ball.") },
            { PBEItem.UpGrade, new PBELocalizedString("なかに　いろんな　じょうほうが\nつまった　とうめいな　きかい。\nシルフ　カンパニーせい。", "안에 여러 가지 정보가\n가득한 투명한 기계다.\n실프주식회사 제품.", "Dispositif transparent rempli de\ndonnées diverses et variées.\nFabriqué par la Sylphe SARL.", "Ein durchsichtiges Gerät. Es\nenthält alle möglichen Daten.\nHergestellt von Silph Co.", "Peculiar dispositivo fabricado en\nSilph S. A. que contiene todo tipo\nde datos.", "Apparecchio trasparente pieno zeppo\ndi dati di ogni tipo. È un prodotto\ndella Silph SpA.", "A transparent device filled with all\nsorts of data.\nIt was produced by Silph Co.") },
            { PBEItem.WacanBerry, new PBELocalizedString("ポケモンに　もたせると\nこうかばつぐんの　でんき　わざを\nうけたとき　いりょくが　よわまる。", "포켓몬에게 지니게 하면\n효과가 뛰어난 전기 기술을\n당했을 때 위력이 약해진다.", "Tenue, cette Baie affaiblit une\nattaque ennemie super efficace\nde type Électrik.", "Als getragenes Item schwächt diese\nBeere besonders effektive Attacken\ndes Angreifers vom Typ Elektro.", "Si la lleva un Pokémon, debilita un\nataque supereficaz de tipo Eléctrico\nde un enemigo.", "Se tenuta da un Pokémon, indebolisce\nl'attacco superefficace di tipo Elettro\ndel nemico.", "Weakens a supereffective Electric-type\nattack against the holding Pokémon.") },
            { PBEItem.WaterGem, new PBELocalizedString("みずの　タイプの　ジュエル。\nもたせると　いちどだけ　みずの\nわざの　いりょくが　つよまる。", "물타입의 주얼.\n지니게 하면 한 번만 물\n기술의 위력이 강해진다.", "Objet à tenir. Joyau augmentant\nune fois la puissance des capacités\nde type Eau.", "Ein Juwel vom Typ Wasser. Es\nerhöht einmalig die Stärke\neiner Attacke vom Typ Wasser.", "Joya de un solo uso que potencia\nlos movimientos de tipo Agua.\nDebe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nGioiello che potenzia una sola volta\nle mosse di tipo Acqua.", "A gem with an essence of water. When\nheld, it strengthens the power of a\nWater-type move only once.") },
            { PBEItem.WaterStone, new PBELocalizedString("ある　とくていの　ポケモンを\nしんかさせる　ふしぎな　いし。\nすんだ　みずいろを　している。", "어느 특정한 포켓몬을\n진화시키는 이상한 돌.\n맑고 푸른 색을 띠고 있다.", "Une pierre étrange qui fait évoluer\ncertaines espèces de Pokémon.\nElle est de couleur bleue.", "Dieser spezielle Stein löst bei\nbestimmten Pokémon die Entwicklung\naus. Er ist hellblau.", "Curiosa piedra que hace evolucionar\na determinadas especies de Pokémon.\nEs de color azul.", "Pietra particolare che fa evolvere\ndeterminate specie di Pokémon.\nHa delle macchioline azzurre.", "A peculiar stone that makes certain\nspecies of Pokémon evolve.\nIt is a clear, light blue.") },
            { PBEItem.WatmelBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.WaveIncense, new PBELocalizedString("ふしぎな　かおりの　おこう。\nもたせると　みずタイプの\nわざの　いりょくが　あがる。", "이상한 향기의 향로.\n지니게 하면 물타입\n기술의 위력이 올라간다.", "Objet à tenir. Encens au parfum\nexotique augmentant la puissance\ndes capacités de type Eau.", "Ein Item zum Tragen. Sein\nausgefallenes Aroma verstärkt\nAttacken vom Typ Wasser.", "Incienso que tiene un curioso aroma\nque potencia los movimientos de\ntipo Agua. Debe llevarlo un Pokémon.", "Strumento da far tenere a un Pokémon.\nIncenso dal curioso aroma che potenzia\nle mosse di tipo Acqua.", "An item to be held by a Pokémon. It is\nincense with a curious aroma that\nboosts the power of Water-type moves.") },
            { PBEItem.WepearBerry, new PBELocalizedString("イッシュちほうでは　とても\nめずらしい　きのみ。\nマニアが　たかく　かってくれる。", "하나지방에서는 매우\n희귀한 나무열매.\n마니아가 비싼 값에 산다.", "Baie très rare à Unys.\nLes amateurs en donnent un bon prix.", "Eine Beere, die nur äußerst selten\nin der Einall-Region zu finden ist.\nKenner geben ihr letztes Hemd dafür.", "Una Baya muy rara en la región\nde Teselia. Muy apreciada\npor los gourmets.", "Bacca molto rara a Unima.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A Berry which is very rare in the\nUnova region.\nA maniac will buy it for a high price.") },
            { PBEItem.WhiteFlute, new PBELocalizedString("しろい　ガラスで　できている\nおもちゃの　ふえ。\nマニアが　たかく　かってくれる。", "하얀 유리로 만들어진\n장난감 피리.\n마니아가 비싼 값에 산다.", "Jouet en forme de flûte en verre\nblanc. Les amateurs en donnent\nun bon prix.", "Eine Spielzeugflöte aus weißem Glas.\nSammler geben ihr letztes Hemd dafür\nher.", "Una flauta de juguete hecha de\ncristal blanco.\nMuy valorada por los coleccionistas.", "Flauto giocattolo fatto di vetro bianco.\nSi può vendere a un prezzo piuttosto\nalto agli intenditori.", "A toy flute made from white glass.\nA maniac will buy it for a high price.") },
            { PBEItem.WhiteHerb, new PBELocalizedString("もたせた　ポケモンの　のうりょくが\nさがったとき　いちどだけ　もとの\nじょうたいに　もどす。", "지니게 한 포켓몬의 능력이\n떨어졌을 때 한 번만 원래\n상태로 돌아온다.", "Objet à tenir qui restaure les stats\nbaissées. Ne peut être utilisé qu'une\nfois.", "Ein Item, das einem Pokémon zum Tragen\ngegeben werden kann. Es hebt einmalig\njede negative Statusveränderung auf.", "Restaura cualquier característica\nreducida en combate. Solo se puede\nusar una vez. Debe llevarla un Pokémon.", "Strumento da far tenere a un Pokémon.\nRipristina le statistiche ridotte in\nlotta. Si può usare una sola volta.", "An item to be held by a Pokémon.\nIt restores any lowered stat in\nbattle. It can be used only once.") },
            { PBEItem.WhtApricorn, new PBELocalizedString("しろい　ぼんぐり。\nなんの　においも　しない。\n", "하얀색의 규토리.\n어떠한 향기도 나지 않는다.\n", "Un Noigrume de couleur blanche.\nIl ne dégage aucun arôme.", "Eine weiße Aprikoko.\nRiecht nach überhaupt nichts.", "Bonguri de color blanco que no\ndesprende aroma alguno.", "Una Ghicocca di colore bianco.\nNon emana alcun odore.", "A white Apricorn.\nIt doesn't smell like anything.") },
            { PBEItem.WideLens, new PBELocalizedString("ものが　おおきく　みえる　レンズ。\nもたせると　わざの　めいちゅうりつが\nすこし　あがる。", "물건이 크게 보이는 렌즈.\n지니게 하면 기술의 명중률이\n조금 올라간다.", "Objet à tenir. Magnifique loupe\naugmentant un peu la Précision\ndes capacités.", "Ein Item zum Tragen.\nDieses Vergrößerungsglas\nerhöht die Genauigkeit etwas.", "Debe llevarla un Pokémon.\nAumenta ligeramente la Precisión de\nlos movimientos.", "Strumento da far tenere a un Pokémon.\nLente d'ingrandimento che aumenta\ndi poco la precisione delle mosse.", "An item to be held by a Pokémon.\nIt is a magnifying lens that slightly\nboosts the accuracy of moves.") },
            { PBEItem.WikiBerry, new PBELocalizedString("ポケモンに　もたせると\nピンチのとき　\xFF28\xFF30を　かいふくする。\nきらいな　あじだと　こんらん　する。", "포켓몬에게 지니게 하면\n위급할 때 HP를 회복한다.\n싫어하는 맛이면 혼란에 빠진다.", "Tenue, cette Baie restaure des PV en\ncas de besoin, mais rend confus si\nle Pokémon n'aime pas son goût.", "Als getragenes Item füllt die Beere die\nKP eines Pokémon auf, doch ihr Aroma\nverwirrt jene, die es nicht mögen.", "Los Pokémon pueden llevarla o usarla\npara restaurar algunos PS. Su sabor\npuede causar confusión.", "Se tenuta da un Pokémon in difficoltà,\nrestituisce PS. Se al Pokémon non piace\nil suo sapore, può causare confusione.", "If held by a Pokémon, it restores the\nuser's HP in a pinch, but will cause\nconfusion if it hates the taste.") },
